INSERT INTO `hymn` (`id`, `musical_part_id`, `title`, `num`, `ref`, `lyrics`) VALUES
(1, NULL, 'Éternel, c’est à Toi', '1', 'Ps 28', 'Éternel, c’est à Toi que je crie.<br>Mon rocher ne reste pas sourd !<br>Ne T’éloigne pas sans me répondre,<br>Je serais semblable à ceux<br>Qui descendent dans la fosse !<br><br>Quand je crie à Toi,<br>Quand j’élève les mains vers ton sanctuaire<br>Écoute la voix de mes supplications !<br>Éternel, c’est à Toi que je crie<br>Mon rocher ne reste pas sourd !<br>Ne T’éloigne pas sans me répondre,<br>Je serais semblable à ceux<br>Qui descendent dans la fosse !<br><br>Béni soit le Nom de l’Éternel,<br>Car Il exauce ma voix !<br>Il est ma force et mon bouclier,<br>En lui mon cœur se confie<br>Et je suis secouru.<br>J’ai de l’allégresse dans le cœur,<br>Et je le loue par mes chants.<br>Il est la force de son peuple,<br>Le rocher des délivrances, des délivrances !'),
(2, NULL, 'La solitude', '2', 'Es 35', '<span style=''padding-left:15px''></span>La solitude s’égaiera,<br><span style=''padding-left:15px''></span>Comme un narcisse fleurira<br><span style=''padding-left:15px''></span>Et couverte de belles fleurs,<br><span style=''padding-left:15px''></span>Elle tressaillira de bonheur<br><br>Car des eaux couleront dans le désert,<br>Et des ruisseaux dans la solitude.<br>Le mirage se changera en étang,<br>La terre desséchée en sources d’eaux.<br><br>Il y aura un chemin tout tracé,<br>Une route appelée la voie sainte,<br>Les saints de l’Éternel y marcheront,<br>Les insensés ne pourront s’égarer.<br><br>L’allégresse et la joie s’approcheront,<br>Douleurs, gémissements s’enfuiront.<br>Comme un fleuve sera tout leur bonheur,<br>L’éternelle joie les couronnera.'),
(3, NULL, 'Pourquoi crains-tu, mon âme ?', '3', 'Ca 50', 'Pourquoi crains-tu, mon âme ? Au fort de la souffrance,<br>Le Seigneur n’est-il pas ton appui, ton soutien ?<br>Élève en haut les yeux; Il est ta délivrance.<br>Il ne te laisse pas : mon âme, ne crains rien !<br><br>Qu’il me faille affronter tourments, combats, épreuves,<br>Passer par le creuset où l’on affine l’or,<br>Entrer dans la fournaise ou traverser les fleuves,<br>Il reste mon Sauveur, mon guide, mon trésor.<br><br>Non, je ne craindrai rien ! Ni Satan, ni le monde<br>Ne peuvent me ravir des bras du Bon Berger.<br>Là je savoure en paix sa charité profonde.<br>Là je suis pour toujours à l’abri du danger.'),
(4, NULL, 'Jésus, divin modèle', '4', 'CV 31B', '<span style=''padding-left:15px''></span>Jésus, divin modèle de douceur et de paix,<br><span style=''padding-left:15px''></span>Ta sainte voix m’appelle à marcher, désormais<br><span style=''padding-left:15px''></span>Dans ce sentier de grâce, de support, de bonté,<br><span style=''padding-left:15px''></span>Où tu laissas la trace de ton humilité.<br><br>Tu passas sur la terre, dans l’ineffable amour,<br>Que toute ta carrière a montré chaque jour.<br>Tu nous dis : "Je vous aime, nul n’aime comme moi.<br>Enfants, faites de même, car l’amour est ma loi".<br><br>Que mon esprit se range à cette loi d’amour<br>Pour vivre à ta louange, d’un cœur droit sans détour<br>Comme Toi, charitable, patient, humble et doux<br>Saint, pur, irréprochable, faisant du bien à tous'),
(5, NULL, 'Seigneur, je veux te suivre', '5', '', 'Seigneur, je veux Te suivre, et sans impatience,<br>Le front toujours serein, ne regarder qu''à Toi.<br>Trouver dans cette épreuve un peu plus de science<br>Pour vivre de ta grâce et marcher par la foi.<br><br><span style=''padding-left:15px''></span>Viens donc mettre une fin au charnel ministère<br><span style=''padding-left:15px''></span>Qui me barre l’accès des œuvres de l’Esprit.<br><span style=''padding-left:15px''></span>J’accepte de la croix le souverain cautère.<br><span style=''padding-left:15px''></span>J’accepte tout, Seigneur, je veux être guéri.<br><br>Je ne veux plus subir le joug de l’habitude,<br>Ni croire que l’Esprit a cessé d’inspirer.<br>Je ne veux redouter aucune certitude,<br>Ni craindre qu’avec Toi je puisse m’égarer.<br><br>Je ne veux surtout pas avoir le bruit de vivre<br>Sans les fleuves d’eau vive et la fécondité.<br>Je ne veux pas qu’on pense en me voyant te suivre<br>Qu’en vain je me prévaux de ta fidélité.'),
(6, NULL, 'Rendez grâces avec joie', '6', 'Cl 1', '<span style=''padding-left:15px''></span>Rendez grâces avec joie, rendez grâces avec joie<br><span style=''padding-left:15px''></span>Au Père, au Père, qui vous a rendus capables<br><span style=''padding-left:15px''></span>D’avoir part, d’avoir part à l’héritage<br><span style=''padding-left:15px''></span>Des saints dans la lumière !<br><br>Il nous a délivrés de la puissance des ténèbres.<br>Il nous a transportés dans le royaume de son Fils bien-aimé<br>En qui nous avons la rédemption, le pardon des péchés,<br>Nous, qui par nos œuvres mauvaises, étions autrefois ennemis.<br><br>Dieu a voulu, par Lui, tout réconcilier avec Lui-même,<br>Faisant la paix par Lui.<br>Oui, la paix par Lui, par le sang de Sa croix,<br>Pour nous faire paraître devant Lui sans reproche et saints,<br>Si du moins nous demeurons fondés, inébranlables dans la foi.'),
(7, NULL, 'Quand je contemple', '7', 'Ps 8', '<span style=''padding-left:15px''></span>Quand je contemple les cieux, ouvrage de tes mains,<br><span style=''padding-left:15px''></span>La lune et les étoiles que tu as créées,<br><span style=''padding-left:15px''></span>Qu’est-ce que l’homme, pour que tu te souviennes de lui ?<br><span style=''padding-left:15px''></span>Et le fils de l’homme<br><span style=''padding-left:15px''></span>Pour que tu prennes garde à lui ? (fin : bis)<br><br>Tu l’as fait de peu inférieur à Toi.<br>Tu l’as couronné de gloire.<br>Tu lui as donné la domination.<br>Tu as tout placé sous ses pieds;<br>Les brebis et les bœufs, les animaux des champs,<br>Tous les oiseaux du ciel, les poissons de la mer.<br><br>Seigneur, mon rocher, mon libérateur,<br>Que ton nom est magnifique !<br>Dans les cieux s’élève ta majesté,<br>Ils racontent ta puissance.<br>Tu confonds l’ennemi, tu fermes sa bouche.<br>Tu brises sa tête; il demeure vaincu.'),
(8, NULL, 'Dieu est ma délivrance', '8', 'Es 12', 'Dieu est ma délivrance, je serai plein de confiance.<br>Je ne craindrai donc rien.<br>L''Éternel est ma force et le sujet de mes louanges.<br>C’est Lui qui m’a sauvé.<br>Vous irez puiser de l’eau avec joie aux sources du salut (bis)<br>Louez l''Éternel, invoquez son nom !<br>Publiez ses œuvres parmi les nations !<br>Poussez des cris de joie, héritiers du pardon !'),
(9, NULL, 'Bienheureux ceux qui placent', '9', 'Ps 84', '<span style=''padding-left:15px''></span>Bienheureux ceux qui placent en Toi leur appui !<br><span style=''padding-left:15px''></span>Ils trouvent dans leur cœur des chemins tout tracés.<br><span style=''padding-left:15px''></span>Leur force toujours augmente pendant la marche.<br><br>Oh ! Que tes demeures sont aimables.<br>Eternel, mon âme soupire, languit de tes parvis.<br>Mon cœur et ma chair poussent des cris vers mon Dieu.<br>Éternel, mon Dieu, écoute donc ma prière !<br><br>Le passereau lui-même trouve une maison.<br>L’hirondelle un nid pour y mettre ses petits.<br>Heureux sont tous ceux qui habitent ta maison !<br>Ils peuvent te célébrer encore et toujours.<br><br>Mon Dieu est un soleil et un sûr bouclier.<br>Il ne refuse aucune bonté aux intègres.<br>L’Éternel dispense la grâce et la gloire.<br>Heureux celui qui met en Toi sa confiance !<br><br>Mieux vaut un jour dans tes parvis que mille ailleurs.<br>Je préfère rester au seuil de ta maison,<br>Plutôt que d’habiter le palais des méchants.<br>Car dans ta présence, ô Dieu, j’aime me trouver !'),
(10, NULL, 'Avec des cris de joie', '10', 'Ps 63', '<span style=''padding-left:15px''></span>Avec des cris de joie ma bouche chantera,<br><span style=''padding-left:15px''></span>J’élèverai mes mains et te célébrerai.<br><span style=''padding-left:15px''></span>(bis)<br><br>Ô Dieu, tu es mon Dieu, mon âme te recherche<br>Et soupire après toi sur la terre desséchée.<br><br>Lorsque je pense à toi, esseulé sur ma couche,<br>Je médite sur Toi les veilles de la nuit.<br><br>Je connais le bonheur à l’ombre de tes ailes,<br>Car tu es mon secours, ta droite me soutient.<br><br>J’aime te contempler quand dans ton sanctuaire<br>Ta gloire resplendit, ta puissance se révèle.'),
(11, NULL, 'Je louerai l''Éternel', '11', 'Ps 9', 'Je louerai l''Éternel de tout mon cœur,<br>Je raconterai tout, toutes Tes merveilles,<br>Je ferai de Toi le sujet de ma joie et de mon allégresse.<br>Je chanterai ton nom, Dieu Très-Haut, Dieu Très-Haut !'),
(12, NULL, 'Aujourd’hui, c’est encore un jour', '12', 'CGG 77', 'Aujourd’hui, c''est encore un jour où Dieu fait grâce,<br>Où son appel d’amour te convie à la croix.<br>C’est le jour du salut: près de toi, Jésus passe.<br>Oh ! Réponds, sans tarder, à sa divine voix.<br><br>Aujourd’hui, c’est encore un jour où la lumière<br>Peut descendre du ciel jusqu’au fond de ton cœur;<br>Laisse-Lui révéler ta profonde misère,<br>Et viens la déposer à la croix du Sauveur.<br><br>Aujourd’hui, c’est encore un jour où Dieu délivre,<br>Où de l’esclave Il fait un fils de Sa maison.<br>Désormais, si tu veux, libre tu pourras vivre,<br>Car Jésus a payé le prix de ta rançon.<br><br>Aujourd’hui, retentit encore le beau message<br>De la grâce de Dieu, de son immense amour.<br>Si tu veux croire enfin, le divin héritage<br>Sera ta part heureuse au céleste séjour.'),
(13, NULL, 'Mon Dieu sonde-moi', '13', 'Ps', 'Mon Dieu, sonde-moi et connais mon cœur;<br>Éprouve-moi et connais mes pensées !<br>Et si je suis sur la mauvaise voie,<br>Conduis-moi donc sur la voie de l’éternité !<br><br><span style=''padding-left:15px''></span>C’est vers Toi, Éternel, que se tournent mes yeux,<br><span style=''padding-left:15px''></span>C’est auprès de Toi que je cherche un refuge,<br><span style=''padding-left:15px''></span>N’abandonne pas mon âme ! (bis)<br><span style=''padding-left:15px''></span>(Car aucun vivant n’est juste devant Toi.) (À la fin)<br><br>Éternel, mets une garde à ma bouche,<br>Oh veille sur la porte de mes lèvres !<br>Préserve mon cœur des choses mauvaises,<br>Ainsi que des actions qui déshonorent ton nom !'),
(14, NULL, 'Mon âme, apporte au Seigneur', '14', '', 'Mon âme, apporte au Seigneur ta prière avec ferveur.<br>Puisqu’il t’invite à prier, il ne peut que t’écouter.<br>Tu ne peux trop demander, car Dieu peut tout te donner.<br>Oui, pour la gloire de son nom et l’honneur de sa maison !<br><br><span style=''padding-left:15px''></span>Toi, qui t’approches du Roi,<br><span style=''padding-left:15px''></span>Sans cesse rappelle-toi<br><span style=''padding-left:15px''></span>Que tout-puissant est ton Dieu<br><span style=''padding-left:15px''></span>Et qu’il fait tout ce qu’il veut<br><br>Souviens-toi que le ruisseau de l’Éternel est plein d’eau,<br>Oui, d’une eau pure qui jaillit, vivifie et rafraîchit.<br>D’abord, ôte de mon cœur mon péché, ô mon Sauveur !<br>Libère-moi, par ton sang, de ce fardeau si pesant !<br><br>Révèle en moi ton pouvoir, à chaque instant fais-moi voir<br>Tout ce que tu veux de moi. Augmente, augmente ma foi !<br>Nourris-moi de ton amour, que j’en vive chaque jour !<br>Oui, sois mon guide, mon gardien. Oh ! Tiens ma main dans ta main !'),
(15, NULL, 'Le ciel était voilé', '15', 'AF 504', 'Le ciel était voilé, la route était obscure.<br>Voyageur désolé, j’errais à l’aventure.<br>Chaque arbre du chemin était une menace.<br>Et je cherchais en vain la porte de la Grâce.<br><br>Enfin las et transi, je tombai sur la route<br>En disant : "C’est ici que je mourrai sans doute !",<br>Quand un rayon des cieux, pour moi perçant l’espace,<br>Vint montrer à mes yeux la porte de la Grâce.<br><br>Mais je doutais encore, se peut-il, ô merveille,<br>Que seule quand tout dort, la Grâce toujours veille ?<br>"Pitié ! Je vais mourir !" murmurai-je à voix basse.<br>Et je la vis s’ouvrir, la porte de la Grâce.<br><br>Et je vis sur le seuil, debout dans la lumière<br>Jésus ! Quel doux accueil il fit à ma misère !<br>"Je t’attendais : pourquoi, dit-il, pauvre âme lasse<br>Viens-tu si tard ? C’est moi, c’est moi qui suis la Grâce.<br>Pourquoi viens-tu si tard ? C’est moi qui suis la Grâce".'),
(16, NULL, 'Ô mes fils, cessez d’être négligents', '16', '2 Cr 29;Hb 6', '<span style=''padding-left:15px''></span>Ô mes fils, cessez donc d’être si négligents,<br><span style=''padding-left:15px''></span>Car vous avez été choisis par l’Éternel<br><span style=''padding-left:15px''></span>Pour vous tenir à son service devant Lui. (bis)<br><br>Cette espérance, vous la possédez déjà<br>Comme une ancre de l’âme, sûre et solide.<br>N’abandonnez pas cette entière assurance.<br>C’est à elle qu’est attachée la rémunération !'),
(17, NULL, 'Chantez à l’Éternel', '17', 'Es 42', 'Chantez à l’Éternel,<br>Chantez à l’Éternel un cantique nouveau<br>(bis)<br><br>Chantez Ses louanges, célébrez son nom<br>Jusqu’aux extrémités de la terre !<br>Du sommet des montagnes poussez des cris de joie,<br>Et rendez gloire à l’Éternel !'),
(18, NULL, 'Maudit soit l’homme', '18', 'Jr 17', 'Maudit soit l’homme qui se confie dans l’homme,<br>Qui prend la chair pour son appui,<br>Et qui détourne son cœur de l’Éternel !<br>Tout comme un misérable dans le désert,<br>Il ne voit point arriver le bonheur.<br>Il habite les lieux brûlés du désert,<br>Une terre salée, sans habitants.<br><br>Béni soit l’homme qui se confie en Jésus,<br>Dont l’Éternel est l’espérance,<br>Et qui recherche le Seigneur l’Éternel !<br>Il est comme un arbre planté près des eaux<br>Qui étend ses racines vers le courant.<br>Il n’aperçoit point la chaleur quand elle vient,<br>Et il ne cesse de porter du fruit.'),
(19, NULL, 'Vous qui passez par la souffrance', '19', '', 'Vous qui passez par la souffrance,<br>Meurtris sous les coups du malheur,<br>Criez au Dieu de délivrance;<br>Il apaisera vos douleurs.<br><br><span style=''padding-left:15px''></span>Cette lumière, c’est la Parole<br><span style=''padding-left:15px''></span>Du Dieu d’amour et de bonté.<br><span style=''padding-left:15px''></span>Oui, c’est par elle qu’il nous console.<br><span style=''padding-left:15px''></span>Elle seule est la vérité.<br><br>Ne restez pas dans la nuit sombre,<br>Accablés dans votre chagrin.<br>Une lumière luit dans l’ombre<br>Pour éclairer votre chemin.<br><br>N’oubliez pas que Dieu vous aime<br>Et que, pour vous, il a donné<br>Pour vous sauver, son Fils lui-même,<br>Mort sur la croix pour nos péchés.<br><br>Apportez-lui votre détresse.<br>Ne doutez pas de son amour.<br>Il est fidèle à sa promesse.<br>Comptez sur lui de jour en jour.'),
(20, NULL, 'Il n’avait ni beauté', '20', 'Es 53', 'Il n’avait ni beauté, aucun éclat,<br>Non, rien qui puisse attirer nos regards.<br>Son aspect n’avait rien, rien pour nous plaire.<br>Il fut l’homme de douleur et souffrant.<br><br><span style=''padding-left:15px''></span>Pourtant ce sont nos souffrances,<br><span style=''padding-left:15px''></span>Nos souffrances qu’il porta.<br><span style=''padding-left:15px''></span>C’est de nos douleurs qu’il s’est chargé<br><span style=''padding-left:15px''></span>Et le châtiment qui nous donne la paix<br><span style=''padding-left:15px''></span>Oui, qui nous donne la paix,<br><span style=''padding-left:15px''></span>Est tombé sur lui. (ter)<br><br>Nous étions tous errants, tous égarés.<br>Chacun de nous suivait sa propre voie.<br>Nous l’avons dédaigné, oui, méprisé<br>Et nous n’avons fait de lui aucun cas.<br><br>Qui a cru qu’il était frappé par Dieu,<br>Meurtri, brisé, blessé pour nos péchés ?<br>Qui a cru, qui a cru que Dieu, sur lui<br>Faisait retomber nos iniquités ?'),
(21, NULL, 'Je te célèbre', '21', 'Ps 138', 'Je te célèbre de tout mon cœur, je chante tes louanges<br>Je célèbre ton nom à cause de ta bonté.<br>Le jour où je t’ai invoqué, tu m’as exaucé.<br>Tu m’as rassuré et fortifié.<br>Et dans la détresse, tu me rends la vie,<br>Tu t’élèves contre mes ennemis.<br>L’Éternel agira en ma faveur !<br>Éternel, ta bonté dure toujours !<br>Je te célèbre de tout mon cœur, je chante tes louanges.<br>Je célèbre ton nom, à cause de ta bonté.'),
(22, NULL, 'Toi, mon ami, fortifie-toi', '22', '2 Tm 2', 'Toi, mon ami, fortifie-toi dans la grâce.<br>Souffre avec moi comme un soldat de Jésus-Christ.<br>Ne t’embarrasse pas des choses de la vie. (bis)<br><br><span style=''padding-left:15px''></span>Et ce que tu as entendu, confie-le à des fidèles<br><span style=''padding-left:15px''></span>Qui soient capables à leur tour<br><span style=''padding-left:15px''></span>De bien l’enseigner à d’autres.<br><br>Comme l’athlète combat suivant les règles,<br>De même que le laboureur doit travailler,<br>Travaille aussi avant de recueillir les fruits (bis)<br><br>Efforce-toi de te montrer au Père<br>Comme un ouvrier n’ayant point à rougir.<br>Dispense la Parole de la vérité. (bis)<br><br>Fuis les passions que connaît la jeunesse.<br>Recherche la douceur et la charité,<br>La paix avec ceux qui invoquent le Seigneur. (bis)'),
(23, NULL, 'Je lève les yeux', '23', 'Ps 121', '<span style=''padding-left:15px''></span>Je lève les yeux vers les montagnes;<br><span style=''padding-left:15px''></span>D’où me viendra le secours ?<br><span style=''padding-left:15px''></span>Le secours me vient de l’Éternel<br><span style=''padding-left:15px''></span>Qui a fait les cieux et la terre.<br><span style=''padding-left:15px''></span>Je lève les yeux vers les montagnes;<br><span style=''padding-left:15px''></span>D’où me viendra le secours ?<br><br>Il ne permettra point que ton pied chancelle.<br>Celui qui te garde ne sommeillera pas.<br>Voici, il ne sommeille, ni ne dort<br>Celui qui te garde, ô Israël.'),
(24, NULL, 'Si je parlais le plus parfait langage', '24', 'CGG 177;1 Co 13', 'Si je parlais le plus parfait langage,<br>Si même un ange me prêtait le sien,<br>Que me vaudrait ce futile avantage ?<br>Si je n’ai pas l’amour, je ne suis rien !<br>Je ne suis rien qu’un airain qui résonne,<br>Une cymbale au rythme répété.<br>Tout manque encore aux grâces que Dieu donne<br>Si je n’ai pas au cœur l’amour, la charité.<br><br>Et quand j’aurais le don de prophétie,<br>Quand tout secret me serait révélé,<br>Quand j’atteindrais au savoir, au génie,<br>Quand je devrais, martyr, être brûlé,<br>Ou si ma foi transportait la montagne<br>Et ma pitié distribuait mon bien,<br>Quelque mérite en cela que je gagne<br>Sans cet amour, tout ne me servirait de rien.<br><br>La charité se montre patiente<br>Et bienveillante à l’égard du prochain,<br>N’est pas jalouse et non plus ne se vante,<br>Ne s’enfle pas d’orgueil, est sans dédain,<br>Pleine d’égards, ne fait tort à personne,<br>Tout égoïsme est chez elle écarté,<br>Point ne s’irrite et jamais ne soupçonne,<br>Réprouve l’injustice et veut la vérité.<br><br>Que de douceur et de paix elle apporte<br>Car elle excuse tout, elle croit tout !<br>Tout elle espère et tout elle supporte,<br>Mettant son baume et son parfum partout.<br>Les plus beaux dons disparaissent et meurent,<br>Cette vertu vit en éternité.<br>Foi, espérance et charité demeurent,<br>Mais de ces trois, la plus grande est la charité.'),
(25, NULL, 'Ne soyez pas surpris', '25', '1 Pi 2;4', 'Ne soyez pas surpris,<br>Comme d’une chose étrange qui vous arrive,<br>De la fournaise qui est au milieu de vous<br>Pour vous éprouver.<br>Mais réjouissez-vous d’avoir part aux souffrances,<br>Aux souffrances de Christ, afin que vous soyez,<br>Vous soyez vous aussi dans la joie<br>Quand sa gloire apparaîtra ! (bis)<br><br>Supportez la souffrance;<br>Quand vous faites le bien, c’est une grâce,<br>Car c’est à cela que vous avez été, été appelés.<br>Christ a souffert pour vous, il porta sur le bois,<br>Nos péchés en son corps, lui qui n’a pas péché,<br>Et par les meurtrissures duquel<br>Vous avez été guéris. (bis)<br><br>Si pour le nom de Christ<br>Vous êtes outragés, vous êtes heureux<br>Parce que l’Esprit de gloire, l’Esprit de Dieu,<br>Repose sur vous.<br>Oui, réjouissez-vous d’avoir part aux souffrances,<br>Aux souffrances de Christ, afin que vous soyez,<br>Vous soyez vous aussi dans la joie<br>Quand sa gloire apparaîtra ! (bis)'),
(26, NULL, 'La foi, c’est certitude', '26', 'Hb 11', 'La foi, c’est certitude et ferme assurance<br>Des choses qu’on ne voit pas et qu’on espère.<br>C’est la louange et toute la confiance<br>Avant d’avoir vu l’intervention du Père.<br><br>Sans elle, impossible de vraiment lui plaire<br>Car si nous voulons adorer sa personne<br>Il nous faut croire qu’il vit et rémunère<br>Chacun de ceux qui le cherchent et se donnent.<br><br>La raison pourra trouver inadmissible<br>Que Dieu créa le monde par sa Parole.<br>Nous croyons qu’il fut formé par l’invisible.<br>Dieu confond les sages par les choses folles.<br><br>Avertis des choses sans les voir encore,<br>Et saisis d''une crainte respectueuse,<br>Entrons dans l''obéissance qui l''honore<br>Sans écouter nos pensées tumultueuses.'),
(27, NULL, 'Alléluia', '27', 'Lc 1', '<span style=''padding-left:15px''></span>Alléluia, alléluia, alléluia ! (bis)<br><span style=''padding-left:15px''></span>Un Sauveur nous est donné, alléluia, alléluia !<br><br>Un Sauveur qui nous délivre de la main de nos ennemis,<br>Pour permettre à ses enfants de le servir sans rien craindre,<br>Et de marcher ainsi pour lui en sainteté et justice<br>Les jours de leur vie ici-bas. (bis)<br><br>Un Sauveur qui nous éclaire ainsi que ceux qui sont assis<br>Dans les ténèbres de la nuit et dans les ombres de la mort.<br>Il est venu pour diriger chacun des pas de notre vie<br>Dans le seul chemin de la paix. (bis)'),
(28, NULL, 'La trompette sonnera', '28', '1 Co 15;1 Th 4;Ap 7', 'La trompette sonnera, et enfin retentira,<br>Et nous serons changés, enfin, enfin ressuscités.<br>Notre ennemie, la mort sera vaincue, et engloutie dans la victoire<br>Et pour toujours de la gloire céleste nous serons vêtus.<br><br>La trompette sonnera, et enfin retentira,<br>Et le Seigneur viendra du ciel, du ciel il descendra.<br>Sur les nuées nous serons enlevés, transformés à son image.<br>Nous le verrons dans sa magnificence éternellement.<br><br>À Dieu, qui est sur son trône, et au Seigneur Jésus-Christ<br>Soient rendus tout honneur, la gloire, la force et la louange !<br>Devant l’Agneau nous serons jour et nuit dans son temple à son service,<br>Et nous serons conduits par sa présence aux sources de vie.'),
(29, NULL, 'Ils erraient dans l’immense désert', '29', 'Ps 107', 'Ils erraient dans l’immense désert, ils marchaient dans la solitude.<br>Ils souffraient de la faim, de la soif, leur âme était bien languissante.<br>Il leur fit suivre le droit chemin, il satisfit leur âme altérée.<br>Il combla de biens l’âme affamée.<br><br>Ceux qui vivaient captifs, enchaînés à cause de leur cœur révolté,<br>Ceux qu’il a brisés par la souffrance à cause de leur cœur méprisant,<br>Il les fit sortir des ténèbres, il rompit leurs chaînes et leurs liens,<br>Il les délivra de leur misère.<br><br>Par leur conduite coupable, ils s’étaient rendus bien malheureux !<br>Ils touchaient aux portes de la mort, refusant toute nourriture.<br>Il leur envoya sa Parole, il mit sa main sur eux, les guérit;<br>Ils furent retirés de la fosse.<br><br>Éperdus en face du danger, saisis de vertige, titubant,<br>Ils chancelaient tous comme un homme ivre, perdant toute leur habileté.<br>Il fit arrêter la tempête, il apporta le calme et la paix,<br>Les conduisant au port désiré.<br><br>Louez l’Éternel car il est bon, car son amour sera éternel !<br>Qu’ainsi disent tous ses rachetés, ceux qu’il délivra de l’ennemi.<br>Dans leur détresse, ils l’invoquèrent; il répondit à leur prière,<br>De leurs angoisses il les délivra.'),
(30, NULL, 'Ouvre mes yeux', '30', 'Ps 119', '<span style=''padding-left:15px''></span>Ouvre mes yeux pour que je contemple<br><span style=''padding-left:15px''></span>Les merveilles de tes ordonnances<br><span style=''padding-left:15px''></span>Je t’invoque de tout cœur, Éternel,<br><span style=''padding-left:15px''></span>Écoute ma voix selon ta bonté. (bis)<br><br>Ô Dieu, de tout mon cœur je te cherche,<br>Ne me laisse pas égarer loin de toi !<br>Je serre ta Parole dans mon cœur<br>Afin de ne pas pécher contre toi. (bis)<br><br>Ma part est de garder ta Parole.<br>J’éloigne mon pied de tous les mauvais chemins,<br>Ne veux m’écarter de toutes tes lois<br>Car toi seul, Seigneur, tu peux m’enseigner. (bis)<br><br>Ta Parole est une lampe à mes pieds<br>Et une lumière le long de mon sentier.<br>Sa révélation seule nous éclaire,<br>Elle donne aux simples de la science. (bis)'),
(31, NULL, 'Ô mon âme', '31', 'Ps 103', '<span style=''padding-left:15px''></span>Ô mon âme, oui, je veux bénir l’Éternel,<br><span style=''padding-left:15px''></span>Que tout en moi célèbre son saint Nom !<br><span style=''padding-left:15px''></span>Ô mon âme, oui, je veux bénir l’Éternel,<br><span style=''padding-left:15px''></span>N’oublier aucun de ses bienfaits !<br><br>C’est lui qui pardonne toutes tes iniquités,<br>Qui guérit toutes tes maladies.<br>C’est lui qui délivre ton âme de la mort,<br>Qui te couronne de sa charité.<br><br>C’est lui qui rassasie de bienfaits tes vieux jours,<br>Qui te fait rajeunir comme l’aigle.<br>Comme un père prend compassion de tous ses enfants,<br>L’Éternel bénit tous ceux qui le craignent.<br><br>Il est miséricordieux et compatissant;<br>L’Éternel ne conteste pas sans cesse<br>Mais autant les cieux sont au-dessus de la terre,<br>Autant sa bonté est grande pour nous.'),
(32, NULL, 'Va avec la force que tu as', '32', 'Jg 6;Js 1;Jr 1', '<span style=''padding-left:15px''></span>Va, va, va, avec la force que tu as.<br><span style=''padding-left:15px''></span>Je serai Mmi-même avec toi;<br><span style=''padding-left:15px''></span>Fortifie-toi et prends courage !<br><span style=''padding-left:15px''></span>(bis)<br><br>Ne dis pas : "Je suis un enfant".<br>Tu iras vers tous ceux que je te montrerai,<br>Et tu diras tout ce que je t’ordonnerai.<br>Voici, je mets mes paroles dans ta bouche.<br>Je veille sur ma Parole pour l’exécuter.'),
(33, NULL, 'À force de marcher', '33', 'Es 57', '<span style=''padding-left:15px''></span>À force de marcher, tu te fatigues<br><span style=''padding-left:15px''></span>Et tu ne dis pas :" j’y renonce !"<br><span style=''padding-left:15px''></span>Tu trouves encore de la vigueur dans ta main;<br><span style=''padding-left:15px''></span>Aussi n’es-tu pas abattu.<br><br>Et qui redoutais-tu, qui craignais-tu<br>Pour que tu ne te souviennes plus de moi ?<br>Quand tu crieras, qui te délivrera ?<br>Tes œuvres ne te profiteront pas. (bis)<br><br>Mais moi, je suis avec le cœur contrit<br>Afin de ranimer l’esprit brisé.<br>Je ne veux pas contester à toujours<br>Quand les âmes tombent en défaillance. (bis)<br><br>J’ai vu ses voies et je le guérirai.<br>Sur la voie je lui servirai de guide.<br>Je le consolerai et ceux qui pleurent,<br>Je mettrai la louange sur les lèvres. (bis)'),
(34, NULL, 'Je répandrai une eau pure', '34', 'Ez 36', '<span style=''padding-left:15px''></span>Je répandrai une eau pure, et vous serez purifiés.<br><span style=''padding-left:15px''></span>Je laverai vos souillures et briserai vos idoles.<br><span style=''padding-left:15px''></span>Je donnerai un cœur nouveau,<br><span style=''padding-left:15px''></span>Mettrai en vous l’Esprit nouveau !<br><span style=''padding-left:15px''></span>J''ôterai de votre corps le cœur de pierre<br><span style=''padding-left:15px''></span>Pour vous donner un cœur de chair.<br><span style=''padding-left:15px''></span>Je mettrai mon Esprit en vous.<br><span style=''padding-left:15px''></span>Je ferai que vous suiviez mes ordonnances,<br><span style=''padding-left:15px''></span>Et que vous observiez et pratiquiez mes lois.<br><br>Alors vous vous souviendrez de votre conduite coupable,<br>De vos actions qui n’étaient pas bonnes,<br>Vous vous prendrez vous-mêmes en dégoût<br>À cause de vos iniquités.'),
(35, NULL, 'Je chante la justice', '35', 'Ps 101', '<span style=''padding-left:15px''></span>Je chante la justice et la bonté.<br><span style=''padding-left:15px''></span>C''est à toi, Éternel, que je chante.<br><span style=''padding-left:15px''></span>Je prends garde à la voie droite.<br><span style=''padding-left:15px''></span>Quand viendras-tu à moi ?<br><br>Je marche dans la vérité de mon cœur<br>Au milieu de ma maison.<br>Je ne mets rien de mauvais devant mes yeux.<br>Je hais la conduite des pécheurs.<br>Je ne veux pas connaître le méchant,<br>Celui qui calomnie son prochain en secret.<br>J''ai les yeux sur les fidèles du pays<br>Pour qu''ils demeurent auprès de moi.'),
(36, NULL, 'Fais-moi dès le matin', '36', 'Ps 143', '<span style=''padding-left:15px''></span>Fais-moi dès le matin entendre ta bonté.<br><span style=''padding-left:15px''></span>Fais-moi connaître le chemin où je dois marcher.<br><span style=''padding-left:15px''></span>Car je me confie en toi; conduis-moi dans tes voies !<br><br>Éternel, écoute ma prière,<br>Prête l’oreille à mes supplications.<br>L’ennemi poursuit mon âme,<br>Il foule à terre ma vie.<br>J’habite dans les ténèbres<br>Comme ceux qui sont morts depuis longtemps. (bis)'),
(37, NULL, 'Les terres d’un homme riche', '37', 'Lc 12;Mt 16', 'Les terres d’un homme riche lui avaient beaucoup rapporté.<br>Il raisonna en lui-même, se disant : "Que ferai-je donc ?<br>Voici : j’abattrai mes greniers et j’en bâtirai de plus grands,<br>J’amasserai ma récolte et j’y poserai tous mes biens.<br>Alors je me dirai : mon âme, réjouis-toi, repose-toi.<br>Tu peux manger, boire et dormir, devant toi s’ouvre cette vie."<br><br><span style=''padding-left:15px''></span>Cette nuit-là, Dieu lui parla :"insensé, aujourd’hui-même<br><span style=''padding-left:15px''></span>Je rappellerai ton âme, et ce que tu as préparé<br><span style=''padding-left:15px''></span>À qui sera-t-il donc donné, à qui sera-t-il donc donné ?"<br><br>Que servirait-il à l’homme de gagner l’or du monde entier,<br>S’il perdait un jour son âme, sa fortune et toute sa gloire ?<br>Ou que pourrait donner cet homme, en échange de cette vie ?<br>N’est-elle pas plus que l’habit, la nourriture et l’argent ?<br>Ne t’amasse pas ces trésors, ceux qui rouillent et qui ternissent,<br>Amasse-les donc dans le ciel, un jour, tu les retrouveras.<br><br><span style=''padding-left:15px''></span>Cette nuit, Dieu peut te parler : "Insensé, aujourd’hui-même<br><span style=''padding-left:15px''></span>Je puis rappeler ton âme, et ce que tu as préparé<br><span style=''padding-left:15px''></span>À qui sera-t-il donc donné, à qui sera-t-il donc donné ?"<br><span style=''padding-left:15px''></span><br><span style=''padding-left:15px''></span>Coda :<br><span style=''padding-left:15px''></span>Il en est ainsi de celui qui amasse pour lui-même,<br><span style=''padding-left:15px''></span>Et qui n’est pas riche pour Dieu. (ter)'),
(38, NULL, 'Je t''exalte, ô seigneur', '38', 'Ps 29;30', 'Je t''exalte, ô Seigneur, car tu m''as relevé.<br>Oui, j''ai crié à toi et tu m''as soulagé.<br>Dans ma sécurité, je crus ne point broncher.<br>Alors je fus troublé.<br>Éternel, j''ai crié à toi, j''ai imploré ton nom.<br>Secours-moi, aie pitié de moi !<br><br><span style=''padding-left:15px''></span>Tu délias mon sac et me ceignis de joie<br><span style=''padding-left:15px''></span>Afin que mon cœur muet te chante.<br><span style=''padding-left:15px''></span>Mon Dieu, je te louerai toujours ! (bis)<br><br>L''Éternel est vivant, il est majestueux,<br>Sa voix brise les cœurs, enfante le bonheur.<br>Il bénit ses enfants et de son bras puissant<br>Il règne sur son trône.<br>Rendez honneur à l''Éternel ! Oui gloire soit à Dieu !<br>Tous les peuples prosternez-vous !'),
(39, NULL, 'Je devance les veilles', '39', 'Ps 119;Hb 6', '<span style=''padding-left:15px''></span>Je devance les veilles, j''ouvre les yeux pour lire ta Parole.<br><span style=''padding-left:15px''></span>Je devance l''aurore, et je crie, j''espère en tes promesses.<br><br>C''est par la foi et par la persévérance<br>Que nous hériterons des promesses.<br>Conservons jusqu''à la fin cette espérance.<br>Ayons du zèle, non de la paresse.'),
(40, NULL, 'J''ai cherché l''Éternel', '40', 'Ps 34', '<span style=''padding-left:15px''></span>J''ai cherché l''Éternel, et il m''a répondu,<br><span style=''padding-left:15px''></span>Il m''a délivré de toutes mes frayeurs.<br><span style=''padding-left:15px''></span>J''ai cherché l''Éternel, et il m''a répondu,<br><span style=''padding-left:15px''></span>Je le bénirai en tout temps, en tout temps.<br><br>L''Éternel est près de ceux qui ont le cœur brisé,<br>Et il sauve ceux qui ont un esprit abattu.<br>Et quand les justes crient, l''Éternel les entend.<br>Ceux qui l''ont pour refuge échappent au châtiment.<br><br>Recherche et poursuis la paix, éloigne-toi du mal.<br>Cherche en lui ton refuge et tu seras heureux.<br>Et si tu crains l''Éternel, rien ne te manquera;<br>Au jour de la détresse il te délivrera.'),
(41, NULL, 'L''éternel est ma lumière', '41', 'Ps 25;27;28;30', '<span style=''padding-left:15px''></span>L''Éternel est ma lumière, de qui aurais-je crainte ?<br><span style=''padding-left:15px''></span>L''Éternel est mon salut, de qui aurais-je peur ?<br><br>Il voit ma misère et ma peine, et il pardonne tous mes péchés.<br>II m''a guéri, m''a relevé, m''a affermi, m''a ceint de joie.<br><br>II enseigne aux humbles la voie, espère en lui, fortifie-toi !<br>En mon Dieu mon cœur se confie, et en lui je suis secouru.<br><br>Ô Dieu, j''élève à toi mon âme, conduis-moi dans ta vérité !<br>Et enseigne-moi tes sentiers ! Je mets en toi mon espérance.'),
(42, NULL, 'Je reconnais que tu peux tout', '42', 'Jb 42', 'Je reconnais que tu peux tout, rien ne s''oppose à tes pensées.<br>Quel est celui qui a la folie d''obscurcir mes desseins ?<br>Oui, j''ai parlé, sans les comprendre<br>De merveilles qui me dépassent.<br>Oui, j''ai parlé, sans les comprendre<br>De merveilles que je ne conçois pas.<br><br>Écoute-moi, et je parlerai,<br>Je t''interrogerai, tu m''instruiras.<br>(bis)<br><br>Mon oreille avait entendu parler de toi.<br>Mais maintenant mon œil t''a vu, (bis)<br>C''est pourquoi je me repens. (bis)'),
(43, NULL, 'Oui, c''est par grâce', '43', 'Ep 2', '<span style=''padding-left:15px''></span>Oui, c''est par grâce que je suis sauvé (bis)<br><span style=''padding-left:15px''></span>Par le moyen de la foi.<br><span style=''padding-left:15px''></span>Oui, c''est par grâce que je suis sauvé.<br><br>Cela ne vient pas de moi,<br>C''est le don de Dieu,<br>C''est le don de Dieu<br>(bis)<br><br>Dieu conduit dans les œuvres<br>Déjà préparées,<br>Déjà préparées.<br>(bis)'),
(44, NULL, 'N''oublie pas', '44', 'Hb 12;Lm 3', '<span style=''padding-left:15px''></span>N''oublie pas, n''oublie pas<br><span style=''padding-left:15px''></span>Que Dieu t''aime comme un fils.<br><span style=''padding-left:15px''></span>N''oublie pas, n''oublie pas<br><span style=''padding-left:15px''></span>Que Dieu t''aime et te châtie.<br><br>Mon fils ne méprise pas,<br>(non mais crois !)<br>Supporte le châtiment,<br>(non mais crois !)<br>Ne te décourage pas<br>(non mais crois !)<br>Quand ton père te reprend.<br><br>Dieu te traite comme un fils,<br>(oh, oui crois !)<br>Il te châtie pour ton bien.<br>(oh, oui crois !)<br>Il veut te donner la vie,<br>(oh, oui crois !)<br>Confie-toi et ne crains rien.<br><br>Tu te tiendras silencieux,<br>(oh, oui crois !)<br>Solitaire puisqu''il le veut,<br>(oh, oui crois !)<br>La bouche dans la poussière<br>(oh, oui crois !)<br>Sans perdre toute espérance.<br><br>Fortifie donc tes mains<br>(oh, oui crois !)<br>Et tes genoux affaiblis.<br>(oh, oui crois !)<br>Recherche ce qui est saint<br>(oh, oui crois !)<br>Pour que tu sois affermi.<br><br><span style=''padding-left:15px''></span>Oh, oui crois ! Oh, oui crois<br><span style=''padding-left:15px''></span>Que Dieu t''aime comme un fils.<br><span style=''padding-left:15px''></span>Oh, oui crois ! Oh, oui crois<br><span style=''padding-left:15px''></span>Que Dieu t''aime et te châtie.'),
(45, NULL, 'Comme Christ ressuscité', '45', 'Rm 6', 'Comme Christ ressuscité, de même nous aussi<br>Marchons donc par la foi, sachant que l''homme ancien<br>A été crucifié, le corps du mal détruit.<br>Ne soyons plus esclaves du péché !<br><br>Regardons-nous comme morts, mais vivants pour Jésus.<br>Le péché n''aura plus de pouvoir sur nous tous.<br>Nous avons obéi, de cœur été instruits.<br>Livrons notre vie entière à Jésus !'),
(46, NULL, 'La loi de l’Éternel', '46', 'Ps 19', '<span style=''padding-left:15px''></span>La loi de l’Éternel est parfaite, elle restaure l’âme.<br><span style=''padding-left:15px''></span>Les ordonnances de Dieu sont droites.<br><span style=''padding-left:15px''></span>son témoignage est véritable, il rend sage l’ignorant,<br><span style=''padding-left:15px''></span>Ses commandements éclairent les yeux.<br><br>La crainte de Dieu est pure, elle subsiste à toujours.<br>Les jugements de Dieu sont vrais, justes éternellement.<br>Ils sont bien plus précieux que l’or,<br>Plus doux que le miel des rayons.<br>Ils sont bien plus précieux que l’or,<br>Plus doux que le miel des rayons.<br><br>Ton serviteur aussi reçoit toutes les instructions<br>La récompense est grande pour ceux qui les observent<br>Qui connaît ses égarements ?<br>Pardonne-moi ceux que j’ignore.<br>Qui connaît ses égarements ?<br>Pardonne-moi ceux que j’ignore.'),
(47, NULL, 'Revenez de vos mauvaises voies', '47', 'Ez 18;33;Ps 53;Rm 3;Ga 2;3', '<span style=''padding-left:15px''></span>Revenez de vos mauvaises voies,<br><span style=''padding-left:15px''></span>Pourquoi donc mourriez-vous ?<br><span style=''padding-left:15px''></span>(bis)<br><br>L''insensé dit en son cœur : "Il n''y a point de Dieu"<br>Et il s''est tant corrompu parmi les pécheurs<br>Qu''il ne peut faire le bien qu''il voudrait.<br>Du haut des cieux Dieu regarde : "Qui donc me cherche ?<br>Pas un seul ne fait le bien, tous sont égarés,<br>Pas un seul ne fait le bien, tous sont pervertis."<br><br>"Ce que je désire, dit Dieu, c''est un changement.<br>Change ta conduite et vis, laisse le péché !<br>Oui, que le méchant revienne de son mal !<br>Détournez-vous de vos transgressions, changez de voie !<br>Faites-vous un cœur nouveau, un esprit nouveau;<br>Faites-vous un cœur nouveau par notre Sauveur !"<br><br>Nous sommes tous des coupables, dit la loi de Dieu.<br>Aucun par ses œuvres ne sera justifié<br>Car la vraie justice nous est donnée.<br>Par Jésus-Christ crucifié au bois, pour ceux qui croient.<br>Afin que quiconque croit ne périsse pas<br>Mais qu''il ait toujours la vie, la vie éternelle.<br><br><span style=''padding-left:15px''></span>Oh ! Venez, revenez à Jésus,<br><span style=''padding-left:15px''></span>Il a tout accompli !<br><span style=''padding-left:15px''></span>(bis)'),
(48, NULL, 'Mon dieu à toi j''élève mon âme', '48', 'Ps 25', '<span style=''padding-left:15px''></span>Mon Dieu à toi j''élève mon âme.<br><span style=''padding-left:15px''></span>Éternel, en toi je me confie.<br><span style=''padding-left:15px''></span>Que je ne sois pas couvert de honte<br><span style=''padding-left:15px''></span>Puisque tu es le Dieu de mon salut !<br><br>Les sentiers de l''Éternel sont faits de miséricorde<br>Pour ceux qui gardent sa loi, ses commandements.<br>Il pardonne l''iniquité, efface nos transgressions.<br>Il montre aux pécheurs la voie et la justice.<br><br>Celui qui craint l''Éternel, Dieu lui montre le chemin<br>Et son âme reposera, remplie de bonheur.<br>Des angoisses de son âme et des chagrins de son cœur,<br>Dieu délivre des détresses pour toujours.'),
(49, NULL, 'C''est donc en vain qu''ils le prient', '49', 'Es 59;1', '<span style=''padding-left:15px''></span>C''est donc en vain qu''ils le prient<br><span style=''padding-left:15px''></span>En élevant vers lui leurs mains.<br><span style=''padding-left:15px''></span>C''est donc en vain qu''ils le prient,<br><span style=''padding-left:15px''></span>Le ciel leur semble être d''airain.<br><br>Non, sa main n''est pas trop courte,<br>Son oreille n''est pas trop sourde,<br>Pour vous entendre et vous écouter,<br>Pour vous atteindre et vous sauver.<br><br>Mais ce sont là tous vos crimes<br>Qui le blessent et qui le briment.<br>Vos péchés vous cachent sa face.<br>De vous écouter, il se lasse.<br><br><span style=''padding-left:15px''></span>Coda :<br><span style=''padding-left:15px''></span>Quand vous étendez vos mains<br><span style=''padding-left:15px''></span>Je détourne de vous mes yeux.<br><span style=''padding-left:15px''></span>Quand vous multipliez vos prières<br><span style=''padding-left:15px''></span>Je n''écoute pas.<br><span style=''padding-left:15px''></span>Venez et plaidons, nous dit Dieu<br><span style=''padding-left:15px''></span>Lavez-vous, purifiez-vous !<br><span style=''padding-left:15px''></span>Si vos péchés sont comme le cramoisi,<br><span style=''padding-left:15px''></span>Ils deviendront blancs comme la neige, comme la neige.'),
(50, NULL, 'Heureux l''homme qui place', '50', 'Ps 40', '<span style=''padding-left:15px''></span>Heureux l''homme qui place en l''Éternel sa confiance,<br><span style=''padding-left:15px''></span>Que tous ceux qui le craignent soient dans l''allégresse<br><span style=''padding-left:15px''></span>Et se réjouissent en lui !<br><span style=''padding-left:15px''></span>Heureux l''homme qui place en l''Éternel sa confiance !<br><span style=''padding-left:15px''></span>Que tous ceux qui aiment son salut<br><span style=''padding-left:15px''></span>Disent sans cesse : "Exalté soit l''Éternel, exalté soit l''Éternel !"<br><br>J''avais mis en l''Éternel mon espérance.<br>Il s''est incliné vers moi, il a écouté mes cris,<br>Il m''a retiré du fond de la boue<br>Et il a dressé mes pas sur le roc.<br>Il a mis dans ma bouche un cantique nouveau,<br>Une louange à notre Dieu.<br>Oui, il a dressé mes pas sur le roc.<br>Beaucoup l''ont vu, et ont eu de la crainte,<br>Et ils se sont confiés en l''Éternel. (bis)<br><br>Il ne veut ni sacrifice ni offrande.<br>Je suis pauvre et indigent, mais le Seigneur pense à moi,<br>Il est mon aide et mon libérateur.<br>Je veux faire ta volonté, mon Dieu !<br>Je ne cache pas ta bonté et ta fidélité.<br>Ta loi est au fond de mon cœur.<br>Je publie ta vérité et ton salut,<br>Tes merveilles et tes desseins en notre faveur.<br>Ne me refuse pas tes compassions ! (bis)'),
(51, NULL, 'Souviens-toi', '51', 'Ec 12', '<span style=''padding-left:15px''></span>Souviens-toi, souviens-toi de ton Créateur !<br><span style=''padding-left:15px''></span>Souviens-toi de ton Créateur pendant les jours de ta jeunesse,<br><span style=''padding-left:15px''></span>Avant que la poussière retourne à la terre comme elle y était.<br><span style=''padding-left:15px''></span>Souviens-toi, souviens-toi de ton Créateur !<br><span style=''padding-left:15px''></span>Souviens-toi de ton Créateur !<br><span style=''padding-left:15px''></span>Avant que l''esprit ne retourne à Dieu qui l''a donné.<br><br>Réjouis-toi, réjouis-toi et marche dans les voies de ton cœur !<br>Réjouis-toi, réjouis-toi, va selon les regards de tes yeux !<br>Mais sache, mais sache que Dieu te jugera. (bis)'),
(52, NULL, 'Si quelqu''un est en christ', '52', '2 Co 5;Cl 1', '<span style=''padding-left:15px''></span>Si quelqu''un est en Christ<br><span style=''padding-left:15px''></span>Il est une nouvelle création.<br><span style=''padding-left:15px''></span>Les choses anciennes sont passées.<br><span style=''padding-left:15px''></span>Toutes choses sont devenues nouvelles.<br><br>Car Dieu était en Christ<br>Pour réconcilier le monde en offrant sa vie.<br>Nous qui étions ennemis de Dieu<br>Il nous a réconciliés.<br><br>Christ était sans péché.<br>Il devint péché pour nous, oui, à cause de nous !<br>Pour nous faire paraître devant lui<br>Justes, saints et pardonnés.<br><br>Tout cela vient de Dieu<br>Qui nous a réconciliés avec lui par Christ.<br>Qui nous a donné le ministère<br>De la réconciliation.<br>Qui nous a donné le ministère<br>De la réconciliation.'),
(53, NULL, 'Éternel, tu me feras connaitre', '53', 'Ps 16', '<span style=''padding-left:15px''></span>Éternel, tu me feras connaître le sentier de la vie,<br><span style=''padding-left:15px''></span>De la vie, de la vie.<br><span style=''padding-left:15px''></span>Devant toi la joie abonde,<br><span style=''padding-left:15px''></span>La joie abonde toujours.<br><br>Garde-moi, garde-moi, ô Dieu !<br>Car en toi je cherche un refuge.<br>Tu es mon Seigneur, mon souverain bien.<br>Avec toi je ne craindrai rien.<br><br>Je bénis, bénis l''Éternel.<br>Même dans la nuit mon cœur m''exhorte.<br>J''ai l''Éternel toujours devant les yeux.<br>L''Éternel est mon conseiller.<br><br>Jamais je ne chancellerai<br>Car l''Éternel est à ma droite.<br>Ainsi mon esprit est dans l''allégresse,<br>Et tout mon cœur est dans la joie.'),
(54, NULL, 'Venez à moi', '54', 'Mt 11', '<span style=''padding-left:15px''></span>Venez à moi, vous qui êtes fatigués !<br><span style=''padding-left:15px''></span>Venez à moi, vous tous qui êtes chargés !<br><span style=''padding-left:15px''></span>Venez à moi, vous qui portez le joug,<br><span style=''padding-left:15px''></span>Le joug de la détresse, le joug de la mort !<br><span style=''padding-left:15px''></span>Venez à moi, vous qui traînez vos chaînes,<br><span style=''padding-left:15px''></span>Les chaînes du doute, les chaînes de l''angoisse.<br><br><span style=''padding-left:15px''></span>Fin : "Venez à moi", nous dit Jésus. (bis)<br><br>"Venez à moi, nous dit Jésus, <br>Et je vous donnerai du repos,<br>(bis)<br>Du repos pour vos cœurs meurtris,<br>Repos pour vos âmes lassées."<br><br>Prenez mon joug, car il est doux.<br>Recevez toutes mes instructions.<br>(bis)<br>Mon fardeau est doux et léger.<br>Je suis doux et humble de cœur.'),
(55, NULL, 'À celui qui offre', '55', 'Ps 50', '<span style=''padding-left:15px''></span>À celui qui offre pour sacrifice<br><span style=''padding-left:15px''></span>Des actions de louange à son Dieu,<br><span style=''padding-left:15px''></span>Et qui veille sur sa voie<br><span style=''padding-left:15px''></span>Je ferai voir le salut de Dieu. (bis)<br><br>Au jour de la détresse invoque-moi;<br>Je te délivrerai, tu me glorifieras.<br>(bis)<br><br>Dieu, l’Éternel, nous parle : Rassemblez-vous !<br>Au nom du sacrifice qui nous a réunis.<br>(bis)'),
(56, NULL, 'Je vous ai aimés', '56', 'Ml', '"Je vous ai aimés", dit l’Éternel.<br>Vous dites : "En quoi nous as-tu aimés ?"<br>Un fils honore son père et vous êtes rebelles.<br>Où est la crainte de mon nom car vous me profanez ?<br>Un fils honore son père et vous êtes rebelles.<br>Où est la crainte de mon nom car vous me profanez ?<br><br><span style=''padding-left:15px''></span>Prions Dieu maintenant pour qu''il ait pitié de nous !<br><span style=''padding-left:15px''></span>À nous cet ordre maintenant de donner gloire à son saint nom !<br><br>Revenez à moi, dit l’Éternel.<br>Vous dites : En quoi faut-il revenir ?<br>Voici vous me trompez, moi qui suis l’Éternel.<br>Livrez avec joie vos trésors, je veux tous vous bénir.<br>Voici vous me trompez, moi qui suis l’Éternel.<br>Livrez avec joie vos trésors, je veux tous vous bénir.<br><br>Ils seront à moi, dit l’Éternel,<br>Tous ceux qui craignent et honorent mon nom.<br>Tous ceux qui sont à moi verront ma compassion.<br>Ils connaîtront ma justice et ma pleine guérison.<br>Tous ceux qui sont à moi verront ma compassion.<br>Ils connaîtront ma justice et ma pleine guérison.'),
(57, NULL, 'Jésus-christ est la lumière', '57', 'Jn 8', 'Jésus-Christ est la lumière du monde;<br>Celui qui le suit ne marchera pas dans les ténèbres.<br>Oh, oh...<br><br>Mais il aura la lumière de la vie;<br>Il ne craindra plus la mort, mais il vivra avec Jésus.<br>Oh, oh...<br><br>Seigneur, tu es la lumière de nos vies.<br>Nous Te bénissons, nous t''adorons, Seigneur, notre Sauveur !<br>Oh, oh...<br><br>Tu as donné la lumière dans ce monde.<br>Tu es mort sur la croix en versant ton sang pour nos péchés.<br>Oh, oh...'),
(58, NULL, 'Heureux l''homme qui ne marche pas', '58', 'Ps 1', 'Heureux l''homme qui ne marche pas<br>Selon le conseil des méchants,<br>Qui ne s''arrête pas sur la voie des pécheurs,<br>Ni en compagnie des moqueurs,<br>Mais qui trouve son plaisir dans la loi de l''Éternel<br>Et qui la médite jour et nuit.<br>Il est comme un arbre près des eaux<br>Qui donne son fruit en sa saison,<br>Dont le feuillage ne se flétrit pas;<br>Tout ce qu''il fait lui réussit.<br><br>Il n''en est pas ainsi des méchants;<br>Comme la paille dissipée au vent.<br>Ils ne résistent pas au jour du jugement,<br>Ni les pécheurs parmi les justes.<br>Mais le Seigneur est avec ceux qui aiment sa loi<br>Et qui la méditent jour et nuit.<br>La voie des pécheurs mène à la ruine<br>Mais le Seigneur connaît leur voie.<br>Il connaît aussi la voie du juste<br>Et il bénit tout ce qu''il fait.'),
(59, NULL, 'Revenez enfants rebelles', '59', 'Jr 3', 'Revenez enfants rebelles,<br>Mon regard ne sera pas sévère.<br>Revenez enfants rebelles,<br>Car je suis miséricordieux.<br>Oui, nous avons péché contre l''Éternel, (bis)<br>Contre l''Éternel !<br><br>Revenez enfants rebelles,<br>Reconnaissez vos iniquités<br>Et ne suivez plus les penchants,<br>Les penchants de votre mauvais cœur.<br>Oui, nous avons péché contre l''Éternel.<br>Nous n''avons pas écouté, écouté sa voix,<br>Écouté sa voix !<br><br>Revenez enfants rebelles,<br>Je pardonnerai vos infidélités.<br>Ma colère ne dure pas toujours<br>Car je suis, car je suis votre Maître.<br>Oui, nous voici Seigneur, nous allons à toi,<br>Car tu es l''Éternel, l''Éternel notre Dieu,<br>L''Éternel notre Dieu (bis)');
INSERT INTO `hymn` (`id`, `musical_part_id`, `title`, `num`, `ref`, `lyrics`) VALUES
(60, NULL, 'La bienveillance de l’Éternel remplit la terre', '60', 'Ps 33', 'La bienveillance de l''Éternel remplit la terre.<br>Les cieux ont été faits par sa sainte Parole.<br>Il dit et la chose arrive ! il ordonne et elle existe !<br><br><span style=''padding-left:15px''></span>Notre âme espère en l''Éternel,<br><span style=''padding-left:15px''></span>(Notre cœur met en lui sa joie,<br>Car il est notre bouclier.)<br><span style=''padding-left:15px''></span>Il est notre Rocher suprême.<br><span style=''padding-left:15px''></span>(Notre âme espère en l''Éternel,<br>Il est notre rocher.)<br><br>Heureux celui que Dieu choisit pour héritier !<br>L''Éternel regarde du haut des cieux les hommes,<br>Il observe tous les habitants, lui qui leur forme le cœur à tous.<br><br>L''œil de l''Éternel est sur tous ceux qui Le craignent<br>Afin d''arracher quelques-uns d''eux à la mort<br>Et de les faire survivre pendant la grande famine.'),
(61, NULL, 'Et l''Ésprit et l''Épouse', '61', 'Ap 22', '<span style=''padding-left:15px''></span>Et l''Esprit et l''Épouse disent : "Viens Seigneur !"<br><span style=''padding-left:15px''></span>(Reviens, Seigneur !)<br><span style=''padding-left:15px''></span>Que tous ceux qui entendent disent : "Viens bientôt !"<br><span style=''padding-left:15px''></span>(Reviens bientôt !)<br><span style=''padding-left:15px''></span>Tenons pour certaine sa venue prochaine !<br><span style=''padding-left:15px''></span>(Reviens, Seigneur !)<br><span style=''padding-left:15px''></span>Parole prophétique, lumière dans la nuit.<br><span style=''padding-left:15px''></span>(Reviens bientôt !)<br><br>Prenez donc garde à vous-mêmes<br>De crainte que vos cœurs ne s''endorment<br>Par les soucis du manger et du boire !<br><br>Ne dormons pas comme les autres;<br>Soyons sobres, veillons !<br>Car vous le savez bien vous-même qu''il viendra dans la nuit...<br><br>Pour avoir de l''assurance<br>Demeurez donc en lui,<br>Afin que, lorsqu''il paraîtra, vous ne soyez confus !<br><br>Ayez entière espérance<br>Dans la grâce de Dieu<br>Qui vous sera manifestée quand Jésus reviendra.'),
(62, NULL, 'Je vous laisse la paix', '62', 'Jn 14;Ph 4;1 Pi 5', '<span style=''padding-left:15px''></span>Je vous laisse la paix,<br><span style=''padding-left:15px''></span>Je vous donne ma paix.<br><span style=''padding-left:15px''></span>Je ne la donne pas comme le monde la donne !<br><span style=''padding-left:15px''></span>Que votre cœur ne se trouble pas,<br><span style=''padding-left:15px''></span>Que votre cœur ne s’alarme pas,<br><span style=''padding-left:15px''></span>Croyez en Dieu ! Croyez en moi !<br><br>Ne vous inquiétez de rien.<br>Faites connaître vos besoins.<br>Par des prières à Dieu. Et la paix de Dieu<br>Gardera vos cœurs, vos pensées en Christ. (bis)<br><br>Dieu résiste aux orgueilleux.<br>Humiliez-vous sous sa main,<br>Et déchargez-vous sur lui de tous vos soucis,<br>Car Dieu lui-même prendra soin de vous. (bis)'),
(63, NULL, 'Que Dieu ait pitié de nous', '63', 'Ps 67', 'Que Dieu ait pitié de nous et que Dieu nous bénisse.<br>Qu''il fasse luire sa face, afin que sur la terre<br>Nous connaissions sa voie, les nations son salut !<br><br><span style=''padding-left:15px''></span>Les nations Te louent, ô Dieu, les peuples t''adorent. (bis)<br><br>Les nations se réjouissent et sont dans l''allégresse<br>Car tu juges les peuples, tu juges avec droiture.<br>Tu conduis les nations, toute la terre te craint.'),
(64, NULL, 'Cherchez l''Éternel', '64', 'Es 55', 'Cherchez l''Éternel pendant qu''il se trouve,<br>Et invoquez-le tandis qu''il est près !<br>Que le méchant abandonne sa voie<br>Et l''homme d''iniquité ses pensées !<br>Car vos pensées ne sont pas mes pensées.<br>Vos voies ne sont pas celles du Seigneur.<br>Mais si vous retournez à l''Éternel,<br>Notre Dieu aura pitié de vous.<br><br>Comme la pluie ou la neige en hiver<br>Descend des cieux et n''y retourne pas<br>Sans avoir arrosé, fécondé la terre,<br>Donné de la semence au semeur,<br>Il en est ainsi de toute ma Parole;<br>Elle ne reviendra pas à moi sans effet,<br>Sans avoir exécuté ma volonté<br>Et avoir accompli mes desseins.<br><br>Vous sortirez le cœur rempli de joie,<br>Serez conduits sur des chemins de paix.<br>Les montagnes éclateront d''allégresse<br>Et tous les arbres battront des mains.<br>Au lieu de l''épine s''élèvera le cyprès,<br>Au lieu de la ronce le myrte grandira.<br>Oui, cherchez l''Éternel pendant qu''il se trouve !<br>Invoquez-le tandis qu''il est près ! (bis)'),
(65, NULL, 'Il est beau de louer', '65', 'Ps 92', '<span style=''padding-left:15px''></span>Il est beau de louer, de célébrer l''Éternel,<br><span style=''padding-left:15px''></span>D''annoncer le matin ta bonté.<br><span style=''padding-left:15px''></span>Tu me réjouis par tes œuvres, ô Éternel !<br><span style=''padding-left:15px''></span>Je chante avec allégresse l''ouvrage de Tes mains.<br><br>Que tes œuvres sont grandes et tes pensées profondes.<br>L''homme stupide n''y connaît rien, l''insensé n''y prend point garde.<br>Si les méchants croissent comme l''herbe et ceux qui font le mal fleurissent<br>C''est pour être anéantis. Mais Toi, tu es le Très-Haut.<br><br>Plantés dans la maison de Dieu, les justes, comme le palmier,<br>Croissent dans les parvis de Dieu, verdoyants, sont pleins de sève,<br>Et dans leur vieillesse portent des fruits. Ils s''élèvent comme le cèdre.<br>C''est pour glorifier leur Dieu. L''Éternel est leur rocher.'),
(66, NULL, 'Heureux les affligés', '66', 'Mt 5', 'Heureux les affligés; ils seront consolés,<br>Les pauvres en esprit; le royaume est à eux.<br>Heureux les débonnaires; ils hériteront la terre.<br>Heureux ceux qui ont faim et soif de la justice;<br>Ils seront rassasiés !<br><br>Heureux ceux qui procurent la vraie paix autour d''eux;.<br>Ils seront appelés fils de notre Seigneur.<br>Auront miséricorde les miséricordieux<br>Heureux tous les cœurs purs car ils verront leur Dieu.<br>Soyez dans l''allégresse !<br><br>Heureux les méprisés; le ciel leur appartient !<br>Et si l''on dit du mal sur vous à cause de Dieu,<br>Soyez tous dans la joie et réjouissez-vous.<br>Car votre récompense sera grande dans le ciel.<br>Soyez tous dans la joie !<br><br>Les prophètes avant vous aussi furent outragés.<br>Si le sel de la terre et la lumière du monde<br>Venaient à disparaître pour l''humanité,<br>Que vos œuvres soient vues pour glorifier le Père !<br>Oui, réjouissez-vous !'),
(67, NULL, 'Vous avez été rachetés', '67', '1 Co 6', 'Vous avez été rachetés à un grand prix.<br>Vous avez été rachetés à un grand prix.<br>Au nom du Seigneur Jésus, vous avez été lavés,<br>Sanctifiés, justifiés, justifiés.<br>Glorifiez donc Dieu dans vos corps et votre esprit.<br>Tous ceux qui s''attachent au Seigneur sont un en lui.<br>Nos corps sont membres de Christ, car tout appartient à Dieu.<br>Ne devenez pas esclaves des hommes !<br><br>Pour nous, il n''y a qu''un seul Dieu, un seul Seigneur<br>Qui a créé tout ce qui est, par qui nous sommes.<br>Qu''as-tu que tu n''aies reçu, de quoi te glorifies-tu<br>Puisqu''il nous a tirés de la poussière ?<br>Et pourtant l''Esprit du Seigneur habite en nous;<br>Nous ne nous appartenons plus, plus à nous-mêmes<br>Soyons fermes dans la foi ! Faisons tout avec amour !<br>Rendons grâces en toutes choses au Seigneur ! (bis)'),
(68, NULL, 'Si tu donnes de ta subsistance', '68', 'Es 58', '<span style=''padding-left:15px''></span>Si tu donnes de ta subsistance<br><span style=''padding-left:15px''></span>À celui qui meurt de faim,<br><span style=''padding-left:15px''></span>Si tu rassasies l''âme indigente,<br><span style=''padding-left:15px''></span>Ta lumière se lèvera.<br><span style=''padding-left:15px''></span>L''Éternel sera toujours ton guide.<br><span style=''padding-left:15px''></span>Il rassasiera ton âme aride,<br><span style=''padding-left:15px''></span>Ta source sera intarissable.<br><br>Donne ton pain à celui qui a faim !<br>Reçois dans ta maison les malheureux !<br>Si tu vois un homme nu, couvre-le;<br>Ne te détourne pas de ton prochain !<br><br>Dénoue les liens de la méchanceté !<br>Brise les chaînes de la servitude !<br>Renvoie libres ceux qui sont opprimés;<br>Éloigne-toi des gestes menaçants !<br><br>Ta lumière poindra comme l''aurore !<br>Ta guérison germera promptement !<br>Ta justice marchera devant toi,<br>La splendeur de Dieu t''accompagnera.'),
(69, NULL, 'Avec quoi irai-je ?', '69', 'Mi 6;Ps 51', 'Avec quoi irai-je devant l''Éternel,<br>Et m''inclinerai-je devant le Très-Haut ?<br>Me prosternerai-je avec des offrandes ?<br>Donnerai-je pour mon crime un premier-né ? (bis)<br><br><span style=''padding-left:15px''></span>On t''a fait connaître, ô homme, ce qui est bien.<br><span style=''padding-left:15px''></span>Ce que l''Éternel demande de toi,<br><span style=''padding-left:15px''></span>C''est que tu pratiques la justice,<br><span style=''padding-left:15px''></span>Que tu aimes la miséricorde.<br><span style=''padding-left:15px''></span>Avec ton Dieu, marche humblement !<br><br>Tu ne prends pas plaisir aux grands sacrifices.<br>Tu n''agrées d''holocaustes pour pardonner.<br>Le sacrifice à Dieu : c''est l''esprit brisé.<br>Ne dédaigne pas, Seigneur, un cœur contrit ! (bis)'),
(70, NULL, 'Ne crains pas (canon)', '70', 'Es 43', 'A. Ne crains pas, ne crains pas,<br>B. Je t''ai racheté !<br>C. Ne crains pas, ne crains pas,<br>D. Je ne t''abandonnerai pas !'),
(71, NULL, 'Invoque-moi', '71', 'Ps 91;92', 'Invoque-moi et je te répondrai.<br>Puisque tu m''aimes, je te délivrerai.<br>Je te protégerai, puisque tu connais mon nom !<br>Et je te ferai voir, je te ferai voir mon salut !<br>Invoque-moi et je te répondrai.<br>Puisque tu m''aimes, je te délivrerai,<br>Je te délivrerai et tu Me glorifieras.<br>Oh, si tu m''écoutais, ton bonheur durerait toujours !<br><br><span style=''padding-left:15px''></span>Il est beau de louer l''Éternel,<br><span style=''padding-left:15px''></span>D''annoncer le matin sa bonté,<br><span style=''padding-left:15px''></span>Sa fidélité pendant les nuits, pendant les nuits !<br><span style=''padding-left:15px''></span>Je chanterai tant que je vivrai<br><span style=''padding-left:15px''></span>La bonté, l''amour de l''Éternel !<br><span style=''padding-left:15px''></span>Je célébrerai sa sainteté, sa sainteté !<br><br>Enseigne-moi tes voies, ô Éternel;<br>Je marcherai dans ta fidélité,<br>Et dispose mon cœur à la crainte de ton nom !<br>Je me confie en toi. En toi, mon Dieu, je ne crains rien.<br>Je ne crains pas les terreurs de la nuit.<br>Aucun fléau n''approchera de moi<br>Car tu es mon secours, mon refuge chaque jour !<br>Car tu es mon secours et mon refuge chaque jour !'),
(72, NULL, 'Vous serez mes témoins', '72', 'Ac 1', 'Vous serez mes témoins, a dit Jésus. (bis)<br>Vous recevrez une puissance, le Saint-Esprit. (bis)<br>Merci Seigneur, car j''étais perdu.<br>Pour toi je veux vivre maintenant;<br>Prends ma vie, Seigneur, oui prends-la ! (bis)<br><br>Vous serez mes témoins, a dit Jésus. (bis)<br>Et jusqu''au bout du monde vous parlerez de moi. (bis)<br>Oui, je veux dire au monde entier<br>Que tu m''as sauvé, m''as racheté.<br>Envoie-moi, Seigneur, envoie-moi ! (bis)<br><br>Nous sommes ses témoins, a dit Jésus. (bis)<br>Et nous chantons au monde entier : Jésus est le Roi ! (bis)<br>Il est le Sauveur et le Seigneur !<br>Il a donné sa vie sur la croix.<br>Tu peux l''accepter, viens à lui ! (bis)'),
(73, NULL, 'Louez l’éternel, il est bon', '73', 'Ps 66', '<span style=''padding-left:15px''></span>Louez l’Éternel ! Il est bon.<br><span style=''padding-left:15px''></span>Chantez la gloire de son nom !<br><span style=''padding-left:15px''></span>Célébrez-le par vos louanges !<br><span style=''padding-left:15px''></span>Dites : "Ô Dieu, tes œuvres sont grandes !"<br><br>La terre se prosterne et chante en ton nom,<br>Chante en ton honneur, ta force et ta gloire.<br><br>Venez, contemplez les œuvres de Dieu;<br>Il est redoutable dans sa majesté.'),
(74, NULL, 'Poussez vers le Seigneur', '74', 'Ps 100', 'Poussez vers le Seigneur des cris de joie, (bis)<br>Vous tous, habitants de la terre !<br>Servez le Seigneur avec joie,<br>Venez avec allégresse en sa présence !<br>Sachez que le Seigneur est Dieu !<br>C’est lui qui nous a faits et nous lui appartenons.<br>Nous sommes son peuple et le troupeau de son pâturage.<br>Oh, oh...<br>Entrez dans ses portes avec des louanges, (bis)<br>Dans ses parvis avec des cantiques !<br>Célébrez-le, bénissez son nom !<br>Car le Seigneur est bon, sa bonté dure toujours<br>Et sa fidélité de génération en génération.<br>Car le Seigneur est bon, sa bonté dure toujours<br>Et sa fidélité de génération en génération.'),
(75, NULL, 'En vérité, en vérité, je vous le dis', '75', 'Jn 6;8;10;11;5;14', 'En vérité, en vérité, je vous le dis:<br>Je suis le pain, le pain de vie.<br>(bis)<br>Ma chair, mon sang sont une nourriture;<br>Celui qui en mange demeure en moi.<br>Celui qui vient à moi n''aura plus faim.<br>Celui qui croit en moi n''aura plus soif.<br><br>En vérité, en vérité, je vous le dis:<br>Je suis la lumière du monde.<br>(bis)<br>Cette lumière est la vraie lumière<br>Qui éclaire le cœur de tous les hommes.<br>Qui la suit ne marche plus dans la nuit<br>Mais il a la lumière de la vie.<br><br>En vérité, en vérité, je vous le dis:<br>Je suis la Porte des brebis.<br>(bis)<br>Quiconque entre par moi sera sauvé,<br>Il trouvera de très beaux pâturages.<br>Je suis venu pour vous donner la vie,<br>Pour vous combler de vie en abondance.<br><br>En vérité, en vérité, je vous le dis:<br>Je suis le berger des brebis.<br>(bis)<br>Le berger donne sa vie pour les brebis.<br>Elles entendent ma voix, je les connais.<br>Elles ont la vie, ne périront jamais.<br>Personne ne les ravira de ma main.<br><br>En vérité, en vérité, je vous le dis:<br>Je suis résurrection et vie.<br>(bis)<br>Et l''heure vient, elle est déjà venue,<br>Où les morts entendront la voix de Dieu.<br>Tous ceux qui l''auront entendue vivront.<br>Qui croit en moi vit éternellement.<br><br>Coda :<br><span style=''padding-left:15px''></span>En vérité, en vérité, je vous le dis:<br><span style=''padding-left:15px''></span>Je suis le chemin et la vie.<br><span style=''padding-left:15px''></span>En vérité, en vérité, je vous le dis:<br><span style=''padding-left:15px''></span>Je suis la vérité, la vie.'),
(76, NULL, 'Réjouissez-vous (canon)', '76', 'Ph 4;Ep 5', 'A. Réjouissez-vous...<br>B. ...réjouissez-vous<br>C. Réjouissez-vous...<br>D. ...toujours dans le Seigneur !<br>Et rendez grâces, et rendez grâces<br>Et rendez grâces en toute chose au Seigneur !'),
(77, NULL, 'Ô Dieu, prête l''oreille', '77', 'Ps 55', 'Ô Dieu, prête l''oreille et entends ma prière !<br>Ô Dieu, ne te dérobe pas à mes supplications !<br>Écoute-moi et réponds-moi !<br>J''erre çà et là dans mon chagrin et mon âme s''agite,<br>Car l''ennemi m''entoure, il me poursuit ! Il me poursuit !<br><br>Et moi, je crie à Dieu, l''Éternel me sauvera !<br>Le soir, le matin, à midi, je soupire et je gémis<br>Et mon Sauveur entend ma voix.<br>Il me rend la paix et me délivre; quand je suis dans la crainte<br>Je me confie en toi ! tu me soutiendras, tu me soutiendras !'),
(78, NULL, 'Mon Dieu, mon Dieu pourquoi ?', '78', 'Ps 22', 'Mon Dieu, mon Dieu, pourquoi m''as-tu abandonné,<br>Et t''éloignes-tu sans me secourir ?<br>Mon Dieu, mon Dieu, pourquoi, sans écouter mes plaintes,<br>T''éloignes-tu sans me secourir ?<br>Des scélérats rôdent autour de moi,<br>Semblables au lion qui déchire et rugit.<br>Mon Dieu, je crie le jour et tu ne réponds pas.<br>Dans la nuit, et je n''ai point de repos.<br><br>Oh ! Ne T’éloigne pas, quand la détresse est proche,<br>Quand personne ne vient à mon secours.<br>Ils ont percé mes mains, ils ont percé mes pieds,<br>Et je pourrais compter tous mes os.<br>Ils se sont partagé mes vêtements,<br>Ils ont tiré au sort ma tunique.<br>Pourtant tu es le Saint, tu délivrais nos pères.<br>Ils criaient à toi; ils étaient sauvés.<br>Oh ! Ne t''éloigne pas, ne cache pas ta face<br>Toi qui es ma force, viens à mon secours,<br>Éternel, Éternel !'),
(79, NULL, 'Je vous exhorte donc', '79', 'Rm 12', '<span style=''padding-left:15px''></span>Je vous exhorte donc, mes frères, oui mes frères,<br><span style=''padding-left:15px''></span>Par les compassions de Dieu,<br><span style=''padding-left:15px''></span>Je vous exhorte donc, mes frères,<br><span style=''padding-left:15px''></span>À offrir vos corps comme un sacrifice vivant,<br><span style=''padding-left:15px''></span>Saint, agréable à Dieu,<br><span style=''padding-left:15px''></span>Ce qui sera de votre part<br><span style=''padding-left:15px''></span>Un culte raisonnable, un culte raisonnable.<br><br>Ne vous conformez pas au monde, mais soyez transformés<br>Par le renouvellement de l''intelligence.<br><br>Afin de discerner la volonté, la volonté de Dieu,<br>Ce qui est bon et parfait, bon et agréable.'),
(80, NULL, 'Les sacrifices', '80', 'Ps 32;51', '<span style=''padding-left:15px''></span>Les sacrifices qui plaisent à l''Éternel,<br><span style=''padding-left:15px''></span>C''est un esprit brisé et une âme humiliée.<br><span style=''padding-left:15px''></span>Éternel ! Crée en moi un cœur purifié,<br><span style=''padding-left:15px''></span>Un esprit disposé à écouter ta voix.<br><br>Éternel Dieu ! Tant que je me suis tu,<br>Mes os se consumaient, mon âme gémissait.<br>Alors je dis : "Mon Dieu, j''avoue mes transgressions,<br>Ne me rejette pas, accorde ton pardon !"<br>Tu détournas les yeux de mon iniquité.<br>Tu me rendis la joie, la joie de mon salut.'),
(81, NULL, 'Mon fils', '81', 'Pr 1;Jc 1', '<span style=''padding-left:15px''></span>Mon fils, si des pécheurs te séduisent,<br><span style=''padding-left:15px''></span>Ne te laisse pas gagner par eux.<br><span style=''padding-left:15px''></span>Mon fils, s''ils te disent : "Viens avec nous !"<br><span style=''padding-left:15px''></span>Détourne ton pied de leur sentier,<br><span style=''padding-left:15px''></span>Ne te mets pas en chemin avec eux. (fin : bis)<br><br>Quelqu''un mettra-t-il du feu dans son sein<br>Sans danger qu''il ne s''enflamme ?<br>Peut-on marcher sur des charbons ardents<br>Sans que les pieds ne soient brûlés ?<br><br>Chacun de nous est tenté,<br>Amorcé par sa propre convoitise.<br>La convoitise enfante le péché,<br>Et le péché produit la mort !<br><br>Supporte avec patience la tentation,<br>Persévère dans l''épreuve;<br>Tu recevras la couronne de vie<br>Que le Seigneur nous a promise.'),
(82, NULL, 'Heureux tous ceux...', '82', 'Ps 119', '<span style=''padding-left:15px''></span>Heureux tous ceux qui sont intègres dans leur voie,<br><span style=''padding-left:15px''></span>Qui marchent dans la loi de Dieu.<br><span style=''padding-left:15px''></span>Heureux ceux qui gardent ses commandements,<br><span style=''padding-left:15px''></span>Qui le suivent de tout leur cœur.<br><br>Celui qui marche dans ses voies<br>Ne commet plus d''iniquités.<br>Tu as prescrit tes ordonnances<br>Pour qu''on les observe avec soin.<br><br><span style=''padding-left:15px''></span>Puissent mes actions être bien réglées,<br><span style=''padding-left:15px''></span>Et que je garde tes statuts !<br><span style=''padding-left:15px''></span>Alors je ne rougirai point devant toi,<br><span style=''padding-left:15px''></span>Devant tous tes commandements.<br><br>Je te louerai dans la droiture.<br>Je serre ta parole dans mon cœur<br>Afin de ne pas retomber.<br>Béni sois-Tu, ô Éternel !<br><br><span style=''padding-left:15px''></span>Heureux tous ceux qui sont intègres dans leur voie,<br><span style=''padding-left:15px''></span>Qui marchent dans la loi de Dieu.<br><span style=''padding-left:15px''></span>Heureux ceux qui gardent ses commandements,<br><span style=''padding-left:15px''></span>Qui le suivent de tout leur cœur.'),
(83, NULL, 'Demandez', '83', 'Mt 7;Jn 15;Lc 18', '<span style=''padding-left:15px''></span>Demandez, on vous donnera,<br><span style=''padding-left:15px''></span>Oui, cherchez et vous trouverez !<br><span style=''padding-left:15px''></span>Frappez et l''on vous ouvrira,<br><span style=''padding-left:15px''></span>Car quiconque demande reçoit.<br><br>Si mes paroles demeurent en vous,<br>Et si vous demeurez en moi,<br>Demandez tout ce que vous voudrez;<br>Cela vous sera accordé.<br><br>Priez, ne vous relâchez donc pas !<br>Dieu justifiera ses élus.<br>Et ceux qui crient à lui jour et nuit<br>Sans tarder seront exaucés.'),
(84, NULL, 'Qui est comme toi ?', '84', 'Ex 15', '<span style=''padding-left:15px''></span>Qui est comme toi parmi les dieux ?<br><span style=''padding-left:15px''></span>Qui est comme toi, magnifique en sainteté,<br><span style=''padding-left:15px''></span>Digne de louanges, opérant des prodiges ?<br><span style=''padding-left:15px''></span>Soyez saints, car je suis saint !<br><br>Par ta bienveillance tu nous as conduits.<br>Par ta puissance tu me diriges<br>Vers la demeure de ta sainteté.<br><br>Tu m''amèneras, tu m''établiras<br>Sur la montagne de l''héritage,<br>Au sanctuaire fondé par tes mains.'),
(85, NULL, 'À lui soit la gloire', '85', 'Ep 3', '<span style=''padding-left:15px''></span>À lui soit la gloire dans l''Église et en Jésus-Christ,<br><span style=''padding-left:15px''></span>Dans toutes les générations, aux siècles des siècles !<br><span style=''padding-left:15px''></span>Gloire à celui qui peut faire, par la puissance qui agit,<br><span style=''padding-left:15px''></span>Infiniment au-delà de ce que nous demandons.<br><span style=''padding-left:15px''></span>À lui soit la gloire dans l''Église, et en Jésus-Christ !<br><br>Je fléchis les genoux devant le Père,<br>Duquel tire son nom toute famille<br>Dans les cieux et sur la terre,<br>Afin qu''il nous donne, selon la richesse de sa gloire,<br>D''être puissament fortifiés<br>Par son Esprit dans l''homme intérieur.<br><br>Que Jésus dans nos cœurs par la foi vive,<br>Enracinés et fondés dans l''amour,<br>Pour comprendre sa longueur,<br>Sa grande profondeur qui surpasse toute connaissance,<br>En sorte que, par son Esprit,<br>La plénitude remplisse notre cœur.'),
(86, NULL, 'Louez le nom de l''Éternel', '86', 'Ps 135', '<span style=''padding-left:15px''></span>Louez ! Louez le nom de l''Éternel !<br><span style=''padding-left:15px''></span>Louez ! Louez le nom de Dieu !<br><span style=''padding-left:15px''></span>Louez, serviteurs de l''Éternel<br><span style=''padding-left:15px''></span>Qui vous tenez dans la maison de l''Éternel.<br><span style=''padding-left:15px''></span>Louez ! Louez le nom de l''Éternel ! Louez !<br><span style=''padding-left:15px''></span>Louez dans les parvis de sa maison.<br><span style=''padding-left:15px''></span>Louez l''Éternel, car l''Éternel est bon !<br><span style=''padding-left:15px''></span>Chantez à son nom, car il est favorable !<br><span style=''padding-left:15px''></span>Car l''Éternel nous a choisis.<br><br>Je sais que l''Éternel est grand !<br>Oui, notre Seigneur est au-dessus de tous les dieux.<br>Tout ce que l''Éternel veut, il le fait,<br>Dans les cieux et sur la terre,<br>Dans les mers et dans tous les abîmes.'),
(87, NULL, 'Si donc vous êtes ressuscités', '87', 'Cl 3;Ph 3', 'Si donc vous êtes ressuscités,<br>Êtes ressuscités avec Christ,<br>Alors cherchez les choses d''en-haut,<br>Où Christ est à la droite de Dieu.<br>Affectionnez-vous aux choses d''en-haut<br>Et non à celles qui sont sur la terre.<br><br><span style=''padding-left:15px''></span>Car vous êtes morts et votre vie est cachée avec Christ.<br><span style=''padding-left:15px''></span>Si donc vous êtes ressuscités,<br><span style=''padding-left:15px''></span>Alors cherchez les choses d''en-haut,<br><span style=''padding-left:15px''></span>Où Christ est à la droite de Dieu.<br><br>Mais notre cité est dans les cieux<br>D''où nous attendons aussi Jésus.<br>Et quand Christ, notre vie, paraîtra,<br>Alors nous paraîtrons avec lui.<br>Dans sa gloire il nous emportera,<br>Et il nous rendra semblables à lui.'),
(88, NULL, 'Tu es la source d''eau vive', '88', 'Jr 17', '<span style=''padding-left:15px''></span>Tu es la source d''eau vive, grande est ma plaie !<br><span style=''padding-left:15px''></span>Guéris-moi, Éternel, et je serai guéri !<br><span style=''padding-left:15px''></span>Je viens à toi, mon seul refuge et mon appui;<br><span style=''padding-left:15px''></span>Ô mon Dieu, sauve-moi et je serai sauvé !<br><br>Le péché écrit avec un burin de fer,<br>Devant Dieu est gravé sur la table des cœurs,<br>Il attire sur nous le jour du malheur;<br>Nous avons allumé le feu de sa colère.<br><br>Le cœur de l''homme est tortueux par-dessus tout.<br>L''Éternel éprouve le cœur, sonde les reins,<br>Rend à chacun selon l''œuvre de ses mains<br>Et manifeste la grandeur de son courroux.'),
(89, NULL, 'Oh ! Soumettez-vous à Dieu !', '89', 'Jc 4', '<span style=''padding-left:15px''></span>Oh ! Soumettez-vous à Dieu !<br><span style=''padding-left:15px''></span>(Soumettez-vous à Dieu !)<br><span style=''padding-left:15px''></span>Approchez-vous de lui;<br><span style=''padding-left:15px''></span>Il s''approchera de vous.<br><span style=''padding-left:15px''></span>(Approchez-vous de lui !)<br><span style=''padding-left:15px''></span>Ne lui résistez plus.<br><span style=''padding-left:15px''></span>Humiliez-vous devant lui, il vous élèvera.<br><br>D''où peuvent venir vos luttes<br>Et d''où viennent vos querelles ?<br>N''est-ce pas de vos passions<br>Qui combattent dans vos membres ?<br><br>Et si nous aimons le monde<br>Nous sommes des adultères.<br>Dieu aime avec jalousie<br>L''Esprit Saint qui nous habite.<br><br>Oui, sentons notre misère,<br>Soyons dans le deuil, les larmes.<br>Changeons la joie en tristesse<br>Nettoyons nos mains souillées !'),
(90, NULL, 'Servez l''Éternel', '90', 'Ps 100;105;106;116;130', '<span style=''padding-left:15px''></span>Servez l''Éternel avec joie !<br><span style=''padding-left:15px''></span>Il nous a faits, nous sommes à lui.<br><span style=''padding-left:15px''></span>Servez l''Éternel avec joie !<br><span style=''padding-left:15px''></span>Célébrez-Le, bénissez son nom !<br><br>Car si tu gardais le souvenir de nos iniquités, ô Éternel,<br>Qui donc subsisterait devant toi ?<br>Seigneur, qui pourrait subsister devant toi ?<br><br><span style=''padding-left:15px''></span>Chantez, chantez à l''Éternel !<br><span style=''padding-left:15px''></span>J''étais affaibli, il m''a sauvé.<br><span style=''padding-left:15px''></span>Chantez, chantez à l''Éternel !<br><span style=''padding-left:15px''></span>Glorifiez-vous de son saint nom !<br><br>Car l''Éternel m''a fait du bien,<br>Il a délivré mon âme de la mort.<br>Le pardon se trouve auprès de lui.<br>Heureux qui observe et pratique ses lois !'),
(91, NULL, 'Il t''a fait sortir', '91', 'Dt 4;6;7;8;11', 'Il t''a fait sortir de la maison de servitude.<br>Il a fait jaillir pour toi l''eau vive du rocher.<br>Il t''a nourri de la manne que tu ne connaissais pas<br>Afin de t''humilier et de t''éprouver<br>Pour te faire ensuite du bien !<br>Si tu écoutes et si tu observes ses ordonnances,<br>L''Éternel, ton Dieu, t''aimera et te bénira.<br>Si tu aimes de toutes tes forces<br>Et de toute ton âme l''Éternel, ton Dieu,<br>Et si tu le cherches de tout ton cœur, alors tu le trouveras.<br><br>Tous les jours de ta vie sur ton âme, sur ton âme,<br>Veille attentivement et prends garde, prends garde à toi<br>De crainte que tu n''oublies les choses que tu as vues.<br>Car l''homme ne vit pas de pain seulement<br>Mais de toute parole de Dieu !<br>Ne t''allie pas aux nations qui servent les idoles,<br>Car tu es un peuple saint pour l''Éternel ton Dieu.<br>Fais tout ce qui est droit<br>Et ce qui est bien aux yeux de l''Éternel.<br>Et garde-toi d''oublier l''Éternel, l''Éternel, ton Dieu.'),
(92, NULL, 'Éternel, mon dieu, je t''invoque', '92', 'Ps 141;142;143', '<span style=''padding-left:15px''></span>Éternel, mon Dieu, je t''invoque,<br><span style=''padding-left:15px''></span>Oh ! Viens en hâte auprès de moi !<br><span style=''padding-left:15px''></span>Prête l''oreille à ma voix !<br><span style=''padding-left:15px''></span>Quand je t''invoque, que ma prière soit devant toi !<br><br>Que ma prière soit devant toi comme l''encens,<br>Et l''élévation de mes mains comme l''offrande,<br>Comme l''offrande du soir.<br>De ma voix je crie à lui, de ma voix je l''implore.<br>Je répands ma plainte, je lui raconte ma détresse.<br><br>Et quand mon esprit est abattu dans mon cœur,<br>Exauce-moi dans ta justice, ô mon Sauveur,<br>Dans ta justice, Seigneur !<br>Ô Dieu, tu es mon refuge, mon partage sur la terre,<br>Mon libérateur, le rocher de mon salut !<br><br><span style=''padding-left:15px''></span>Éternel, mon Dieu, je t''adore<br><span style=''padding-left:15px''></span>Car par ta grâce tu m''as sauvé<br><span style=''padding-left:15px''></span>Je te livre mon âme, mon corps,<br><span style=''padding-left:15px''></span>Pour te servir à la louange de ta gloire (bis)'),
(93, NULL, 'L’Esprit du Seigneur', '93', 'Es 61', 'L’Esprit du Seigneur l’Éternel est sur moi,<br>Car l’Éternel m’a choisi pour porter aux malheureux<br>De bonnes nouvelles. Il m’a envoyé<br><br>Pour guérir ceux qui ont le cœur brisé,<br>Pour proclamer aux captifs la liberté,<br>Et aux prisonniers la délivrance.<br>Pour consoler tous les affligés,<br>Pour leur donner un diadème,<br>Au lieu de cendre, au lieu du deuil,<br>Un vêtement de louange,<br>Une huile de joie, au lieu d’un esprit abattu.<br><br>L’Esprit du Seigneur, l’Éternel est sur Christ,<br>Car l’Éternel l''a choisi pour porter aux malheureux<br>De bonnes nouvelles. Il L’a envoyé (Pour guérir..)'),
(94, NULL, 'Réjouis-toi', '94', '', 'Réjouis-toi, réjouis-toi en l''Éternel !<br>Mets en lui ton espoir; il entend ta voix.<br>Sois sans crainte, car il vient à ton secours.<br>Car il est avec toi pour te délivrer.<br>Il fait jaillir des fleuves sur les dunes,<br>Et il change le désert en sources d''eau.<br>Il fait germer l''herbe sur les montagnes.<br>Alléluia, bénissons-le !<br>Lui qui fait marcher les aveugles<br>Sur un chemin nouveau.<br><br>Car l''Éternel est un Dieu juste,<br>Heureux tous ceux qui espèrent en lui;<br>Il leur donne du pain dans l''angoisse,<br>Et dans la détresse donne de l''eau.<br>Il te fera grâce, quand tu crieras.<br>Quand tu crieras, il t''exaucera,<br>Car ainsi a parlé l''Éternel, le Dieu d''Israël.<br><br>Réjouis-toi, réjouis-toi en l''Éternel.<br>Il pardonne, il guérit, il est un soutien,<br>Un appui, un refuge et un secours.<br>Je le célébrerai tant que je vivrai.<br>C''est dans la tranquillité et le repos<br>Que sera, que sera notre salut,<br>Et dans notre foi sera notre force.<br>Alléluia, bénissons-le !<br>Il est près de ceux qui l''invoquent avec sincérité'),
(95, NULL, 'Du fond de l''abîme', '95', 'Ps', 'Du fond de l''abîme je t''invoque, ô Éternel !<br>Je suis abattu, courbé, entièrement brisé.<br>J''ai péché contre toi et j''ai fait ce qui est mal.<br>Ne me châtie pas dans ta fureur, ô Éternel,<br>Car je reconnais mes iniquités.<br>Comme un lourd fardeau, elles sont trop pesantes pour moi.<br>Ô Seigneur, délivre-moi, aie pitié, pitié de moi !<br><br>Éternel ! Ô Éternel ! C’est en toi que j''espère.<br>Crée en moi un cœur pur, un esprit bien disposé.<br>Sois pour moi un rocher protecteur, un sûr abri;<br>Je ne chancellerai plus.<br>Car je n''ai plus de vigueur à cause de mes péchés,<br>Et il n''y a rien de sain ni de bon dans mon cœur.<br>Ne t''éloigne pas, ne t''éloigne pas !<br>Aie pitié de moi et viens à mon secours !<br><br>Garde-moi, ô Dieu, car je cherche en toi mon refuge.<br>J''ai crié à toi; tu m''as guéri, tu m''as sauvé.<br>En lui mon cœur se confie et je suis secouru.<br>Il est le rocher des délivrances de son oint.<br>Le secours de l''homme n''est que vanité.<br>Mais la bonté de l''Éternel vaut mieux que la vie.<br>Le Seigneur m''a exaucé, et de lui vient mon salut.'),
(96, NULL, 'Je m''écrie : "Loué soit l''Éternel !"', '96', 'Ps 18', '<span style=''padding-left:15px''></span>Je m''écrie : "Loué soit l''Éternel !" et je suis délivré !<br><span style=''padding-left:15px''></span>Je m''écrie : "Loué !"; je suis délivré de tous mes ennemis !<br><br>Les liens de la mort m''avaient environné,<br>Ceux du sépulcre m''avaient entouré.<br>Dans ma détresse, j''ai crié vers mon Dieu.<br>Mon cri est parvenu à ses oreilles.<br><br>Et la terre fut ébranlée et trembla;<br>Il abaissa les cieux et descendit.<br>Le Très-Haut tonna, fit retentir sa voix,<br>Dispersa mes ennemis de ses flèches.<br><br>Il étendit sa main d''en-haut, me saisit,<br>Et il me retira des grandes eaux.<br>Il me délivra de l''ennemi puissant.<br>Il m''a mis au large parce qu''il m''aime.'),
(97, NULL, 'Ma grâce te suffit', '97', '2 Co 12;Hb 13;1 Co 15', '<span style=''padding-left:15px''></span>Ma grâce te suffit<br><span style=''padding-left:15px''></span>Car Ma puissance s''accomplit<br><span style=''padding-left:15px''></span>Dans la faiblesse.<br><span style=''padding-left:15px''></span>Ma grâce te suffit<br><span style=''padding-left:15px''></span>Tous les jours de ta vie.<br><br>Quand dans la maladie ou bien la souffrance<br>Ta force vient à faiblir, n''oublie pas ceci :<br><br>Il est bon que ton cœur en soit affermi.<br>La grâce est en Jésus-Christ, confie-toi en lui.<br><br>Oui, sa grâce envers moi n''est pas restée vaine.<br>Je ne la rejette pas ! Ne t''en prive pas !'),
(98, NULL, 'Quand ton peuple', '98', '1 Rs 8', 'Quand ton peuple sera battu devant l''ennemi<br>Pour avoir péché contre toi,<br>S''il t''adresse des prières et des supplications,<br>De ton peuple, Seigneur, de ton peuple, Seigneur,<br>Tu pardonneras le péché !<br><br><span style=''padding-left:15px''></span>Ô Éternel, ne nous abandonne pas ! (bis)<br><br>Quand ton peuple subira des plaies, des maladies,<br>Seigneur, écoute-le des cieux !<br>Ta justice rendra à chacun selon ses voies.<br>Car toi seul tu connais, car toi seul tu connais<br>Le cœur de tous les humains.<br><br><span style=''padding-left:15px''></span>Ô Éternel, élève nos cœurs vers toi !<br><span style=''padding-left:15px''></span>Afin que nous marchions dans toutes tes voies !<br><br>Éternel, il n''y a pas de Dieu semblable à toi,<br>Ni dans les cieux, ni sur la terre.<br>Tu gardes ton alliance envers tes serviteurs<br>Qui marchent en ta présence, qui marchent en ta présence<br>Qui marchent de tout leur cœur.<br><br><span style=''padding-left:15px''></span>Ô Éternel, nous rendons gloire à ton nom ! (bis)'),
(99, NULL, 'Éternel, tu nous donnes la paix', '99', 'Es 25;26', 'Éternel, tu nous donnes la paix, oh... !<br>Car tout ce que nous faisons<br>C''est toi qui l''accomplis pour nous.<br>À celui qui écoute ta voix, oh... !<br>(Oh... ! À celui qui marche dans tes voies)<br>Tu assures la paix, oui la paix,<br>(Tu assures la paix, la paix)<br>Parce qu''il se confie en toi.<br><br>Seigneur, tu as été un refuge, oh... !<br>Et un abri pour le faible,<br>Le malheureux qui crie à toi.<br>Mais tu abats l''orgueil des méchants, oh... !<br>(Oh... ! tu rends à chacun selon ses voies)<br>Et en toi nous avons espéré,<br>(En toi nous avons espéré)<br>Et c''est toi qui nous as sauvés.'),
(100, NULL, 'Reviens à ton Dieu', '100', 'Os 14', 'Reviens, reviens à ton Dieu, reviens !<br>(Reviens à ton Dieu, reviens !<br>Oui, reviens à ton Dieu, reviens à lui !)<br>Reviens à lui<br>(Reviens à lui !)<br>Reviens, reviens à ton Dieu, car tu es tombé.<br>(Reviens, reviens ! Reviens à ton Dieu !<br>Oh, oui ! Reviens !)<br>Tu es tombé par ton péché.<br>(Tu es tombé, tu es tombé par ton péché ! Ton péché !)<br>Apporte avec toi, apporte avec toi,<br>(Apporte avec toi, apporte avec toi,)<br>Des paroles, et reviens à ton Dieu !<br>(Avec toi des paroles et reviens à ton Dieu !)<br>Oh, oui ! Reviens ! Oh, oui ! Reviens !<br>(Reviens à ton Dieu !)<br><br>Dis-lui : Oh ! Pardonne-moi !<br>(Oh ! pardonne-moi, pardonne !<br>Oh, oui ! Pardonne-moi !)<br>Accueille, accueille-moi !<br>(Accueille-moi ! Accueille-moi !)<br>Au lieu, au lieu d''offrande,<br>(Au lieu d''offrande, je t''offrirai...)<br>Je t''offrirai l''hommage de mes lèvres.<br>(... l''hommage je t''offrirai, je t''offrirai<br>L''hommage de mes lèvres.)<br>Je me confierai, non plus, plus en moi,<br>(Je me confierai non plus en moi,)<br>En mes forces, mais en toi,<br>(En moi, mais en toi en qui je trouve,<br>Toi en qui je trouve la compassion.<br>Je trouve la compassion.)<br><br>J''aurai, j''aurai, moi, ton Dieu<br>(J''aurai, moi, ton Dieu, j''aurai,<br>Oui j''aurai, moi, ton Dieu,)<br>Pour toi l''amour généreux.<br>(Pour toi, l''amour généreux.)<br>Voici, je réparerai l''infidélité,<br>(Je réparerai l''infidélité, réparerai,)<br>Ton inconstance envers ton Dieu.<br>(Ton inconstance, ton inconstance<br>Envers ton Dieu, ton Dieu.)<br>Je t''exaucerai, je serai pour toi comme la rosée.<br>(Je t''exaucerai, je serai pour toi, pour toi,)<br>C''est de moi que viendra ton fruit, oh oui, ton fruit.<br>(Comme la rosée; c''est de moi que viendra,<br>Que viendra ton fruit.)'),
(101, NULL, 'J''aime l''éternel', '101', 'Ps 116', 'J''aime l''Éternel, car il entend ma voix,<br>(J''aime l''Éternel, car il entend ma voix,)<br>Et il a penché son oreille vers moi.<br>Et je le loue, parce qu''il m''a exaucé.<br>(Et je le loue parce qu''il m''a exaucé.)<br>Il est devenu mon salut.<br>J''étais malheureux, mais mon Dieu m''a sauvé.<br>(J''étais malheureux, mais mon Dieu m''a sauvé.)<br>Il a délivré mon âme de la mort,<br>Et mes yeux des larmes, mes pieds de la chute.<br>Il a détaché mes liens (il a délivré mon âme de la mort !)<br><br>J''aime l''Éternel, car il m''a fait du bien.<br>Il est mon secours et mon bouclier.<br>(J''aime l''Éternel, car il m''a fait du bien.)<br>L''Éternel est un Dieu plein de compassion,<br>Je l''invoquerai toute ma vie.<br>(L''Éternel est un Dieu plein de compassion.)<br>J''invoquerai le nom, le nom de l''Éternel,<br>Et j''élèverai la coupe des délivrances.<br>(J''invoquerai le nom, le nom de l''Éternel,)<br>Et je chanterai devant tout son peuple,<br>Tous ses bienfaits envers moi.<br>(Et je chanterai ses bienfaits envers moi.)'),
(102, NULL, 'Car je sais', '102', 'Rm 8', '<span style=''padding-left:15px''></span>Car je sais que ni la mort, ni la vie,<br><span style=''padding-left:15px''></span>Ni les anges, ni les dominations,<br><span style=''padding-left:15px''></span>Ni les choses à venir, ni les choses présentes,<br><span style=''padding-left:15px''></span>Ne pourront me séparer de mon Dieu.<br><span style=''padding-left:15px''></span>Car je sais qu''aucune autre créature,<br><span style=''padding-left:15px''></span>Ni la hauteur, même la profondeur,<br><span style=''padding-left:15px''></span>Ne pourront me séparer de l''amour de Dieu,<br><span style=''padding-left:15px''></span>Manifesté en Jésus-Christ, notre Seigneur.<br><br>Si Dieu est pour nous, qui sera contre nous ?<br>Lui qui n''a pas épargné son Fils<br>Mais qui l''a livré pour nous,<br>Comment ne nous donnera-t-il pas aussi<br>Toutes choses avec lui ?'),
(103, NULL, 'Celui qui vaincra', '103', 'Ap 2;3', 'Celui qui vaincra, je ferai de lui (bis)<br>Une colonne dans le temple de mon Dieu.<br>Je le ferai asseoir avec moi sur mon trône.<br>Celui qui vaincra sera revêtu (bis)<br>De vêtements blancs.<br>Je confesserai son nom devant mon Père et devant ses anges<br>Parce qu''il en est digne.<br><br>Celui qui vaincra, je lui donnerai (bis)<br>L''étoile du matin. Je lui donnerai à manger<br>De l''arbre de la vie du paradis de Dieu.<br>Entends ce que l''Esprit dit aux églises, (bis)<br>Et sois vigilant.<br>Sois fidèle jusqu''à la mort, et je te donnerai la couronne,<br>La couronne de vie. (bis)'),
(104, NULL, 'Ton trône, ô Dieu, est éternel', '104', 'Hb 1', 'Ton trône, ô Dieu, est éternel. (bis)<br>Le sceptre de ton règne est un sceptre d''équité.<br>Tu as aimé la justice, tu as haï l''iniquité.<br>C''est pourquoi, ô Dieu, ton Dieu t''a oint<br>D''une huile de joie au-dessus de tes égaux.<br>Mais toi, Seigneur, tu restes le même,<br>Tes années ne finiront pas.<br><br>Toi, Seigneur, au commencement (bis)<br>Tu as fondé la terre et tu as formé les cieux.<br>Ils sont l''ouvrage de tes mains;<br>Tu subsistes, mais ils passeront.<br>Ils vieilliront tous; tu les rouleras<br>Comme un manteau et tous ils seront changés.<br>Mais toi, Seigneur, tu restes le même,<br>Tes années ne finiront pas.'),
(105, NULL, 'Béni soit notre Dieu', '105', '1 Pi 1', 'Béni soit notre Dieu, Père de notre Seigneur Jésus-Christ,<br>Qui nous a régénérés selon sa grande miséricorde,<br>Oui, pour une espérance vivante,<br>Par la résurrection de Jésus-Christ<br>D''entre les morts, oui, pour un héritage<br>Réservé dans les cieux, réservé dans les cieux.<br><br>Il nous a rachetés, non pas par de l''argent ou de l''or,<br>De la vaine manière de vivre héritée de nos pères,<br>Mais par le sang précieux de Christ,<br>Comme d''un agneau, comme d''un agneau sans tache<br>Et sans défaut, prédestiné avant<br>La fondation du monde. Loué soit notre Dieu !'),
(106, NULL, 'Dieu a choisi', '106', '1 Co 1;Rm 5', 'Dieu a choisi les choses folles du monde<br>Pour confondre les sages.<br>Dieu a choisi les choses faibles du monde<br>Pour confondre les fortes.<br>Dieu a choisi les choses viles du monde,<br>Celles qu''on méprise, celles qui ne sont pas,<br>Pour réduire à rien celles qui sont.<br>Afin que nulle chair ne se glorifie devant Dieu.<br><br><span style=''padding-left:15px''></span>Or, c''est par lui que vous êtes en Jésus-Christ<br><span style=''padding-left:15px''></span>Qui, de par Dieu, a été fait pour vous sagesse,<br><span style=''padding-left:15px''></span>Justice, sanctification et rédemption.<br><br>Lorsque nous étions encore pécheurs,<br>Oui, Christ est mort pour nous.<br>(bis)<br>Et maintenant que nous sommes justifiés,<br>Nous sommes justifiés par la foi,<br>Nous sommes réconciliés avec Dieu.<br>De la colère, nous serons sauvés par Jésus-Christ.'),
(107, NULL, 'Ce n''est pas nous', '107', 'Ep 2;Jn 14;15', 'Ce n''est pas nous qui l''avons choisi<br>Mais c''est lui qui nous a établis<br>Afin que nous allions et que nous portions du fruit.<br><br><span style=''padding-left:15px''></span>Car nous sommes son ouvrage.<br><span style=''padding-left:15px''></span>Nous avons été créés en Jésus-Christ<br><span style=''padding-left:15px''></span>Pour de bonnes œuvres<br><span style=''padding-left:15px''></span>Que Dieu a préparées d''avance,<br><span style=''padding-left:15px''></span>Pour de bonnes œuvres, qui sont préparées d''avance<br><span style=''padding-left:15px''></span>Afin que nous les pratiquions. (bis)<br><br>Il est le cep, nous sommes les sarments.<br>Et celui qui demeure en lui<br>Et en qui il demeure porte beaucoup de fruit.<br><br>Celui qui croit, qui croit en Jésus,<br>Il fera, oui, les œuvres qu''il fit,<br>Il en fera aussi de plus grandes que lui.'),
(108, NULL, 'Je me réjouirai', '108', 'Es 61', 'Je me réjouirai en l''Éternel, mon Dieu, en l''Éternel mon Dieu,<br>(Oh ! je me réjouirai en Dieu, en l''Éternel !)<br>Mon âme sera ravie d''allégresse en lui, en l''Éternel.<br>(Alléluia, alléluia ! Gloire à lui, gloire à notre Dieu !)<br>Car il m''a revêtu des vêtements du salut,<br>(Oh ! je me réjouirai dans le Seigneur, je serai ravi d''allégresse.)<br>Et il m''a couvert du manteau de la délivrance.<br>(En l''Éternel, le Dieu de ma justice.)<br>Il m''a couvert du manteau de la délivrance.<br>(Car il m''a revêtu de son salut.)<br>Je me réjouirai en l''Éternel, mon Dieu, en l''Éternel mon Dieu<br>(Oh ! je me réjouirai en Dieu, en l''Éternel !)<br>Mon âme sera ravie d''allégresse en lui,<br>(Alléluia, alléluia ! Gloire à lui !)<br>En l''Éternel, en l''Éternel, mon Dieu.<br>(Gloire à notre Seigneur, à notre Sauveur !)'),
(109, NULL, 'Je t''instruirai', '109', 'Ps 32', 'Je t''instruirai et je te montrerai<br>La voie que tu dois suivre.<br>Je te conseillerai, je te conseillerai,<br>J''aurai le regard sur toi, j''aurai le regard sur toi.<br>Je t''instruirai et je te montrerai<br>La voie que tu dois suivre.<br>Je te conseillerai, j''aurai le regard sur toi.'),
(110, NULL, 'À celui qui peut', '110', 'Jd 24,25', 'À celui qui peut nous préserver<br>De toute chute, oui, de toute chute,<br>Et nous faire paraître devant lui<br>Irrépréhensibles et dans l''allégresse,<br>À notre Dieu, par Jésus notre Seigneur,<br>À lui soient la gloire et la majesté,<br>La force et la puissance<br>Dès avant tous les temps.<br>Et maintenant, et dans tous les siècles,<br>À lui soient la gloire et la majesté,<br>La force et la puissance<br>Dès avant tous les temps,<br>Dans tous les siècles, amen !'),
(111, NULL, 'Frères, n''aimez pas le monde', '111', '1 Jn 2; Ti 2; 1 Co 2; 1 Pi 1', '<span style=''padding-left:15px''></span>Refrain :<br><span style=''padding-left:15px''></span>Frères, n''aimez pas le monde,<br><span style=''padding-left:15px''></span>Ni tout ce qui est dans le monde.<br><span style=''padding-left:15px''></span>Si quelqu''un aime le monde,<br><span style=''padding-left:15px''></span>L''amour du Père n''est point en lui.<br><span style=''padding-left:15px''></span>Et le monde passera et sa convoitise aussi.<br><br><span style=''padding-left:15px''></span>Coda :<br><span style=''padding-left:15px''></span>Mais celui qui fait la volonté de Dieu<br><span style=''padding-left:15px''></span>Demeure éternellement.<br><br>Car la grâce de Dieu,<br>Source de salut pour tous les hommes,<br>Nous enseigne à renoncer<br>Aux convoitises mondaines,<br>Et à vivre dans l''attente<br>De la bienheureuse espérance,<br>La manifestation de notre Sauveur Jésus-Christ.<br><br>Nous n''avons pas reçu<br>L''esprit du monde, mais l''Esprit qui vient de Dieu<br>Afin que nous connaissions<br>Ce que Dieu nous a donné par sa grâce,<br>Nous qui sommes gardés<br>Dans la puissance de Dieu par la foi,<br>Pour le grand salut qui sera bientôt révélé.'),
(112, NULL, 'Que le Dieu de paix', '112', '1 Th 5', 'Que le Dieu de paix vous sanctifie<br>Lui-même tout entiers.<br>Ainsi que tout votre être, l''esprit, l''âme et le corps,<br>Soit conservé, sans défaut, sans tache<br>Pour le retour du Seigneur Jésus.<br>Celui qui vous a appelés est fidèle,<br>Et c''est lui qui le fera (bis)'),
(113, NULL, 'Servez Christ, le Seigneur', '113', 'Cl 3;4', '<span style=''padding-left:15px''></span>Servez Christ, le Seigneur, rachetez le temps ! (bis)<br><span style=''padding-left:15px''></span>Et chantez à Dieu (bis)<br><span style=''padding-left:15px''></span>De tout votre cœur<br><span style=''padding-left:15px''></span>Sous l''inspiration de la grâce.<br><br>Quoi que vous fassiez, en paroles ou en œuvres,<br>Oui, faites tout au nom du Seigneur Jésus<br>En rendant grâces, par lui, à Dieu le Père.<br>Et prenez garde au ministère que vous avez reçu,<br>Reçu dans le Seigneur,<br>Afin de le bien remplir, le bien remplir.<br><br>Que la paix de Christ, à laquelle vous êtes appelés<br>Pour former un seul corps, règne dans vos cœurs.<br>Et que la Parole de Christ habite en vous.<br>Et revêtez-vous, revêtez-vous de l''amour qui est le lien<br>Le lien de perfection.<br>Et soyez reconnaissants, reconnaissants.'),
(114, NULL, 'Heureux celui', '114', 'Ps 65', '<span style=''padding-left:15px''></span>Heureux celui que tu choisis,<br><span style=''padding-left:15px''></span>Que tu admets dans ta présence,<br><span style=''padding-left:15px''></span>Pour qu''il habite dans tes parvis,<br><span style=''padding-left:15px''></span>Dans ta maison !<br><br>Nous nous rassasieron<br>Du bonheur de ta maison,<br>De la sainteté de ton temple.<br>Nous nous rassasierons,<br>Du bonheur de ta maison,<br>De la sainteté de ton temple (bis)'),
(115, NULL, 'Il n''y a de salut', '115', 'Ac 4;Jn 14', '<span style=''padding-left:15px''></span>Il n''y a de salut en aucun autre nom,<br><span style=''padding-left:15px''></span>Il n''y a de salut en aucun autre nom que celui de Jésus.<br><span style=''padding-left:15px''></span>Car il n''y a sous le ciel<br><span style=''padding-left:15px''></span>Aucun autre nom qui ait été donné<br><span style=''padding-left:15px''></span>Parmi les hommes,<br><span style=''padding-left:15px''></span>Par lequel nous devions être sauvés. (bis)<br><br>Et ce nom, c''est celui de Jésus<br>Qui a dit : "Je suis".<br>Il a dit : "Je suis le chemin, la vérité, je suis la vie;<br>Nul ne vient au Père que par moi."'),
(116, NULL, 'Seigneur, tu as été pour nous un refuge', '116', 'Ps 90', '<span style=''padding-left:15px''></span>Seigneur, tu as été pour nous un refuge,<br><span style=''padding-left:15px''></span>De génération en génération.<br><span style=''padding-left:15px''></span>Avant que tu aies créé la terre et le monde,<br><span style=''padding-left:15px''></span>D’éternité en éternité tu es Dieu.<br><br>Aie pitié de nous Éternel, toi qui fais<br>Rentrer les hommes dans la poussière,<br>Et qui leur dis : fils de l’homme,<br>Retournez, oui, retournez !<br>Car à tes yeux, Seigneur,<br>Mille ans sont comme une veille,<br>Une veille de la nuit, (bis)<br>De la nuit.<br><br>Qui prend garde à la force de ton courroux,<br>Selon la crainte qui t’est due ?<br>Nos années s’évanouissent<br>Comme un son. Enseigne-nous<br>À bien compter nos jours<br>Afin que nous appliquions<br>À la sagesse notre cœur, (bis)<br>Notre cœur.');
INSERT INTO `hymn` (`id`, `musical_part_id`, `title`, `num`, `ref`, `lyrics`) VALUES
(117, NULL, 'Tu as été contre moi', '117', 'Os 10;11;13', 'Tu as été contre moi, contre Dieu l’Éternel,<br>Qui pouvait te secourir.<br>Oui, je suis l’Éternel, ton Dieu.<br>Mais tu n’as pas vu que je te guérissais. (bis)<br>Par des liens d’humanité, des cordages d’amour,<br>Je t’ai attiré à moi.<br>Mais tu t’es éloigné de moi,<br>Et tu n’as pas vu que je te guérissais. (bis)<br><br><span style=''padding-left:15px''></span>Il est temps, il est temps<br><span style=''padding-left:15px''></span>De chercher l’Éternel<br><span style=''padding-left:15px''></span>(bis)<br><span style=''padding-left:15px''></span>Jusqu’à ce qu’il vienne et répande<br><span style=''padding-left:15px''></span>Pour vous la justice. (bis)<br><br>Moi je voudrais te sauver,<br>J’aime la miséricorde<br>Et non les sacrifices.<br>Défriche-toi un champ nouveau.<br>Reconnais que tu as péché contre moi. (bis)<br>Tu as cultivé le mal, tu as mangé le fruit,<br>Le fruit de la fourberie.<br>Tes œuvres sont devant ma face,<br>Et mon cœur s’agite au-dedans de moi. (bis)'),
(118, NULL, 'Ces choses ont été écrites', '118', 'Jn 20', 'Ces choses ont été écrites, afin que vous croyiez<br>Que Jésus est le Christ, le Fils du Dieu vivant.<br>Ces choses ont été écrites, afin que vous croyiez,<br>Et qu’en croyant vous receviez la vie,<br>Oui la vie en son nom, la vie en son nom.<br>Ces choses ont été écrites, afin que vous croyiez<br>Que Jésus est le Christ, le Fils du Dieu vivant.'),
(119, NULL, 'Toute grâce excellente', '119', 'Jc 1', 'Toute grâce excellente et tout don parfait<br>Descendent d’en-haut, du Père des lumières,<br>Chez Lequel il n’y a ni changement ni ombre de variation.<br>Toutes grâces descendent du Père.'),
(120, NULL, 'Notre Père', '120', 'Mt 6', 'Notre Père qui es aux cieux,<br>Que ton nom soit sanctifié.<br>Que ton règne vienne.<br>Que ta volonté soit faite<br>Sur la terre comme au ciel.<br>Que ta volonté soit faite<br>Sur la terre comme au ciel.<br>Donne-nous aujourd’hui<br>Notre pain quotidien.<br>Pardonne-nous nos offenses<br>Comme nous aussi nous pardonnons<br>À ceux qui nous ont offensés.<br>Ne nous induis pas en tentation,<br>Mais délivre-nous du malin.<br>Car c’est à toi qu’appartiennent<br>Aux siècles des siècles,<br>Le règne, la puissance et la gloire.'),
(121, NULL, 'Je porterai mes regards', '121', 'Es 66;Os 14;Am 5', '<span style=''padding-left:15px''></span>Je porterai mes regards<br><span style=''padding-left:15px''></span>Sur celui qui craint ma parole,<br><span style=''padding-left:15px''></span>Sur celui qui souffre<br><span style=''padding-left:15px''></span>Et qui a l’esprit abattu.<br><span style=''padding-left:15px''></span>(bis)<br><br>Droites sont les voies, les voies de l’Éternel;<br>Les justes y marcheront.<br>Droites sont les voies, les voies de l’Éternel.<br>Mais les rebelles y tomberont, y tomberont.<br><br>Oui, que la justice soit comme un torrent,<br>Qui jamais ne tarit !<br>Oui, que la justice soit comme un torrent.<br>La droiture comme un courant d’eau, un courant d’eau !<br><br>Recherchez le bien, le bien et non le mal<br>Afin que vous viviez.<br>Recherchez le bien, le bien et non le mal,<br>Pour que le Seigneur l’Éternel soit avec vous.'),
(122, NULL, 'Chantez à l’éternel', '122', 'Ps 147;150', '<span style=''padding-left:15px''></span>Chantez à l’Éternel ! Car il est agréable<br><span style=''padding-left:15px''></span>Et beau de le louer.<br><span style=''padding-left:15px''></span>Chantez à l’Éternel, louez-le pour sa grâce<br><span style=''padding-left:15px''></span>Et glorifiez son nom !<br><span style=''padding-left:15px''></span>Chantez à l’Éternel, louez-le pour sa grâce.<br><span style=''padding-left:15px''></span>Louez-le, louez-le et glorifiez son nom !<br><br>Ce n’est pas dans la vigueur du cheval qu’il se complaît.<br>Ce n’est pas la robustesse de l’homme qu’il agrée.<br>Il aime ceux qui le craignent, ceux qui espèrent en sa bonté,<br>Ceux qui espèrent en sa bonté.<br><br>Louez-le dans l’étendue où éclate sa puissance !<br>Et louez-le selon l’immensité de sa grandeur !<br>Pour la gloire de son règne, sa majesté, ses compassions,<br>Sa majesté, ses compassions.'),
(123, NULL, 'La bienveillance de l''Éternel', '123', 'Lm 3;Ps 37;72', 'La bienveillance de l’Éternel n’est pas épuisée.<br>Ses compassions se renouvellent chaque matin.<br>Oui, l’Éternel est bon pour qui espère en lui,<br>Pour celui qui le cherche.<br>Et c’est pourquoi j’espère et je veux m’attendre à lui.<br>Le jour où je l’ai invoqué, il s’est approché,<br>Il a entendu mes cris.<br><br><span style=''padding-left:15px''></span>Car le Seigneur ne rejette pas à toujours (bis)<br><span style=''padding-left:15px''></span>Fin : ...à toujours<br><br>Remets ton sort à l’Éternel, il te soutiendra<br>Car il ne laissera jamais chanceler le juste.<br>Béni soit le Seigneur, c’est lui qui est pour nous<br>Le Dieu des délivrances.<br>Car il délivrera le pauvre et le malheureux.<br>Il a pitié du misérable et de l’indigent,<br>Et il sauvera leurs vies.'),
(124, NULL, 'Rendons grâces à Dieu', '124', '2 Co 2;1 Co 15;Ti 3', '<span style=''padding-left:15px''></span>Rendons grâces à Dieu, rendons grâces à Dieu,<br><span style=''padding-left:15px''></span>À Dieu, qui nous fait toujours triompher en Christ !<br><span style=''padding-left:15px''></span>Car Christ est ressuscité et il est vivant.<br><span style=''padding-left:15px''></span>Il est vivant aux siècles des siècles,<br><span style=''padding-left:15px''></span>Aux siècles des siècles.<br><br>La chair et le sang ne peuvent hériter le royaume de Dieu,<br>Mais le Seigneur nous a sauvés<br>Non parce que nous aurions fait des œuvres de justice,<br>Mais selon sa miséricorde,<br>Par le baptême de la régénération<br>Et le renouvellement du Saint-Esprit.'),
(125, NULL, 'Souvenez-vous', '125', 'Ep 1;2;3', 'Souvenez-vous, souvenez-vous<br>Que vous étiez en ce temps-là sans Christ,<br>Sans aucune espérance et sans Dieu dans le monde,<br>Sans Dieu à cause de votre cœur endurci.<br>Vous étiez morts par vos péchés<br>Mais Dieu vous a rendus vivants avec Christ.<br>Vous avez, par la foi en lui, la liberté<br>De vous approcher de Dieu avec confiance.<br><br><span style=''padding-left:15px''></span>Car Christ est notre paix (bis)<br><span style=''padding-left:15px''></span>Par lui seul nous avons accès auprès du Père.<br><span style=''padding-left:15px''></span>Car Christ est notre paix (bis)<br><span style=''padding-left:15px''></span>Notre paix, oui notre paix.<br><br>Souvenez-vous, souvenez-vous<br>Que c’est par grâce que vous êtes sauvés.<br>Que le Père de gloire éclaire votre cœur<br>Sur l’espérance qui s’attache à son appel,<br>Sur la richesse de la gloire<br>De l’héritage qui vous est réservé,<br>À cause du grand amour dont il nous a aimés<br>Montrant en Jésus-Christ sa bonté envers nous.'),
(126, NULL, 'Combats', '126', '1 Tm 6;2 Tm 2;4', '<span style=''padding-left:15px''></span>Combats,<br><span style=''padding-left:15px''></span>Combats le bon combat,<br><span style=''padding-left:15px''></span>Garde la foi !<br><span style=''padding-left:15px''></span>Et tu recevras la couronne de vie.<br><span style=''padding-left:15px''></span>Combats,<br><span style=''padding-left:15px''></span>Combats le bon combat,<br><span style=''padding-left:15px''></span>Achève ta course !<br><span style=''padding-left:15px''></span>Et tu recevras la couronne promise.<br><br>Sois sobre en tout, supporte tout,<br>Les souffrances, l’opprobre.<br>Témoigne, évangélise,<br>Remplis bien ton service.<br><br>Le Seigneur nous délivrera<br>De toute œuvre mauvaise.<br>Il nous ouvrira la porte<br>Du royaume céleste.'),
(127, NULL, 'Pour moi, je regarderai', '127', 'Mi 7', '<span style=''padding-left:15px''></span>Pour moi, je regarderai vers l’Éternel,<br><span style=''padding-left:15px''></span>Je mettrai mon espérance dans le Dieu de mon Salut;<br><span style=''padding-left:15px''></span>Mon Dieu m’exaucera.<br><br><span style=''padding-left:15px''></span>Fin : ... m’exaucera.<br><br>Ne te réjouis pas à mon sujet, mon ennemi !<br>Car si je tombe, je me relève;<br>Si je suis assis dans les ténèbres,<br>L’Éternel sera ma lumière.<br>Je supporterai sa colère,<br>Puisque j’ai péché contre lui,<br>Jusqu’à ce qu’il défende ma cause<br>Et me fasse droit;<br>Il me fera sortir à la lumière,<br>Et je contemplerai sa justice.<br>Mon ennemi le verra et sera couvert de honte,<br>Lui qui me disait: "Où est l’Éternel, ton Dieu ?"'),
(128, NULL, 'Je louerai l’éternel', '128', 'Ps 111', '<span style=''padding-left:15px''></span>Je louerai l’Éternel de tout mon cœur,<br><span style=''padding-left:15px''></span>De tout mon cœur, oui, de tout mon cœur,<br><span style=''padding-left:15px''></span>Dans la réunion des hommes droits.<br><span style=''padding-left:15px''></span>Grandes sont les œuvres de l’Éternel,<br><span style=''padding-left:15px''></span>De l’Éternel, oui, de l’Éternel,<br><span style=''padding-left:15px''></span>Elles sont recherchées par tous ceux qui les aiment,<br><span style=''padding-left:15px''></span>Par tous ceux qui les aiment.<br><br>L’Éternel est miséricordieux et compatissant,<br>Il a envoyé la délivrance à son peuple,<br>Sa justice subsiste à jamais. (bis)<br>Il a donné de la nourriture à ceux qui le craignent;<br>Et toutes ses ordonnances sont véritables,<br>Affermies pour l’éternité, (bis)<br>L’éternité.<br><br>La vérité et le droit sont l’ouvrage de ses mains.<br>Il a établi pour toujours son alliance,<br>Son nom est redoutable et saint. (bis)<br>Toute son œuvre n’est que splendeur et magnificence,<br>Il a laissé la mémoire de ses prodiges<br>Et sa gloire subsiste à jamais, (bis)<br>Oui, à jamais!'),
(129, NULL, 'À toi, Seigneur', '129', 'Ps 24;50;53;112', '<span style=''padding-left:15px''></span>À toi, Seigneur, le monde et tout ce qu’il renferme,<br><span style=''padding-left:15px''></span>Les océans, les mers, les vallées et les monts !<br><span style=''padding-left:15px''></span>À toi le jour, à toi la nuit !<br><span style=''padding-left:15px''></span>Notre Dieu vient, il parle et convoque la terre,<br><span style=''padding-left:15px''></span>Écoutez donc sa voix !<br><span style=''padding-left:15px''></span>(1) Vous tous qui craignez Dieu. (bis)<br><span style=''padding-left:15px''></span>(2) Vous tous qui cherchez Dieu. (bis)<br><br>L’insensé dit en son cœur: «Il n’y a point de Dieu.»<br>Le sage ne se confie qu’en son intelligence,<br>Il s’attache aux honneurs, à l’argent;<br>Mais ils sont tous deux comme une ombre qui passe,<br>Et ils n’emportent rien en mourant.<br><br>Heureux l’homme, oui, heureux l’homme qui craint l’Éternel,<br>Celui qui prend plaisir à ses commandements !<br>Il se sait étranger sur la terre,<br>Et il glorifie celui qui l’a créé;<br>En l’Éternel son cœur se confie.<br><br><span style=''padding-left:15px''></span>À toi, Seigneur, le monde et tout ce qu’il renferme,<br><span style=''padding-left:15px''></span>Les océans, les mers, les vallées et les monts !<br><span style=''padding-left:15px''></span>À toi le jour, à toi la nuit !<br><span style=''padding-left:15px''></span>Notre Dieu vient, il parle et convoque la terre,<br><span style=''padding-left:15px''></span>Écoutez donc sa voix,<br><span style=''padding-left:15px''></span>Vous tous qui aimez Dieu. (ter)'),
(130, NULL, 'Celui qui demeure', '130', 'Ps 91', 'Celui qui demeure sous l’abri du Très-Haut<br>Repose à l’ombre du Tout-Puissant.<br>Je dis à l’Éternel :<br>Mon refuge et ma forteresse,<br>Oui, mon Dieu en qui je me confie !<br>Et sous ses ailes se trouve le refuge.<br>Il vous couvrira de ses plumes,<br>Car sa fidélité<br>Est un bouclier. (bis)'),
(131, NULL, 'Que l’éternel Dieu vous bénisse', '131', 'Nb 6', 'Que l’Éternel Dieu vous bénisse,<br>Et que le Tout-Puissant vous garde !<br>Que le Seigneur, l’Éternel,<br>Fasse luire sa face sur vous,<br>Et que Dieu vous accorde sa grâce !<br>Qu’il tourne vers vous sa face,<br>Et qu’il vous donne sa paix, sa paix !<br>Que l’Éternel Dieu vous bénisse,<br>Et que le Tout-Puissant vous garde !<br>Que le Seigneur, l’Éternel,<br>Fasse luire et resplendir sa face sur vous !'),
(132, NULL, 'Ainsi qu’un père', '132', 'Ps 4;5;103', 'Ainsi qu’un père a compassion de ses enfants,<br>L’Éternel a compassion de ceux qui le craignent.<br>Il sait de quoi nous sommes formés,<br>Il se souvient que nous sommes poussière...<br>Les jours de l’homme sont comme l’herbe des champs.<br>L’éclat de toute chair passe comme une fleur;<br>L’herbe sèche et la fleur tombe<br>Quand le vent, le vent de l’Éternel, souffle dessus.<br><br>Éternel, dès le matin, tu entends ma voix.<br>Ecoute ma prière, aie pitié de moi !<br>Aplanis la voie sous mes pas !<br>Et dans la détresse, sauve-moi !<br>Quand je crie, réponds-moi, ô Dieu de ma justice !<br>Tu n’abandonnes pas qui te cherche vraiment,<br>Tu délivres et tu guéris,<br>Tu sauves ceux dont le cœur est droit, le cœur est droit.'),
(133, NULL, 'Maintenant encore', '133', 'Jl 2;Lm 3;Os 6', '<span style=''padding-left:15px''></span>Maintenant encore, revenez à moi de tout votre cœur,<br><span style=''padding-left:15px''></span>Et reconnaissez que vous avez péché,<br><span style=''padding-left:15px''></span>Vous qui avez abandonné l’Éternel.<br><span style=''padding-left:15px''></span>Car il fait grâce, car il fait grâce, l’Éternel fait grâce.<br><br>Oui, Dieu est bon pour qui espère en lui,<br>Attendez-vous à lui,<br>Car ainsi a parlé l’Éternel: "Cherchez-moi et vivez !"<br><br><span style=''padding-left:15px''></span>Maintenant encore, criez au Seigneur<br><span style=''padding-left:15px''></span>Riche en bienveillance,<br><span style=''padding-left:15px''></span>Lent à la colère et compatissant.<br><span style=''padding-left:15px''></span>Oh oui, appelez l’Éternel au secours !<br><span style=''padding-left:15px''></span>Car il fait grâce, car il fait grâce,<br><span style=''padding-left:15px''></span>L’Éternel fait grâce.<br><br>Venez, retournons à l’Éternel,<br>Car il nous guérira;<br>Il a frappé, il bandera nos plaies, il nous rendra la vie.<br><br><span style=''padding-left:15px''></span>Terre, sois sans crainte, sois dans l’allégresse et réjouis-toi,<br><span style=''padding-left:15px''></span>Oui, l’Éternel a eu pitié de son peuple.<br><span style=''padding-left:15px''></span>Louez l’Éternel, rendez gloire à son nom !<br><span style=''padding-left:15px''></span>Car il fait grâce, car il fait grâce, l’Éternel fait grâce.'),
(134, NULL, 'Celui qui croit en moi', '134', 'Jn 4;7;11', 'Celui qui croit en moi, (bis)<br>Oui, celui-là vivra, nous dit Jésus-Christ,<br>Car il est la résurrection,<br>Car Jésus est la vie.<br>Celui qui croit en lui vivra, même s’il est mort.<br>Celui qui vit et croit en lui,<br>Oui, celui-là ne mourra jamais.<br><br>Celui qui croit en moi, (bis)<br>Des fleuves d’eau vive couleront de son sein.<br>L’eau que je donnerai<br>Deviendra une source d’eau,<br>Et cette eau jaillira dans la vie éternelle.<br>Toi qui as soif, toi, viens et bois,<br>Et tu n’auras plus jamais soif.'),
(135, NULL, 'Que revient-il à l’homme', '135', 'Ec 3', 'Que revient-il à l’homme de ses occupations,<br>De l’effort du travail, et de toutes ses peines?<br>Il a comme seul bonheur son bien-être ici-bas,<br>Le soin de recueillir le fruit de son travail. (bis)<br><br>Connais-tu la sagesse sans avoir de chagrin ?<br>Connais-tu la justice, tout à l’entour de toi ?<br>Qui sait ce qui est bon pour l’homme ici-bas ?<br>Il n’y a sur la terre aucun qui fasse le bien. (bis)<br><br>Quel est le sort de l’homme et celui de la bête ?<br>Ils retournent à la terre, et quelle est donc leur fin ?<br>Mais reconnais qu’en toi il y a la pensée<br>De la vie éternelle; Dieu l’a mise dans ton cœur. (bis)'),
(136, NULL, 'Recommande ton sort', '136', 'Ps 37;Lm 3', 'Recommande ton sort à l’Éternel,<br>Mets en lui ta confiance et il agira.<br>(bis)<br>L’Éternel a de la bonté<br>Pour ceux qui espèrent en lui.<br>Il est bon d’attendre en silence<br>Le secours de l’Éternel. (bis)'),
(137, NULL, 'Ô Dieu, je te bénis', '137', '2 Tm 1;Ep 1', 'Ô Dieu, je te bénis,<br>Toi qui m’as tant aimé.<br>Seigneur, tu m’avais choisi<br>Avant que tout fût créé.<br>Ta grâce m’a été donnée<br>Dès avant les temps éternels,<br>En Jésus ton bien-aimé.<br>Ô Seigneur, je réponds à ton appel.<br><br><span style=''padding-left:15px''></span>Car, lorsque j’étais encore un pécheur,<br><span style=''padding-left:15px''></span>Tu es mort pour moi, Seigneur.<br><span style=''padding-left:15px''></span>Moi, qui fus autrefois ton ennemi,<br><span style=''padding-left:15px''></span>Tu m’as aimé en sacrifiant ta vie.<br><span style=''padding-left:15px''></span>C’est pourquoi, je te louerai toujours, Seigneur.<br><br>Ô Père, sois béni,<br>Toi qui m’as destiné<br>À entrer dans tes parvis,<br>Parmi tous tes héritiers.<br>Tu as résolu dans ton amour<br>Et ton bienveillant dessein,<br>De célébrer pour toujours<br>La gloire de ta grâce par tes saints.'),
(138, NULL, 'Seigneur ouvre mes lèvres', '138', 'Ps 40;51;57', 'Seigneur, ouvre mes lèvres,<br>Et je proclamerai ta louange,<br>Car tu es mon secours !<br>Oui, je crie de joie, je crie de joi<br>À l’ombre de tes ailes.<br>Elève-toi sur les cieux, ô Dieu,<br>Et que ta gloire soit, oui, soit<br>Sur toute la terre. (bis)<br><br>Seigneur, ouvre mes lèvres,<br>Et je proclamerai ta justice,<br>Et ta fidélité !<br>Donne-moi un cœur, un cœur tout simple;<br>Que je craigne ton nom !<br>Oui, garde-moi dans ta paix, ô Dieu;<br>Je me confie en toi, en toi,<br>Et je ne crains rien. (bis)<br><br>Seigneur ouvre mes lèvres,<br>Et je proclamerai ta splendeur !<br>Je chanterai ton nom,<br>Toi, mon espérance, mon espérance,<br>Et mon puissant refuge !<br>Elève-toi sur les cieux, ô Dieu,<br>Et que ta gloire soit, oui, soit<br>Sur toute la terre. (bis)'),
(139, NULL, 'Comme une biche soupire', '139', 'Ps 42', '<span style=''padding-left:15px''></span>Comme une biche soupire<br><span style=''padding-left:15px''></span>Après des courants d’eau,<br><span style=''padding-left:15px''></span>Ainsi mon âme soupire<br><span style=''padding-left:15px''></span>Après toi, ô mon Dieu !<br><span style=''padding-left:15px''></span>Mon âme a soif de Dieu,<br><span style=''padding-left:15px''></span>A soif du Dieu vivant,<br><span style=''padding-left:15px''></span>Comme une biche soupire<br><span style=''padding-left:15px''></span>Après des courants d’eau.<br><br>Pourquoi t’abats-tu, mon âme,<br>Et gémis-tu au-dedans de moi ?<br>Pourquoi t’abats-tu, mon âme ?<br>Espère en Dieu, il est ton secours.<br>Oui je le louerai encore;<br>Il est mon Dieu, il est mon secours.<br>Oui, je le louerai encore;<br>Il est mon Dieu, il est mon salut.'),
(140, NULL, 'L’Éternel sera ta lumière', '140', 'Es 60;Ap.21', '<span style=''padding-left:15px''></span>L’Éternel sera ta lumière à toujours,<br><span style=''padding-left:15px''></span>Ton Dieu sera ta gloire.<br><span style=''padding-left:15px''></span>(bis)<br><br>Voici, je fais toutes choses nouvelles,<br>Ces paroles sont certaines et véritables.<br>À celui qui a soif je donnerai l’eau de la vie,<br>L’eau de la vie gratuitement.'),
(141, NULL, 'Fais de l’Éternel tes délices', '141', 'Ps 37;Pr 14', 'Fais de l’Éternel tes délices,<br>Et il te donnera, il te donnera,<br>Ce que ton cœur désire.<br>Confie-toi en lui et pratique le bien;<br>Et l’Éternel sera, sera toujours ton guide.<br>Remets-lui ton sort, il te soutiendra, il te soutiendra !<br>Garde le silence devant lui,<br>Et ne t’irrite pas, ne t’irrite pas,<br>Car ce serait mal faire,<br>Contre qui vient à bout de ses mauvais desseins.<br>Il sait que les pensées de tout homme sont vaines.<br>Il rend à chacun, oui, selon ses voies,<br>Oui, selon ses voies, il est l’Éternel !<br><br>Ce n’est pas à l’homme, quand il marche,<br>À diriger ses pas, diriger ses pas,<br>Je le sais, Éternel,<br>La conduite de l’homme n’est pas en son pouvoir.<br>Telle voie paraît droite puis elle mèn e à la mort,<br>Mais les voies de l’Éternel sont parfaites,<br>Ses voies sont parfaites.<br>Oui, remets ton sort à l’Éternel;<br>C’est lui qui agira, lui qui agira,<br>Car il soutient les justes.<br>Il te rassasiera au jour de la famine.<br>Il t’entoure de sa grâce, il t’appelle par ton nom,<br>Afin que tu saches qu''il est l’Éternel,<br>Il est l’Éternel ! (bis)'),
(142, NULL, 'Si l’Éternel ne bâtit', '142', 'Ps 4;127;Mt 7', '<span style=''padding-left:15px''></span>Si l’Éternel ne bâtit la maison,<br><span style=''padding-left:15px''></span>Ceux qui la bâtissent travaillent en vain;<br><span style=''padding-left:15px''></span>Si l’Éternel ne garde la ville,<br><span style=''padding-left:15px''></span>Celui qui la garde veille en vain.<br><br>La maison qui est bâtie sur le sable<br>S’effondre dès qu’arrive l’orage,<br>Mais si elle est bâtie, bâtie sur le roc,<br>La tempête ne l’ébranlera pas ! (bis)<br>Toi seul, Éternel, toi seul, tu me donnes<br>La sécurité dans ma demeure,<br>Je me couche, je m’endors, je m’endors en paix.<br>Tu me gardes et je ne crains rien. (bis)'),
(143, NULL, 'Nous ne voulons pas', '143', '1 Th 4', '<span style=''padding-left:15px''></span>Nous ne voulons pas, mes frères,<br><span style=''padding-left:15px''></span>Que vous soyez dans l’ignorance<br><span style=''padding-left:15px''></span>Au sujet de ceux qui dorment (bis)<br><span style=''padding-left:15px''></span>Afin que vous ne vous attristiez pas<br><span style=''padding-left:15px''></span>Comme les autres,<br><span style=''padding-left:15px''></span>Qui n’ont pas d’espérance. (bis)<br><br>Car le Seigneur lui-même,<br>À un signal donné,<br>À la voix d’un archange,<br>Au son de la trompette de Dieu,<br>Descendra du ciel !<br>Et les morts en Jésus-Christ<br>Ressusciteront en premier lieu.<br>Nous les vivants sur terre,<br>Nous serons enlevés<br>Avec eux dans les airs,<br>Et nous rencontrerons le Seigneur,<br>Oui, dans les nuées !<br>Ainsi nous serons toujours<br>Avec notre Seigneur Jésus-Christ.'),
(144, NULL, 'Père, nous nous confions', '144', 'Es 30;Pr 3;Ex 14', 'Père, nous nous confions en toi.<br>En ce jour, avec ses labeurs,<br>Renouvelle nos forces,<br>Renouvelle notre ardeur !<br>Père, nous nous confions en toi.<br><br>C’est dans le calme et le repos,<br>Dans la confiance et l’abandon,<br>Que sera notre force,<br>Que sera notre salut !<br>Aussi nous nous confions en lui.<br><br>L’Éternel combattra pour nous,<br>Et nous garderons le silence.<br>N’ayons aucune crainte,<br>Espérons et attendons !<br>Voyons comment il sauvera.<br><br>Confie-toi en lui de tout coeur,<br>Il aplanira tes sentiers.<br>Ne sois pas sage à tes yeux,<br>Reconnais-le dans tes voies;<br>Ce sera salutaire pour toi !'),
(145, NULL, 'Envoie ta lumière', '145', 'Ps 43', 'Envoie ta lumière, ta fidélité,<br>Pour qu’elles me guident et me conduisent à ta demeure !<br>J’irai vers l’autel de mon Sauveur,<br>Le célébrerai de tout mon cœur.'),
(146, NULL, 'À celui qui nous aime', '146', 'Ap 1', 'À celui qui nous aime,<br>Et nous a délivrés,<br>À celui qui nous aime,<br>Et nous a lavés<br>Par son sang de nos péchés,<br>Lui qui a fait de nous<br>Un royaume de sacrificateurs<br>Pour Dieu son Père,<br>À lui soit la gloire !<br>À Lui soit la puissance,<br>Aux siècles des siècles!<br>Amen! Amen !'),
(147, NULL, 'Voici, je vous envoie', '147', 'Mt 10;1 Pi 5', '<span style=''padding-left:15px''></span>Voici, Je vous envoie<br><span style=''padding-left:15px''></span>Comme des brebis au milieu des loups.<br><span style=''padding-left:15px''></span>Soyez prudents comme les serpents,<br><span style=''padding-left:15px''></span>Et simples comme les colombes.<br><br>L’ennemi rôde autour de nous,<br>Cherchant qui il dévorera.<br>Résistons-lui fermes dans la foi,<br>N’ayons plus aucune crainte !<br>Le Seigneur regarde les justes,<br>Il est attentif à leur prière.'),
(148, NULL, 'Que tout ce qui est vrai', '148', 'Ph 4', '<span style=''padding-left:15px''></span>Que tout ce qui est vrai et qui est honorable,<br><span style=''padding-left:15px''></span>Que tout ce qui est juste et tout ce qui est pur,<br><span style=''padding-left:15px''></span>Ce qui est vertueux et digne de louanges<br><span style=''padding-left:15px''></span>Soit l’objet de vos pensées,<br><span style=''padding-left:15px''></span>Et le Dieu de paix sera avec vous.<br><br>Que votre douceur soit connue de tous les hommes.<br>Le Seigneur est proche, ne vous inquiétez de rien;<br>Mais, en toutes choses, avec des actions de grâces,<br>Faites connaître à Dieu vos besoins,<br>Et la paix de Dieu, qui surpasse toute connaissance,<br>Gardera vos cœurs et vos pensées en Jésus-Christ.'),
(149, NULL, 'Confie-toi', '149', 'Pr 3', '<span style=''padding-left:15px''></span>Confie-toi en l’Éternel de tout ton cœur,<br><span style=''padding-left:15px''></span>Et ne t’appuie pas sur ta sagesse,<br><span style=''padding-left:15px''></span>Reconnais-le dans toutes tes voies;<br><span style=''padding-left:15px''></span>Et il aplanira ton sentier.<br><span style=''padding-left:15px''></span>Crains l’Éternel, et détourne-toi du mal;<br><span style=''padding-left:15px''></span>Ce sera la santé pour tes muscles,<br><span style=''padding-left:15px''></span>Un rafraîchissement pour tes os.<br><span style=''padding-left:15px''></span>Crains l’Éternel, oui, crains l’Éternel.<br><br>Ne sois point sage à tes propres yeux,<br>Et que ton cœur garde mes préceptes.<br>Car ils prolongeront la durée de tes jours,<br>Et ils augmenteront ta paix.'),
(150, NULL, 'Car un enfant', '150', 'Es 9', 'Car un enfant nous est né,<br>Un Fils nous est donné,<br>Et la domination<br>Reposera sur son épaule;<br>On l''appellera Admirable,<br>On l''appellera Conseiller,<br>Dieu Puissant, Père Éternel,<br>Prince de la paix, de la paix.'),
(151, NULL, 'Pour moi, dans mon innocence', '151', 'Ps 17;Es 55', '<span style=''padding-left:15px''></span>Pour moi, dans mon innocence,<br><span style=''padding-left:15px''></span>Je verrai ta face, dès le réveil,<br><span style=''padding-left:15px''></span>Je me rassasierai de ton image<br><span style=''padding-left:15px''></span>Dès le réveil.<br><span style=''padding-left:15px''></span>Pour moi, dans mon innocence<br><span style=''padding-left:15px''></span>Je me rassasierai de ton image,<br><span style=''padding-left:15px''></span>Dès le réveil.<br><br>Les hommes de ce monde,<br>Leur part est dans la vie,<br>Et tu remplis leur ventre de tes biens,<br>Leurs enfants sont rassasiés.<br><br>Pourquoi travaillez-vous<br>Pour ce qui n’est pas du pain ?<br>Ecoutez et mangez ce qui est bon;<br>Votre âme se délectera.'),
(152, NULL, 'Consolez mon peuple', '152', 'Es 40', 'Consolez, consolez mon peuple ! (bis)<br>Parlez-lui, parlez à son cœur !<br>Et criez-lui que sa servitude est finie.<br>Son iniquité est expiée;<br>Il a reçu, de la main de Dieu,<br>Au double de ses péchés.<br>Oui, consolez mon peuple !'),
(153, NULL, 'Oh ! mon coeur est affermi', '153', 'Ps 57;36', 'Oh! mon cœur est affermi,<br>Mon cœur est affermi;<br>Je chanterai ton nom,<br>Et je réveillerai l’aurore.<br>Car ta bonté, Seigneur,<br>Atteint jusqu’aux cieux,<br>Oui, atteint jusqu’aux cieux.<br>Et ta fidélité, ô mon Dieu !<br>Atteint jusqu’aux nues.<br>Elève-toi sur les cieux,<br>Et que ta gloire soit sur toute la terre !'),
(154, NULL, 'Quiconque aime', '154', 'Jn 13;1 Jn 4;5', 'Quiconque aime est né de Dieu.<br>Celui-là connaît Dieu.<br>Car l’amour est de Dieu,<br>Et Dieu est Amour, Dieu est Amour.<br>L’amour nous fut manifesté;<br>Dieu nous a envoyé son Fils unique dans le monde<br>Afin que nous vivions par lui.<br><br><span style=''padding-left:15px''></span>Jésus nous a donné un commandement nouveau:<br><span style=''padding-left:15px''></span>«Aimez-vous les uns les autres,<br><span style=''padding-left:15px''></span>omme Je vous ai aimés.<br><span style=''padding-left:15px''></span>Tous ainsi connaîtront que vous êtes mes disciples.»<br><span style=''padding-left:15px''></span>Oui, aimons-nous les uns les autres,<br><span style=''padding-left:15px''></span>Comme il nous a aimés,<br><span style=''padding-left:15px''></span>Et comme il s’est donné Pour nous.<br><br>Nous aimons les enfants de Dieu,<br>Lorsque nous aimons Dieu,<br>Et que nous pratiquons<br>Ses commandements, ses commandements.<br>Et ceux-ci ne sont pas pénibles;<br>Ce qui est né de Dieu,<br>Par la foi triomphe du monde.<br>La victoire, c’est notre foi.'),
(155, NULL, 'Louez, louez, louez l’Éternel', '155', 'Es 25', 'Louez, louez, louez l’Éternel !<br>Chantez, chantez, chantez en son honneur,<br>Car il a compassion de nous.<br>Il sera notre guide, il sera notre guide.<br>Il est un abri contre la tempête,<br>Un ombrage contre la chaleur.<br>En lui nous avons espéré,<br>Réjouissons-nous de son salut. (bis)'),
(156, NULL, 'Fais-moi grâce', '156', 'Ps 57;69;66;Es 12', 'Fais-moi grâce, ô Dieu, fais-moi grâce !<br>Car en toi mon âme cherche un refuge.<br>Je cherche un refuge à l’ombre de tes ailes.<br>Au jour de ma détresse, je crie à toi mon Sauveur,<br>Et je ne crains rien, je ne crains rien.<br><br>Ô Dieu! tu connais ma folie,<br>Et mes fautes ne te sont point cachées,<br>Aussi je t’adresse ma prière, Éternel !<br>Oh réponds ! réponds-moi en m’assurant ton secours,<br>Approche-toi et délivre-moi !<br><br>Tu t’es irrité contre moi,<br>Mais maintenant tu m’as consolé.<br>Ta bonté atteint, oui, atteint jusqu’aux cieux.<br>Je loue le Dieu Très-Haut qui agit en ma faveur,<br>Il m’enverra du ciel le salut.<br><br>Oui, Dieu vient au-devant de moi,<br>Dans sa bienveillance il me relève.<br>Venez et contemplez les œuvres de mon Dieu !<br>L’Éternel est son nom. Exultez devant lui,<br>Il amène notre âme à la vie. (bis)'),
(157, NULL, 'Espérant contre toute espérance', '157', 'Rm 4', 'Espérant contre toute espérance,<br>C’est en toi que je me suis confié;<br>Toi, qui appelles à l’existence<br>Ce qui n’a jamais, jamais existé !<br><br>Face à la promesse du Dieu vivant,<br>Je ne faiblis pas dans mon espérance.<br>Sans considérer ni la chair, ni le sang,<br>Je crois, et je le loue par avance.<br><br>Sans vaciller dans l’incrédulité,<br>Soyons encore convaincus de ceci:<br>Dieu est tout puissant, oui, pour réaliser<br>Et accomplir tout ce qu’il a promis.<br><br>Jusqu’à la fin, retenons fermement<br>Le sûr fondement de notre assurance.<br>Proclamons toujours et tous joyeusement:<br>Christ vainqueur en toutes circonstances !'),
(158, NULL, 'Que le Dieu de l’espérance', '158', 'Rm 15', 'Que le Dieu<br>(Oui, que le Dieu)<br>De l’espérance<br>(De l’espérance)<br>Vous remplisse<br>(Vous remplisse...)<br>De toute joie, de toute joie.<br>Que le Dieu<br>(Oui, que le Dieu)<br>De l’espérance<br>(De l’espérance)<br>Vous remplisse<br>(Vous remplisse...)<br>De toute paix, dans la foi,<br>Pour que vous abondiez<br>En espérance,<br>Par la puissance du Saint-Esprit.<br><br>Que le Dieu<br>(Oui, que le Dieu)<br>De la patience<br>(De la patience)<br>Vous accorde<br>(Vous accorde...)<br>Un même cœur, une pensée.<br>Que le Dieu<br>(Oui, que le Dieu)<br>Qui nous console<br>(Qui nous console)<br>Vous accorde<br>(Vous accorde...)<br>Une même âme, une seule voix,<br>Pour que vous glorifiiez<br>Oui, tous ensemble,<br>Dieu notre Père, et Jésus-Christ.'),
(159, NULL, 'Jésus-Christ est le même', '159', 'Hb 13', 'Jésus-Christ est le même hier, aujourd’hui,<br>Jésus-Christ est le même éternellement.<br>Confessons de nos lèvres ce nom glorieux,<br>Comme un sacrifice de louange à Dieu.<br>Offrons-le sans cesse, confessons sans cesse<br>Jésus-Christ éternellement.'),
(160, NULL, 'Quand les montagnes', '160', 'Es 54;Jr 31', '<span style=''padding-left:15px''></span>Quand les montagnes s’éloigneraient,<br><span style=''padding-left:15px''></span>Quand les collines chancelleraient,<br><span style=''padding-left:15px''></span>Mon amour ne s’éloignera point de toi, (bis)<br><span style=''padding-left:15px''></span>Et mon alliance de paix ne chancellera pas,<br><span style=''padding-left:15px''></span>Dit l’Éternel, qui a compassion de toi,<br><span style=''padding-left:15px''></span>Ainsi dit l’Éternel, qui a compassion de toi.<br><br>Je t’aime d’un amour éternel, (bis)<br>C’est pourquoi Je te conserve Ma bonté. (bis)'),
(161, NULL, 'Nous savons que nous avons', '161', '2 Co 5', 'Nous savons que nous avons dans les cieux<br>Un édifice qui est l’ouvrage de Dieu;<br>Une demeure éternelle,<br>Qui n’a pas été faite par la main des hommes.<br>Voici pourquoi nous regardons<br>Non pas aux choses qui sont visibles,<br>Mais à celles qui sont invisibles.<br>Les choses visibles sont momentanées,<br>Mais les choses invisibles sont éternelles.<br><br>Car c’est le Seigneur qui nous a formés;<br>Ne plaçons pas notre confiance en nous-mêmes,<br>Mais plaçons-la en lui seul.<br>Béni soit l’Éternel, qui est le Dieu et Père<br>De notre Seigneur Jésus-Christ,<br>Le Dieu de toute consolation;<br>Il nous console dans nos afflictions.<br>Car un moment de légère affliction<br>Produit pour nous un poids éternel de gloire.'),
(162, NULL, 'Mon fils, si tu reçois', '162', 'Pr 2', 'Mon fils, si tu reçois mes paroles,<br>Si tu gardes avec toi mes préceptes,<br>Si tu tiens ton oreille attentive à la sagesse,<br>Inclinant ton cœur à l’intelligence,<br>C’est alors que tu pourras comprendre<br>La droiture, l’équité, la justice,<br>Les routes et les sentiers qui cheminent vers le bien,<br>Ainsi que la crainte de l’Éternel.<br><br>Oui, si tu appelles la sagesse,<br>Et comme l’argent tu la recherches,<br>Si tu la poursuis comme un trésor,<br>Alors elle descendra dans ton cœur,<br>Faisant les délices de ton âme.<br>La réflexion veillera sur toi.<br>Ainsi tu trouveras,<br>De Dieu, la connaissance.'),
(163, NULL, 'Vers qui d’autre que toi', '163', 'Jn 6', 'Vers qui d’autre que toi,<br>Toi le Dieu véritable,<br>Jésus-Christ notre roi,<br>Pourrions-nous donc aller ?<br>Car toi seul tu détiens<br>Les vraies paroles,<br>Les paroles de la vie éternelle. (bis)'),
(164, NULL, 'L’Éternel m’a sauvé', '164', 'Es 38;40;43;54', '<span style=''padding-left:15px''></span>L’Éternel m’a sauvé. (L’Éternel m’a sauvé.) (bis)<br><span style=''padding-left:15px''></span>Je ferai résonner mes instruments<br><span style=''padding-left:15px''></span>Tous les jours de ma vie,<br><span style=''padding-left:15px''></span>À la maison de l’Éternel.<br><br>Ceux qui espèrent en l’Éternel<br>Renouvellent leur force;<br>Ils courent et ne se lassent pas,<br>Ils marchent et ne se fatiguent pas.<br><br>Parce que tu as du prix à ses yeux,<br>Il te rachète.<br>Avec un amour éternel<br>Il aura compassion de toi.'),
(165, NULL, 'Vous qui avez connu la grâce', '165', 'Cl 1', 'Vous qui avez connu la grâce de Dieu<br>Selon la vérité,<br>Soyez, soyez remplis de la connaissance<br>De sa volonté,<br>En toute sagesse et intelligence spirituelle.<br>Marchez d’une manière digne du Seigneur<br>Pour lui plaire, (bis)<br>Et pour l’honorer.<br><br>Et rejetez tous les péchés dans lesquels<br>Vous viviez autrefois.<br>Revêtez-vous d’ardente compassion, de douceur,<br>De patience.<br>Tout ce que vous faites,<br>Oui, faites-le pour le Seigneur;<br>Vous recevrez de lui la récompense,<br>Oui, croissez dans la connaissance de Dieu.'),
(166, NULL, 'Si tu confesses', '166', 'Rm 10', 'Si tu confesses de ta bouche<br>Le Seigneur Jésus,<br>Et si tu crois dans ton cœur<br>Que Dieu L’a ressuscité des morts,<br>Tu seras sauvé. (bis)<br>Car c’est en croyant du cœur<br>Qu’on parvient à la justice,<br>Et c''est en confessant de la bouche<br>Qu''on parvient au salut,<br>Selon ce que dit l’Écriture:<br>"Quiconque croit en lui ne sera point confus". (bis)'),
(167, NULL, 'Voici, je me tiens a la porte', '167', 'Ap 3', 'Voici, je me tiens à la porte (bis)<br>Nous dit Jésus,<br>Je suis à la porte et je frappe<br>Pour que l’on entende ma voix.<br>Toi, si tu entends mon appel<br>Et si tu m’ouvres ta porte,<br>Alors, j’entrerai dans ta demeure,<br>Et je souperai avec toi, et toi avec moi.<br>Alors, j’entrerai dans ta demeure,<br>Et je souperai avec toi, et toi avec moi.<br>Et toi avec moi.'),
(168, NULL, 'Abandonnons les idoles', '168', '1 Th 1', 'Abandonnons les idoles<br>Pour servir le Dieu vivant et vrai !<br>Abandonnons les idoles<br>Pour attendre celui qui vient !<br>C’est Jésus-Christ, le Fils de Dieu;<br>Pour nous sauver, il vient des cieux.<br>Convertissons nos cœurs à Dieu,<br>Et tournons-nous vers lui !'),
(169, NULL, 'Oui, je proclamerai', '169', 'Dt 32', 'Oui, je proclamerai le nom de l’Éternel,<br>Et je rendrai hommage à mon Dieu.<br>Il est notre rocher,<br>Ses œuvres sont parfaites,<br>Car toutes ses voies sont équitables.<br>Oui, je proclamerai le nom de l’Éternel,<br>Et je rendrai hommage à mon Dieu.<br>Car c’est un Dieu fidèle et sans iniquité,<br>C’est lui qui est juste et droit. (bis)'),
(170, NULL, 'Annonce-moi', '170', 'Ps 51', 'Annonce-moi l’allégresse et la joie,<br>Et les os que tu as brisés se réjouiront.<br>Oh ! Crée en moi un cœur pur !<br>Et renouvelle en moi un esprit bien disposé.<br>Rends-moi la joie de ton salut,<br>Et qu’un esprit de bonne volonté me soutienne.<br>Qu’un esprit de bonne volonté me soutienne.'),
(171, NULL, 'Chantez au Seigneur', '171', 'Ps 149', 'Chantez au Seigneur un cantique nouveau,<br>Et dans l’assemblée, chantez ses louanges.<br>Qu’on se réjouisse en celui qui créa<br>Les cieux, la terre, et tout ce qui s’y trouve.<br>Qu’on se réjouisse en celui qui créa<br>Les cieux et la terre.<br>Louez son nom, célébrez sa gloire ! (bis)'),
(172, NULL, 'Garde ton coeur', '172', 'Pr 4', 'Garde ton cœur plus que toute autre chose,<br>Car de lui viennent les sources de la vie.<br>Et de ta bouche écarte le mensonge,<br>Éloigne le détour de tes lèvres.<br>Que tes yeux regardent en face,<br>Et que tes paupières se dirigent devant toi.<br>Considère ton chemin et que toutes tes voies<br>Soient bien réglées.<br>N’incline pas à droite,<br>N’incline pas à gauche,<br>Et détourne ton pied du mal.<br>N’incline pas à droite,<br>N’incline pas à gauche,<br>Et détourne ton pied du mal.'),
(173, NULL, 'Je leur donnerai', '173', 'Jr 32', 'Je leur donnerai un même cœur,<br>Je leur donnerai une même pensée,<br>Pour qu’ils connaissent que je suis l’Éternel,<br>Et qu’ils me craignent pour leur bonheur<br>Et celui de leurs enfants,<br>Et qu’ils ne s’éloignent pas de moi.<br><br>J’aurai avec eux une alliance éternelle,<br>Je prendrai plaisir à leur faire du bien,<br>Et s’ils m’invoquent, oui, je leur répondrai,<br>Et je leur rendrai selon leurs voies.<br>S’ils s’attachent à l’Éternel,<br>Je ne me détournerai plus d’eux.'),
(174, NULL, 'Je t’exalterai', '174', 'Ps 145', '<span style=''padding-left:15px''></span>Je t’exalterai, ô mon Dieu,<br><span style=''padding-left:15px''></span>Et je bénirai ton nom.<br><span style=''padding-left:15px''></span>Oui, chaque jour je te bénirai,<br><span style=''padding-left:15px''></span>Et je célébrerai ton nom<br><span style=''padding-left:15px''></span>A toujours et à perpétuité.<br><span style=''padding-left:15px''></span>Mon Dieu je T’exalterai !<br><br>L’Éternel est grand, digne de louanges<br>Et Sa grandeur est insondable.<br>Puissent les générations célébrer ses œuvres<br>Et publier ses hauts faits !<br>Oui, je chanterai toujours tes merveilles<br>Et ta splendeur majestueuse.<br>Qu’on proclame en souvenir ta bonté immense<br>En célébrant ta justice !'),
(175, NULL, 'N’abandonnons pas l’Éternel', '175', 'Jr 2;15;Ps 36', 'N’abandonnons pas l’Éternel,<br>Lui qui est pour nous une source d’eau vive,<br>Pour nous creuser des citernes,<br>Des citernes crevassées.<br>Seigneur, tu n’es pas une source trompeuse,<br>Une eau dont on n’est pas sûr.<br>Seigneur, tu n’es pas une source trompeuse,<br>Une eau dont on n’est pas sûr.<br>Car auprès de toi est la source,<br>Car auprès de toi est la source de la vie.<br>Et par ta lumière nous voyons la lumière.<br>Combien est précieuse ta bonté, ô Dieu !<br>Oui de l’abondance de ton temple,<br>De ton abondance, tu rassasies les humains,<br>Et tu les abreuves à l’eau de ton torrent,<br>À l’eau du torrent de tes délices, Seigneur !'),
(176, NULL, 'Sentons et voyons', '176', 'Ps 34;Rm 11;Ps 90;89', 'Sentons et voyons combien le Seigneur<br>A de la bonté pour nous.<br>Goûtons-le toujours, en y demeurant fermes,<br>Pour ne pas être retranchés.<br>Rassasions nos cœurs de cette bonté<br>Aux fondements éternels,<br>Et que ses desseins soient toujours accomplis<br>Par la puissance de Dieu.<br><br>Tu es l’Éternel, l’Éternel<br>Compatissant et bon,<br>Lent à la colère, riche en bonté<br>Et en bienveillance.<br>Tu es l’Éternel, l’Éternel<br>Compatissant et bon,<br>Tu es l’Éternel. (bis)'),
(177, NULL, 'Ô Éternel fais-moi connaître', '177', 'Ps 25', '<span style=''padding-left:15px''></span>Ô Éternel, fais-moi connaître tes voies,<br><span style=''padding-left:15px''></span>Et enseigne-moi tes sentiers !<br><span style=''padding-left:15px''></span>Conduis-moi dans toute ta vérité.<br><span style=''padding-left:15px''></span>Ô mon Dieu, instruis-moi ! (bis)<br><span style=''padding-left:15px''></span>Car tu es mon Dieu, le Dieu de mon salut<br><span style=''padding-left:15px''></span>Tu es toujours mon espérance.<br><span style=''padding-left:15px''></span>Éternel, souviens-toi de ta bonté<br><span style=''padding-left:15px''></span>Et de ta bienveillance,<br><span style=''padding-left:15px''></span>Car elles sont éternelles.<br><br>L’Éternel a de la bonté;<br>Oui notre Seigneur est droit.<br>C’est pourquoi il montre aux pécheurs et aux humbles<br>Les sentiers de la justice.'),
(178, NULL, 'L’Éternel connaît', '178', 'Ps 94', '<span style=''padding-left:15px''></span>L’Éternel connaît les pensées de l’homme,<br><span style=''padding-left:15px''></span>Et il sait qu’elles sont vaines.<br><span style=''padding-left:15px''></span>Heureux l’homme que tu corriges, ô Éternel !<br><span style=''padding-left:15px''></span>Et que tu instruis, tu instruis par ta loi,<br><span style=''padding-left:15px''></span>Pour l’apaiser au jour du malheur.<br><span style=''padding-left:15px''></span>Car l’Éternel ne délaisse pas son peuple. (bis)<br><br>Si l’Éternel n’était pas mon secours,<br>Mon appui, mon refuge,<br>Mon âme serait bien vite<br>Dans la demeure du silence.<br>Quand je dis : "Mon pied chancelle !"<br>Ta bonté, ô Dieu, me sert d’appui,<br>Tes consolations réjouissent mon âme.'),
(179, NULL, 'Nous portons un merveilleux nom', '179', 'Jc 2;1 Pi 4;Mt 10', '<span style=''padding-left:15px''></span>Nous portons un merveilleux nom,<br><span style=''padding-left:15px''></span>C’est le nom du Seigneur.<br><span style=''padding-left:15px''></span>C’est pourquoi n’en ayons point honte,<br><span style=''padding-left:15px''></span>Il est notre Sauveur.<br><br>C’est la foi dans ce nom béni<br>Qui nous a sauvés;<br>Dans le nom de Jésus-Christ<br>Nous sommes délivrés.<br><br>Par lui, nous pouvons adresser<br>À Dieu nos prières,<br>Il saura nous exaucer,<br>Puisqu’il est notre Père.<br><br>Et si nous venons à souffrir<br>En tant que chrétiens,<br>N’en ayons point à rougir,<br>Mais louons-le sans fin.<br><br>Oui, confessons-le ici-bas,<br>Car un jour aux cieux<br>Jésus-Christ confessera<br>Notre nom devant Dieu.'),
(180, NULL, 'Exhortons-nous', '180', 'Hb 3; 10;1 Th 4', 'Exhortons-nous toujours les uns les autres,<br>Aussi loin que l’on peut dire : Aujourd’hui !<br>Afin qu’aucun de nous ne s’endurcisse<br>Par la séduction du péché.<br><br><span style=''padding-left:15px''></span>Car nous sommes devenus<br><span style=''padding-left:15px''></span>Participants de Christ,<br><span style=''padding-left:15px''></span>Pourvu que nous retenions jusqu’à la fin,<br><span style=''padding-left:15px''></span>Notre ferme assurance,<br><span style=''padding-left:15px''></span>Que nous avons depuis le commencement,<br><span style=''padding-left:15px''></span>Notre ferme assurance,<br><span style=''padding-left:15px''></span>Que nous avons depuis le commencement.<br><br>Édifions-nous toujours les uns les autres,<br>Car nous voyons s’approcher le grand jour.<br>Consolons-nous toujours par ces paroles,<br>Afin d’être trouvés veillant.'),
(181, NULL, 'Mon enfant, donne-moi ton coeur', '181', 'Pr 23;22;24', '<span style=''padding-left:15px''></span>Mon enfant, donne-moi ton cœur<br><span style=''padding-left:15px''></span>Et que tes yeux se plaisent dans mes voies.<br><span style=''padding-left:15px''></span>Si ton cœur est sage,<br><span style=''padding-left:15px''></span>Mon cœur à moi aussi se réjouira.<br><span style=''padding-left:15px''></span>Ne te fatigue pas pour t’enrichir :<br><span style=''padding-left:15px''></span>La richesse s’envole comme l’aigle.<br><span style=''padding-left:15px''></span>Prête l’oreille et écoute les paroles des sages,<br><span style=''padding-left:15px''></span>Afin que ta confiance soit en l’Éternel.<br><span style=''padding-left:15px''></span>Que ton cœur n’envie pas les pécheurs,<br><span style=''padding-left:15px''></span>Mais que tout le jour il craigne l’Éternel.<br><br>Celui qui veille sur ta vie,<br>Ne rendra-t-il pas à chacun selon ses voies ?<br>Connais la sagesse pour ton âme,<br>Si tu la trouves, il y a un avenir.'),
(182, NULL, 'Le Seigneur nous a accordé', '182', '2 Co 5;Rm 10', 'Le Seigneur nous a accordé un si grand salut<br>Que nous ne devons négliger.<br>Jusqu’aux lieux les plus éloignés, le Seigneur Jésus,<br>À travers nous, veut le porter.<br><br><span style=''padding-left:15px''></span>Car l’amour de Jésus-Christ nous presse,<br><span style=''padding-left:15px''></span>Notre Seigneur seul est mort pour tous<br><span style=''padding-left:15px''></span>Afin que les vivants ne vivent plus pour eux,<br><span style=''padding-left:15px''></span>Mais pour celui qui est mort et ressuscité pour eux.<br><br>Prêchons partout le nom nouveau de Jésus le Christ,<br>Qu’il soit invoqué à jamais !<br>Sur les montagnes, qu’ils sont beaux les pieds de celui<br>Qui s’en va publier la paix !'),
(183, NULL, 'Ô Éternel, Je cherche en toi mon refuge', '183', 'Ps 71', 'Ô Éternel, je cherche en toi mon refuge:<br>Que jamais je ne sois confondu !<br>Dans ta justice, sauve-moi, délivre-moi !<br>Sois pour moi un rocher qui me serve d’asile,<br>Où je puisse toujours me retirer.<br><br><span style=''padding-left:15px''></span>Ô Dieu, qui est semblable à toi ?<br><span style=''padding-left:15px''></span>Tu nous as fait éprouver bien des détresses;<br><span style=''padding-left:15px''></span>Mais tu nous redonneras la vie.<br><span style=''padding-left:15px''></span>En te célébrant, j’aurai la joie sur mes lèvres,<br><span style=''padding-left:15px''></span>La joie dans mon âme que tu as délivrée.<br><br>Car tu es mon espérance, Seigneur Éternel !<br>Et mon puissant refuge, c’est toi.<br>Je dirai, oui, je dirai tes œuvres, Éternel !<br>Que ma bouche soit toujours remplie de tes louanges,<br>Et que chaque jour elle te glorifie !'),
(184, NULL, 'Christ mourut pour nos péchés', '184', '1 Co 15;Cl 1', '<span style=''padding-left:15px''></span>Christ mourut pour nos péchés,<br><span style=''padding-left:15px''></span>Il fut enseveli, selon les Écritures.<br><span style=''padding-left:15px''></span>Puis le troisième jour, Jésus ressuscita<br><span style=''padding-left:15px''></span>Selon les Écritures.<br><span style=''padding-left:15px''></span>Voilà ce que nous avons reçu,<br><span style=''padding-left:15px''></span>Ce qui nous a été annoncé;<br><span style=''padding-left:15px''></span>C’est l’Évangile, par lui nous sommes sauvés,<br><span style=''padding-left:15px''></span>En lui demeurons fermes.<br><br>Que ce que nous avons entendu<br>Au commencement demeure en nous,<br>Ainsi nous demeurons dans le Fils et dans le Père.<br>Notre Seigneur a fait la promesse<br>Qu’il donnerait la vie éternelle.<br>La parole de la vérité,<br>Qui nous fit connaître l’espérance,<br>Du milieu de nous va grandissant de par le monde.<br>Que jamais nous n’écartions nos cœurs<br>De l’espérance de l’Évangile !'),
(185, NULL, 'Nous sommes plus que vainqueurs', '185', 'Rm 8', '<span style=''padding-left:15px''></span>Nous sommes plus que vainqueurs (bis)<br><span style=''padding-left:15px''></span>Par celui qui nous a tant aimés, tant aimés.<br><span style=''padding-left:15px''></span>Dans toutes nos épreuves<br><span style=''padding-left:15px''></span>Nous sommes plus que vainqueurs<br><span style=''padding-left:15px''></span>Par celui qui nous a tant aimés, aimés.<br><br>Nous savons que toutes choses<br>Concourent au bien<br>De tous ceux qui aiment Dieu,<br>Qui sont appelés.<br>Dieu nous a prédestinés<br>Et il nous a appelés,<br>Puis nous a justifiés et glorifiés.<br>Qui pourra nous accuser,<br>Qui pourra nous condamner ?<br>Jésus est ressuscité,<br>Intercédant pour nous.'),
(186, NULL, 'Moi qui étais autrefois', '186', '1 Tm 1', 'Moi qui étais autrefois<br>Un grand pécheur devant Dieu,<br>Rempli de haine et de meurtre,<br>Un homme violent,<br>J’ai obtenu le pardon<br>Du Dieu miséricordieux,<br>Car j’étais dans l’ignorance,<br>Dans l’incrédulité.<br>Oui, sa grâce a surabondé.<br><br><span style=''padding-left:15px''></span>C’est une parole certaine,<br><span style=''padding-left:15px''></span>Entièrement digne<br><span style=''padding-left:15px''></span>D’être reçue,<br><span style=''padding-left:15px''></span>Que Jésus-Christ est venu<br><span style=''padding-left:15px''></span>Dans le monde pour sauver les pécheurs,<br><span style=''padding-left:15px''></span>Dont moi, je suis le premier.<br><br>Jésus a montré en moi<br>Son éternelle patience,<br>Pour que je sois un exemple<br>Pour tous les croyants,<br>Qui, pour la vie éternelle,<br>Mettront leur confiance en lui.<br>Au Roi des siècles, immortel,<br>Invisible, et seul Dieu,<br>Soit la gloire pour toujours, amen !'),
(187, NULL, 'Le Fils est l’image', '187', 'Cl 1', 'Le Fils est l’image du Dieu invisible,<br>Le premier-né de toute la création.<br>En lui ont été créées toutes choses,<br>Qui sont dans les cieux et sur la terre.<br>(bis)<br><br>Tout a été créé par lui et pour lui. (bis)<br>Il est avant toutes choses, (bis)<br>Et toutes choses subsistent en lui. (bis)');
INSERT INTO `hymn` (`id`, `musical_part_id`, `title`, `num`, `ref`, `lyrics`) VALUES
(188, NULL, 'Tu es digne', '188', 'Ap 5;7', '<span style=''padding-left:15px''></span>Tu es digne de recevoir<br><span style=''padding-left:15px''></span>La louange, la majesté,<br><span style=''padding-left:15px''></span>Agneau de Dieu.<br><span style=''padding-left:15px''></span>À toi, pour l’éternité,<br><span style=''padding-left:15px''></span>Soient force, honneur et gloire !<br><br>Car tu versas ton sang,<br>Toi qui as racheté<br>Des hommes de toute nation.<br>Et tu as lavé leur vie du péché.<br>Au prix de ton sang, ils sont délivrés,<br>Et ils régneront, oui, à jamais.<br><br>Et le jour et la nuit,<br>Debout devant le trône,<br>Servant le Dieu de leur salut,<br>L’Agneau les conduit aux sources de vie.<br>Il sèche les pleurs, guérit les douleurs.<br>Ils sont consolés, oui, à jamais.'),
(189, NULL, 'Ainsi parle l’Éternel', '189', 'Jr 29;30;33', 'Ainsi parle l’Éternel :<br>Ta plaie est bien douloureuse.<br>Nul ne défend ta cause Pour bander ta plaie.<br>Car je t’ai frappé, oui je t’ai puni,<br>À cause de tes fautes, de tes péchés.<br><br><span style=''padding-left:15px''></span>Mais voici, je lui accorderai<br><span style=''padding-left:15px''></span>(Oui,)<br><span style=''padding-left:15px''></span>Un entier rétablissement.<br><span style=''padding-left:15px''></span>Je les guérirai et je leur ouvrirai<br><span style=''padding-left:15px''></span>Une source abondante<br><span style=''padding-left:15px''></span>De paix et de fidélité.<br><br>Moi, je connais les projets<br>Que je forme à ton sujet;<br>Projets de joie, de paix,<br>Et non de malheur.<br>Car ton avenir est plein d’espérance.<br>Oui, louez l’Éternel, car il est bon.'),
(190, NULL, 'Ce qui fait votre joie', '190', '1 Pi 1;5', 'Ce qui fait votre joie,<br>Malgré la tristesse présente,<br>Est que vous puissiez recevoir<br>Le salut pour prix de votre foi.<br>Mais il faut maintenant<br>Que l’épreuve attriste vos âmes,<br>Et que votre précieuse foi<br>Soit ainsi éprouvée comme l’or.<br><br><span style=''padding-left:15px''></span>Ceci produira, le jour où Jésus<br><span style=''padding-left:15px''></span>Apparaîtra dans le ciel,<br><span style=''padding-left:15px''></span>Louange, gloire et honneur. (bis)<br><span style=''padding-left:15px''></span>Lui que vos yeux n’ont pas encore vu,<br><span style=''padding-left:15px''></span>Vous l’aimez et croyez en lui. (bis)<br><br>Le Dieu de toute grâce,<br>Qui vous appela en Jésus<br>À son éternité glorieuse,<br>Affermira lui-même votre être.<br>Après le peu de temps<br>Que vous souffrirez ici-bas,<br>Le Père vous perfectionnera,<br>Afin de vous rendre inébranlables.'),
(191, NULL, 'Il a fait les montagnes', '191', 'Am 4;Jr 10;Rm 11', 'Il a fait les montagnes,<br>Il a créé le vent.<br>Il fait connaître à l’homme<br>Toutes ses pensées.<br>Il change les ténèbres<br>Et les transforme en jour.<br>Voici son nom : c’est Dieu, l’Éternel,<br>Le Dieu des armées.<br><br><span style=''padding-left:15px''></span>Nul n’est semblable à toi,<br><span style=''padding-left:15px''></span>À toi, ô Éternel !<br><span style=''padding-left:15px''></span>Tu es grand, et ton nom<br><span style=''padding-left:15px''></span>Est grand par ta puissance.<br><span style=''padding-left:15px''></span>Qui ne te craindrait, roi des nations ?<br><span style=''padding-left:15px''></span>C’est à toi que la crainte est due.<br><span style=''padding-left:15px''></span>Parmi tous les sages des nations,<br><span style=''padding-left:15px''></span>Nul n’est semblable à toi.<br><br>Ô profondes richesse,<br>Science et sagesse de Dieu !<br>Combien sont insondables<br>Tous ses jugements !<br>Quel homme a pu connaître<br>La pensée du Seigneur ?<br>Car c’est de lui, par lui et pour lui<br>Que sont toutes choses.'),
(192, NULL, 'Celui qui garde', '192', '1 Jn 3', '<span style=''padding-left:15px''></span>Celui qui garde ses commandements<br><span style=''padding-left:15px''></span>Demeure en Dieu et Dieu en lui.<br><br>Et c’est ici son commandement :<br>Que nous croyions au nom de Jésus-Christ,<br>Que nous nous aimions les uns les autres, (bis)<br>Comme il nous a aimEs (bis)<br><br>Nous avons connu l’amour de Dieu<br>En ce qu’il nous a donné son Fils Jésus.<br>Donnons aussi notre vie pour les frères,<br>Donnons notre vie, oui, pour les frères,<br>Comme il nous a aimés. (bis)'),
(193, NULL, 'Ce n’est pas par des fables', '193', '2 Pi 1', 'Ce n’est pas par des fables<br>Que nous vous avons fait connaître<br>La gloire et la puissance<br>Du Seigneur Jésus-Christ,<br>Sa gloire et son avènement.<br>Mais c’est comme ayant vu<br>Sa majesté de nos yeux,<br>Comme ayant entendu la voix du Père<br>Disant : "Celui-ci est mon Fils bien-aimé".<br>Croyez, croyez en lui,<br>Croyez en Jésus-Christ !<br><br>Et tenons pour certaine<br>Cette parole prophétique,<br>Donnée comme une lampe<br>Brillant dans les ténèbres,<br>Jusqu’à ce que le jour paraisse.<br>Que l’Étoile du matin<br>Se lève dans nos cœurs,<br>Jésus-Christ glorifié par Dieu, le Père<br>Disant : "Celui-ci est mon Fils bien-aimé".<br>Croyez, croyez en lui,<br>Croyez en Jésus-Christ !'),
(194, NULL, 'Toutes les voies', '194', 'Pr', '<span style=''padding-left:15px''></span>Toutes les voies de l’homme<br><span style=''padding-left:15px''></span>sont droites à ses yeux.<br><span style=''padding-left:15px''></span>Mais Celui qui pèse les cœurs, c’est l’Éternel.<br><span style=''padding-left:15px''></span>Reconnais-le dans toutes tes voies,<br><span style=''padding-left:15px''></span>Et il aplanira tes sentiers. (bis)<br><br>Si tu inclines ton cœur à la raison,<br>Si tu la cherches comme l’argent,<br>Alors tu comprendras la crainte de l''Éternel,<br>Tu trouveras la connaissance de Dieu.<br><br>Recommande à l’Éternel tes oeuvres,<br>Et tes projets, oui, réussiront.<br>L’Éternel a tout fait, oui, a tout fait pour un but,<br>L’Éternel a fait toutes choses pour un but.'),
(195, NULL, 'Il est un fleuve', '195', 'Ap 22;Ps 46;87', 'Il est un fleuve dont les courants<br>Réjouissent la cité de Dieu,<br>Le sanctuaire des demeures du Très-Haut.<br>Dieu est au milieu d’elle : elle ne chancelle pas;<br>Il la secourt dès l’aube du matin.<br><br>Elle est fondée sur les montagnes saintes.<br>L’Éternel aime les portes de Sion<br>Par privilège sur les autres demeures.<br>Elle est le sujet de propos glorieux.<br><br>Il est limpide comme le cristal,<br>Le fleuve de l’eau de la vie<br>Qui sort du trône de Dieu et de l’Agneau.<br>Sur les bords de ce fleuve<br>Siège l’arbre de vie,<br>Portant son fruit, guérissant les nations.'),
(196, NULL, 'Béni soit notre Dieu', '196', '2 Co 1', 'Béni soit notre Dieu,<br>Le Père de Jésus-Christ,<br>Le Père des miséricordes,<br>Qui est aussi le Dieu de toute consolation.<br>Il nous console dans nos afflictions,<br>Afin qu''à notre tour nous puissions consoler<br>Tous ceux qui sont aussi affligés.<br>Car, si nous avons part aux peines et aux tristesses,<br>Nous avons part à la consolation.<br>Et comme les souffrances abondent en notre cœur,<br>La consolation abonde en Jésus.'),
(197, NULL, 'Le coeur de l''homme', '197', 'Pr.16;19;27;Jc 4', '<span style=''padding-left:15px''></span>Le cœur de l''homme médite sa voie<br><span style=''padding-left:15px''></span>Mais c''est l''Éternel qui affermit ses pas;<br><span style=''padding-left:15px''></span>Il fait tout pour un but,<br><span style=''padding-left:15px''></span>L''Éternel est Roi.<br><br>Il y a dans le cœur de l''homme beaucoup de projets.<br>Mais c''est le dessein de l''Éternel qui toujours s''accomplit.<br>Ne te vante pas du lendemain, car tu ne sais ce qu''il enfantera.<br>Si le Seigneur le veut, tu vivras; ne te glorifie pas !'),
(198, NULL, 'Je bénis le Très-Haut', '198', 'Dn 4', 'Je bénis le Très-Haut et je glorifie<br>Celui qui vit éternellement,<br>Celui dont la domination<br>Ainsi que le royaume<br>Subsistent d''âge en âge.<br>Tous à ses yeux ne sont que néant.<br>Il agit comme il lui semble bon<br>Avec l''armée des cieux,<br>Avec les habitants de la terre.<br>Personne ne résiste à sa main;<br>Ni ne peut dire : "Que fais-tu ?"<br>Je glorifie le Roi des cieux<br>Dont les œuvres sont vraies<br>Et qui abaisse ceux qui marchent avec orgueil.<br>Je glorifie le Roi des cieux<br>Dont les œuvres sont vraies<br>Et qui abaisse ceux qui marchent avec orgueil.'),
(199, NULL, 'Cherchez premièrement', '199', 'Mt 6', '<span style=''padding-left:15px''></span>Cherchez premièrement le royaume de Dieu.<br><span style=''padding-left:15px''></span>Cherchez premièrement la justice de Dieu.<br><span style=''padding-left:15px''></span>Et toutes ces choses vous seront données,<br><span style=''padding-left:15px''></span>Oui, vous seront données par-dessus.<br><br>Ne dites pas : "Que mangerons-nous ?"<br>Ou : "Que boirons-nous ?"<br>Ne dites pas : "De quoi serons-nous donc vêtus ?"<br>Ne vous inquiétez pas du lendemain<br>Car il aura soin de lui-même;<br>À chaque jour suffit sa peine.'),
(200, NULL, 'Si vous invoquez', '200', '1 Pi 1;Hb 12;Rm 2;11;2 Co 7', 'Si vous invoquez comme Père<br>Celui qui juge l''œuvre de chacun,<br>Conduisez-vous tous avec crainte<br>Pendant votre séjour ici-bas.<br>C''est auprès de Dieu que se trouvent<br>Le pardon et l''oubli de nos péchés,<br>Mais il faut que les hommes éprouvent<br>Crainte, et respect pour sa sainteté.<br><br><span style=''padding-left:15px''></span>Nous avons reçu un royaume éternel,<br><span style=''padding-left:15px''></span>Qui ne sera jamais ébranlé ;<br><span style=''padding-left:15px''></span>Montrons au Seigneur notre reconnaissance,<br><span style=''padding-left:15px''></span>Avec crainte et piété, par l''adoration.<br><br>Ne méprisons pas les richesses<br>De la bonté de Dieu à notre égard;<br>Elle nous mène à la repentance;<br>Craignons sans nous livrer à l''orgueil.<br>Possédant de telles promesses,<br>Bien-aimés, purifions notre cœur<br>Du péché et de ses souillures,<br>En achevant notre sainteté.'),
(201, NULL, 'Voici, bénissez l''Éternel', '201', 'Ps 134;135', 'Voici, bénissez l''Éternel,<br>Vous tous ses serviteurs,<br>Qui vous tenez dans la maison De l''Éternel.<br>Élevez vos mains vers le sanctuaire,<br>Et bénissez, oui, bénissez l''Éternel !<br><br>Louez l''Éternel ! Chantez à son nom<br>Car il est bon ! Notre Seigneur est au-dessus<br>De tous les dieux.<br>Ses fidèles diront la gloire de son règne,<br>Ils parleront de sa puissance éternelle.<br><br>Venez, terre entière, avec joie,<br>Avec acclamations !<br>Reconnaissez que l''Éternel<br>Est le seul Dieu !<br>Entrez dans ses portes avec des louanges,<br>Célébrez-le, oui, bénissez l''Éternel !'),
(202, NULL, 'Ils rebâtiront', '202', 'Es 61;62', 'Ils rebâtiront sur d''anciennes ruines,<br>Ils relèveront d''antiques décombres.<br>Au lieu de la honte et de la confusion,<br>Ils auront la joie éternelle.<br>Ainsi feront-ils la joie de leur Dieu,<br>Alors les nations verront son salut.<br>Ils seront nommés les rachetés de Dieu,<br>Le peuple saint de l''Éternel.<br>Ils seront nommés les rachetés de Dieu,<br>Le peuple saint de l''Éternel.<br><br>Ils hériteront l''alliance éternelle.<br>Par fidélité, je leur donnerai<br>Une double part dont ils se réjouiront;<br>Telle sera leur rétribution.<br>On t''appellera par un nom nouveau,<br>Tu rayonneras dans la main de Dieu;<br>Tu ne seras plus nommée la délaissée,<br>Mais la couronne de l''Éternel.<br>Tu ne seras plus nommée la délaissée,<br>Mais la couronne de l''Éternel.'),
(203, NULL, 'Il s''est dépouillé', '203', 'Ph 2', 'Il s''est dépouillé lui-même<br>De sa condition divine<br>Pour devenir un simple homme,<br>Et un serviteur.<br>Devenant semblable aux hommes,<br>Il s''est humilié,<br>Se rendant obéissant<br>Jusqu''à la mort de la croix.<br>Aussi Dieu l''a souverainement élevé,<br>Lui donnant le nom<br>Qui est au-dessus de tout nom.<br><br>Que toute bouche et toute langue<br>Confesse Jésus-Christ,<br>Que tout genou partout fléchisse<br>Sur la terre et dans les cieux,<br>Afin qu''il soit proclamé<br>Que Jésus est Seigneur;<br>Que Jésus est Seigneur<br>À la gloire de Dieu le Père.'),
(204, NULL, 'Toi, que j''ai pris', '204', 'Es 41', 'Toi, que J''ai pris aux extrémités de la terre, (bis)<br>Toi, que J''ai pris, et que J''ai appelé d''une contrée lointaine<br>À qui J''ai dit :<br>"Tu es mon serviteur, je te choisis,<br>Je te choisis, ne te rejette point."<br><br>Ne crains rien, car je suis avec toi, (bis)<br>Ne promène pas des regards inquiets,<br>Car je suis ton Dieu, (bis)<br>Qui saisit ta main, qui te dit: "Ne crains rien !"<br>Car je suis ton Dieu, (bis)<br>Je te fortifie et je te soutiens,<br>De ma droite triomphante. (bis)'),
(205, NULL, 'L''Éternel est mon berger', '205', 'Ps 23', '<span style=''padding-left:15px''></span>L’Éternel est mon berger :<br><span style=''padding-left:15px''></span>Je ne manquerai de rien.<br><span style=''padding-left:15px''></span>Il me fait reposer dans de verts pâturages.<br><span style=''padding-left:15px''></span>Près des eaux paisibles et tranquilles,<br><span style=''padding-left:15px''></span>Dans les sentiers de la justice,<br><span style=''padding-left:15px''></span>Il restaure et conduit mon âme<br><span style=''padding-left:15px''></span>À cause de son nom.<br><br>Quand je marche dans la vallées<br>De l’ombre de la mort,<br>Je ne crains aucun mal,<br>Car tu es avec moi : (bis)<br>Ta houlette et ton bâton me rassurent.<br><br>Oui, en face de mes ennemis,<br>Tu dresses une table<br>Vis-à-vis de moi;<br>Et tu oins d’huile ma tête, (bis)<br>Tu remplis ma coupe et elle déborde.<br><br><span style=''padding-left:15px''></span>Oui, le bonheur et la grâce<br><span style=''padding-left:15px''></span>Chemineront avec moi<br><span style=''padding-left:15px''></span>Tout au long des jours de ma vie,<br><span style=''padding-left:15px''></span>Tous les jours de ma vie.<br><span style=''padding-left:15px''></span>Et je pourrai demeurer<br><span style=''padding-left:15px''></span>Dans la maison du Seigneur,<br><span style=''padding-left:15px''></span>Oui, jusqu’à la fin de mes jours,<br><span style=''padding-left:15px''></span>Pour la fin de mes jours.'),
(206, NULL, 'Oh, combien j''aime ta loi', '206', 'Ps 119', '<span style=''padding-left:15px''></span>Oh ! Combien j''aime ta loi, Seigneur,<br><span style=''padding-left:15px''></span>Car sur elle, toujours je médite.<br><span style=''padding-left:15px''></span>Que tes paroles sont douces à mon palais,<br><span style=''padding-left:15px''></span>Plus que le miel à ma bouche.<br><span style=''padding-left:15px''></span>Pour toujours ta loi est mon héritage,<br><span style=''padding-left:15px''></span>Elle est la joie de mon cœur.<br><br>Je vois des bornes à tout ce qui est parfait;<br>Tes commandements n''ont pas de limites.<br>À toujours, ô Éternel, ta parole subsiste dans les cieux.<br>Elle est éprouvée, elle est la vérité,<br>Ta justice est éternelle.'),
(207, NULL, 'Élargis l''espace de ta tente', '207', 'Es 54', '<span style=''padding-left:15px''></span>Élargis l’espace de ta tente,<br><span style=''padding-left:15px''></span>Qu’on déploie les couvertures de ta demeure:<br><span style=''padding-left:15px''></span>Ne retiens pas, ne retiens pas !<br><span style=''padding-left:15px''></span>Allonge tes cordages et affermis tes pieux.<br><br>Tu te répandras à droite,<br>Tu te répandras à gauche,<br>Tes enfants seront disciples du Seigneur,<br>Et tu seras affermie par la justice.'),
(208, NULL, 'Jésus, afin de sanctifier...', '208', 'Hb 13', 'Jésus, afin de sanctifier le peuple<br>Par son sang, a souffert hors de la porte.<br>Sortons donc hors du camp pour aller à lui,<br>En acceptant de porter son opprobre.<br>Car nous n''avons pas ici-bas<br>De cité permanente,<br>Mais nous cherchons celle qui est à venir.<br>Par lui, offrons sans cesse à Dieu<br>Un sacrifice de louange,<br>Le fruit de lèvres qui confessent son nom. (bis)'),
(209, NULL, 'Ton nom lui seul est élevé', '209', 'Ps 113;148', '<span style=''padding-left:15px''></span>Ton nom lui seul est élevé,<br><span style=''padding-left:15px''></span>Sa majesté est au-dessus des cieux,<br><span style=''padding-left:15px''></span>Au-dessus des nations,<br><span style=''padding-left:15px''></span>Oui, que ton nom soit célébré !<br><span style=''padding-left:15px''></span>Louez, serviteurs du Seigneur,<br><span style=''padding-left:15px''></span>Du lever du soleil jusqu’au couchant,<br><span style=''padding-left:15px''></span>Louez son nom béni,<br><span style=''padding-left:15px''></span>Dès maintenant et à jamais ! (fin : bis)<br><br>Qui donc est comparable à l’Éternel, notre Dieu ?<br>Son trône et sa demeure sont élevés dans les cieux.<br>Mais sur la terre, il porte ses regards,<br>De la poussière, il ôte l’indigent,<br>Du fumier, il retire le pauvre,<br>Afin de le placer avec les grands.'),
(210, NULL, 'Quel Dieu est semblable ?', '210', 'Mi 7;1 Jn 1;Ps 103', 'Quel Dieu est semblable à toi, Seigneur,<br>Qui seul peux pardonner l’iniquité,<br>Qui peux mettre en oubli<br>Les péchés de ton peuple,<br>Et ne peux conserver ta colère à toujours ?<br>Car Dieu prend plaisir à la bonté<br>Et il aura encore compassion de nous.<br>Sous ses pieds il mettra<br>Toutes nos transgressions,<br>Au fond des mers il les jettera.<br>Sous ses pieds il mettra<br>Toutes nos transgressions,<br>Au fond des mers il les jettera.<br><br>Quel Dieu est semblable à toi, Seigneur ?<br>Qui est fidèle et juste pour pardonner ?<br>Lorsque nous confessons<br>La faute et le péché,<br>Nous sommes purifiés par le sang de Jésus.<br>Si Dieu n’oubliait pas nos péchés,<br>Oui, comment pourrions-nous vivre devant lui ?<br>Mais autant l’Occident<br>S’éloigne de l’Orient,<br>Autant ils sont éloignés de nous.<br>Mais autant l’Occident<br>S’éloigne de l’Orient,<br>Autant ils sont éloignés de nous.'),
(211, NULL, 'J''espère en Jésus', '211', 'Ps 130;27', '<span style=''padding-left:15px''></span>J''espère en Jésus, oui, mon âme espère,<br><span style=''padding-left:15px''></span>En attendant sa promesse.<br><span style=''padding-left:15px''></span>Bien plus que les gardes<br><span style=''padding-left:15px''></span>S''attendent au matin,<br><span style=''padding-left:15px''></span>Mon âme s''attend à lui.<br><span style=''padding-left:15px''></span>Mets ton espoir en l''Éternel,<br><span style=''padding-left:15px''></span>Car le pardon et la rédemption<br><span style=''padding-left:15px''></span>sont près de lui en abondance.<br><span style=''padding-left:15px''></span>Oui, de tous tes péchés,<br><span style=''padding-left:15px''></span>C''est lui qui te rachète.<br><br>Fortifie-toi en l''Éternel, et que ton cœur s''affermisse !<br>Oh, si je n''étais pas certain de voir la bonté de l''Éternel<br>Sur la terre des vivants !'),
(212, NULL, 'Il est le Dieu vivant', '212', 'Dn 6;1 Tm 6', 'Il est le Dieu vivant, et il subsiste à jamais,<br>Son royaume ne sera jamais détruit.<br>Sa domination, oui, sa domination<br>Durera jusqu''à la fin.<br>C''est lui qui sauve et qui délivre par des signes et des prodiges,<br>Sur la terre et dans les cieux. (bis)<br><br>Le Dieu de toute gloire qui donne la vie à tous,<br>Qui habite une lumière inaccessible,<br>Est le Roi des rois, le Seigneur des seigneurs.<br>À lui puissance et honneur !<br>C''est lui qui sauve et qui délivre par des signes et des prodiges,<br>Sur la terre et dans les cieux. (bis)'),
(213, NULL, 'Ne méprise pas', '213', 'Hb 12', 'Ne méprise pas le châtiment du Seigneur,<br>Et ne perds pas courage lorsqu''il te reprend,<br>Car le Seigneur châtie ceux qu''il aime. (bis)<br>Fortifie-donc tes mains languissantes<br>Et tes genoux affaiblis !<br>Suis de tes pieds des voies droites, (bis)<br>Afin que ce qui est boiteux ne dévie,<br>Mais plutôt se raffermisse.<br>Afin que ce qui est boiteux ne dévie,<br>Mais plutôt se raffermisse.'),
(214, NULL, 'Je suis dans la maison de Dieu', '214', 'Ps 52', 'Je suis dans la maison de Dieu (bis)<br>Comme un olivier,<br>Comme un olivier verdoyant.<br>Je me confie dans ta bonté (bis)<br>Éternellement,<br>Éternellement, à jamais, à jamais.<br>(bis)<br><br>Je te louerai toujours (bis)<br>Parce que tu as agi,<br>Et je veux, je veux espérer<br>En ton nom,<br>En présence de tes fidèles. (bis)<br>Je veux espérer en ton nom<br>Car il est favorable.<br>Je veux espérer en ton nom<br>Car il est favorable.'),
(215, NULL, 'Si tu traverses les eaux', '215', 'Es 43;54', 'Si tu traverses les eaux,<br>Je serai avec toi.<br>Si tu traverses les fleuves,<br>Ils ne te submergeront pas.<br>Si tu passes par le feu,<br>Tu ne te brûleras pas,<br>Car je suis avec toi. (bis)<br><br><span style=''padding-left:15px''></span>Sois sans crainte, car je t''ai racheté,<br><span style=''padding-left:15px''></span>Je t''ai appelé par ton nom, par ton nom.<br><span style=''padding-left:15px''></span>Sois sans crainte car, toi, tu es à moi,<br><span style=''padding-left:15px''></span>Je suis avec toi, car je suis avec toi.<br><br>Si l''on forme des complots,<br>Je serai avec toi,<br>Et tout instrument de guerre<br>Sera sans effet contre toi.<br>Toute langue qui s''élève<br>Sera condamnée par toi,<br>Car je suis avec toi. (bis)'),
(216, NULL, 'Je disais: l''Éternel m''abandonne', '216', 'Es 49;Jr 31', 'Je disais: "L''Éternel, mon Dieu, m''abandonne,<br>Oui, le Seigneur m''oublie !<br>Ma destinée est cachée devant lui,<br>Il n''aperçoit point mon droit !"<br>Une femme oublie-t-elle l''enfant qu''elle allaite,<br>N''a-t-elle pas pitié du fruit de ses entrailles ?<br>Quand elle l''oublierait, je ne t''oublierai point;<br>Voici, je t''ai gravé sur mes mains, sur mes mains.<br><br><span style=''padding-left:15px''></span>N''es-tu pas pour moi comme un fils chéri,<br><span style=''padding-left:15px''></span>Un enfant qui fait toujours mes délices ?<br><span style=''padding-left:15px''></span>Car, plus je parle de toi,<br><span style=''padding-left:15px''></span>Plus ton souvenir est en moi.<br><span style=''padding-left:15px''></span>Aussi mes entrailles sont émues en ta faveur,<br><span style=''padding-left:15px''></span>Je suis ému en ta faveur.<br><span style=''padding-left:15px''></span>J''aurai compassion de toi.<br><br>Tu m''as châtié et j''ai été châtié<br>Comme un veau qui n''est pas dompté.<br>Fais-moi revenir et je reviendrai;<br>Tu es l''Éternel, mon Dieu !<br>Après ma faute je suis honteux et confus,<br>Car je porte l''opprobre de ma jeunesse.<br>Oh, fais-moi revenir, et je reviendrai,<br>Car tu es l''Éternel, mon Seigneur et mon Dieu!'),
(217, NULL, 'Je donnerai des lèvres pures', '217', 'So 3', '<span style=''padding-left:15px''></span>Je donnerai des lèvres pures (bis)<br><span style=''padding-left:15px''></span>À tous les peuples, à tous les peuples,<br><span style=''padding-left:15px''></span>Pour qu''ils invoquent tous mon nom. (bis)<br><br>Mes serviteurs viendront pour m''adorer,<br>Ensemble, à mon service.<br>Mes dispersés m''apporteront l''offrande<br>D''au-delà des fleuves.<br>J''ôterai du milieu de toi<br>Le triomphe de l''arrogance.<br>Je laisserai un peuple affaibli<br>Trouvant son refuge en mon nom.'),
(218, NULL, 'J''aurai pour eux', '218', 'Os 14', 'J''aurai pour eux un amour généreux<br>Je serai comme la rosée, la rosée.<br>Ils fleuriront comme le lis et ils s''enracineront.<br>Leurs rameaux s''étendront.<br>Ils auront la splendeur,<br>La magnificence de l''olivier, et la senteur du Liban.<br>Je les exaucerai, je les suivrai des yeux,<br>Je serai comme un cyprès verdoyant;<br>C''est de moi que vient ton fruit.<br><br>Oui, revenez, revenez à l''Éternel,<br>Dites-lui : "Ô Dieu, reçois-nous, reçois-nous !<br>Nous t''offrirons des sacrifices, ô Seigneur, pardonne-nous !"<br>Oui, tous ils reviendront pour s''asseoir à son ombre.<br>Ils redonneront de la vie, oui, de la vie au froment.<br>Je les exaucerai, je les suivrai des yeux,<br>Je serai comme un cyprès verdoyant;<br>C''est de moi que vient ton fruit.'),
(219, NULL, 'Louez l''éternel, car il est bon', '219', 'Ps 136', '<span style=''padding-left:15px''></span>(Femmes, pour tout le chant:)<br><span style=''padding-left:15px''></span>Car Sa miséricorde dure à toujours, (bis)<br><span style=''padding-left:15px''></span>À toujours !<br><br>(Hommes :)<br>Louez l''Éternel, car il est bon !<br>Louez le Seigneur des seigneurs,<br>Celui qui, seul, fait de grands prodiges,<br>Celui qui a fait les cieux avec intelligence !<br><br>Celui qui, sur les eaux, étendit la terre,<br>Celui qui fit les grands luminaires,<br>Le soleil pour dominer et présider le jour,<br>La lune et les étoiles pour dominer et présider la nuit.<br><br>Celui qui rappela à son souvenir<br>Le temps où nous étions humiliés,<br>Et qui nous délivra de ceux qui nous oppressaient.<br>Louez le Dieu des cieux; sa bienveillance dure à toujours !'),
(220, NULL, 'Éternel, tu me sondes', '220', 'Ps 139', '<span style=''padding-left:15px''></span>Éternel, tu me sondes et tu me connais,<br><span style=''padding-left:15px''></span>C''est toi qui as formé mes reins.<br><span style=''padding-left:15px''></span>Toutes tes œuvres sont des merveilles,<br><span style=''padding-left:15px''></span>Et mon âme le reconnaît bien.<br><br>Quand je n''étais qu''une masse informe,<br>Tes yeux, oui, tes yeux me voyaient,<br>Et sur ton livre étaient inscrits<br>Tous les jours qui étaient fixés,<br>Avant qu''aucun d''eux n''existât.<br><br><span style=''padding-left:15px''></span>Coda:<br><span style=''padding-left:15px''></span>C''est pourquoi je te louerai toujours,<br><span style=''padding-left:15px''></span>Tant que je vivrai, tant que je vivrai.<br><span style=''padding-left:15px''></span>C''est pourquoi je te louerai toujours<br><span style=''padding-left:15px''></span>Tant que j''existerai.'),
(221, NULL, 'Qu''ils sont beaux', '221', 'Rm 10', 'Qu''ils sont beaux, sur les montagnes,<br>Les pieds des messagers de paix !<br>Qu''ils sont beaux, sur les montagnes,<br>Les pieds de ceux qui annoncent la paix !<br>Comment pourront-ils invoquer<br>Celui en qui ils n''ont pas la foi ?<br>(Comment pourront-ils invoquer Dieu ?)<br>Comment croiront-ils en celu<br>Dont ils n''ont pas entendu la voix ?<br>(Comment pourront-ils avoir la foi ?)<br><br><span style=''padding-left:15px''></span>Selon qu''il est écrit :<br><span style=''padding-left:15px''></span>Qu''ils sont beaux les pieds de ceux qui<br><span style=''padding-left:15px''></span>sont les messagers<br><span style=''padding-left:15px''></span>Annonçant une bonne nouvelle<br><span style=''padding-left:15px''></span>Qui est l''Évangile de paix.<br><span style=''padding-left:15px''></span>(bis)<br><br>Car leurs voix, par toute la terre,<br>Ont fait retentir leurs accents !<br>Jusqu''aux extrémités de la terre,<br>Leurs voix sont allées faire entendre leurs accents !<br>Comment croiront-ils si nul homme<br>Ne se lève pour annoncer ?<br>(Comment croiront-ils si nul ne parle ?)<br>Se lèvera-t-il, s''il ne s''est trouvé<br>Personne pour l''envoyer ?<br>(Comment parler sans être envoyé ?)'),
(222, NULL, 'Convertis pour servir', '222', '1 Th 1;1 Pi 2;Cl 4;Ep 2', '<span style=''padding-left:15px''></span>Convertis pour servir le Dieu vivant et vrai,<br><span style=''padding-left:15px''></span>Appelés pour construire le saint temple du Seigneur,<br><span style=''padding-left:15px''></span>Approchez-le, pierre vivante<br><span style=''padding-left:15px''></span>Rejetée par les hommes,<br><span style=''padding-left:15px''></span>Mais choisie et précieuse devant Dieu.<br><span style=''padding-left:15px''></span>Prenez garde au ministère qui vous a été donné,<br><span style=''padding-left:15px''></span>Accordé, dans le Seigneur!<br><br>Vous êtes édifiés sur le fondement des apôtres,<br>Des prophètes, et de Jésus<br>Qui est la pierre angulaire.<br>En lui, tout l''édifice bien coordonné<br>S''édifie pour être un temple saint dans le Seigneur.'),
(223, NULL, 'Celui qui domine', '223', '2 Sm 23', '<span style=''padding-left:15px''></span>Celui qui domine sur les humains est juste,<br><span style=''padding-left:15px''></span>Celui qui domine dans la crainte de Dieu,<br><span style=''padding-left:15px''></span>Est semblable à la lumière du matin<br><span style=''padding-left:15px''></span>Quand le soleil se lève.<br><br>Il fait sortir de la terre,<br>Après la pluie, la verdure.<br>N''en est-il pas ainsi<br>De ma maison avec Dieu ?<br>Puisqu''il a fait avec moi<br>Une alliance éternelle,<br>Ne fera-t-il pas germer<br>Tout mon salut ?'),
(224, NULL, 'Non, pas à nous', '224', 'Ps 115;Es 42', '<span style=''padding-left:15px''></span>Non, pas à nous, Éternel, pas à nous,<br><span style=''padding-left:15px''></span>Mais à ton nom, à ton nom donne gloire !<br><span style=''padding-left:15px''></span>Pour ton amour, pour ta fidélité,<br><span style=''padding-left:15px''></span>Pour ta bonté, à toi seul soit la gloire!<br><span style=''padding-left:15px''></span>(bis)<br><br>"Jamais ma gloire ne sera pour un autre,<br>(Jamais ma gloire ne sera,)<br>Jamais ma gloire ne sera pour un autre !"<br>(Ne sera pour un autre.)<br>Dit le Seigneur, dit le Seigneur,<br>"Je ne donnerai pas ma gloire à un autre."<br>(Elle ne sera pour un autre.)'),
(225, NULL, 'Bénissez l''Éternel, vous, ses anges', '225', 'Ps 103', '<span style=''padding-left:15px''></span>Bénissez l''Éternel, vous, ses anges,<br><span style=''padding-left:15px''></span>Bénissez l''Éternel, vous, toutes ses armées,<br><span style=''padding-left:15px''></span>Oui, bénissez l''Éternel, vous, toutes ses œuvres,<br><span style=''padding-left:15px''></span>Dans tous les lieux de sa domination !<br><span style=''padding-left:15px''></span>Louange et gloire à l''Éternel !<br><br>La bienveillance de l''Éternel dure d''éternité en éternité,<br>Et sa miséricorde pour la postérité<br>De ceux qui gardent son alliance<br>Et se souviennent de ses commandements,<br>Afin de les accomplir. (bis)'),
(226, NULL, 'Louez l''Éternel, louez le seigneur !', '226', 'Ps 145-150', 'Louez l''Éternel, louez le Seigneur ! (bis)<br>Louez l''Éternel, car il est bon,<br>Et sa miséricorde demeure !<br><br>Chantez l''Éternel, chantez le Seigneur ! (bis)<br>Chantez l''Éternel, acclamez le Roi,<br>Le Souverain, le Roi de la terre !<br><br>Servez l''Éternel, servez le Seigneur ! (bis)<br>Servez l''Éternel, servez dans la joie,<br>Dans l''allégresse, en sa présence !'),
(227, NULL, 'À toi, Seigneur, la grandeur', '227', '1 Ch. 29', '<span style=''padding-left:15px''></span>À toi, Seigneur, la grandeur, la puissance,<br><span style=''padding-left:15px''></span>La splendeur, l''éternité et l''éclat !<br><span style=''padding-left:15px''></span>Car c''est de toi que viennent toutes choses:<br><span style=''padding-left:15px''></span>La richesse, l''abondance et la gloire.<br><br>C''est toi qui domines tout,<br>Toi qui t''élèves au-dessus de tout,<br>Souverainement.<br><br>Dans ta main est la puissance<br>Et le pouvoir de tout agrandir,<br>De tout affermir.<br><br><span style=''padding-left:15px''></span>Coda :<br><span style=''padding-left:15px''></span>Maintenant, notre Dieu, nous te célébrons,<br><span style=''padding-left:15px''></span>Et nous louons la splendeur de ton Nom.<br><span style=''padding-left:15px''></span>Tu éprouves le cœur et tu aimes la droiture;<br><span style=''padding-left:15px''></span>Éternel, nous te bénissons !<br><span style=''padding-left:15px''></span>(bis)'),
(228, NULL, 'Le salut est à notre Dieu', '228', 'Ap 7', 'Le salut est à notre Dieu<br>Et à l''Agneau, assis sur le trône !<br>Que le peuple adore son Dieu<br>En proclamant toujours son nom !<br><br><span style=''padding-left:15px''></span>La louange, la sagesse et la gloire à notre Dieu !<br><span style=''padding-left:15px''></span>Car l''honneur et la puissance sont à toi, notre Seigneur !<br><span style=''padding-left:15px''></span>Tu es digne de recevoir toute notre louange.<br><span style=''padding-left:15px''></span>À l''Agneau force et pouvoir !<br><span style=''padding-left:15px''></span>Oui, gloire à l''Agneau !<br><br>Celui qui vient viendra bientôt;<br>Il nous emportera dans sa gloire.<br>Que le peuple adore son Dieu<br>En célébrant sans cesse l''Agneau.'),
(229, NULL, 'Voici, tu aimeras', '229', 'Dt 6', 'Voici, tu aimeras l''Éternel Dieu,<br>Tu aimeras ton Dieu.<br>Oui, aime-le de toute ton âme,<br>De toutes tes forces, de tout ton cœur !<br>Et ces paroles que je te donne<br>Habiteront dès aujourd''hui dans ton cœur.<br>Oui, sers ton Dieu d''un cœur sans partage,<br>D''une âme fidèle et d''un cœur entier,<br>Et l''Éternel sera avec toi, sera avec toi ! (bis)<br><br>Voici, tu serviras l''Éternel Dieu,<br>Tu serviras ton Dieu.<br>(bis)<br>Écoute-le, gardant son alliance,<br>Aimant ses préceptes, honorant sa loi.<br>Si tu le cherches et si tu l''aimes,<br>Oui, tu verras que tu pourras le trouver.<br>Fortifie-toi, agis pour servir,<br>Sans l''abandonner, surtout ne crains rien,<br>Car l''Éternel sera avec toi, sera avec toi ! (bis)'),
(230, NULL, 'Ils viennent en pleurant', '230', 'Jr 31', '<span style=''padding-left:15px''></span>Ils viennent en pleurant,<br><span style=''padding-left:15px''></span>Et moi, je les conduis<br><span style=''padding-left:15px''></span>Au milieu de leurs larmes<br><span style=''padding-left:15px''></span>Et de leurs supplications.<br><span style=''padding-left:15px''></span>Moi, je les conduis<br><span style=''padding-left:15px''></span>Jusqu''à des torrents d''eau<br><span style=''padding-left:15px''></span>Par un chemin uni,<br><span style=''padding-left:15px''></span>Où ils ne chancellent pas.<br><br>Car moi, l''Éternel, je suis un Père<br>Pour le peuple que j''ai choisi.<br>Oui, celui-ci est mon premier-né;<br>Si je l''ai dispersé, je le rassemblerai;<br>Comme un berger, je le garderai.<br><br><span style=''padding-left:15px''></span>Coda:<br><span style=''padding-left:15px''></span>Jusqu''à des torrents d''eau,<br><span style=''padding-left:15px''></span>Et ils ne chancellent pas.'),
(231, NULL, 'Ô Dieu, tu comptes les pas', '231', 'Ps 56', '<span style=''padding-left:15px''></span>Ô Dieu, tu comptes les pas<br><span style=''padding-left:15px''></span>De ma vie errante.<br><span style=''padding-left:15px''></span>Ô, Dieu, recueille mes larmes,<br><span style=''padding-left:15px''></span>Recueille-les dans ton outre !<br><span style=''padding-left:15px''></span>Ne sont-elles pas inscrites dans ton livre ? (bis)<br><br>Je reconnais que Dieu est pour moi.<br>De l''Éternel, je loue la parole.<br>En l''Éternel Dieu je me confie;<br>Je ne craindrai donc rien.<br>Mes ennemis reculent ensemble<br>Au jour où je crie vers mon Dieu.<br>En l''Éternel Dieu je me confie;<br>Que peuvent contre moi des hommes ?<br>Ô Dieu, j''accomplirai tous mes voeux !<br><br>Je t''offrirai ma reconnaissance<br>Car c''est toi seul qui as délivré<br>Mon âme de la mort.<br>Éternel, mon Dieu, tu m''as gardé<br>En sauvant mes pieds de la chute<br>Pour que je marche devant ta face,<br>Seigneur, à la lumière des vivants,<br>À la lumière des vivants.'),
(232, NULL, 'Voici, oh! qu''il est agréable', '232', 'Ps 133', '<span style=''padding-left:15px''></span>Voici, oh! qu''il est agréable,<br><span style=''padding-left:15px''></span>Et qu''il est doux<br><span style=''padding-left:15px''></span>Pour des frères de demeurer ensemble,<br><span style=''padding-left:15px''></span>Demeurer ensemble!<br><br>C''est comme l''huile précieuse,<br>Comme la rosée de l''Hermon<br>Qui descend sur les montagnes de Sion.<br>Car c''est là que l''Éternel envoie la bénédiction,<br>La vie, oui, la vie pour l''éternité.'),
(233, NULL, 'Je te louerai', '233', 'Ps 104', '<span style=''padding-left:15px''></span>Je te louerai,<br><span style=''padding-left:15px''></span>Ô Dieu, tant que vivrai,<br><span style=''padding-left:15px''></span>J''invoquerai ton nom<br><span style=''padding-left:15px''></span>Tant que j''existerai !<br><span style=''padding-left:15px''></span>Que mes paroles te soient agréables !<br><span style=''padding-left:15px''></span>mon Rocher, et mon Rédempteur,<br><span style=''padding-left:15px''></span>Tu es mon Seigneur!<br><br>Ô Dieu, tu es digne<br>De recevoir la louange!<br>L''honneur et la gloire<br>Sont à toi pour l''éternité!'),
(234, NULL, 'L''Éternel restaure', '234', 'Ps 147', 'L''Éternel restaure et rétablit son peuple.<br>Il guérit tous ceux qui ont le cœur brisé.<br>Notre Dieu soutient les malheureux<br>En soignant leurs blessures.<br>Il connaît le nombre des étoiles;<br>À toutes il leur a donné un nom.<br>Oui, notre Seigneur est grand,<br>Puissant par sa force;<br>Son intelligence n''a point de limites.<br><br><span style=''padding-left:15px''></span>Dieu a de l''amour<br><span style=''padding-left:15px''></span>Pour ceux qui le craignent,<br><span style=''padding-left:15px''></span>Pour ceux dont le cœur s''attend à sa bonté.<br><span style=''padding-left:15px''></span>(bis)<br><br>L''Éternel prépare la pluie pour la terre;<br>Par sa vertu l''herbe germe sur les monts.<br>Il envoie ses ordres sur la terre,<br>Sa parole s''élance.<br>L''Éternel la révèle à son peuple,<br>À lui seul, et non pas aux nations.<br>Non, le Seigneur n''agrée pas<br>La force de l''homme;<br>L''Éternel ne met pas son plaisir en elle.'),
(235, NULL, 'Oh, je voudrais', '235', 'Jb 19', 'Oh, je voudrais que mes paroles,<br>Que mes paroles soient écrites,<br>Qu''elles soient écrites dans un livre !<br>Je voudrais qu''avec un burin de fer,<br>Je voudrais qu''avec du plomb<br>Elles soient pour toujours<br>Gravées dans le roc!<br><br><span style=''padding-left:15px''></span>Mais je sais que mon Rédempteur,<br><span style=''padding-left:15px''></span>Je sais que mon Rédempteur est vivant,<br><span style=''padding-left:15px''></span>Il est vivant !<br><span style=''padding-left:15px''></span>Je sais qu''il se lèvera le dernier sur la terre,<br><span style=''padding-left:15px''></span>Le dernier sur la terre ! (coda)<br><span style=''padding-left:15px''></span>Quand mon corps sera poussière, il se lèvera. (bis)<br><span style=''padding-left:15px''></span>Après que ma chair aura été détruite,<br><span style=''padding-left:15px''></span>Moi-même, oui, moi-même,<br><span style=''padding-left:15px''></span>Je contemplerai Dieu !<br><span style=''padding-left:15px''></span>(bis à la fin)<br><br><span style=''padding-left:15px''></span>Coda: Sur la terre !<br><br>C''est bien lui que je contemplerai<br>Et il me sera favorable.<br>Mes yeux le verront, non ceux d''un autre !<br>Maintenant mon témoin est dans le ciel,<br>Mon témoin est dans les cieux;<br>Mon cœur languit d''attente<br>Au-dedans de moi !'),
(236, NULL, 'Souviens-toi de l''Éternel', '236', 'Dt 8;Js 22', 'Souviens-toi de l''Éternel, ton Dieu,<br>Garde-toi d''oublier l''Éternel !<br>Reconnais en ton cœur<br>Que ton Dieu te châtie<br>Comme un homme châtie son enfant.<br>Prends soin d''observer ses commandements<br>Et de toujours les mettre en pratique.<br>Aimez l''Éternel, marchez dans ses voies,<br>Servez-Le de tout votre cœur !<br>Aimez l''Éternel, marchez dans ses voies,<br>Servez-Le de tout votre cœur !'),
(237, NULL, 'Vous qui étiez autrefois', '237', 'Cl 1', 'Vous qui étiez autrefois étrangers<br>Et ennemis de Dieu par vos pensées<br>Et par vos œuvres mauvaises,<br>Oui, par vos œuvres mauvaises,<br>Il vous a maintenant réconciliés<br>Par sa mort dans le corps de sa chair.<br><br><span style=''padding-left:15px''></span>Pour vous faire paraître<br><span style=''padding-left:15px''></span>Saints et sans défauts,<br><span style=''padding-left:15px''></span>Pour vous faire paraître<br><span style=''padding-left:15px''></span>Saints et sans reproches devant lui,<br><span style=''padding-left:15px''></span>Sans défauts et sans reproches devant Lui.<br><br>Demeurez donc établis dans la foi<br>Pour ne pas être éloignés de la grâce;<br>Cette divine espérance, (bis)<br>De l''Évangile qui fut annoncée<br>Sous le ciel à toute créature.'),
(238, NULL, 'Mon coeur exulte', '238', '1 Sm 2', '<span style=''padding-left:15px''></span>Mon cœur exulte en l''Éternel, notre Dieu ! (bis)<br><span style=''padding-left:15px''></span>Nul n''est saint comme toi, Éternel !<br><span style=''padding-left:15px''></span>Il n''est point de rocher comme notre Dieu !<br><br>Ne multipliez donc pas vos paroles,<br>Vos paroles toujours plus arrogantes !<br>Que l''audace ne sorte pas de votre bouche,<br>Car l''Éternel est un Dieu qui connaît tout. (bis)<br><br>L''Éternel fait mourir et il fait vivre,<br>l''Éternel fait mourir et il fait vivre.<br>C''est lui seul qui peut abaisser et relever,<br>Car il possède les colonnes de la terre. (bis) (Coda)<br><br><span style=''padding-left:15px''></span>Coda:<br><span style=''padding-left:15px''></span>Et c''est sur elles qu''il a posé le monde.<br><span style=''padding-left:15px''></span>Il gardera les pas de ses fidèles,<br><span style=''padding-left:15px''></span>Les pas de ses fidèles.<br><span style=''padding-left:15px''></span>Il gardera les pas de ses fidèles,<br><span style=''padding-left:15px''></span>Les pas de ses fidèles.'),
(239, NULL, 'Nous dirons aux générations', '239', 'Ps 78', 'Nous dirons aux générations futures<br>Les louanges de l''Éternel, notre Dieu !<br>Nous dirons aux générations futures<br>Les louanges de l''Éternel, notre Dieu !<br>Ce que nous avons entendu de nos pères<br>Nous ne le cacherons pas à leurs enfants,<br>(Nous le dirons à leurs enfants.)<br>Pour qu''ils ne soient pas indociles<br>Ainsi que l''étaient leurs pères avant eux,<br>Mais qu''ils mettent leur confiance en Dieu,<br>(Mais qu''ils placent leur...)<br>Leur confiance en Dieu,<br>(Confiance en Dieu, en Dieu.)<br>Sans mettre en oubli ses œuvres !<br>Mais qu''ils mettent leur confiance en Dieu,<br>(Mais qu''ils placent leur...)<br>Leur confiance en Dieu,<br>(Confiance en Dieu, en Dieu.)<br>Sans mettre en oubli ses œuvres !<br><br>Nous dirons aux générations futures<br>Les louanges de l''Éternel, notre Dieu !<br>Ce que nous avons entendu de nos pères<br>Nous ne le cacherons pas à leurs enfants, amen !'),
(240, NULL, 'Éternel, enseigne-moi', '240', 'Ps 86', 'Éternel, enseigne-moi tes voies !<br>Je marcherai dans ta vérité,<br>Donne à mon cœur la simplicité<br>Afin qu''il craigne toujours ton nom !<br>Je te célébrerai de tout cœur,<br>Toujours je te rendrai gloire, Seigneur,<br>Car ta bienveillance est grande pour moi.<br><br><span style=''padding-left:15px''></span>Tu es bon et clément,<br><span style=''padding-left:15px''></span>Riche en compassion<br><span style=''padding-left:15px''></span>Pour tous ceux qui t''invoquent !<br><span style=''padding-left:15px''></span>Tu es bon et clément,<br><span style=''padding-left:15px''></span>Riche en compassion,<br><span style=''padding-left:15px''></span>Oui, pour tous ceux qui t''invoquent !<br><br>Je t''invoque au jour de ma détresse,<br>Car toi, Éternel, Tu me réponds.<br>Un jour, toutes les nations viendront<br>Se prosterner devant toi, Seigneur !<br>Nul n''est comme toi parmi les dieux,<br>Rien ne ressemble à tes œuvres, ô Dieu,<br>Toi qui es grand et qui fais des prodiges!'),
(241, NULL, 'Pour moi, je chanterai', '241', 'Ps 59', '<span style=''padding-left:15px''></span>Pour moi, je chanterai ta force.<br><span style=''padding-left:15px''></span>Au matin, j''acclamerai ta bienveillance. (bis)<br><span style=''padding-left:15px''></span>(bis)<br><br>Car tu es pour moi une forteresse<br>Un refuge au milieu de la détresse.<br>Oui, tu es ma force et ma forteresse;<br>Je psalmodierai en ton honneur.<br>C''est toi qui me sauves,<br>Toi qui me délivres,<br>Je chanterai en ton honneur.'),
(242, NULL, 'Je suis l’Éternel', '242', 'Es 43;44', 'Je suis l’Éternel, je suis ton Sauveur<br>Je te prends par la main et je te délivre.<br>Je t’ai établi pour faire alliance avec toi,<br>Afin que tu sois la lumière des nations.<br><br><span style=''padding-left:15px''></span>C’est moi qui efface tes transgressions<br><span style=''padding-left:15px''></span>Pour l’amour de moi, par égard pour mon nom.<br><span style=''padding-left:15px''></span>Plaidons ensemble, et j’oublierai tes péchés,<br><span style=''padding-left:15px''></span>Car tu as un grand prix à mes yeux,<br><span style=''padding-left:15px''></span>Et je t’aime.<br><br>Je suis l’Éternel, oui, je t’ai choisi,<br>Je mettrai mon Esprit sur ta descendance.<br>Ils se confieront dans le nom de l’Éternel,<br>Et ils germeront au beau milieu des nations.<br><br><span style=''padding-left:15px''></span>Coda :<br><span style=''padding-left:15px''></span>Au sein des peuples,<br><span style=''padding-left:15px''></span>Leur gloire germera.<br><span style=''padding-left:15px''></span>Ils se confieront dans le nom de l''Éternel,<br><span style=''padding-left:15px''></span>Le nom de l''Éternel.'),
(243, NULL, 'Que l’étranger', '243', 'Es 56', 'Que l’étranger qui s’attache au Seigneur<br>Ne dise pas : «Il me retranchera !»<br>Car à tous ceux qui conserveront mon jour<br>Pour ne pas le profaner,<br>Mais pour mieux le sanctifier,<br><br><span style=''padding-left:15px''></span>Dieu dit : "Dans ma maison,<br><span style=''padding-left:15px''></span>Ils recevront une place et un nom."<br><span style=''padding-left:15px''></span>Dieu dit :"Dans ma cité,<br><span style=''padding-left:15px''></span>Mieux que des fils ils seront adoptés,<br><span style=''padding-left:15px''></span>Un nom éternel leur sera donné qui subsistera,<br><span style=''padding-left:15px''></span>Et jamais il ne périra."<br><br>Car l’étranger qui suivra le Seigneur<br>Pour le servir et l’aimer de tout cœur,<br>Sera conduit dans sa maison de prière;<br>Là, son Dieu le réjouira;<br>Ses offrandes il recevra.'),
(244, NULL, 'Ne dis pas', '244', 'Rm 10', 'Ne dis pas que le commandement<br>Est au-dessus de tes forces,<br>Et ne dis pas que la parole<br>Se trouve hors de ta portée.<br>Elle n’est pas aux confins de la mer<br>Ni dans l’étendue céleste;<br>Elle est tout près de toi<br>Dans ta bouche et dans ton cœur<br>Afin que tu la pratiques.<br>Elle est tout près de toi<br>Dans ta bouche et dans ton cœur<br>Afin que tu la pratiques.<br><br>Aujourd’hui, si tu entends sa voix,<br>N’endurcis pas ton cœur, ne te dérobe pas.<br>Car si tu confesses de ta bouche le nom de Jésus-Christ,<br>Et si tu crois tout au fond de ton cœur<br>Qu’il est ressuscité des morts,<br>Alors tu seras sauvé. (bis)'),
(245, NULL, 'Sois sans crainte', '245', 'Es 54', '<span style=''padding-left:15px''></span>Sois sans crainte, car tu ne seras pas honteux.<br><span style=''padding-left:15px''></span>Ne sois pas confus, (bis)<br><span style=''padding-left:15px''></span>Tu ne seras pas déshonoré.<br><span style=''padding-left:15px''></span>(Tu n''es pas déshonoré.)<br><br>"Mon alliance de paix ne chancellera pas."<br>(Mon alliance..)<br>A promis l''Éternel qui a pitié de toi.<br>(... durera.)<br>Car Sa bienveillance ne s''ébranlera pas.<br>(Ma bienveillance, a dit l''Éternel, ne s''ébranlera pas.)<br>"Tes enfants deviendront des disciples du Seigneur."<br>(Tes enfants se-)<br>A promis l''Éternel, ton Dieu, ton Rédempteur.<br>(-ront disciples.)<br>Je leur donnerai une prospérité.<br>(Ils recevront, a dit l''Éternel, une prospérité.)'),
(246, NULL, 'Seigneur, je t''exalte', '246', 'Ps 30', 'Seigneur, je t''exalte, car tu m''as relevé,<br>Et, de l''abîme, tu m''as fait remonter.<br>Lorsque j''ai crié à toi, toi tu m''as guéri,<br>Et changé mes larmes en allégresse.<br><br><span style=''padding-left:15px''></span>Chantez à l''Éternel, vous qui l''aimez,<br><span style=''padding-left:15px''></span>Chantez par vos louanges sa sainteté !<br><span style=''padding-left:15px''></span>Car sa colère dure un seul instant,<br><span style=''padding-left:15px''></span>Mais sa grâce toute la vie.<br><span style=''padding-left:15px''></span>Chantez à l''Éternel, vous qui l''aimez,<br><span style=''padding-left:15px''></span>Chantez par vos louanges sa sainteté !<br><span style=''padding-left:15px''></span>Le soir arrivent les pleurs,<br><span style=''padding-left:15px''></span>Et le matin, l''allégresse.<br><br>Éternel, mon Dieu, je te louerai toujours.<br>Je te louerai car tu m''as ceint de joie.<br>Tu as délié mon sac afin que mon cœur<br>Ne soit pas muet et qu''il te chante.'),
(247, NULL, 'Quand Christ notre vie', '247', 'Cl 3', 'Quand Christ, notre vie, paraîtra,<br>À notre tour nous paraîtrons<br>Avec lui dans sa gloire,<br>Avec lui dans sa gloire,<br>(1) Sa gloire. (2) Avec lui dans sa gloire.<br>(bis)<br><br>Coda:<br>Oui, nous paraîtrons dans sa gloire !<br>(Sa gloire !)'),
(248, NULL, 'Écoute, mon fils l''instruction', '248', 'Pr;Dt 32;Jr 6', 'Écoute, mon fils, l''instruction de ton père,<br>Ne rejette pas l''enseignement de ta mère.<br>Que la loyauté et la vérité<br>Ne t''abandonnent pas.<br>Lie-les à ton cou,<br>Écris-les dans ton cœur.<br>Garde, sans qu''elles s''éloignent,<br>La raison et la réflexion;<br>Elles seront la vie de ton âme,<br>La grâce pour ton cou.<br><br><span style=''padding-left:15px''></span>Souviens-toi des jours d''autrefois.<br><span style=''padding-left:15px''></span>Considère les années<br><span style=''padding-left:15px''></span>De génération en génération.<br><span style=''padding-left:15px''></span>Interroge ton père,<br><span style=''padding-left:15px''></span>Il te l''annoncera,<br><span style=''padding-left:15px''></span>Tes anciens, ils te le diront.<br><span style=''padding-left:15px''></span>Place-toi sur les chemins,<br><span style=''padding-left:15px''></span>Regarde et informe-toi<br><span style=''padding-left:15px''></span>Des antiques sentiers<br><span style=''padding-left:15px''></span>Où donc est le vrai chemin;<br><span style=''padding-left:15px''></span>Marches-y, oh oui, marches-y !<br><br>Je t''ai enseigné la voie de la sagesse,<br>Je t''ai enseigné le chemin de la droiture.<br>Saisis l''instruction, ne la lâche pas;<br>N''oublie pas mes paroles.<br>Ne suis pas des voies<br>Qui ne sont pas frayées.<br>N''entre pas dans le sentier des méchants,<br>Car la voie des justes<br>Resplendira comme la lumière<br>Jusqu''au milieu du jour.');
INSERT INTO `hymn` (`id`, `musical_part_id`, `title`, `num`, `ref`, `lyrics`) VALUES
(249, NULL, 'Dieu est grand', '249', 'Ps 48', 'Dieu est grand et digne de louanges<br>Dans sa ville, sur sa montagne sainte !<br>Belle est la colline du Seigneur,<br>Contemplez la cité du Roi,<br>Joie de toute la terre !<br><br><span style=''padding-left:15px''></span>Oui, nous pensons à Toi,<br><span style=''padding-left:15px''></span>Nous pensons à ton nom,<br><span style=''padding-left:15px''></span>À ta bonté au milieu de ton temple.<br><span style=''padding-left:15px''></span>Car ta louange retentit au loin,<br><span style=''padding-left:15px''></span>Jusqu''aux extrémités du monde.<br><span style=''padding-left:15px''></span>Oui, ta louange retentit au loin,<br><span style=''padding-left:15px''></span>Jusqu''aux extrémités du monde.<br><br>Car ta droite est pleine de justice.<br>Que ton peuple devant toi se réjouisse<br>Devant toi, devant tes jugements !<br>Oui, pour toujours jusqu''à la mort<br>Tu seras notre guide !'),
(250, NULL, 'Montrons au Seigneur', '250', 'Hb 6', 'Montrons au Seigneur, jusqu''à la fin,<br>Du zèle à son service<br>Pour garder une pleine espérance.<br>Ainsi, nous ne nous lasserons pas,<br>Nous suivrons ceux qui, par la foi,<br>Héritent des promesses.<br><br><span style=''padding-left:15px''></span>Non, Dieu n''est pas injuste pour oublier (bis)<br><span style=''padding-left:15px''></span>L''amour que vous n''avez cessé<br><span style=''padding-left:15px''></span>Pour lui de manifester<br><span style=''padding-left:15px''></span>En rendant vos services aux frères. (bis)<br><br>Dieu nous a accordé ses promesses,<br>Il nous a fait serment ;<br>Là nous puisons tout notre courage.<br>Nous dont le refuge est de saisir<br>Le salut que Dieu vint offrir,<br>Voilà notre espérance !'),
(251, NULL, 'Oui, je te loue', '251', 'Es 12;Ps13', 'Oui, je te loue, ô Éternel,<br>Car ta colère s''est détournée.<br>Oui je te loue, ô Éternel<br>Car c''est toi qui m''as consolé.<br>Si tu gardais le souvenir<br>Des iniquités, Éternel,<br>Qui devant toi subsisterait<br>C''est toi seul qui peux pardonner.<br><br><span style=''padding-left:15px''></span>Voici, Dieu est ma délivrance,<br><span style=''padding-left:15px''></span>Je serai plein de confiance,<br><span style=''padding-left:15px''></span>Car l''Éternel, Dieu, est ma force,<br><span style=''padding-left:15px''></span>Le sujet de mes louanges.<br><br>Regarde-moi, ô réponds-moi !<br>Ô donne à mes yeux la clarté<br>Pour que je ne m''endorme pas<br>Du sommeil profond de la mort.<br>Afin que tous mes ennemis<br>Ne disent pas: "Il est vaincu"<br>Qu''ils ne soient pas dans l''allégresse<br>Si je venais à chanceler.'),
(252, NULL, 'Sur tes murs, Jérusalem', '252', 'Es 62', 'Sur tes murs Jérusalem,<br>J''ai placé des gardes<br>Qui ne doivent jamais se taire.<br>Vous qui invoquez l''Éternel,<br>Point de répit pour vous !<br>Franchissez, franchissez les portes,<br>Frayez, oui, frayez la route,<br>Préparez un chemin pour mon peuple<br>Et dites-lui que son Sauveur vient.<br>Consolez, consolez mon peuple,<br>Criez-lui que sa servitude est finie<br>Jusqu''à ce que son salut paraisse,<br>Sa délivrance comme un flambeau,<br>Jusqu''à ce que son salut paraisse,<br>Sa délivrance comme un flambeau.'),
(253, NULL, 'Ô toi mon fils', '253', 'Es 44', 'Ô toi mon fils, tu es mon serviteur,<br>Je t’ai choisi, tu es mon serviteur.<br>Ô toi mon fils, je ne t’oublierai pas.<br>J’efface tes transgressions comme un nuage<br>Et tes péchés comme une nuée.<br>Reviens à moi car je t’ai racheté.<br>Je dis à l’abîme: dessèche-toi !<br>Je tarirai tes fleuves.<br><br>Prête l’oreille aux paroles de Dieu<br>Qui t’a formé, formé dès ta naissance.<br>Moi l’Éternel, j’ai fait toutes ces choses.<br>Oui, J’ai déployé les cieux, étendu la terre,<br>Je les appelle et ils se présentent.<br>Nuées des cieux, profondeurs de la terre,<br>Criez d’allégresse car Dieu l’a fait;<br>Sa gloire est manifestée dans son peuple entier. (bis)'),
(254, NULL, 'Vous mes enfants', '254', '1 Jn 3;2', 'Vous mes enfants, n''aimez pas en paroles,<br>Vous mes enfants, n''aimez pas avec la langue,<br>Mais en actions et en vérité, oui, en vérité.<br>Par là nous connaîtrons<br>Que nous sommes de la vérité,<br>Et nous rassurerons notre cœur devant lui.<br><br>Si notre cœur ne nous condamne pas,<br>Nous paraissons devant lui dans l''assurance.<br>Celui qui aime est dans la lumière, oui, dans la lumière.<br>Par là nous reconnaissons<br>Que nous demeurons en lui,<br>Et qu''il demeure en nous.<br>Son amour est parfait, L''amour de Dieu est en nous.'),
(255, NULL, 'Dieu a tant aimé', '255', 'Jn 3;Ga 2', '<span style=''padding-left:15px''></span>Dieu a tant aimé le monde<br><span style=''padding-left:15px''></span>Qu''il a donné son Fils unique,<br><span style=''padding-left:15px''></span>Afin que quiconque croit en lui<br><span style=''padding-left:15px''></span>Ait la vie éternelle et ne périsse pas.<br><br>Désormais, ma vie sur la terre<br>C''est vivre dans la foi au Fils de Dieu<br>Qui m''a aimé et s''est livré lui-même pour moi.<br>(bis)'),
(256, NULL, 'Grâce, miséricorde', '256', '', 'Grâce, miséricorde et paix de la part de Dieu, (bis)<br>Dieu le Père, Dieu notre Sauveur,<br>Et de Jésus, notre espérance!<br>Que la grâce et que la paix de Dieu <br>Vous soient accordées.<br>Que la grâce et que la paix de Dieu <br>Vous soient accordées.<br><br>Grâce, miséricorde et paix de la part de Dieu, (bis)<br>Que l''amour de notre Seigneur,<br>Avec la foi, vous soient donnés !<br>L''Éternel se souviendra de vous,<br>Vous et vos enfants.<br>L''Éternel se souviendra de vous,<br>Vous et vos enfants.'),
(257, NULL, 'Rassasie-nous', '257', 'Ps 90', 'Rassasie-nous chaque matin<br>De ta bonté, ô Éternel!<br>Et nous serons toute la vie<br>Dans l''allégresse et dans la joie.<br>Réjouis-nous autant de jours<br>Que tu nous as humiliés,<br>Autant d''années que nous avons<br>Vu le malheur.<br><br><span style=''padding-left:15px''></span>Que la grâce du Seigneur,<br><span style=''padding-left:15px''></span>Notre Dieu, soit sur nous,<br><span style=''padding-left:15px''></span>Affermis l''ouvrage de nos mains ! (bis)<br><br>Que ton action soit révélée<br>À chacun de tes serviteurs<br>Et que ta gloire soit répandue<br>À tout jamais sur leurs enfants.<br>Enseigne-nous à bien compter<br>Tous les jours de notre vie,<br>Afin que nous nous appliquions<br>À la sagesse.'),
(258, NULL, 'L''Éternel a de la bonté', '258', 'Lm 3', '<span style=''padding-left:15px''></span>L''Éternel a de la bonté<br><span style=''padding-left:15px''></span>Pour qui espère en lui,<br><span style=''padding-left:15px''></span>Pour celui qui Le cherche. (bis)<br><span style=''padding-left:15px''></span>Il est bon d''attendre en silence,<br><span style=''padding-left:15px''></span>D''attendre le secours.<br><span style=''padding-left:15px''></span>Il vient de l''Éternel. (bis)<br><br>Il est bon de porter le joug dans sa jeunesse,<br>De rester solitaire et silencieux<br>(solitaire, silencieux, silencieux)<br>Sans perdre espérance.<br>(Oh!..)<br>Sans perdre espérance.<br>C''est selon Sa miséricorde que Dieu nous afflige.<br>Ce n''est pas volontiers qu''il humilie<br>(Ce n''est pas volontiers qu''il afflige,)<br>Les enfants des hommes,<br>(Oh!...)<br>Les enfants des hommes.'),
(259, NULL, 'Lorsqu''au Seigneur', '259', '2 Co 3;4', 'Lorsqu''au Seigneur le cœur se convertit<br>Le voile est enlevé.<br>Selon l''Écriture le Seigneur, c''est l''Esprit,<br>Où il se trouve règne la liberté.<br><br><span style=''padding-left:15px''></span>Nous tous dont le visage est découvert,<br><span style=''padding-left:15px''></span>Nous sommes transformés,<br><span style=''padding-left:15px''></span>En contemplant ainsi qu''en un miroir,<br><span style=''padding-left:15px''></span>La gloire du Seigneur.<br><span style=''padding-left:15px''></span>Nous sommes transformés à son image,<br><span style=''padding-left:15px''></span>Nous sommes transformés de gloire en gloire,<br><span style=''padding-left:15px''></span>Ainsi que par le Seigneur, l''Esprit,<br><span style=''padding-left:15px''></span>Par le Seigneur, l''Esprit.<br><br>Dans notre cœur, Dieu, notre créateur,<br>Fait briller la lumière.<br>Sur la face de Jésus-Christ le Sauveur<br>Resplendit la connaissance du Père.'),
(260, NULL, 'De loin l''Éternel...', '260', 'Jr 31', '<span style=''padding-left:15px''></span>De loin l''Éternel se montre à moi :<br><span style=''padding-left:15px''></span>"Je t''aime d''un amour éternel,<br><span style=''padding-left:15px''></span>C''est pourquoi je te conserve ma bienveillance,<br><span style=''padding-left:15px''></span>Ma bienveillance."<br><span style=''padding-left:15px''></span>De loin l''Éternel se montre à moi :<br><span style=''padding-left:15px''></span>"Je t''aime d''un amour éternel,<br><span style=''padding-left:15px''></span>Je te rebâtirai et tu seras rebâtie, maison du Seigneur." (bis)<br><br>Tu auras encore tes tambourins pour parure<br>Et tu sortiras au milieu des danses joyeuses.<br>Tu planteras toujours tes vignes<br>Sur les montagnes de ton pays.<br>Tu planteras encore et récolteras.<br><br>Car il est un jour où ceux qui veillent s''écriront :<br>"Levez-vous, montons ensemble auprès du Seigneur !"<br>Car ainsi notre Seigneur parle :<br>"Poussez des cris de joie sur mon peuple,<br>Éclatez d''allégresse devant les nations !"'),
(261, NULL, 'Revêtue de sa gloire', '261', 'Apoc. 19;21;22', 'Revêtue de sa gloire,<br>Venant d''auprès de Dieu,<br>Son éclat se compare<br>À ceux de minéraux précieux.<br>La ville sainte vient du ciel;<br>Notre Jérusalem !<br>L''Agneau sera au milieu d''elle;<br>Plus jamais d''anathème !<br><br><span style=''padding-left:15px''></span>Ses serviteurs le serviront, ils le serviront,<br><span style=''padding-left:15px''></span>Et son nom sera sur leur front, sur leur front.<br><span style=''padding-left:15px''></span>Ses serviteurs le serviront, ils verront sa face,<br><span style=''padding-left:15px''></span>Et son saint nom sera sur leur front;<br><span style=''padding-left:15px''></span>Pour toujours ils verront sa face.<br><br>Christ a donné son ange<br>Pour parler aux églises.<br>Quand montent nos louanges<br>L''Épouse et le Saint-Esprit disent :<br>"Amen, oui, viens Seigneur Jésus !"<br>Il dit : "Je viens bientôt."<br>Que tous ceux qui l''ont attendu<br>Célèbrent le Très-Haut !<br><br>L''Épouse se prépare ;<br>Bientôt vient le festin.<br>Ainsi elle se pare<br>Avec un habit de fin lin.<br>Louez notre Dieu, le Seigneur,<br>Vous tous, petits et grands!<br>Vous tous, venez Ses serviteurs,<br>Louez le Tout-Puissant !<br><br><span style=''padding-left:15px''></span>Ses serviteurs le serviront, ils le serviront,<br><span style=''padding-left:15px''></span>Et son nom sera sur leur front, sur leur front.<br><span style=''padding-left:15px''></span>Ses serviteurs le serviront, ils le serviront,<br><span style=''padding-left:15px''></span>Son nom sera écrit sur leur front,<br><span style=''padding-left:15px''></span>Et ils verront sa face.'),
(262, NULL, 'C''est Dieu qui pardonne', '262', 'Ps 103', 'C''est Dieu qui pardonne tes iniquités,<br>Qui guérit toutes tes maladies.<br>C''est lui qui délivre ta vie de la fosse,<br>Qui te couronne de compassion.<br>C''est lui qui rassasie de biens ta vieillesse,<br>Qui te fait rajeunir comme l''aigle.<br>C''est lui qui délivre ta vie de la fosse,<br>Qui te couronne de compassion.<br><br>Mon âme, bénis l''Éternel ! (bis)<br>(Bénis l''Éternel !)<br>Que tout ce qui est en moi<br>(Que tout en moi...)<br>Bénisse son nom.<br>Mon âme, bénis l''Éternel !<br><br>Mon âme, bénis l''Éternel ! (bis)<br>(Bénis l''Éternel !)<br>Que tout ce qui est en moi<br>Bénisse son nom.<br>(Bénisse le nom...)<br>Mon âme, bénis l''Éternel !<br>(De l''Éternel !)'),
(263, NULL, 'Là-haut, là-haut', '263', '', 'Là-haut, là-haut, dans la maison du Père<br>Est la demeure où je dois être admis.<br>Ils sont là-haut les vrais biens que j''espère<br>Et le repos qui m''est promis.<br>Par la valeur de l''œuvre expiatoire<br>Qui m''a rendu parfait devant mon Dieu,<br>J''hériterai l''incorruptible gloire<br>Près de l''Agneau dans le saint lieu.<br><br>Jamais péché ni douleur ni détresse<br>Ne m''atteindront dans cet heureux séjour.<br>Le deuil fait place à la pure allégresse<br>Dans les parvis du Dieu d''amour.<br>Mes yeux verront la beauté immortelle<br>Du Rédempteur sur son trône exalté.<br>Aux rayons de sa lumière éternelle<br>Tout sera joie et sainteté.'),
(264, NULL, 'Écoute, écoute', '264', 'Es 44', 'Écoute, écoute, oui, toi que J''ai choisi.<br>Ainsi dit l''Éternel : "C''est bien moi qui t''ai fait<br>Et qui, dès ta naissance, t''ai formé et porté."<br>Je suis ton soutien, Je suis ton soutien.<br>Ne crains rien, oui, ne crains rien, ne crains rien ! (bis)<br><br>Je répandrai des eaux sur le sol altéré<br>Ainsi que des ruisseaux sur la terre desséchée.<br>Et sur ta descendance, répandrai mon Esprit,<br>Et Ma bénédiction sur ta postérité.<br>Ne crains rien, oui, ne crains rien, ne crains rien ! (bis)<br><br>Auprès des courants d''eau, comme un saule ils pousseront<br>Et celui-ci dira : "Je suis à l''Éternel!"<br>Un autre avec amour dira le nom d''Israël,<br>De sa main écrira : "Je suis à l''Éternel."<br>Ne crains rien, oui, ne crains rien, ne crains rien ! (bis)'),
(265, NULL, 'Vous qui êtes la lumière', '265', 'Mt 5', '<span style=''padding-left:15px''></span>Vous qui êtes la lumière,<br><span style=''padding-left:15px''></span>La lumière dans ce monde,<br><span style=''padding-left:15px''></span>Que votre lumière luise,<br><span style=''padding-left:15px''></span>Qu''elle luise pour les hommes<br><span style=''padding-left:15px''></span>Afin qu''ils voient vos bonnes œuvres,<br><span style=''padding-left:15px''></span>Et qu''ils glorifient Dieu.<br><span style=''padding-left:15px''></span>Vous qui êtes la lumière,<br><span style=''padding-left:15px''></span>La lumière dans ce monde,<br><span style=''padding-left:15px''></span>Que votre lumière luise<br><span style=''padding-left:15px''></span>Et brille pour tous les hommes.<br><br>Une ville sur une montagne<br>Jamais ne sera cachée.<br>On n''allume pas une lampe<br>Pour qu''elle soit sous le boisseau,<br>Mais elle doit se poser sur le chandelier<br>Afin qu''elle éclaire<br>Ceux qui habitent la maison.'),
(266, NULL, 'À cause de son avidité', '266', 'Es 57', 'À cause de son avidité coupable,<br>Je me suis irrité, et je l''ai frappé.<br>Je me suis caché dans mon indignation,<br>Et le rebelle a suivi le chemin de son cœur.<br><br><span style=''padding-left:15px''></span>J''ai vu ses voies et je le guérirai<br><span style=''padding-left:15px''></span>Et je lui servirai de guide.<br><span style=''padding-left:15px''></span>Je le consolerai,<br><span style=''padding-left:15px''></span>Lui, et ceux qui pleurent avec lui.<br><span style=''padding-left:15px''></span>Heureux celui à qui les transgressions<br><span style=''padding-left:15px''></span>Et le péché sont pardonnés !<br><span style=''padding-left:15px''></span>Celui qui se confie en Dieu<br><span style=''padding-left:15px''></span>Est environné de sa grâce.<br><br>Mais les méchants ressemblent aux flots terribles<br>Qui jamais ne s''apaisent, dont les eaux bouillonnent.<br>Il n''est point de paix, dit Dieu, pour les méchants;<br>Leurs sentiers sont tortueux; point de justice en eux !'),
(267, NULL, 'Demandez la paix de Jérusalem', '267', 'Ps 122', 'Demandez la paix, la paix de Jérusalem !<br>Cette ville est bâtie pour être un témoin.<br>Je suis dans la joie à tes portes, Jérusalem !<br>Mes pieds se sont arrêtés à la maison du Seigneur.<br><br><span style=''padding-left:15px''></span>Je fais des vœux pour ton bonheur,<br><span style=''padding-left:15px''></span>Je fais des vœux pour ton repos,<br><span style=''padding-left:15px''></span>À cause de notre maison,<br><span style=''padding-left:15px''></span>La maison de notre Dieu.<br><span style=''padding-left:15px''></span>À cause de tous mes amis,<br><span style=''padding-left:15px''></span>De tous mes frères, je te dis:<br><span style=''padding-left:15px''></span>«Que ceux qui t’aiment pour toujours<br><span style=''padding-left:15px''></span>Jouissent ensemble du repos».<br><br>Demandez la paix, la paix de Jérusalem !<br>Là se sont réunies les tribus de Dieu,<br>Pour louer le nom, le saint nom de l’Éternel,<br>Afin de célébrer Dieu dans la maison du Seigneur.'),
(268, NULL, 'Ne t’irrite pas', '268', 'Ps 37', 'Ne t’irrite pas contre les méchants,<br>Non, n’envie pas tous ceux qui font le mal,<br>Car ils seront fauchés comme l’herbe,<br>Ils se flétriront comme le gazon vert.<br><br><span style=''padding-left:15px''></span>Abandonne la fureur, laisse la colère,<br><span style=''padding-left:15px''></span>Ne t’irrite pas, ce serait mal faire,<br><span style=''padding-left:15px''></span>Car j’ai été jeune et j’ai vieilli<br><span style=''padding-left:15px''></span>Et je n’ai jamais vu<br><span style=''padding-left:15px''></span>Que le juste soit abandonné<br><span style=''padding-left:15px''></span>Et que ses enfants mendient.<br><span style=''padding-left:15px''></span>Car l’Éternel connaît chacun de tes jours<br><span style=''padding-left:15px''></span>Il prend plaisir à ta voie,<br><span style=''padding-left:15px''></span>Il affermit tes pas.<br><span style=''padding-left:15px''></span>Viendrais-tu à tomber,<br><span style=''padding-left:15px''></span>Il prendrait ta main.<br><span style=''padding-left:15px''></span>Tu ne chancellerais pas,<br><span style=''padding-left:15px''></span>Mets ton espoir en Dieu,<br><br><span style=''padding-left:15px''></span>(2e fois : Coda) En Dieu !<br><br>Si de l’Éternel tu fais tes délices,<br>Il t’offrira ce que ton cœur désire.<br>Recommande ton sort à ton Dieu,<br>En lui mets ta foi et Il agira.<br><br><span style=''padding-left:15px''></span>Coda :<br><span style=''padding-left:15px''></span>Ta justice paraîtra ainsi que la lumière<br><span style=''padding-left:15px''></span>Et ton droit comme le soleil,<br><span style=''padding-left:15px''></span>Lorsqu’il brille en plein midi.'),
(269, NULL, 'Si, Jésus, quelqu’un veut te suivre', '269', '', 'Si, Jésus, quelqu’un veut te suivre,<br>Qu’il se charge toujours de sa croix.<br>Pour ses biens qu’il renonce à vivre,<br>En perdant tout à cause de toi.<br><br><span style=''padding-left:15px''></span>Ô Seigneur, tu sais que je t’aime,<br><span style=''padding-left:15px''></span>De moi je ne veux faire aucun cas;<br><span style=''padding-left:15px''></span>Aussi je renonce à moi-même,<br><span style=''padding-left:15px''></span>Pour suivre ma course avec joie.<br><span style=''padding-left:15px''></span>Que ta promesse me soutienne:<br><span style=''padding-left:15px''></span>«Qui perd sa vie la retrouvera».<br><span style=''padding-left:15px''></span>Et dans la foi je la fais mienne;<br><span style=''padding-left:15px''></span>Ainsi, ton amour l’accomplira.<br><br>Aimes-tu ton père ou ta mère,<br>Ta femme, tes enfants plus que moi ?<br>Si c’est ta vie que tu préfères,<br>Tu ne peux être digne de moi.<br><br>Sois crucifié pour le monde;<br>Que pour toi ses attraits soient ternis.<br>Qu’ainsi la vie de Christ abonde ;<br>De la croix ne sois pas l’ennemi.'),
(270, NULL, 'Qui me delivrera ?', '270', 'Rm 5;8', 'Qui me délivrera, qui me délivrera ? Grâces soient rendues à Dieu Par Jésus,<br>notre Seigneur !<br>Car il n’y a maintenant<br>Pour ceux qui sont en JésusAucune condamnation ! (bis)<br>La loi de l’Esprit de Vie,<br>Celle qui est en Jésus-Christ, Pour toujours m’a délivré De la mort et du péché.<br>Grâces soient rendues à Dieu<br>Par Jésus-Christ notre Seigneur !'),
(271, NULL, 'Oui, mon alliance avec lui', '271', 'Ml 2', '<span style=''padding-left:15px''></span>Oui, mon alliance avec lui subsiste,<br><span style=''padding-left:15px''></span>Dit le Seigneur, l’Éternel.<br><span style=''padding-left:15px''></span>C’est une alliance de vie, une alliance de paix<br><span style=''padding-left:15px''></span>Afin qu’il me craigne ;<br><span style=''padding-left:15px''></span>Et il éprouvera pour moi de la crainte,<br><span style=''padding-left:15px''></span>Il trembla devant mon nom.<br><span style=''padding-left:15px''></span>Non, jamais on ne trouvait<br><span style=''padding-left:15px''></span>Mensonge ou fausseté dans sa bouche,<br><span style=''padding-left:15px''></span>Mais plutôt l’on entendait<br><span style=''padding-left:15px''></span>La loi de vérité sur ses lèvres.<br><br>Il a marché avec moi, dans la paix, la droiture,<br>En sauvant beaucoup d’hommes.<br>Car le sacrificateur détient la connaissance;<br>C’est à sa bouche qu’on demande la loi,<br>Car il est un messager du Seigneur,<br>Un envoyé de l’Éternel.'),
(272, NULL, 'Toi qui es notre Père', '272', '', 'Si, méchants que nous sommes,<br>À nos fils nous donnons<br>Le pain qui vient des hommes,<br>À plus forte raison,<br>Quand Tes enfants réclament, tu ne refuses pas<br>Et tu nourris leur âme de ton divin repas.<br><br><span style=''padding-left:15px''></span>Toi qui es notre Père,<br><span style=''padding-left:15px''></span>Toi qui nous donnes vie,<br><span style=''padding-left:15px''></span>De Toi nos cœurs espèrent<br><span style=''padding-left:15px''></span>L’Esprit qui est promis.<br><span style=''padding-left:15px''></span>Oh, reçois les offrandes !<br><span style=''padding-left:15px''></span>Du peuple qui te prie !<br><span style=''padding-left:15px''></span>À ceux qui le demandent<br><span style=''padding-left:15px''></span>Accorde ton Esprit !<br><br>Nous avons confiance en ce que tu as dit.<br>Sur notre descendance, tu gardes ton Esprit.<br>Ô Dieu, fais qu’il repose sur nos petits enfants;<br>Dans leur bouche dépose ta Parole en tout temps.'),
(273, NULL, 'Approchons-nous', '273', 'Hb 4;10', 'Approchons-nous du trône de la grâce,<br>Pour obtenir de Dieu miséricorde.<br>Approchons-nous afin de trouver grâce,<br>Grâce et secours au sein de nos besoins.<br>Car nous avons un sacrificateur;<br>Jésus le Fils, qui traversa les cieux.<br><br>Approchons-nous avec un cœur sincère,<br>Rempli de foi, purifié par son sang.<br>Approchons-nous, le corps lavé d’eau pure<br>Et la conscience purifiée du péché.<br>Car nous avons, grâce au sang de Jésus,<br>Un libre accès au sein du sanctuaire.<br><br><span style=''padding-left:15px''></span>Coda :<br><span style=''padding-left:15px''></span>Car nous avons, grâce au sang de Jésus,<br><span style=''padding-left:15px''></span>Un libre accès au sanctuaire<br><span style=''padding-left:15px''></span>Par le chemin inauguré pour nous,<br><span style=''padding-left:15px''></span>Chemin nouveau et vivant au travers du <span style=''padding-left:15px''></span>voile.<br><span style=''padding-left:15px''></span>Retenons fermement notre espérance !'),
(274, NULL, 'Ces commandements', '274', 'Dt 6', '<span style=''padding-left:15px''></span>Ces commandements que je te donne<br><span style=''padding-left:15px''></span>Seront dans ton cœur.<br><span style=''padding-left:15px''></span>(bis)<br><span style=''padding-left:15px''></span>Lorsque tu iras dans ta maison,<br><span style=''padding-left:15px''></span>Quand tu iras en voyage,<br><span style=''padding-left:15px''></span>Tu enseigneras à tes enfants<br><span style=''padding-left:15px''></span>Tous mes commandements.<br><br>Ils seront comme un signe,<br>Sur tes mains tu les lieras.<br>Il seront comme des fronteaux<br>Entre tes yeux.<br>Écris-les sur tes portes,<br>Sur les poteaux de ta maison;<br>L’Éternel te donnera<br>Le séjour qu’il t’a promis.'),
(275, NULL, 'Moi, je suis toujours avec toi', '275', 'Ps 73;Jr 15;20', 'Moi, je suis toujours avec toi,<br>Car tu m’as saisi la main droite.<br>Et par ton conseil, tu me dirigeras,<br>Puis tu me recevras dans ta gloire.<br>Quel autre ai-je au ciel que toi ?<br>Sur terre, je ne prends plaisir qu’en toi.<br><br><span style=''padding-left:15px''></span>Tu resteras toujours<br><span style=''padding-left:15px''></span>Le rocher de mon cœur,<br><span style=''padding-left:15px''></span>Car m’approcher de toi<br><span style=''padding-left:15px''></span>Restera mon seul bien.<br><span style=''padding-left:15px''></span>Et même si mon cœur<br><span style=''padding-left:15px''></span>Et ma chair se consument,<br><span style=''padding-left:15px''></span>Tu resteras toujours<br><span style=''padding-left:15px''></span>mon partage et mon bien.<br><span style=''padding-left:15px''></span>Oui, même si mon cœur<br><span style=''padding-left:15px''></span>Et ma chair se consument,<br><span style=''padding-left:15px''></span>Tu resteras toujours<br><span style=''padding-left:15px''></span>Mon partage et mon bien.<br><br>Toi, tu m’as saisi, Éternel,<br>Et je me suis laissé saisir.<br>Tu m’as persuadé, j’ai reçu tes paroles,<br>Elles ont rempli mon cœur d’allégresse.<br>Comme un feu qui me consume,<br>Ainsi sont-elles au-dedans de moi.'),
(276, NULL, 'La grâce de dieu', '276', 'Ti 2', '<span style=''padding-left:15px''></span>La grâce de Dieu a été manifestée,<br><span style=''padding-left:15px''></span>Elle est source de salut pour tous les hommes.<br><span style=''padding-left:15px''></span>La grâce de Dieu nous enseigne à renoncer<br><span style=''padding-left:15px''></span>À l’impiété, aux convoitises mondaines;<br><span style=''padding-left:15px''></span>La grâce nous enseigne.<br><br>Elle nous enseigne à vivre dans notre siècle,<br>Selon la sagesse et la piété,<br>Tout en attendant l’heureuse espérance,<br>L’avènement du grand sauveur.<br>Car, pour nous racheter, il s’offrit lui-même<br>Et nous purifia de nos péchés,<br>Pour se faire un peuple qui lui appartienne,<br>Rempli de zèle pour des œuvres.'),
(277, NULL, 'Bienheureux ceux qui placent', '277', 'Ps 84', 'Bienheureux ceux qui placent en toi<br>Leur appui, leur confiance.<br>Lorsqu’ils traversent la vallée des pleurs,<br>Ils la changent en un lieu plein de sources.<br>En eux, ils trouvent des chemins tracés.<br>Leur forces sont renouvelées sans cesse;<br>Ils se présenteront devant leur Dieu.<br><br>Bienheureux ceux qui placent en toi<br>Leur appui, leur confiance.<br>Lorsqu’ils traversent la vallée des pleurs,<br>Ils la changent en un lieu plein de sources.<br>Ils la transforment en un lieu plein de sources,<br>En un lieu plein de sources.<br>(Oui, plein de sources !)'),
(278, NULL, 'Je publierai les louanges', '278', 'Es 63', '<span style=''padding-left:15px''></span>Je publierai les louanges,<br><span style=''padding-left:15px''></span>La grâce et la bienveillance<br><span style=''padding-left:15px''></span>De l’Éternel, notre Dieu,<br><span style=''padding-left:15px''></span>D’après ce qu’il a fait pour nous.<br><span style=''padding-left:15px''></span>(bis)<br><br>Je dirai la bonté immense<br>Témoignée envers sa maison.<br>Tous les biens accordés par grâce<br>Et selon son grand amour.<br>Car il les a appelés son peuple<br>En demeurant toujours leur Sauveur,<br>Pour toujours leur Sauveur.<br><br>Au sein de toutes leurs détresses<br>Ils n’ont pas été sans secours.<br>Celui qui est devant sa face<br>Leur a donné son salut.<br>Dans sa bonté et sa charité<br>Il les a lui-même rachetés,<br>Il les a rachetés.'),
(279, NULL, 'Jésus est ma vie', '279', '', 'Jésus est ma vie,<br>Oui, Christ est ma vie,<br>Et ce n’est plus moi qui vis,<br>C’est lui qui vit en moi.<br>(C’est Jésus qui vit en moi.)<br><br>Jésus est ma gloire,<br>Il relève ma tête,<br>Et en moi il se glorifie,<br>Car Christ est ma vie !<br>(Jésus se glorifie, car Christ est ma vie !)'),
(280, NULL, 'Ô dieu, je bénirai ton nom', '280', '', '<span style=''padding-left:15px''></span>Ô Dieu, je bénirai ton nom à perpétuité<br><span style=''padding-left:15px''></span>Et je raconterai ta puissance.<br><span style=''padding-left:15px''></span>Éternel, je louerai ton nom pour l’éternité,<br><span style=''padding-left:15px''></span>tu es le Saint des saints, le Roi de gloire !<br><span style=''padding-left:15px''></span>Ô Dieu, tu régneras d’âge en âge.<br><br>Tu as sauvé mon âme de la mort,<br>tu m’as accordé ton salut.<br>La vie éternelle et la joie du ciel<br>Sont devenues mon espérance.'),
(281, NULL, 'Oui, je vais créer', '281', 'Es 65;Ap 21', 'Oui, je vais créer<br>(Je vais..)<br>De nouveaux cieux, une nouvelle terre.<br>(Créer une nouvelle terre.)<br>Oui, je vais créer<br>(Je vais..)<br>De nouveaux cieux, une nouvelle terre.<br>(Créer une nouvelle terre.)<br>Vous ne vous rappellerez plus<br>(Les choses passées...)<br>Les choses du passé.<br>(Ne reviendront plus à l’esprit)<br>Ces choses seront oubliées;<br>(Les choses passées...)<br>Elles ne reviendront plus à l’esprit.<br>(Ne reviendront plus à l’esprit.)<br><br>Je vis descendre du ciel<br>(Je vis la cité de...)<br>La cité de l’Éternel.<br>(L’Éternel.)<br>Comme une épouse elle est parée<br>(Elle vient comme une épouse)<br>Pour son époux qui l’a aimée.<br>(Préparée pour son époux.)<br><br>Réjouissez-vous<br>(Soyez...)<br>Et demeurez toujours dans l’allégresse !<br>(Soyez toujours dans l’allégresse !)<br>(bis)<br>Bientôt le Seigneur va créer<br>(Pour la joie...)<br>Son peuple pour la joie.<br>(Le Seigneur va créer notre cité,)<br>Il va créer pour l’allégresse<br>(Pour la joie...)<br>Bientôt son éternelle cité.<br>(Le Seigneur va créer notre cité)'),
(282, NULL, 'Familles des peuples', '282', 'Ps 96', '<span style=''padding-left:15px''></span>Familles des peuples, rendez gloire à l’Éternel ! (bis)<br><span style=''padding-left:15px''></span>Rendez la gloire, rendez l’honneur<br><span style=''padding-left:15px''></span>Et la puissance, rendez gloire pour son nom,<br><span style=''padding-left:15px''></span>Oui, gloire pour son nom !<br><span style=''padding-left:15px''></span>Et la puissance, rendez gloire pour son nom,<br><span style=''padding-left:15px''></span>Oui, gloire pour son nom !<br><br>Apportez des offrandes, venez en sa présence,<br>Prosternez-vous devant l’Éternel<br>Avec de saints ornements !<br>Tremblez devant lui, vous, tous les humains !<br>Le monde est affermi; il ne chancelle point.'),
(283, NULL, 'Pousse des cris de triomphe', '283', 'So 3', '<span style=''padding-left:15px''></span>Pousse des cris de triomphe,<br><span style=''padding-left:15px''></span>Exulte de tout ton cœur ;<br><span style=''padding-left:15px''></span>L’Éternel a détourné les jugements.<br><span style=''padding-left:15px''></span>(Dieu a détourné les jugements.)<br><span style=''padding-left:15px''></span>Pousse des cris de triomphe<br><span style=''padding-left:15px''></span>(Pousse des cris,)<br><span style=''padding-left:15px''></span>Non, ne crains plus le malheur ;<br><span style=''padding-left:15px''></span>(Pousse des cris.)<br><span style=''padding-left:15px''></span>Il a éloigné de toi ton ennemi !<br><br>L’Éternel est au milieu de toi<br>Comme un héros qui sauve.<br>Il fera de toi sa grande joie,<br>Sa plus grande joie.<br>L’Éternel gardera le silence<br>Dans son grand amour.<br>Il éprouvera pour toi<br>Des transports d’allégresse.<br>Il éprouvera pour toi<br>Des transports d’allégresse.'),
(284, NULL, 'Je chercherai la brebis égarée', '284', 'Ez 34', 'Je chercherai la brebis égarée,<br>Ramènerai celle qui est perdue.<br>Je panserai la brebis blessée,<br>Fortifierai la souffrante.<br>Tout mon troupeau a été dispersé,<br>Il est errant sur les monts élevEs<br>Aucun berger ne vient le chercher;<br>Alors l’Éternel se présente:<br><br><span style=''padding-left:15px''></span>C’est moi, c’est moi ton Dieu,<br><span style=''padding-left:15px''></span>C’est moi, l’Éternel.<br><span style=''padding-left:15px''></span>Voici, tout mon troupeau sera secouru.<br><span style=''padding-left:15px''></span>J’établirai sur eux un seul berger;<br><span style=''padding-left:15px''></span>Il les fera paître,<br><span style=''padding-left:15px''></span>Il sera le bon Berger.<br><span style=''padding-left:15px''></span>Il les fera paître,<br><span style=''padding-left:15px''></span>Il sera le bon Berger.<br><br>Je conclurai mon alliance de paix.<br>Les brebis seront en sécurité,<br>Seront conduites près des ruisseaux,<br>Auprès d’un bon pâturage.<br>C’est moi qui leur donnerai du repos.<br>Par l’eau du ciel, moi, je les bénirai.<br>Je briserai leur joug et leurs liens;<br>Elles verront Celui qui les sauve.'),
(285, NULL, 'Retiens dans la foi', '285', '2 Tm 1;2', '<span style=''padding-left:15px''></span>Retiens dans la foi et dans l’amour,<br><span style=''padding-left:15px''></span>Retiens dans l’amour de Jésus-Christ,<br><span style=''padding-left:15px''></span>Retiens le modèle des saines paroles,<br><span style=''padding-left:15px''></span>Des saines paroles<br><span style=''padding-left:15px''></span>Que tu as reçues, oui,<br><span style=''padding-left:15px''></span>Que tu as reçues.<br><br>Que le Seigneur répande<br>Sa grande miséricorde.<br>(bis)<br>Il nous a consolés. (bis)<br><br>Que le Seigneur vous donne<br>D’obtenir miséricorde.<br>(bis)<br>Auprès du Seigneur,<br>Oui, en ce jour.<br><br>Fuis les passions, les passions<br>Que connaît, oui, la jeunesse.<br>Recherche l’amour et la foi, (bis)<br>La justice de Dieu,<br>La paix de Dieu.'),
(286, NULL, 'Relève-toi, ô peuple fidèle', '286', '', 'Relève-toi, ô peuple fidèle,<br>Et la lumière de Jésus patraîtra !<br>Il est ton Dieu, ranime ton zèle,<br>Que ton premier amour guide tes pas.<br>Victoire, victoire au Saint Fils de Dieu !<br>Sa croix nous délivre et guérit nos coeurs.<br>Couronné de gloire, il règne des cieux.<br>Son Esprit fait vivre et nous rend vainqueurs.<br>Le Seigneur Jésus-Christ est ressuscité.<br>(Jésus-Christ est ressuscité)<br>Et, déclaré Fils de Dieu avec puissance,<br>(Avec puissance !)<br>Il est à sa droite pour l’éternité,<br>(Au ciel pour l’éternité,)<br>Remplissant nos coeurs d’espérance.<br>(Il remplit nos coeurs d’espérance.)<br>Le Seigneur Jésus-Christ est ressuscité.<br>(Jésus-Christ est ressuscité.)<br>Nous le contemplons revêtu de lumière.<br>(Dans la lumière,)<br>Au ciel, avec son sang,<br>Il est remonté,<br>(Au ciel il est remonté,)<br>Intercédant auprès du Père.<br>(Auprès du Père.)'),
(287, NULL, 'Sans l’Éternel qui nous protégea', '287', 'Ps 124', 'Sans l’Éternel qui nous protégea, oui,<br>Sans l’Éternel qui nous protégea,<br>Que les fidèles proclament:<br>(Que les fidèles le proclament !)<br>Sans l’Éternel, les eaux seraient passées sur notre âme,<br>(Béni soit l’Éternel !)<br>Les torrents nous auraient submergés.<br>(Béni soit l’Éternel !)<br>Béni soit notre Seigneur !<br>Il s’est rompu, le filet qui nous emprisonnait,<br>(Béni soit l’Éternel !)<br>Alors notre âme s’est échappée.<br>(Béni soit l’Éternel !)<br>Notre seul secours<br>Est en l’Éternel, notre Dieu<br>(Est en l’Éternel.)<br>Qui a fait l’infini des cieux,<br>Qui a fait les cieux et la terre,<br>Oui, béni soit son nom !'),
(288, NULL, 'Je bénirai l’éternel', '288', 'Ps 34', '<span style=''padding-left:15px''></span>Je bénirai l’Éternel en tout temps<br><span style=''padding-left:15px''></span>Car sa louange demeure en moi.<br><span style=''padding-left:15px''></span>En l’Éternel je me glorifie;<br><span style=''padding-left:15px''></span>Tous, avec moi, célébrez son nom !<br><br>J’ai cherché l’Éternel mon Dieu;<br>Il m’a répondu,<br>Et de toutes mes frayeurs<br>Il m’a délivré.<br>L’ange de l’Éternel est près<br>De ceux qui le craignent,<br>Et de tout danger<br>Toujours, il les délivre.<br><br>Lorsque, vers l’Éternel mon Dieu<br>Je tourne les yeux,<br>Je suis rayonnant de joie,<br>Oui, rayonnant de joie !<br>Heureux l’homme qui cherche en lui,<br>En lui son refuge.<br>Craignez l’Éternel;<br>Rien ne vous manquera.'),
(289, NULL, 'Oui, je veux me réjouir en dieu', '289', 'Hk 3', 'Oui, je veux me réjouir en Dieu,<br>Dans le Dieu de mon salut !<br>Oui, je veux me réjouir en Dieu,<br>(Oui, je veux me réjouir.)<br>Dans le Dieu de mon salut !<br>(De mon salut !)<br>L’Éternel, oui, l’Éternel est ma force,<br>(Oui, l’Éternel Dieu rend mes pieds légers,)<br>Il rend mes pieds légers,<br>(Légers ainsi...)<br>Semblables à ceux des biches.<br>(... que ceux des biches.)<br>L’Éternel, oui, l’Éternel est ma force,<br>(Oui, l’Éternel est ma force; il me fait marcher,)<br>Il me fait marcher sur les lieux élevés<br>(Marcher sur les lieux élevés.)<br>Oui, je veux me réjouir en Dieu,<br>Dans le Dieu de mon salut!<br>Oui, je veux me réjouir en Dieu,<br>(Oui, je veux me réjouir en Dieu,)<br>En l’Éternel, le Dieu de mon salut !<br>(Qui est le Dieu de mon salut !)'),
(290, NULL, 'Je t’aime, ô Éternel', '290', 'Ps 18', 'Je t’aime, ô Éternel, ma force,<br>Seigneur, mon libérateur !<br>Mon rocher et ma haute retraite<br>(Toi, mon rocher et ma haute retraite,)<br>En qui je trouve un abri.<br>(En toi je m’abrite. Tu es ma haute retraite.)<br>Tu fais briller ma lumière,<br>(C’est toi seul qui fais briller ma lumière,)<br>tu éclaires mes ténèbres,<br>(C’est toi seul qui illumines mes ténèbres.)<br>Tu élargis mon chemin;<br>(Et je deviens grand,)<br>Je deviens grand par ta bonté.<br>(Oui, par ta bonté je m’élève. Tu élargis...)<br>C’est toi qui me ceins de force<br>(... mon chemin. C’est toi qui me ceins de force, Éternel,)<br>Et me guide en la voie droite.<br>(Et qui me diriges dans la voie droite.)<br>Qui est Dieu si ce n’est toi,<br>(Qui est comme toi,)<br>Toi, l’Éternel, notre rocher ?'),
(291, NULL, 'Les noces étaient prêtes', '291', 'Mt 22', 'Les noces étaient prêtes,<br>Mais les conviés n’en étaient pas dignes.<br>Ils ont refusé l’offre du Roi,<br>Ils ont outragé ses messagers.<br>Les noces étaient prêtes,<br>Alors le Roi ordonna :<br><br><span style=''padding-left:15px''></span>Allez dans les places et les rues de la ville ;<br><span style=''padding-left:15px''></span>Amenez les pauvres et les boiteux,<br><span style=''padding-left:15px''></span>Tous les estropiés et les aveugles;<br><span style=''padding-left:15px''></span>Amenez ceux que vous trouverez.<br><span style=''padding-left:15px''></span>Allez dans les chemins et le long des haies,<br><span style=''padding-left:15px''></span>Et contraignez-les d’entrer,<br><span style=''padding-left:15px''></span>Afin que la maison soit remplie,<br><span style=''padding-left:15px''></span>Que la maison soit remplie.<br><br>Dieu ne tarde pas,<br>Mais il accomplira sa promesse.<br>Envers nous il use de patience,<br>Ne voulant qu’aucun de nous périsse.<br>Il attend que chacun<br>Des appelés soit entré.'),
(292, NULL, 'Je m’en vais vous préparer', '292', 'Jn 14;17', '<span style=''padding-left:15px''></span>Je m’en vais vous préparer une place<br><span style=''padding-left:15px''></span>Dans la maison de mon Père.<br><span style=''padding-left:15px''></span>Et lorsque je reviendrai<br><span style=''padding-left:15px''></span>Je vous prendrai avec moi.<br><span style=''padding-left:15px''></span>Et lorsque je reviendrai<br><span style=''padding-left:15px''></span>Je vous prendrai avec moi.<br><br>Ô Père, je veux que là où je suis, ceux que tu m’as donnés<br>Soient toujours avec moi afin qu’ils contemplent,<br>Et qu’ils voient ma gloire,<br>La gloire que tu m’as donnée car tu m’as aimé<br>Avant la fondation du monde.'),
(293, NULL, 'Nous étions autrefois éloignés', '293', 'Ep 2', 'Nous étions autrefois, éloignés de Dieu,<br>Appelés infidèles, et sans espérance.<br>Privés du droit de cité, nous étions tous étrangers<br>Aux alliances de la promesse.<br>Nous étions étrangers par toutes nos oeuvres,<br>Par toutes nos pensées, ennemis de Dieu.<br>Égarés par l’ignorance et notre coeur endurci<br>Nous étions dignes d’être haïs.<br><br><span style=''padding-left:15px''></span>Mais par le sang de Jésus,<br><span style=''padding-left:15px''></span>Nous avons tous été rapprochés,<br><span style=''padding-left:15px''></span>Le mur d’inimitié a renversé.<br><span style=''padding-left:15px''></span>Jésus vint annoncer la paix<br><span style=''padding-left:15px''></span>À tous ceux qui étaient loin,<br><span style=''padding-left:15px''></span>Nous ne sommes plus des étrangers.<br><span style=''padding-left:15px''></span>Nous sommes tous citoyens,<br><span style=''padding-left:15px''></span>Habitants de la maison de Dieu,<br><span style=''padding-left:15px''></span>Édifiés sur le roc; le Seigneur Jésus-Christ.<br><span style=''padding-left:15px''></span>Nous sommes tous héritiers,<br><span style=''padding-left:15px''></span>Formant un même corps en Jésus-Christ,<br><span style=''padding-left:15px''></span>Et nous participons à la même promesse<br><span style=''padding-left:15px''></span>Par l’Évangile.<br><br>Nous étions des brebis bien loin du troupeau;<br>Nous n’avions pas reçu la miséricorde.<br>Nos coeurs étaient aveuglés,<br>Nos regards étaient privés<br>De la lumière de l’Évangile.'),
(294, NULL, 'Roi des nations', '294', 'Ap', 'Roi des nations, Roi des nations,<br>Tes oeuvres sont grandes et admirables !<br>(bis)<br><br><span style=''padding-left:15px''></span>Seigneur, qui ne te craindrait,<br><span style=''padding-left:15px''></span>Qui ne glorifierait ton nom ?<br><span style=''padding-left:15px''></span>Seigneur, qui ne te craindrait,<br><span style=''padding-left:15px''></span>Qui ne glorifierait ton nom ?<br><span style=''padding-left:15px''></span>Car, toi seul tu es Saint ! (ter)<br><span style=''padding-left:15px''></span>(1) Tu es Saint !<br><span style=''padding-left:15px''></span>(2) Dieu Tout-Puissant, tu es Saint !<br><br>Dieu Tout-Puissant, Dieu Tout-Puissant,<br>Tes voies sont justes et véritables !<br>(bis)'),
(295, NULL, 'Voici, j’ai mis devant toi', '295', 'Dt 30', 'Voici, J’ai mis devant toi la vie et la mort,<br>Choisis la vie.<br>Voici, J’ai mis devant toi le bien et le mal,<br>Choisis la vie.<br>Afin que tu vives pour aimer ton Dieu,<br>Pour L’aimer, pour t’attacher à lui.<br>Voici la vie et la mort ;<br>Choisis la vie afin que tu vives,<br>Oui, choisis la vie !<br>Choisis la vie afin que tu vives,<br>Oui, choisis la vie !<br><br>Si tu reviens à ton Dieu, si tu obéis<br>De toute ton âme,<br>Dieu aura pitié de toi, il te bénira<br>Et se réjouira.<br>Car, jusqu’aux extrémités du ciel<br>L’Éternel partira te chercher;<br>Il circoncira ton coeur.<br>Choisis la vie afin que tu vives,<br>Oui, choisis la vie !<br>Choisis la vie afin que tu vives,<br>Oui, choisis la vie !'),
(296, NULL, 'Puisqu’on te nomme la bannie', '296', 'Jr 30', 'Puisqu’on te nomme la bannie,<br>Sion, celle dont nul ne prend garde,<br>Je détruirai la tyrannie,<br>Sur tes murs je mettrai mes gardes.<br>Moi, ton Dieu, je te sauverai,<br>Je renverserai l’oppresseur,<br>C’est moi qui panserai tes plaies<br>J’ai compassion de tes demeures.<br><br><span style=''padding-left:15px''></span>Et la voix de ceux qui s’égaient<br><span style=''padding-left:15px''></span>Vers le ciel portera leurs louanges.<br><span style=''padding-left:15px''></span>Sans fin, je les multiplierai,<br><span style=''padding-left:15px''></span>Je fortifierai leurs phalanges.<br><span style=''padding-left:15px''></span>Car la voix de ceux qui s’égaient<br><span style=''padding-left:15px''></span>Vers le ciel portera leurs louanges.<br><span style=''padding-left:15px''></span>Ses fils seront comme autrefois,<br><span style=''padding-left:15px''></span>Je les garderai devant moi. (bis)<br><br>De moi qui ose s’approcher,<br>Et qui engagerait son coeur ?<br>Moi, je le ferai avancer<br>Et il s’approchera sans peur.<br>Je susciterai un Sauveur;<br>Il sera tiré de ton sein.<br>Je serai votre protecteur<br>Et vous serez mon peuple saint.'),
(297, NULL, 'Qui dira les hauts faits de l’éternel ?', '297', 'Ps 106', '<span style=''padding-left:15px''></span>Qui dira les hauts faits de l’Éternel,<br><span style=''padding-left:15px''></span>Qui dira les hauts faits de l’Éternel,<br><span style=''padding-left:15px''></span>Qui voudra publier ses louanges ?<br><span style=''padding-left:15px''></span>Qui voudra publier toutes les louanges du Seigneur ?<br><span style=''padding-left:15px''></span>(bis)<br><br>Éternel, souviens-toi de moi dans ta bonté pour ton peuple !<br>Éternel, exauce ma prière en lui accordant ton secours !<br>Ainsi, je verrai le bonheur de tes élus.<br>Oui, je serai dans l’allégresse et dans la joie de ton peuple,<br>Avec ton héritage, je me glorifierai !<br><br>Éternel, envoie ton salut, viens et rassemble ton peuple<br>Et nous trouverons notre allégresse et notre gloire à te louer.<br>Béni soit l’Éternel, maintenant et pour toujours !<br>Béni soit Dieu, de siècle en siècle, d’éternité en éternité !<br>Que tout le peuple dise : «Amen, amen !»'),
(298, NULL, 'Les bontés de l’éternel', '298', 'Lm 3', 'Les bontés de l’Éternel ne sont pas épuisées,<br>(Ses bontés, ses compassions)<br>Ses compassions ne sont pas à leur terme;<br>(Ne sont pas à leur terme.)<br>Elles se renouvellent tous les matins.<br>Oh! Que ta fidélité est grande !<br>(Ta fidélité est grande !)<br>(bis)'),
(299, NULL, 'Tes yeux verront', '299', 'Es 30;Jc 1', '<span style=''padding-left:15px''></span>Tes yeux verront ceux qui t’instruisent<br><span style=''padding-left:15px''></span>Ils ne se cacheront plus.<br><span style=''padding-left:15px''></span>Tes oreilles entendront une voix derrière toi<br><span style=''padding-left:15px''></span>Qui dira : «Voici le chemin, marchez-y !<br><span style=''padding-left:15px''></span>Voici le chemin, marchez-y !»<br><br>C’est dans le retour à Dieu et dans le repos<br>Que sera votre salut.<br>C’est dans le calme et la confiance<br>Que sera votre force.<br><br>Si vous manquez de sagesse, demandez à Dieu;<br>Il l’accorde sans reproche.<br>Votre Dieu vous donnera avec largesse<br>Si vous le priez sans doutez.'),
(300, NULL, 'À toi, Seigneur, louange', '300', '', 'À toi Seigneur, louange, gloire, honneur !<br>(À toi, à toi, Seigneur, louange et honneur !)<br>Du lieu élevé de ta sainteté<br>(Tu es élevé dans la sainteté.)<br>Entends tous les chants de tes rachetés !<br>(Entends tes racheté !)<br>Tu es le Saint, notre Dieu souverain !<br>(Tu es, tu es le Saint, le Dieu souverain !)<br>Nous t’adorons, oui, nous t’exaltons !<br>(Alléluia ! Oui, nous t’exaltons !)<br>Nous t’adorons, oui, nous t’exaltons !<br>(Alléluia ! Oui, nous t’exaltons !)'),
(301, NULL, 'Nos yeux verront le Roi', '301', 'Es 33', 'Nos yeux verront le Roi dans sa magnificence,<br>Ils verront la cité où nous habiterons.<br>Peuple, regarde la ville de nos fêtes,<br>Là où le Seigneur est magnifique pour nous !<br>Là, il est vraiment magnifique pour nous !<br><br>Nos yeux contempleront la cité magnifique<br>Où, jamais, l’ennemi ne pourra pénétrer.<br>Peuple, regarde, c’est le séjour tranquille !<br>Là est notre juge et notre Roi qui nous sauve,<br>Là est le Seigneur, notre Roi qui nous sauve.'),
(302, NULL, 'Tu dois naître de nouveau', '302', 'Jn 3;6', '<span style=''padding-left:15px''></span>Tu dois naître de nouveau<br><span style=''padding-left:15px''></span>Si tu veux voir le royaume.<br><span style=''padding-left:15px''></span>Si tu ne nais de l’Esprit<br><span style=''padding-left:15px''></span>(De l’Esprit...)<br><span style=''padding-left:15px''></span>Pour toi l’éternité sera inaccessible.<br><span style=''padding-left:15px''></span>(Sinon, l’éternité sera inaccessible.)<br><span style=''padding-left:15px''></span>Le vent souffle où il veut<br><span style=''padding-left:15px''></span>Et tu en entends le bruit<br><span style=''padding-left:15px''></span>Mais toi, tu ne sais d’où il vient<br><span style=''padding-left:15px''></span>(Tu ne sais d’où il vient.)<br><span style=''padding-left:15px''></span>Et ne sais où il va.<br><span style=''padding-left:15px''></span>Il est comme celui qui est né de l’Esprit.<br><br>Ces paroles sont esprit et vie,<br>C’est l’Esprit seul qui nous vivifie.<br>Si tu les reçois en croyant de tout ton cœur,<br>Alors tu pourras être enfant du Seigneur.'),
(303, NULL, 'Ils ont lavé leurs robes', '303', 'Ap 7', 'Ils ont lavé leurs robes<br>Et ils les ont blanchies<br>Dans le sang de l’Agneau<br>Qui les a rachetés.<br>Ils sont auprès du trône,<br>Devant sa majesté,<br>Louant son nom dans son temple<br>Jour et nuit.<br>Terre, sois dans l’allégresse !<br>(Terre, terre, sois dans l’allégresse !)<br>Cieux, réjouissez-vous !<br>(Terre et cieux réjouissez-vous !)<br>Car l’Agneau les conduira<br>Aux sources des eaux de la vie.<br>Terre, sois dans l’allégresse !<br>(Terre, terre, sois dans l’allégresse !)<br>Que les cieux se réjouissent !<br>(Que les cieux se réjouissent !)<br>Car le Seigneur essuiera<br>Toute larme de leurs yeux !'),
(304, NULL, 'Ma part, Éternel', '304', 'Ps 119', 'Ma part, Éternel,<br>C’est de garder tes paroles.<br>Ma part, Éternel,<br>C’est de garder tes paroles.<br>Ô donne-moi l’intelligence<br>Pour apprendre tes statuts,<br>Car je crois à tes commandements.<br>Pour apprendre tes statuts,<br>Car je crois à tes commandements.<br><br>Mon Dieu, je t’appelle<br>De tout mon cœur je t’implore !<br>Mon Dieu, je t’appelle<br>De tout mon cœur je t’implore !<br>Sans cesse mes pas se dirigent (bis)<br>Vers ta loi, ô Éternel;<br>Sans tarder, j’obéis et je crois.<br>Oui, je marche vers ta loi;<br>Sans tarder, j’obéis et je crois');
INSERT INTO `hymn` (`id`, `musical_part_id`, `title`, `num`, `ref`, `lyrics`) VALUES
(305, NULL, 'Je suis dans la joie', '305', 'Ps 122;16', 'Je suis dans la joie<br>Lorsque l’on me dit :<br>«Allons adorer Dieu<br>Au milieu des acclamations de joie<br>Dans sa maison !»<br>Auprès de Dieu je voudrais demeurer<br>Afin de toujours le louer.<br><br><span style=''padding-left:15px''></span>Oh, mon âme, réjouis-toi<br><span style=''padding-left:15px''></span>En présence de ton Roi !<br><span style=''padding-left:15px''></span>Il appelle ses rachetés<br><span style=''padding-left:15px''></span>(Il nous appelle.)<br><span style=''padding-left:15px''></span>Devant lui à se consacrer.<br><span style=''padding-left:15px''></span>Je me réjouirai en Dieu<br><span style=''padding-left:15px''></span>(Je me réjouirai en Dieu et...)<br><span style=''padding-left:15px''></span>Je présenterai mes vœux<br><span style=''padding-left:15px''></span>Et mes sacrifices de reconnaissance<br><span style=''padding-left:15px''></span>Avec joie dans le saint lieu.<br><span style=''padding-left:15px''></span>(Dans le saint lieu.)<br><br>Pour celui qui vient devant l’Éternel<br>La joie est abondante.<br>Grandes sont les délices éternelles<br>En sa présence.<br>Que ta lumière vienne me guider<br>Jusqu’au mont de ta sainteté.'),
(306, NULL, 'Si tu reviens à moi', '306', 'Jr 4', 'Si tu reviens à moi, dit le Seigneur,<br>Si tu reconnais ton iniquité,<br>Si tu renonces à aimer les dieux étrangers,<br>Sur la terre tu ne seras plus voyageur.<br><br>Je reviendrai et je vous rassemblerai.<br>(Je reviendrai.)<br>Je pardonnerai car je suis plein de bonté.<br>(Je pardonnerai.)<br>Je donnerai des bergers selon mon cœur<br>(Des bergers selon mon cœur,)<br>Qui te conduiront par ma sagesse au bonheur.<br>(Te conduiront par ma sagesse,)<br>Ils te conduiront par ma sagesse au bonheur.<br>(Par ma sagesse au bonheur.)'),
(307, NULL, 'Puisque nous sommes environnés', '307', 'Hb 12', 'Puisque nous sommes environnés<br>D’une si grande, si grande nuée de témoins,<br>Rejetons tout fardeau, rejetons le péché<br>Qui vient nous entourer si facilement.<br><br><span style=''padding-left:15px''></span>Courons dans la carrière que Dieu nous a ouverte,<br><span style=''padding-left:15px''></span>(Courons, courons dans la carrière, les yeux sur le Seigneur...)<br><span style=''padding-left:15px''></span>Les regards sur Jésus qui suscite la foi.<br><span style=''padding-left:15px''></span>(... Jésus qui suscite la foi.)<br><span style=''padding-left:15px''></span>Il a souffert la croix et méprisé la honte<br><span style=''padding-left:15px''></span>(Il a souffert la croix, méprisé la honte,)<br><span style=''padding-left:15px''></span>En vue de la joie réservée par le Père.<br><span style=''padding-left:15px''></span>(En vue de la joie, la joie du Père.)<br><br>Prenons courage, car le Seigneur<br>A supporté le mal infligé par les pécheurs.<br>Considérons ceci, ne l’oublions jamais.<br>Ainsi l’âme abattue sera soulagée.<br><br><span style=''padding-left:15px''></span>Courons pour la couronne que Dieu nous a promise.<br><span style=''padding-left:15px''></span>(Courons, courons pour la couronne que le Seigneur...)<br><span style=''padding-left:15px''></span>Jésus la donnera à celui qui vaincra.<br><span style=''padding-left:15px''></span>(...accordera à celui qui vaincra.)<br><span style=''padding-left:15px''></span>Gardons notre espérance et vivons par la foi<br><span style=''padding-left:15px''></span>(Gardons notre espérance, vivons par la foi,)<br><span style=''padding-left:15px''></span>En vue de la joie réservée par le Père.<br><span style=''padding-left:15px''></span>(En vue de la joie, la joie du père.)'),
(308, NULL, 'Je connais tes œuvres', '308', 'Ap 3', 'Je connais tes œuvres (bis)<br>Voici, tu as bien peu de puissance<br>Mais tu as gardé, conservé ma parole<br>Sans jamais renier mon nom,<br>J’ai mis devant toi une porte ouverte<br>Que personne ne peut fermer.<br>À tes ennemis je ferai connaître<br>Que je t’ai vraiment aimé.<br><br>Au sein de l’épreuve (bis)<br>Qui surviendra sur toute la terre,<br>Je te garderai, toi qui as ma parole,<br>La persévérance en moi.<br>Veille à ne jamais perdre ta couronne<br>Et retiens ce que tu as.<br>Je ferai de toi l’une des colonnes<br>Du saint temple de mon Dieu.<br><br><span style=''padding-left:15px''></span>Coda :<br><span style=''padding-left:15px''></span>J’écrirai sur toi le nom de la ville de mon Dieu.'),
(309, NULL, 'Recevez ce commandement', '309', 'Ml', 'Recevez ce commandement<br>Vous qui le servez jour et nuit !<br>Maintenant, prenez donc à cœur<br>De donner gloire à son saint Nom !<br><br><span style=''padding-left:15px''></span>Rendez gloire à l’Éternel, votre Dieu !<br><span style=''padding-left:15px''></span>Publiez et proclamez ses hauts-faits !<br><span style=''padding-left:15px''></span>Acclamez toutes ses merveilles<br><span style=''padding-left:15px''></span>Et chantez en son honneur !<br><span style=''padding-left:15px''></span>De son nom tirez votre gloire,<br><span style=''padding-left:15px''></span>Ne vous lassez pas de L’invoquer !<br><br>Souvenez-vous de ses prodiges<br>Vous qui le servez jour et nuit !<br>Demeurez dans son alliance,<br>Souvenez-vous de sa parole !'),
(310, NULL, 'C’est par lui', '310', 'Rm 1', 'C’est par lui que nous avons connu la grâce,<br>Que nous avons été mis à part.<br>Nous avons été choisis pour l’Évangile<br>Qui annonce Jésus le Seigneur.<br><br>Déclaré Fils de Dieu avec puissance<br>Et selon l’Esprit de sainteté<br>Par sa résurrection,<br>Par sa résurrection d’entre les morts.<br><br>C’est par lui que nous avons reçu la grâce<br>Et la paix par Jésus le Seigneur.<br>À tous ceux qui sont à Dieu et qu’il appelle,<br>Qu’il appelle à être saints, saints et bien-aimés,<br>De notre Père, et de Jésus,<br>Oui que soient répandues sur eux la paix, la grâce.'),
(311, NULL, 'Béni soit le Seigneur', '311', 'Ps 68', 'Béni soit le Seigneur, jour après jour. (bis)<br>Quand on nous accable Dieu nous délivre.<br>Béni soit le Seigneur, jour après jour.<br><br>Il est pour nous le Dieu des délivrances. (bis)<br>C’est lui qui nous sauve du grand abîme.<br>Béni soit le Seigneur jour après jour.<br><br>Royaumes de la terre, chantez à Dieu ! (bis)<br>Il donne à son peuple force et puissance.<br>Béni soit le Seigneur jour après jour.'),
(312, NULL, 'Oh ! mon Dieu, écoute', '312', 'Ps 61', 'Oh! mon Dieu, écoute, écoute mes cris !<br>Sois attentif à ma prière, à mes supplications<br>Lorsque je t’invoque le cœur défaillant.<br>En toi je trouve mon refuge,<br>Je trouve un abri.<br>Ô Dieu, je désire éternellement<br>Demeurer auprès de toi,<br>Être sous tes ailes.<br>Ô Dieu, tu prêtes attention à tous mes vœux.<br>Tu me donnes l’héritage de ceux qui te craignent,<br>De ceux qui craignent ton nom,<br>De tous ceux qui craignent ton nom.'),
(313, NULL, 'Ô Dieu, au milieu de ton peuple', '313', '2 Sm 7', 'Ô Dieu, au milieu de ton peuple<br>Que tu as affermi<br>Je me tiens aujourd’hui,<br>(Je viens et je publie...)<br>Et je publie tes œuvres.<br>(Tes œuvres.)<br>Car tu es grand et il n’est pas<br>D’autre dieu qui, comme toi,<br>Voulut racheter ton peuple.<br>Ô, Dieu, tu as fait des prodiges.<br>(Ô, Dieu, mon Père,)<br>Tu as montré ta gloire<br>(Tu manifestes...)<br>En le sauvant<br>(Ta gloire en le sauvant.)<br>De tous ses esclavages.<br>Et maintenant,<br>(Et maintenant, maintenant,)<br>Il chante ta victoire<br>(Il chante en tout temps,)<br>En tout temps.<br>(Il chante en tout temps.)<br><br>Ô, Dieu, tu m’as fait cette grâce<br>D’être parmi les tiens.<br>Seigneur, aussi, je viens,<br>(Aussi je viens devant...)<br>Je viens devant ta face.<br>(Ta face.)<br>Que partout soient manifestés<br>Ton nom et ta majesté,<br>Dans la grandeur de tes actes.<br>Ô, Dieu, toi qui m’as fait connaître<br>(Ô, Dieu, mon Père,)<br>De Jésus la bonté<br>(Tu fais connaître)<br>Et le mystère,<br>(De Jésus le mystère.)<br>Je viens me consacrer<br>Parmi mes frères.<br>(Parmi mes frères, oui, mes frères,)<br>Sois à jamais mon maître<br>(Sois à jamais mon Père,)<br>Et mon Père.<br>(À jamais mon Père.)'),
(314, NULL, 'S''il est auprès du Seigneur', '314', 'Jb 33', 'S''il est auprès du Seigneur<br>Un des nombreux messagers<br>Qui veut montrer au pécheur<br>La voie qu''il doit emprunter,<br>Alors Dieu a compassion<br>Et s''adresse au messager<br>En disant : «J''ai la rançon<br>Veuille donc le délivrer !»<br><br><span style=''padding-left:15px''></span>Autrefois dans le péché,<br><span style=''padding-left:15px''></span>Enfreignant la loi divine,<br><span style=''padding-left:15px''></span>Je n''ai pas été traité<br><span style=''padding-left:15px''></span>Selon l''horreur de mon crime.<br><span style=''padding-left:15px''></span>Car mon âme fut tirée<br><span style=''padding-left:15px''></span>Des profondeurs de la mort.<br><span style=''padding-left:15px''></span>Désormais, illuminée<br><span style=''padding-left:15px''></span>Par sa lumière, elle adore.<br><br>Le pécheur retrouve enfin<br>La force de sa jeunesse.<br>Sans crainte il prie et il vient<br>Au Père dans l''allégresse<br>Dieu lui laisse voir sa face.<br>Avec joie il le pardonne<br>Pour qu''il puisse rendre grâce<br>Et chanter devant les hommes.'),
(315, NULL, 'Seigneur, à toi louange', '315', 'Ap 5', 'Seigneur, à toi, louange et gloire<br>Car tu remportas la victoire !<br>Auprès de toi le peuple racheté<br>D''un même coeur chantera tes bontés,<br>Chantera tes bontés.<br>À toi le sceptre et la couronne !<br>Que ce chant à jamais résonne:<br>«Digne est l''Agneau de recevoir<br>Toute puissance et toute gloire !»'),
(316, NULL, 'Ouvre ta bouche', '316', 'Pr 31', 'Ouvre ta bouche pour le muet,<br>Pour la cause de ceux qu''on délaisse.<br>Ouvre ta bouche pour proclamer la justice.<br>(Ouvre ta bouche pour la justice.)<br>Que la gloire de Dieu retentisse.<br><br><span style=''padding-left:15px''></span>Combats, combats pour ton peuple et ton Dieu.<br><span style=''padding-left:15px''></span>Combats pour l''amour de la vérité.<br><span style=''padding-left:15px''></span>Combats, combats pour Dieu qui t''a sauvé,<br><span style=''padding-left:15px''></span>Pour l''amour de sa sainteté.<br><br>Ouvre ton coeur pour les malheureux,<br>Pour les brebis qui sont sans berger.<br>Comme un père prend soin de chacun de ses fils,<br>(Comme un père prend soin de ses fils...)<br>Va défendre celui qu''on opprime.'),
(317, NULL, 'J''ai choisi le chemin', '317', 'Ps 119', '<span style=''padding-left:15px''></span>J''ai choisi le chemin de la fidélité,<br><span style=''padding-left:15px''></span>Le chemin de ta sainte loi.<br><span style=''padding-left:15px''></span>Je me tiens attaché à tes commandements;<br><span style=''padding-left:15px''></span>Éternel, garde-moi fidèle !<br><span style=''padding-left:15px''></span>(bis)<br><br>Oui, je cours sur la voie de tes commandements<br>Car tu élargis mon coeur.<br>Ô Seigneur, donne-moi de ton discernement<br>Et je suivrai ta parole.<br>Oui, détourne mes yeux de tout ce qui est vain,<br>Fais-moi vivre dans ta sainte voie!<br>Accomplis ta promesse envers ton serviteur<br>Comme pour tous tes enfants qui craignent ton nom.'),
(318, NULL, 'Tu m''as enseigné dès ma jeunesse', '318', 'Ps 71', '<span style=''padding-left:15px''></span>Tu m''as enseigné dès ma jeunesse.<br><span style=''padding-left:15px''></span>(Ô Dieu,)<br><span style=''padding-left:15px''></span>Depuis, je proclame tes merveilles.<br><span style=''padding-left:15px''></span>(Ô Dieu,)<br><span style=''padding-left:15px''></span>Au sein de la blanche vieillesse,<br><span style=''padding-left:15px''></span>(Ô Seigneur,)<br><span style=''padding-left:15px''></span>Ô Dieu, ne m''abandonne pas !<br><span style=''padding-left:15px''></span>(Ô Seigneur,)<br><span style=''padding-left:15px''></span>Ainsi, j''annoncerai ta force<br><span style=''padding-left:15px''></span>(Je dirai...)<br><span style=''padding-left:15px''></span>À cette génération.<br><span style=''padding-left:15px''></span>(... à cette génération)<br><span style=''padding-left:15px''></span>Ainsi, je dirai ta puissance<br><span style=''padding-left:15px''></span>(... ta puissance.)<br><span style=''padding-left:15px''></span>Aux enfants qui nous viendront.<br><br>Tu nous as fait éprouver bien des détresses,<br>Mais tu nous redonneras la vie.<br>Et moi, j''espèrerai toujours,<br>Je te louerai de plus en plus;<br>Ma bouche publiera toujours<br>Ta gloire et ton salut.<br><br>Console-moi de nouveau<br>Ô Dieu, relève-moi !<br>Et si je viens à faiblir, ô Dieu,<br>Ne me laisse pas sans secours<br>Et je rappellerai toujours<br>Combien est grande ta justice;<br>Elle monte jusqu''aux cieux.'),
(319, NULL, 'Voyez l''amour que dieu notre père', '319', '1 Jn 3', '<span style=''padding-left:15px''></span>Voyez l''amour que Dieu notre Père<br><span style=''padding-left:15px''></span>A témoigné envers nous, pécheurs !<br><span style=''padding-left:15px''></span>(bis)<br><br>Car nous avons été appelés,<br>Oui, nous avons été appelés<br>Enfants de Dieu, enfants de Dieu,<br>Oui, vraiment nous le sommes.<br><br>Dieu mit en nous l''Esprit d''adoption,<br>Dieu mit en nous l''Esprit d''adoption,<br>À Dieu, par lui, à Dieu par Lui<br>Nous crions: «Abba: Père !»'),
(320, NULL, 'Mon oreille avait entendu', '320', 'Jb 42', 'Mon oreille avait entendu<br>Parler de toi, parler de toi.<br>Mais mon oeil, maintenant t''a vu,<br>Seigneur, mon Roi ! (bis)<br>Aujourd''hui mon oeil te voit,<br>Jésus-Christ, le Roi des rois,<br>C''est pourquoi je me repens et je crois.'),
(321, NULL, 'Quand nous étions sans force', '321', 'Rm 5', 'Quand nous étions sans force<br>Jésus souffrit la mort,<br>En sacrifiant sa vie au temps marqué<br>Pour des pécheurs honteux, encore impies,<br>Prouvant ainsi le grand amour de Dieu.<br>À peine mourrait-on pour l''homme juste et bon,<br>Donnerait-on sa vie sur une croix,<br>Pour qui répand le bien autour de lui.<br>Mais Dieu donna Jésus pour des impies.<br><br>Et maintenant, étant justifiés,<br>Nous proclamons la joie d''être sauvés<br>De la colère juste et méritée.<br>Oui, maintenant, étant réconciliés<br>Par Jésus-Christ, pour nous crucifié.<br>Sans cesse nous mettons notre gloire en lui,<br>Jésus, qui nous a sauvés par sa vie.'),
(322, NULL, 'Seigneur, je me livre à toi', '322', '', '<span style=''padding-left:15px''></span>Seigneur, je me livre à toi,<br><span style=''padding-left:15px''></span>Seigneur Jésus, je me livre à toi.<br><span style=''padding-left:15px''></span>Je veux être ton serviteur,<br><span style=''padding-left:15px''></span>Je veux être ton serviteur.<br><br>Prends-moi, que je sois à toi.<br>Que tout mon être soit vivant pour toi.<br>Sois seul ma vie, ma subsistance,<br>Ma raison de vivre ici-bas.<br>Ma seule force pour marcher et persévérer.<br><br>Ainsi que tu t''es donné,<br>Entièrement je veux me donner<br>Et non me complaire en moi-même,<br>Mais perdre ma vie pour jamais<br>Et accomplir ta volonté pour l''éternité.'),
(323, NULL, 'Garde-moi, Éternel', '323', '', 'Garde-moi, Éternel, ainsi que la prunelle.<br>Cache-moi, ô mon Dieu, à l''ombre de tes ailes.<br>Garde-moi, Éternel, car tu es mon refuge;<br>Mes pas sont fermes dans tes voies,<br>Mes pieds ne chancelleront pas.<br><br><span style=''padding-left:15px''></span>Car qui est Dieu si ce n''est l''Éternel<br><span style=''padding-left:15px''></span>Qui me conduit à l''ombre de ses ailes ?<br><span style=''padding-left:15px''></span>Il est mon appui, oui, mon soutien.<br><br>J''ai gardé devant toi tous tes commandements.<br>Je ne m''éloigne pas, non, de tes ordonnances.<br>Je te loue, ô mon Roi, car tu es à ma droite;<br>Je cherche en toi l''intégrité,<br>La justice et la vérité.<br><br><span style=''padding-left:15px''></span>Oui, je te loue car tu es l''Éternel<br><span style=''padding-left:15px''></span>Qui me conduit à l''ombre de ses ailes.<br><span style=''padding-left:15px''></span>Oui, que ton saint nom soit célébré.'),
(324, NULL, 'Dieu de ma louange', '324', 'Ps', '<span style=''padding-left:15px''></span>Dieu de ma louange, ne te tais point !<br><span style=''padding-left:15px''></span>(Ô, mon Dieu, ne te tais point !)<br><span style=''padding-left:15px''></span>Dieu, que ta louange soit toujours dans ma bouche.<br><span style=''padding-left:15px''></span>(Que ta louange soit toujours dans ma bouche.)<br><span style=''padding-left:15px''></span>Toi qui me retires de la mort, de la détresse,<br><span style=''padding-left:15px''></span>(Toi qui me sauves de la détresse,)<br><span style=''padding-left:15px''></span>Pour ôter mes pleurs et les changer en joie.<br><span style=''padding-left:15px''></span>(Qui prends mes pleurs pour les changer en joie.)<br><span style=''padding-left:15px''></span>Dieu de ma louange, ne te tais point !<br><span style=''padding-left:15px''></span>(Ô, mon Dieu, ne te tais point !)<br><br>Car dans le silence se trouvait mon âme.<br>Elle refusait toute consolation<br>(Refusant la consolation.)<br>Craignant que tu l''abandonnes.<br><br>Car ta délivrance a réjoui mon âme<br>Et l''a entourée de nombreux chants de joie<br>(L''entourant de nombreux chants de joie.)<br>Pour que toujours elle te chante.'),
(325, NULL, 'Que mes lèvres fassent jaillir', '325', 'Ps 119', 'Que mes lèvres fassent jaillir la louange,<br>Et que ma langue entonne un hymne<br>À ta promesse, à ta promesse.<br>Que mon âme vive !<br>Et qu''elle te loue !<br>Car ta loi fait toujours mes délices.<br>Que mon âme vive !<br>Et qu''elle te loue !<br>Je n''oublie pas tes commandements.'),
(326, NULL, 'Comme des enfants bien-aimés', '326', 'Ep 5;Cl 3', 'Comme des enfants bien-aimés,<br>Comme des enfants rachetés,<br>Devenez les imitateurs<br>De Dieu notre Seigneur.<br>Marchez dans la vérité,<br>Marchez dans la charité<br>De Christ qui pour nous s''est livré<br>Et nous a tant aimés.<br><br><span style=''padding-left:15px''></span>Instruisez-vous, exhortez-vous<br><span style=''padding-left:15px''></span>Dans toute la sagesse.<br><span style=''padding-left:15px''></span>Instruisez-vous, exhortez-vous,<br><span style=''padding-left:15px''></span>Chantant de saints cantiques,<br><span style=''padding-left:15px''></span>Célébrant les louanges de Dieu<br><span style=''padding-left:15px''></span>Sous l''inspiration de la grâce.<br><span style=''padding-left:15px''></span>Célébrant les louanges de Dieu<br><span style=''padding-left:15px''></span>Sous l''inspiration de la grâce.<br><br>Autrefois soumis à la mort,<br>Désormais venus à la vie,<br>Ainsi que des élus de Dieu,<br>Marchez dans la sainteté,<br>Revêtez l''humilité,<br>Revêtez-vous de bonté,<br>Gardez l''unité de l''Esprit,<br>Par le lien de la paix.'),
(327, NULL, 'Tes mains m''ont formé', '327', 'Jb 10', 'Tes mains m''ont formé, tes mains m''ont créé.<br>Tu m''as façonné et fait tout entier.<br>Oui, tu me connais de toute éternité;<br>Avant que je naisse, tu m''as consacré.<br><br><span style=''padding-left:15px''></span>Tu m''as accordé la grâce et la vie.<br><span style=''padding-left:15px''></span>Tu tiens dans ta main le souffle de vie.<br><span style=''padding-left:15px''></span>Tu m''as protégé, ô Dieu de bonté.<br><span style=''padding-left:15px''></span>Oui, tu m''as guidé dans ta sainteté.<br><span style=''padding-left:15px''></span>Tu m''as conservé sous ta protection<br><span style=''padding-left:15px''></span>Et je te loue, ô Dieu de compassion.<br><br>Toute création me racontera<br>Que l''Éternel Dieu a fait tout cela.<br>À lui la sagesse et toute puissance.<br>Ô Dieu, je proclame ma reconnaissance.'),
(328, NULL, 'J''Écouterai l''éternel', '328', 'Ps 85', 'J''écouterai l''Éternel, l''Éternel mon Dieu<br>Car il annonce la paix à tous ses fidèles.<br>Son salut est près de ceux, de ceux qui le craignent,<br>Afin que la gloire demeure dans notre pays.<br><br><span style=''padding-left:15px''></span>L''Éternel accordera le bonheur. (bis)<br><span style=''padding-left:15px''></span>Et toute la terre donnera ses fruits.<br><span style=''padding-left:15px''></span>La fidélité germera aussi.<br><span style=''padding-left:15px''></span>L''Éternel est le seul Dieu qui bénit. (bis)<br><span style=''padding-left:15px''></span>La justice marchera devant lui,<br><span style=''padding-left:15px''></span>Tracera ses pas sur le sentier de la vie.<br><br>Rétablis-nous, Éternel, toi notre salut.<br>Accorde-nous ta bonté, ta fidélité.<br>Ne rendras-tu pas la vie, la vie éternelle,<br>Afin que ton peuple se réjouisse en toi ?'),
(329, NULL, 'Béni soit celui qui vient', '329', 'Ps 118', '<span style=''padding-left:15px''></span>Béni soit celui qui vient,<br><span style=''padding-left:15px''></span>Qui vient au nom du Seigneur.<br><span style=''padding-left:15px''></span>C''est Dieu qui nous éclaire.<br><span style=''padding-left:15px''></span>(Voici le Seigneur...)<br><span style=''padding-left:15px''></span>Apportons-lui nos sacrifices.<br><span style=''padding-left:15px''></span>Béni soit celui qui vient.<br><br>Nous vous bénissons de la maison de l''Éternel !<br>Seigneur, tu es mon Dieu, je te louerai<br>(Tu es mon Dieu, je te louerai,)<br>Et je t''exalterai.<br>(Je t''exalterai.)<br><br>Voici la journée, celle que l''Éternel a faite.<br>Qu''elle soit le sujet de notre joie<br>(C''est le sujet de notre joie,)<br>Et de notre allégresse.<br>(De notre allégresse.)'),
(330, NULL, 'Nous avons reçu', '330', '', 'Nous avons reçu un don ineffable:<br>Notre Seigneur Jésus, Sauveur admirable.<br>Que notre amour pour lui soit pur, inaltérable.<br>Il est la perle de grand prix,<br>Il est le trésor impérissable.<br><br><span style=''padding-left:15px''></span>Nous avons contemplé sa gloire,<br><span style=''padding-left:15px''></span>Celle du Fils unique de Dieu,<br><span style=''padding-left:15px''></span>Le Fils unique venu du Père.<br><br>Ainsi que Marie<br>Se tenait assise<br>Aux pieds de Jésus-Christ<br>Afin qu''il l''instruise,<br>Cherchons à écouter<br>La voix de notre Maître,<br>Sans vainement nous agiter,<br>Car seule une chose est nécessaire.'),
(331, NULL, 'Divin Seigneur du ciel', '331', '', 'Divin Seigneur du ciel, toi qui relâches et serres<br>La bride aux vents violents, qui fais trembler la terre,<br>Et tiens le frein des eaux, maniant à ton gré<br>Les ressorts naturels de la terre et des cieux,<br>Abaisse jusqu’à nous ta majesté, ô Dieu,<br>Et fais-nous voir le saint au sanctuaire entré.<br><br>Entré dans le palais du roi des rois du monde,<br>Je n’y adore pas la pierre ou l’or immonde.<br>Mais, ployant les genoux tant du cœur que du corps<br>J’y prie en vérité le seul vrai Dieu suprême,<br>Et vois celui qui est ou l’éternité même,<br>Celui qui a brisé le pouvoir de la mort.<br><br>Devant lui je m’incline et chante dans son temple.<br>Ce Dieu qui seul m’est tout, mon âme le contemple.<br>Bien qu’invisible à nous, il demeure au saint lieu.<br>Dieu tient tout par lui-même dans sa puissante main.<br>Du lieu de sa demeure il règne en souverain,<br>Il remplit l’univers, et la terre et les cieux.<br><br>En m’acceptant par grâce, la grâce de ton Fils,<br>Pour tes enfants perdus, sauve-moi de mes vices.<br>Lave-moi par ton sang, Seigneur en qui je crois.<br>Fais-moi vivre à salut et ne me juge à mort.<br>En Jésus je suis mort; le Père est mon lieu fort,<br>Par l’Esprit qui m’anime et affermit ma foi.'),
(332, NULL, 'Du sein de la détresse', '332', 'Ps 118', 'Du sein de la détresse Dieu entendit ma plainte.<br>Il est ma forteresse : je serai sans crainte.<br>Que pourrait l''adversaire ? Le Seigneur est pour moi.<br>Oui, mon âme tiendra ferme; il défend mon droit.<br><br><span style=''padding-left:15px''></span>Ouvrez-moi les portes<br><span style=''padding-left:15px''></span>Les portes de la justice !<br><span style=''padding-left:15px''></span>Par elles j''entrerai en offrant mon sacrifice.<br><span style=''padding-left:15px''></span>C''est ici la porte, par elle entrent les fidèles.<br><span style=''padding-left:15px''></span>Par mon Dieu je vivrai, et je louerai l''Éternel.<br><br>Mieux vaut la confiance placée dans le Seigneur<br>Que la vaine assurance des dominateurs.<br>Des chants de délivrance jaillissent des fidèles<br>Qui célèbrent la puissance du Dieu d''Israël.<br>'),
(333, NULL, 'Bénissons Dieu mon âme', '1a', 'PF', 'Bénissons Dieu, mon âme, en toute chose,<br>lui sur qui seul tout mon espoir repose,<br>Chantons son nom sans nous lasser jamais!<br>Que tout en moi célèbre sa puissance !<br>Surtout, mon âme, exaltons sa clémence,<br>Et n''oublions aucun de ses bienfaits !<br><br>C''est ce grand Dieu qui, par sa pure grâce,<br>De tes péchés la souillure efface:<br>Il te guérit de toute infirmité;<br>Du tombeau même il retire ta vie,<br>Et rend tes jours encore dignes d''envie,<br>T''environnant partout de sa bonté.<br><br>Mais tes faveurs, ô Dieu, sont éternelles<br>Pour qui t''invoque. Et toujours les fidèles,<br>De siècle en siècle, éprouvent ta bonté.<br>Dieu garde ceux qui marchent en sa crainte,<br>Ceux dont le coeur s''attache à sa loi sainte,<br>Tous ceux, enfin, qui font sa volonté.<br><br>Bénissez-le, célébrez ses louanges,<br>Héraults puissants, choeurs immortels des anges<br>Qui le servez aux demeures du ciel !<br>Que ses vertus par vous soient proclamées;<br>Bénissez-le, vous toutes ses armées;<br>Mon âme, adore et bénis l''Éternel !'),
(334, NULL, 'À toi la gloire', '2a', 'AF 118', 'À toi la gloire,<br>Ô Ressuscité !<br>A toi la victoire,<br>Pour l''éternité !<br>Brillant de lumière,<br>L''ange est descendu;<br>Il roule la pierre<br>Du tombeau vaincu.<br><br><span style=''padding-left:15px''></span>À toi la gloire,<br><span style=''padding-left:15px''></span>Ô Ressuscité !<br><span style=''padding-left:15px''></span>À toi la victoire,<br><span style=''padding-left:15px''></span>Pour l’éternité !<br><br>Vois-le paraître !<br>C''est lui, c''est Jésus;<br>Ton Sauveur, ton Maître.<br>Oh, ne doute plus !<br>Sois dans l''allégresse,<br>Peuple du Seigneur,<br>Et redis sans cesse<br>Que Christ est vainqueur.<br><br>Craindrais-je encore ?<br>Il vit à jamais,<br>Celui que j''adore,<br>Le Prince de paix ;<br>Il est ma victoire,<br>Mon puissant soutien,<br>Ma vie et ma gloire;<br>Non, je ne crains rien !'),
(335, NULL, 'Seigneur, que n’ai-je mille voix', '3a', 'AF 51', 'Seigneur, que n''ai-je mille voix<br>Pour chanter tes louanges ! (bis)<br>Et faire monter jusqu''aux anges<br>Les gloires de ta croix,<br>Les gloires de ta croix, ta croix,<br>Les gloires de ta croix.<br><br>Jésus, mon Seigneur et mon Dieu,<br>Que ton souffle m''anime ! (bis)<br>Pour que par moi ton nom sublime<br>Retentisse en tout lieu,<br>Retentisse en tout lieu, tout lieu,<br>Retentisse en tout lieu.<br><br>Désormais je n''ai plus d''effroi.<br>Aucun mal ne m''accable. (bis)<br>Ton sang rend pur le plus coupable,<br>Ton sang coula pour moi,<br>Ton sang coula pour moi, pour moi,<br>Ton sang coula pour moi.'),
(336, NULL, 'Par tous les saints glorifié', '4a', 'AF 46', 'Par tous les saints glorifié,<br>Jésus inspire leurs louanges,<br>Plus belles que le chant des anges:<br>Gloire à l''Agneau ! Gloire à l ''Agneau !<br>Gloire à l''Agneau sacrifié !<br><br>C''est par lui qu''est justifié<br>Tout pécheur qui demande grâce.<br>Prêtres et rois, devant sa face,<br>Chantons l''Agneau (ter) sacrifié !<br><br>Par le Père magnifié,<br>Tout l''univers lui rend hommage.<br>l''Agneau régnera d''âge en âge,<br>Gloire à l''Agneau (ter) sacrifié !<br><br>Par son Esprit vivifié,<br>Je veux, jusqu''à ma dernière heure,<br>Chanter l''amour qui seul demeure,<br>Gloire à l''Agneau (ter) sacrifié !<br><br>Pour nous il fut crucifié;<br>Son sang a racheté notre âme.<br>C''est pourquoi notre amour l''acclame:<br>Gloire à l''Agneau (ter) sacrifié !'),
(337, NULL, 'Du rocher de jacob', '5a', 'AF 32', 'Du rocher de Jacob, toute l''oeuvre est parfaite.<br>Ce que sa bouche a dit, sa main l''accomplira.<br>Alléluia ! Alléluia ! (bis)<br>Car il est notre Dieu, (ter) notre haute retraite !<br><br>C''est pour l''éternité que le Seigneur nous aime,<br>Sa grâce en notre coeur jamais ne cessera.<br>Alléluia ! Alléluia ! (bis)<br>Car il est notre espoir (ter), notre bonheur suprême.<br><br>Louons donc l''Éternel, notre Dieu, notre Père.<br>Le Seigneur est pour nous, contre nous qui sera ?<br>Alléluia ! Alléluia ! (bis)<br>Triomphons en Jésus (ter) et vivons pour lui plaire.'),
(338, NULL, 'Nous attendons le sauveur', '6a', 'AF 129', 'Nous attendons le Sauveur glorieux<br>D''un coeur joyeux, remplis de confiance.<br>Il va régner sur la terre et les cieux,<br>Dans le beau jour de notre délivrance.<br><br><span style=''padding-left:15px''></span>Voir face à face notre Sauveur:<br><span style=''padding-left:15px''></span>Oh, quelle grâce, quel parfait bonheur !<br><span style=''padding-left:15px''></span>Voir face à face notre Sauveur:<br><span style=''padding-left:15px''></span>Oh, quel parfait bonheur !<br><br>Autour de lui nous nous retrouverons,<br>En un clin d''oeil formés à son image;<br>À sa rencontre ensemble nous irons,<br>Et le servir sera notre partage.<br><br>Là le soleil chasse l''obscurité,<br>Un jour sans fin succède à la nuit sombre;<br>Plus d''ouragan dans ce port abrité,<br>Où le bonheur sera pur et sans ombre.<br><br>Tout près de lui nous pourrons approcher.<br>Et notre main par la sienne tenue,<br>Dans les lieux saints, qu''il fera beau marcher,<br>Après la foi, pour toujours par la vue.'),
(339, NULL, 'Christ est ressuscité', '7a', 'CV 9', 'Christ est ressuscité,<br>Qu''en des chants joyeux,<br>Son triomphe en tous lieux<br>Soit exalté !<br><br><span style=''padding-left:15px''></span>A toi la gloire et l''honneur,<br><span style=''padding-left:15px''></span>Ô Sauveur, ô puissant Rédempteur !<br><span style=''padding-left:15px''></span>Du sépulcre tu sortis vainqueur,<br><span style=''padding-left:15px''></span>Prince de vie et Prince de paix,<br><span style=''padding-left:15px''></span>Gloire à toi, Gloire à toi,<br><span style=''padding-left:15px''></span>Gloire à toi, gloire à jamais.<br><br>Christ est ressuscité,<br>Cherche en lui toujours,<br>Ô peuple racheté,<br>Force et secours !<br><br>Christ est ressuscité,<br>Par lui nous vivrons,<br>Et dans l’éternité<br>Nous régnerons.'),
(340, NULL, 'L''Éternel seul est seigneur', '8a', 'AF 10', 'L''Éternel seul est Seigneur,<br>Seul, il est dominateur. (bis)<br>Sur les peuples de la terre:<br>Il est maître souverain (bis)<br>Des ouvrages que sa main,<br>Pour sa gloire, a voulu faire.<br><br>Bienheureux qui, dans son coeur,<br>Par ton Esprit, ô Seigneur, (bis)<br>Hait du péché les souillures;<br>Qui, fuyant la fausseté, (bis)<br>Te sert en sincérité,<br>Levant vers toi des mains pures.<br><br>Ouvrez-vous, célestes lieux,<br>Haussez-vous portes des cieux ! (bis)<br>Car voici le roi de gloire.<br>Quel est ce roi, ce vainqueur ? (bis)<br>C''est Jésus, le Rédempteur,<br>Qui revient de la victoire.<br><br>Avec lui, nous entrerons,<br>Avec lui, nous régnerons (bis)<br>Dans cette gloire éternelle:<br>Ouvrez-vous, portes des cieux ! (bis)<br>Tressaillez, célestes lieux,<br>D''une allégresse nouvelle !'),
(341, NULL, 'Oh, que toute la terre entonne', '9a', 'AF 534', 'Oh, que toute la terre entonne<br>Un hymne à ton honneur !<br>Que l''univers entier résonne<br>De ta gloire, ô Sauveur !<br><br><span style=''padding-left:15px''></span>À toi, louange, honneur et gloire,<br><span style=''padding-left:15px''></span>À ton nom, ô Jésus !<br><span style=''padding-left:15px''></span>Ta croix remporte la victoire<br><span style=''padding-left:15px''></span>Et sauve les perdus.<br><br>Ton nom, c''est l''amour et la vie,<br>La grâce et le pardon,<br>Et l''âme écoute et se confie<br>À toi dans l''abandon.<br><br>Ô Christ, que tout genou fléchisse<br>À ton nom glorieux;<br>Pour t''adorer, que tout s''unisse,<br>Sur terre et dans les cieux.'),
(342, NULL, 'Ma richesse, ma gloire', '10a', 'AF 44', 'Ma richesse, ma gloire<br>Et ma félicité,<br>Ô Jésus, c''est de croire<br>Que tu m''as racheté.<br>Rempli de confiance<br>En toi, divin Sauveur,<br>Je vis dans l''espérance<br>Et je marche sans peur.<br><br>Rien n''ôte les souillures,<br>Rien ne guérit le coeur,<br>Sinon les meurtrissures<br>Et le sang du Sauveur.<br>Oui, Jésus seul nous lave<br>De toute iniquité,<br>Et procure à l''esclave<br>l''heureuse liberté.<br><br>Oh, que toute la terre<br>Confesse à ce sauveur<br>Ses péchés, sa misère;<br>Il fait grâce au pécheur.<br>Son amour insondable<br>Lui fit quitter le ciel,<br>Pour donner au coupable<br>Un salut éternel.<br>'),
(343, NULL, 'Je l’ai trouvé', '11a', 'AF 269', 'Je l''ai trouvé, je l''ai trouvé,<br>Le bonheur ineffable !<br>Je suis sauvé, je suis sauvé,<br>Ô joie inexprimable !<br>Tous mes péchés sont effacés:<br>Le sang de Christ me lave,<br>Les jours des larmes sont passés:<br>Je ne suis plus esclave !<br><br>Oh, quel bonheur ! Oh, quel bonheur !<br>D''avoir Jésus pour maître !<br>Ô mon Sauveur, mon seul Sauveur,<br>À toi seul je veux être !<br>Tu vins briser, puissant vainqueur,<br>Du mal la tyrannie,<br>Affranchissant mon pauvre coeur<br>Et me donnant la vie.<br><br>Dans ton amour tu m''as cherché.<br>Errant bien loin du Père,<br>Tu m''as sauvé de mon péché,<br>Tu fis de moi ton frère.<br>Et maintenant et pour jamais,<br>Sous ton joug je me plie.<br>Je ne puis vivre désormais,<br>Jésus, que de ta vie !<br><br>Ah ! Laissez-moi chanter mon Roi;<br>Oui, que toujours je chante !<br>Jésus n''est-il pas tout pour moi ?<br>Gloire à sa croix sanglante !<br>Sans se lasser, jour après jour,<br>Il m''aime, il m''aime encore...<br>Comment répondre à tant d''amour ?<br>Je crois, j’aime et j’adore !'),
(344, NULL, 'À l''Agneau sur son trône', '12a', 'AF 62', 'À l''Agneau sur son trône<br>Apportons la couronne.<br>Il l''a conquise sur la croix;<br>Il est le Roi des rois.<br>Éveille-toi mon âme !<br>Bénis, adore, acclame,<br>Avec tous les anges du ciel,<br>Jésus, Emmanuel !<br><br>À l''Agneau sur son trône,<br>L''encens et la couronne,<br>Car il est le Verbe incarné.<br>D''une vierge il est né,<br>Ô Sagesse profonde,<br>Le créateur du monde,<br>Pour vaincre le mal triomphant,<br>S''est fait petit enfant !<br><br>À l''Agneau sur son trône<br>La palme et la couronne,<br>Car il est le Prince de paix,<br>Il règne désormais.<br>Au prince de la vie<br>La mort est asservie;<br>Hors de la tombe il est monté,<br>Christ est ressuscité.<br><br>À l''Agneau tous les trônes,<br>Et toutes les couronnes !<br>Il est le maître souverain,<br>Les temps sont dans sa main.<br>Rendons l''honneur suprême<br>À ce Dieu qui nous aime<br>Et qui revient victorieux,<br>Pour nous ouvrir les cieux !'),
(345, NULL, 'Étranger sur la terre', '13a', 'AF 422', 'Étranger sur la terre,<br>Je marche avec bonheur<br>Vers la maison du Père,<br>Vers la maison où m’attend le Seigneur.<br>Vers la maison du Père,<br>Vers la maison où m’attend le Seigneur.<br><br><span style=''padding-left:15px''></span>Oh, quand sera-ce que, face à face,<br><span style=''padding-left:15px''></span>our toujours près de toi,<br><span style=''padding-left:15px''></span>Je te verrai mon roi ?<br><br>C''est là qu''au choeur des anges,<br>Pendant l''éternité,<br>J’unirai mes louanges<br>Pour donner gloire à sa fidélité.<br>J’unirai mes louanges<br>Pour donner gloire à sa fidélité.<br><br>C''est là, devant le trône,<br>Qu''avec tous les élus,<br>Je prendrai ma couronne,<br>Pour la jeter à tes pieds, ô Jésus !<br>Je prendrai ma couronne,<br>Pour la jeter à tes pieds, ô Jésus !<br><br>Ainsi plein d''allégresse,<br>Conduit par ton amour,<br>Je veux marcher sans cesse,<br>Ô mon sauveur, vers ton divin séjour.<br>Je veux marcher sans cesse,<br>Ô mon sauveur, vers ton divin séjour.'),
(346, NULL, 'De canaan quand verrons-nous', '14a', 'AF 631', 'De Canaan, quand verrons-nous<br>Le céleste rivage ?<br>Vers le Jourdain, entendez-vous ?<br>Christ nous appelle tous.<br>Près de lui, doux partage,<br>À l''abri de l''orage,<br>Nous pourrons chanter à jamais<br>Le cantique de paix.<br><br><span style=''padding-left:15px''></span>Oh ! Quel parfait bonheur,<br><span style=''padding-left:15px''></span>Quel bonheur, oui, quel bonheur !<br><span style=''padding-left:15px''></span>Oh ! Quel parfait bonheur<br><span style=''padding-left:15px''></span>Après tant de labeur !<br><span style=''padding-left:15px''></span>Pour toujours réunie,<br><span style=''padding-left:15px''></span>L''Église en sa patrie,<br><span style=''padding-left:15px''></span>Entonnera: "Alléluia !<br><span style=''padding-left:15px''></span>Gloire à toi, roi des rois !"<br><br>Vêtus de blanc, les rachetés<br>De Christ verront la gloire.<br>Par sa vertu ressuscités,<br>Ils diront ses bontés.<br>Célébrant sa victoire,<br>Son oeuvre expiatoire,<br>Autour de son trône, avec eux,<br>Nous lui rendrons nos voeux.<br><br>Du grand jour de l''éternité<br>Quand brillera l''aurore,<br>Tous, consommés dans l''unité<br>Et dans la charité,<br>À celui qu''on adore,<br>Nous redirons encore:<br>Digne est l''Agneau de recevoir<br>Force, empire et pouvoir !'),
(347, NULL, 'Nous sommes au seigneur', '15a', 'AF 300', 'Nous sommes au Seigneur et non point à nous-mêmes.<br>Pour la vie et la mort, nous sommes au Seigneur.<br>C’est pour l’éternité, Jésus, que tu nous aimes;<br>Ta croix nous a rendu la paix et le bonheur.<br><br>Nous sommes au Seigneur. Ah ! vivons pour sa gloire !<br>Et mourons au péché, saints héritiers du ciel.<br>Saisissons chaque jour les fruits de sa victoire<br>Et reposons en paix auprès de l''Éternel.<br><br>Nous sommes au Seigneur. Au fort de la détresse,<br>Le coeur de ses enfants peut s''assurer en lui;<br>À l''heure du combat, au jour de la tristesse,<br>Partout sur mon sentier, son amour a relui.<br><br>Nous sommes au Seigneur ! Dans la sombre vallée,<br>Du dernier ennemi, Jésus me rend vainqueur !<br>La face de mon Dieu ne peut m''être voilée.<br>Car pour l''éternité nous sommes au Seigneur !'),
(348, NULL, 'Voir mon Sauveur', '16a', 'AF 432', 'Voir mon Sauveur face à face,<br>Voir Jésus dans sa beauté,<br>Ô joie, ô suprême grâce !<br>Ô bonheur, félicité !<br><br><span style=''padding-left:15px''></span>Oui, dans ta magnificence,<br><span style=''padding-left:15px''></span>Je te verrai, divin Roi !<br><span style=''padding-left:15px''></span>Pour toujours en ta présence,<br><span style=''padding-left:15px''></span>Je serai semblable à Toi !'),
(349, NULL, 'Levons-nous frères', '17a', 'AF 362', 'Levons-nous, frères, levons-nous,<br>Car voici notre Maître.<br>Il est minuit, voici l''Époux;<br>Jésus-Christ va paraître. (bis)<br><br>Avec les siens il vient régner<br>Et délivrer l''Église;<br>Bientôt il va la couronner<br>De la gloire promise. (bis)<br><br>Et si le monde est contre toi,<br>Ses mépris sont ta gloire;<br>L''amour, l''espérance et la foi<br>Te donnent la victoire. (bis)<br><br>Gloire à Jésus-Christ mon sauveur,<br>Car en lui seul j''espère !<br>Heureux celui qui, dans son coeur,<br>L''adore et Le révère ! (bis)'),
(350, NULL, 'Jésus te confie', '18a', 'AF 614', 'Jésus te confie une oeuvre d''amour,<br>Utile et bénie jusqu''à son retour.<br>Cette sainte tâche, veux-tu l''accomplir,<br>Pour lui sans relâche, sans jamais faiblir ?<br><br><span style=''padding-left:15px''></span>Prie, agis, jour après jour.<br><span style=''padding-left:15px''></span>Sans broncher, suis ton sauveur avec amour !<br><span style=''padding-left:15px''></span>Sois fidèle, obéissant,<br><span style=''padding-left:15px''></span>Et le Maître rendra ton travail puissant.<br><br>Va chercher ton frère, esclave enlacé,<br>Las de sa misère, de son noir passé;<br>Arrache son âme au plaisir trompeur,<br>Le salut proclame, en Christ, ton sauveur.<br><br>Va vers la jeunesse, que le tentateur<br>Veut leurrer sans cesse loin du vrai bonheur;<br>Combattant le doute, parle-lui d’amour;<br>Montre-lui la route du ciel, saint séjour.<br><br>Va vers ceux qui meurent sans Dieu, sans espoir;<br>Dis à ceux qui pleurent quand tout semble noir:<br>"Jésus donne vie, bonheur, joie et paix,<br>A qui se confie en lui pour jamais".'),
(351, NULL, 'La voix de Christ nous appelle', '19a', 'AF 347', 'La voix de Christ nous appelle,<br>Il est temps de s''éveiller:<br>"La moisson est vaste et belle,<br>Qui veut pour moi travailler ?"<br>C''est ton Sauveur, ô mon frère,<br>Dont l''appel s''adresse à toi,<br>Réponds-lui d’un coeur sincère:<br>"Me voici, Maître, prends-moi !"<br><br>Sans franchir les mers bruyantes,<br>tu peux annoncer Jésus.<br>Que d''âmes insouciantes,<br>De coeurs souffrants et perdus !<br>Autour de nous l''oeuvre est grande,<br>Mais petite est notre foi.<br>À Jésus qui nous commande,<br>Répondons: "Maître, aide-moi !"<br><br>Mais que nul ne nous entende<br>Dire encore: "Je ne puis rien !",<br>Lorsque Jésus nous commande<br>De faire et d’aimer le bien.<br>Poursuivons l’oeuvre bénie<br>Avec zèle, amour et foi,<br>Puis notre tâche finie,<br>Nous dirons: "Maître, prends-moi !"'),
(352, NULL, 'Abandonne ta vie', '20a', 'AF 309', 'Abandonne ta vie,<br>Tes craintes et tes voeux,<br>À la grâce infinie<br>Du Souverain des cieux.<br>Lui qui trace la route<br>Aux mondes comme aux vents,<br>Conduira sans nul doute<br>Les pas de ses enfants.<br><br>Qu''il soit ton espérance,<br>Aux bons, aux mauvais jours;<br>Qu''il soit dans la souffrance<br>Ton unique recours.<br>Dis-lui toutes tes peines,<br>Remets-lui tes douleurs;<br>Il a brisé tes chaînes,<br>Il séchera tes pleurs.<br>Espère, âme angoissée,<br><br>Espère en ton sauveur;<br>Il connaît ta pensée,<br>Il a lu dans ton coeur.<br>Il est l''amour suprême,<br>Il est le vrai chemin;<br>Viens à lui car il t''aime,<br>Viens, il te tend la main.<br><br>A ta voix qui m''appelle,<br>Je répondrai: "Seigneur,<br>Je veux, humble et fidèle,<br>Te consacrer mon coeur.<br>Et malgré la tristesse,<br>Je vivrai par la foi,<br>En célébrant sans cesse<br>Le bonheur d''être à toi."'),
(353, NULL, 'Viens au Père', '21a', 'AF 252', 'Viens au Père qui t''appelle,<br>Oh ! Reviens à la maison !<br>Voici l''heure solennelle,<br>Où Dieu t''offre son pardon.<br><br><span style=''padding-left:15px''></span>Aujourd''hui, viens à lui !<br><span style=''padding-left:15px''></span>Ah ! Pourquoi tarderais-tu ?<br><span style=''padding-left:15px''></span>Aujourd''hui, viens à lui !<br><span style=''padding-left:15px''></span>Viens, reçois son grand salut !<br><br>Trop longtemps dans la misère,<br>tu vécus sans vrai bonheur !<br>Se peut-il que tu préfères<br>Fuir encore loin du Sauveur ?<br><br>Ah ! Ton âme est angoissée,<br>En vain tu cherchas la paix;<br>Par sa croix qui fut dressée,<br>Christ la donne et pour jamais.<br><br>À Jésus dis ta souffrance,<br>Ô reconnais ton péché;<br>Mets en lui ton espérance,<br>Car en lui Dieu t''a cherché.'),
(354, NULL, 'Tel que je suis pécheur rebelle', '22a', 'AF 260', 'Tel que je suis, pécheur rebelle,<br>Au nom du sang versé pour moi,<br>Au nom de ta voix qui m''appelle,<br>Jésus, je viens à toi !<br><br>Tel que je suis dans ma souillure,<br>Ne cherchant nul remède en moi,<br>Ton sang lave mon âme impure,<br>Jésus, je viens à toi !<br><br>Tel que je suis, avec mes luttes,<br>Mes craintes, ma timide foi,<br>Avec mes doutes et mes chutes,<br>Jésus, je viens à toi !<br><br>Tel que je suis, je me réclame,<br>De ta promesse par la foi.<br>Au ciel tu recevras mon âme,<br>Jésus, je viens à toi !<br><br>Tel que je suis, ton sacrifice<br>À ma place accomplit la loi:<br>Justifié par ta justice,<br>Jésus, je viens à toi !<br><br>Tel que je suis, Dieu me convie,<br>Ô mon Sauveur, pour être à toi,<br>À toi dans la mort, dans la vie,<br>Jésus, je viens à toi !'),
(355, NULL, 'Ma vie enfin je te donne', '23a', 'AF 600', 'Ma vie enfin je te donne,<br>Ô Jésus, agneau de Dieu !<br>Prends-la, je te l''abandonne,<br>À cette heure et dans ce lieu.<br>Je confesse mes souillures,<br>Mon péché, mon mauvais coeur.<br>Dans le sang de tes blessures,<br>Lave-moi, divin Sauveur !<br><br>Tu connais mes luttes vaines<br>Pour triompher de mon coeur;<br>tu sais le poids de mes chaînes<br>Et ma secrète douleur.<br>Que ta puissance infinie<br>M''affranchisse du péché,<br>Et que par l''Esprit de vie<br>Du mal je sois détaché !<br><br>Scelle à jamais l''alliance<br>Que je traite maintenant,<br>Et dans ton amour immense,<br>Viens la signer de ton sang.<br>Si je regarde à moi-même<br>Un instant je suis perdu !<br>Mais tu sauras, ton coeur m''aime,<br>Me tenir debout, Jésus !'),
(356, NULL, 'Autrefois loin de dieu', '24a', 'CGG 309', 'Autrefois, loin de Dieu, perdu sans espérance,<br>Comme un esclave au mal attaché,<br>Je marchais vers la mort, l''éternelle souffrance:<br>Juste châtiment de mon péché;<br>Mais maintenant, Jésus est mon Sauveur;<br>Je suis lavé de mes souillures;<br>Par son sang et ses meurtrissures,<br>J’ai le pardon, la paix et le bonheur !<br><br>Autrefois, dans la nuit, j''errais seul et sans guide,<br>Ainsi qu''un pauvre aveugle égaré;<br>Je cherchais un chemin dans le désert aride,<br>Le coeur plein d’angoisse et déchiré.<br>Mais maintenant, le Seigneur me conduit;<br>Mon chemin n''est plus solitaire,<br>Je marche à sa pure lumière;<br>Sur mon sentier, toujours sa face luit.<br><br>Autrefois, ébloui par les attraits du monde,<br>Je croyais à leur charme trompeur:<br>Ephémères plaisirs, passagers comme l''onde,<br>Ne laissant qu''amertume et douleur;<br>Mais maintenant, ma joie est en Jésus;<br>Il est mon céleste héritage;<br>Sa gloire sera mon partage;<br>J''en jouirai parmi tous les élus.'),
(357, NULL, 'Ah ! Que je ne sois pas !', '25a', 'AF 311', 'Ah ! Que je ne sois pas comme un rameau stérile,<br>Qui, séparé du tronc, doit périr desséché.<br>Mais que je sois, ô Dieu, comme un sarment fertile<br>Qu''aucun vent d''aquilon n''a du cep arraché.<br><br>Demeure en moi, Jésus, et qu''en toi je demeure,<br>Trouvant dans ton amour le plus fort des liens,<br>Portant beaucoup de fruits, chaque jour, à chaque heure,<br>Et renonçant à tout pour jouir des vrais biens.<br><br>Celui qui croit en toi, ta bouche le déclare,<br>Accomplira, Seigneur, les oeuvres que tu fis.<br>Je crois... et d''où vient donc que mon âme s''égare,<br>Si loin du droit sentier que toujours tu suivis ?<br><br>Hélas ! C''est que souvent je tourne vers le monde<br>Des yeux qui ne devraient s''arrêter que sur toi !<br>Ne me retranche pas... non, Seigneur, mais émonde,<br>Pour que j''apprenne mieux à pratiquer ta loi.<br><br>Toutefois, que jamais mon coeur ne se confie<br>En mes pas chancelants pour arriver au but;<br>Tu donnas pour les tiens, divin Jésus, ta vie,<br>Et c''est mon seul espoir de paix et de salut.'),
(358, NULL, 'À celui qui sera vainqueur', '26a', 'AF 377', 'À celui qui sera vainqueur,<br>Et qui me glorifie,<br>Je donnerai, dit le Seigneur,<br>Au ciel l''arbre de vie !<br><br><span style=''padding-left:15px''></span>Victoire, force, honneur et louanges,<br><span style=''padding-left:15px''></span>Gloire, gloire, puissance à toi, Jésus !<br><br>Un caillou blanc, un nom nouveau<br>Et la manne cachée,<br>Lui seront donnés par l''Agneau,<br>Sa promesse est scellée !<br><br>Revêtu d’un vêtement blanc,<br>Resplendissant de gloire,<br>Sera celui qui, par le sang,<br>Remporta la victoire !<br><br>Avec Christ, celui qui vaincra<br>S''assiéra sur son trône,<br>Et, de ses mains, il recevra<br>L''immortelle couronne.'),
(359, NULL, 'Je vois au loin briller l’étoile', '27a', 'LLB', 'Je vois au loin briller l''étoile<br>Du matin de l''éternité,<br>Et je vois, à travers un voile,<br>Resplendir la sainte cité.<br><br><span style=''padding-left:15px''></span>Oh, je voudrais m''envoler vers toi<br><span style=''padding-left:15px''></span>Et remporter le prix de ma foi.<br><span style=''padding-left:15px''></span>De ce qui passe<br><span style=''padding-left:15px''></span>Le coeur se lasse,<br><span style=''padding-left:15px''></span>Viens, Seigneur Jésus, divin Roi !<br><br>Déjà j''entends le choeur des anges<br>Qui résonne en un chant nouveau.<br>Et ma voix, jointe à leur phalange,<br>Dans les cieux célèbre l''Agneau.<br><br>Je vois le fleuve de l''eau vive,<br>Et du ciel les portiques d''or.<br>Et mon âme, toujours captive,<br>Voudrait prendre enfin son essor.<br><br>Seigneur, tu sais que mon coeur t''aime,<br>Que je suis à toi sans retour.<br>Mon désir et mon voeu suprême<br>C''est d''être à toi seul pour toujours.'),
(360, NULL, 'Je crois au Dieu vivant', '28a', 'LLB', 'Je crois au Dieu vivant, des cieux le créateur.<br>Je crois qu''il est le tout-puissant, le seul Seigneur.<br>Je sais qu''il est amour, il m''aime et me conduit.<br>Il est ma force et mon secours, mon ferme appui.<br><br>Je crois au Fils de Dieu, Jésus le seul Sauveur.<br>Je crois qu''il est venu des cieux, pour nous pécheurs.<br>Il est mort sur la croix, il sort du froid tombeau.<br>Ô joie, il vit et règne en moi, il vient bientôt!<br><br>Je crois au Saint-Esprit, le grand consolateur.<br>Je crois que des divins écrits il est l''auteur.<br>Il a scellé ma foi, il me guide et m''instruit.<br>Par lui Jésus habite en moi, par lui je vis.<br><br>Je crois au Livre saint, suprême autorité<br>Qui montre à tous le seul chemin, la vérité.<br>Il garde pur mon front, m''éclaire et me nourrit;<br>Quand vient le doute, je réponds: "Il est écrit !"<br><br>Ma foi jaillit du coeur, et c''est un don de Dieu.<br>Par elle Dieu me rend vainqueur, me rend heureux.<br>Je sais en qui je crois, je ne chancelle pas,<br>C''est la victoire de la foi. Alléluia !'),
(361, NULL, 'Alléluia, louez l’éternel', '29a', 'JEM 64', 'Alléluia, louez l''Éternel !<br>Car il est bon,<br>Car sa miséricorde dure à toujours,<br>Dure à toujours, amen ! (bis)<br><br>Non pas à nous Seigneur, pas à nous,<br>Mais à ton nom,<br>À ton nom donne gloire pour ton amour,<br>Pour ta fidélité ! (bis)<br><br>Qu''un chant nouveau, joyeux, monte au ciel<br>Vers notre Dieu,<br>Car sa Parole est droite. Il dit un mot,<br>La chose s''accomplit. (bis)<br><br>Ton trône ô Dieu subsiste à jamais.<br>Tu tiens en main<br>Le sceptre de justice, et moi je dis:<br>"Mon oeuvre est pour le Roi." (bis)');
INSERT INTO `hymn` (`id`, `musical_part_id`, `title`, `num`, `ref`, `lyrics`) VALUES
(362, NULL, 'Saisis ma main craintive', '30a', 'AF 401', 'Saisis ma main craintive et conduis-moi.<br>Et que toujours je vive, caché en toi.<br>Sans toi mon tendre Père, pour me guider,<br>Je ne sais sur la terre comment marcher.<br><br>Que ta main me dispense joie ou douleur,<br>Paisible en ta présence, garde mon coeur.<br>En toi pour toute chose se confiant,<br>À Tes pieds se repose ton faible enfant.<br><br>Quand la nuit la plus noire te voilerait,<br>Ton bras jusqu''à la gloire me porterait.<br>Saisis ma main craintive, et conduis-moi.<br>Fais que toujours je vive, caché en toi.'),
(363, NULL, 'Sentinelle vigilante', '31a', 'AF 364', 'Sentinelle vigilante,<br>Qu''en est-il donc de la nuit ?<br>Dis à l''âme somnolente<br>Que déjà le matin luit.<br><br><span style=''padding-left:15px''></span>La nuit passe,<br><span style=''padding-left:15px''></span>Le matin du grand jour luit.<br><span style=''padding-left:15px''></span>Sentinelle,<br><span style=''padding-left:15px''></span>Sois au poste jour et nuit.<br><span style=''padding-left:15px''></span>Sentinelle,<br><span style=''padding-left:15px''></span>Sois au poste jour et nuit.<br><br>Les gardes sur la muraille<br>Nous l''ont dit, entendez-vous ?<br>Au loin gronde la bataille,<br>Tout est sombre autour de nous.<br><br>Point de repos, de relâche,<br>Rachetés de l''Eternel !<br>Travaillez à votre tâche,<br>Car Jésus revient du ciel.'),
(364, NULL, 'Triomphons, chantons d’allégresse', '32a', 'AF 372', 'Triomphons, chantons d''allégresse,<br>Réjouissons-nous devant Dieu.<br>Qu''un chant de louanges, sans cesse,<br>A sa gloire monte en tout lieu.<br><br><span style=''padding-left:15px''></span>Triomphe, triomphe,<br><span style=''padding-left:15px''></span>Tressaille de joie en ton Dieu,<br><span style=''padding-left:15px''></span>Triomphe, triomphe,<br><span style=''padding-left:15px''></span>Tressaille de joie en ton Dieu !<br><br>Chantons à celui qui s''avance<br>Dans les cieux, les cieux éternels.<br>Il fait entendre avec puissance<br>Sa voix; c''est le Dieu d’Israël.<br><br>Il donne à son peuple victoire,<br>Puissance, force, liberté.<br>À notre Dieu rendons la gloire,<br>ouons Sa sainte majesté.<br><br>De concert avec les saints anges,<br>Faisons retentir notre voix.<br>Chantons à jamais les louanges<br>De l''Agneau divin mis en croix.<br>'),
(391, NULL, 'Je ne sais pourquoi dans sa grâce', '33a', 'AF 370', 'Je ne sais pourquoi, dans sa grâce,<br>Jésus m''a tant aimé,<br>Pourquoi par son sang il efface<br>Ma dette, mon péché.<br><br>Mais je sais qu''en lui j''ai la vie,<br>Il m''a sauvé dans son amour;<br>Et gardé par sa main meurtrie,<br>J''attends l''heure de son retour.<br><br>Je ne sais comment la lumière<br>Eclaire tout mon coeur,<br>Comment je compris ma misère,<br>Et reçus mon sauveur.<br><br>Je ne sais quelle est la mesure<br>De joie et de douleur,<br>Que pour moi faible créature,<br>Réserve mon sauveur.<br><br>Je ne sais quand de la victoire<br>L''heure enfin sonnera,<br>Quand l''Agneau, l''Époux, dans sa gloire<br>Avec lui me prendra.'),
(392, NULL, 'Tel que je suis sans rien à moi', '34a', 'AF 253', 'Tel que je suis, sans rien à moi,<br>Sinon ton sang versé pour moi,<br>Et ta voix qui m''appelle à toi,<br>Agneau de Dieu, je viens, je viens.<br><br>Tel que je suis, bien vacillant,<br>En proie au doute à chaque instant;<br>Luttes au dehors, craintes au dedans,<br>Agneau de Dieu, je viens, je viens.<br><br>Tel que je suis, ton coeur est prêt<br>À prendre le mien tel qu''il est,<br>Pour tout changer, Sauveur parfait,<br>Agneau de Dieu, je viens, je viens.<br><br>Tel que je suis, ton grand amour<br>A tout pardonné sans retour.<br>Je veux être à toi dès ce jour,<br>Agneau de Dieu, je viens, je viens.'),
(393, NULL, 'Oh ! jésus je t''aime', '35a', 'AF 603', 'Oh ! Jésus je t''aime, et tu vis en moi.<br>Quel amour extrême me retient à toi.<br>Les plaisirs du monde ne m''attirent plus.<br>Ton amour m''inonde, je t''aime, ô Jésus.<br><br>Oh ! Jésus je t''aime, car tu m''as sauvé,<br>En t''offrant toi-même, ton sang m''a lavé.<br>Sur la croix maudite, pour moi tu mourus.<br>Ta mort est ma vie, je t''aime ô Jésus.<br><br>Qu''ici bas je t''aime jusque dans la mort.<br>À l''heure suprême du dernier effort,<br>Ma voix expirante ne s''entendra plus;<br>Sachez que je chante: "Je t''aime ô Jésus."<br><br>Je verrai ta face, quel ravissement !<br>Je louerai ta grâce éternellement,<br>Et dirai sans cesse avec les élus,<br>L''hymne d''allégresse: "Je t''aime ô Jésus."'),
(394, NULL, 'C’est par la foi', '36a', 'CGG 247', 'C''est par la foi que, voyant l''invisible,<br>Ils se sont levés à l''appel du Seigneur.<br>Rien avec lui ne semblait impossible,<br>Tant Sa promesse enflammait leur ardeur.<br><br>Brûle en nos âmes, flamme sacrée !<br>Et nous suivrons les témoins d''autrefois,<br>Environnés de leur grande nuée,<br>Les regards sur Jésus, Chef de la foi.<br><br>C''est par la foi qu''ils fuyaient l''esclavage,<br>Qu''ils passaient la mer, qu''ils étaient valeureux,<br>Grâce au Seigneur se frayant un passage,<br>Vers la cité préparée pour eux.<br><br>C''est par la foi que, dans l''ignominie,<br>Ils ont enduré des épreuves sans fin,<br>Sans obtenir ici-bas de patrie,<br>Mais saluant le triomphe lointain.<br><br>Foi des anciens ! Qui combat sous ce signe,<br>Suit leur saint exemple et partage leur sort.<br>Eux dont le monde ne fut jamais digne,<br>Eux les témoins qui nous parlent encore.<br><br>Brûle en nos âmes, flamme sacrée !<br>Et nous suivrons les témoins d’autrefois,<br>Environnés de leur grande nuée,<br>Les regards sur Jésus, Chef de la foi.'),
(395, NULL, 'Pécheur, je voudrais te guérir', '37a', 'AF 246', 'Pécheur, Je voudrais te guérir,<br>J''ai vu tes larmes, ta souffrance, <br>Mais pour avoir la délivrance, <br>Il faut apprendre à m''obéir.<br>Voici, Je me tiens à la porte,<br>Je suis ton maître et ton sauveur, <br>C''est le bonheur que je t''apporte,<br>Ne veux-tu pas m''ouvrir ton coeur?<br><br>Sais-tu que je suis né pour toi,<br>Que pour toi j''ai donné ma vie ?<br>Ton coeur est-il l''hôtellerie<br>Sans place même pour ton roi ? <br>Souvent, année après année, <br>Chez toi j''ai frappé mais en vain.<br>Voici le soir de la journée,<br>Ne veux-tu pas m''ouvrir enfin ?<br><br>N''auras-tu pas besoin de moi<br>Bientôt, dans la nuit éternelle ? <br>Dès aujourd''hui, viens sous mon aile,<br>Je serai tout, oui tout pour toi. <br>Le temps rapidement t''emporte,<br>Pourquoi renvoyer à demain ? <br>Trop tard un jour devant ma porte,<br>Tu frapperas peut-être en vain !<br><br>Si tu n''as pas besoin de moi,<br>Écoute, obéis sans comprendre !<br>Jusques à quand devrai-je attendre ?<br>Il faut que je demeure chez toi.<br>Voici, je me tiens à la porte,<br>Je suis ton maître et ton sauveur,<br>C''est le bonheur que je t''apporte,<br>Ne veux-tu pas m''ouvrir ton coeur ?'),
(396, NULL, 'Tout est en christ', '38a', 'LLB', 'Tout est en Christ, il est le suprême bien.<br>En Jésus tout est lumière,<br>Tout est vie, amour parfait.<br>Nous voyons en lui le Père<br>Dont il est le vrai reflet.<br>De Dieu toute la richesse,<br>Les trésors de la sagesse,<br>Sont cachés pour nous en lui.<br>Que son saint nom soit béni !<br><br>Tout est en Christ, il est le suprême bien.<br>Par son Esprit il nous donne<br>Tous les biens du Dieu d''amour.<br>Nous avons par ses souffrances<br>Paix profonde pour toujours.<br>De la croix jaillit la vie,<br>Le pardon, grâce infinie.<br>En donnant son Fils aimé,<br>Dieu pour nous a tout donné.<br><br>Tout est en Christ, il est le suprême bien.<br>En moi tout n''est que faiblesse,<br>Je n''ai rien, je ne puis rien.<br>Sans lumière, sans sagesse,<br>Je suis pauvre de tout bien.<br>Mais en Christ est l''abondance,<br>La victoire, la puissance.<br>Ô Jésus, viens vivre en moi,<br>Viens me rendre riche en toi.<br><br>Tout est en Christ, il est le suprême bien.<br>Ô Seigneur viens et pardonne<br>Car je t''aime encore si peu.<br>Ce n''est pas ce que je donne,<br>C’est moi-même que tu veux.<br>Tu réclames tout mon être:<br>Me voici, Jésus mon maître.<br>Tu n''as rien gardé pour toi,<br>Je ne garde rien pour moi.'),
(397, NULL, 'Suivez, suivez l’agneau', '39a', 'AF 341', 'Suivez, suivez l''Agneau jusqu''au soir de la vie;<br>Vers un gras pâturage il mène son troupeau.<br>Au bonheur des élus Sa grâce vous convie.<br>Suivez toujours, suivez toujours l''Agneau.<br><br>Suivez-le sans frayeur au jour de la tristesse,<br>Déchargez-vous sur lui de vos pesants fardeaux.<br>Suivez-le dans la paix au jour de l''allégresse,<br>Suivez toujours, suivez toujours l''Agneau.<br><br>Mais suivez-le partout. le Berger qui vous mène<br>Vous aime et ne saurait égarer son troupeau.<br>Oui, le joug du Seigneur est une douce chaîne.<br>Suivez toujours, suivez toujours l''Agneau.<br><br>Suivez-le sans broncher : le chemin solitaire<br>Doit vous conduire un jour au céleste repos.<br>Mais pour le suivre au ciel, suivez-le sur la terre.<br>Suivez toujours, suivez toujours l''Agneau.'),
(398, NULL, 'Seigneur, ô pain de vie', '40a', 'AF 148', 'Seigneur, ô pain de vie, que ta bonté,<br>Toujours, nous rassasie de Vérité !<br>Amour qui nous fait vivre, révèle-toi<br>Encore dans le saint livre à notre foi !<br><br>Ô toi dont la puissance créa du pain<br>Pour une foule immense mourant de faim,<br>Vois, ton peuple se presse autour de toi !<br>Secours notre détresse et notre foi !<br><br>Tu bénis tes apôtres, puis, à leur tour,<br>Ils portèrent à d''autres ton grand amour.<br>Ô Parole féconde, que notre foi<br>T''offre à ce pauvre monde qui meurt sans toi !<br><br>C''est toi le pain de vie, verbe puissant !<br>C''est de ton corps meurtri, oui, c''est de ton sang<br>Que notre coeur doit vivre. Ah ! donne-toi<br>Par l’Esprit et le livre à notre foi !'),
(399, NULL, 'Je suis à toi', '41a', 'LP 259', 'Je suis à toi, gloire à ton nom suprême !<br>Ô mon Sauveur, je me range à ta loi.<br>Je suis à toi, je t''adore, je t''aime.<br>Je suis à toi, ô Dieu, je suis à toi.<br>J''errais, perdu dans les sentiers du doute,<br>Le vide au coeur et la mort devant moi,<br>Lorsque tu vins resplendir sur ma route.<br>Je suis à toi, ô Dieu, je suis à toi.<br><br>Jadis, j''étais sous l''empire du monde;<br>Mais aujourd''hui, Jésus-Christ est en moi.<br>Ton joug est doux et ta paix est profonde.<br>Je suis à toi, ô Dieu, je suis à toi.<br>En te trouvant, j''ai trouvé toute chose,<br>Et ce bonheur m''est venu par la foi.<br>C''est sur ton sein qu''en paix je me repose.<br>Je suis à toi, ô Dieu, je suis à toi.<br><br>Nul ne saurait m''effacer de ton livre,<br>Nul ne saurait me soustraire à ta loi !<br>C''est ton regard qui fait mourir et vivre.<br>Je suis à toi, ô Dieu, je suis à toi.<br>Sur cette terre où tu veux que j''habite,<br>Ô mon Sauveur, mon Dieu, je suis à toi !<br>Et dans le ciel où ta grâce m''invite,<br>Encore à toi, ô Dieu, toujours à toi !'),
(400, NULL, 'Grand dieu nous te bénissons', '42a', 'AF 34', 'Grand Dieu nous te bénissons.<br>Nous célébrons tes louanges.<br>Éternel nous t''exaltons<br>De concert avec les anges;<br>Et prosternés devant toi<br>Nous t''adorons, Ô grand Roi.<br>Et prosternés devant toi<br>Nous t''adorons, Ô grand Roi.<br><br>Puisse ton règne de paix<br>S''étendre par tout le monde !<br>Dès maintenant, à jamais,<br>Que sur la terre et sur l''onde,<br>Tous genoux soient abattus<br>Au nom du Seigneur Jésus.<br>Tous genoux soient abattus<br>Au nom du Seigneur Jésus.<br><br>Chantons par le Saint-Esprit<br>Rendons gloire à Dieu le Père !<br>Gloire soit à Jésus-Christ,<br>Notre Sauveur, notre frère !<br>Son immense charité<br>Dure à perpétuité.<br>Son immense charité<br>Dure à perpétuité.'),
(401, NULL, 'Sur ton Église universelle', '43a', 'AF 566', 'Sur ton Église universelle,<br>Objet constant de ton amour,<br>Oh, que ta grâce paternelle,<br>Seigneur, se répande en ce jour !<br>Tes enfants avec confiance<br>Partout fléchissent les genoux.<br>En toi seul est notre espérance,<br>Jésus, sois au milieu de nous !<br><br>Que les promesses de ta Parole<br>Demeurent dans ton souvenir.<br>Que ton Esprit saint nous console<br>Et nous apprenne à te bénir.<br>Ouvre nos yeux à ta lumière,<br>Remplis nos cœurs de ta sagesse,<br>Et que ton Église en prière<br>Obtienne l''effet des promesses.<br><br>Que l''Évangile se répande<br>Partout, de l''aurore au couchant.<br>Que de tous côtés l''on entende<br>Le même cri, le même chant.<br>Oui, que les hommes révoltés<br>Viennent se courber devant toi,<br>Rendant la gloire et la majesté<br>À l''invincible Roi des rois !'),
(402, NULL, 'Torrents d’amour', '44a', 'AF 352', 'Torrents d’amour et de grâce,<br>Amour du Sauveur en croix !<br>À ce grand fleuve qui passe,<br>Je m’abandonne et je crois.<br><br>Je crois à ton sacrifice<br>Ô Jésus, Agneau de Dieu<br>Et couvert par ta justice,<br>J’entrerai dans le saint lieu.<br><br>Ah ! que partout se répande<br>Ce fleuve à la grande voix :<br>Que tout l’univers entende<br>L’appel qui vient de la croix.<br><br>Que toute âme condamnée<br>Pour qui tu versas ton sang<br>Soit au Père ramenée<br>Par ton amour tout-puissant.'),
(403, NULL, 'Non, nous n’offrirons pas', '45a', 'AF 596', 'Non, nous n’offrirons pas à ce Dieu qui nous aime,<br>Qui ne se lasse pas de prodiguer du bien,<br>Et qui, pour nous sauver, s’est immolé lui-même,<br>Un sacrifice qui ne nous coûte rien.<br>Tout appartient à Dieu qui nous a donné l’être.<br>Tous dépendent de lui, le riche et l’indigent.<br>Économes d’un jour des biens de notre maître<br>À lui seul soient notre or et notre argent !<br><br>Rachetés du Sauveur, c’est par le sacrifice<br>Que notre amour pour lui doit enfin s’exprimer.<br>Honte à notre tiédeur, honte à notre avarice !<br>Ô Seigneur, apprends-nous à vraiment T’aimer !<br>Oui, nous voulons donner, car nous voulons connaître<br>Un bonheur plus profond que le bonheur d’avoir !<br>Notre cœur nous redit la parole du Maître,<br>Disant : "Mieux vaut donner que recevoir."<br><br>Coda :<br>Aimons, faisons le bien, la nuit vient où personne<br>Ne peut plus travailler à   l’œuvre du Seigneur.<br>Mais si tu veux, chrétien, que ta main s’ouvre et donne,<br>Commence par donner, oui, par donner ton cœur.'),
(404, NULL, 'À dieu soit la gloire', '46a', 'AC 277', 'À Dieu soit la gloire<br>Pour son grand amour,<br>Car de sa victoire<br>S’est levé le jour :<br>Jésus, à ma place, Est mort sur la croix.<br>Il donne sa grâce<br>À celui qui croit.<br><br>Gloire à Dieu ! (bis)<br>Terre, écoute sa voix !<br>Gloire à Dieu ! (bis)<br>Monde, réjouis- toi !<br>Oh, venez au Père !<br>Jésus est vainqueur !<br>Que toute la terre<br>Chante en son honneur !<br><br>La foi, l’espérance<br>Ont chassé la peur ;<br>Une paix immense<br>Emplit notre cœur.<br>Selon sa promesse,<br>Jésus changera<br>Deuil en allégresse<br>Quand Il reviendra.'),
(405, NULL, 'Redites-moi l’histoire', '47a', 'AF 263', 'Redites-moi l’histoire de l’amour de Jésus;<br>Parlez-moi de la gloire qu’il promet aux élus.<br>J’ai besoin qu’on m’instruise, car je suis ignorant,<br>Qu’à Christ on me conduise comme un petit enfant.<br><br>Redites-moi l’histoire, redites-moi l’histoire,<br>Redites-moi l’histoire, de l’amour de Jésus !<br><br>Redites-moi l’histoire de la crèche à la croix;<br>Éveillez ma mémoire, oublieuse parfois.<br>Cette histoire si belle, dites-là simplement;<br>Elle est toujours nouvelle; répétez-là souvent.<br><br>Redites-moi l’histoire de mon divin Sauveur;<br>C’est lui dont la victoire affranchit le pécheur.<br>Ce glorieux message, Oh, redites-le moi !<br>Lorsque je perds courage, lorsque faiblit ma foi.<br><br>Redites-moi l’histoire, quand le monde trompeur<br>Me vend sa vaine gloire au prix de mon bonheur.<br>Et quand, loin de la terre, je prendrai mon essor,<br>En fermant la paupière, que je l’entende encore !'),
(406, NULL, 'Veille au matin', '48a', 'AF 342', 'Veille au matin, quand un ciel sans nuages<br>Semble annoncer un jour calme et serein :<br>C’est dans ton cœur que peut gronder l’orage<br>Qui fait tomber le pèlerin.<br>Veille au matin, veille le soir, veille et prie toujours.<br><br>Veille à midi, quand les bruits de la terre<br>Font oublier le céleste séjour.<br>Trouve un instant pour être solitaire<br>Dans la prière et dans l’amour.<br>Veille au matin, veille le soir, veille et prie toujours.<br><br>Veille le soir quand se fait le silence:<br>Pense aux bienfaits de ton céleste Ami;<br>Cherche avec soin Sa divine présence,<br>Verse en son coeur tout ton souci.<br>Veille au matin, veille le soir, veille et prie toujours.<br><br>Veille toujours, en tous lieux, à toute heure,<br>Car l’ennemi te guette, à chaque instant,<br>Pour se glisser dans la sainte demeure<br>Où doit régner le tout-puissant.<br>Veille au matin, veille le soir, veille et prie toujours.'),
(407, NULL, 'Chrétien, réjouis-toi', '49a', 'CGG 152', 'Chrétien, réjouis-toi, tressaille d’allégresse<br>Car ton Sauveur est Roi, regarde à lui sans cesse !<br>En son amour, chrétien réjouis-toi,<br>Réjouis-toi toujours !<br><br>Exalte le Seigneur, il a sauvé ton âme.<br>Dieu L’a comblé d’honneur après la croix infâme.<br>En son amour, chrétien réjouis-toi,<br>Réjouis-toi toujours !<br><br>Éclate en saints transports ! Voici, ton Dieu confie<br>Les portes de la mort au prince de la vie.<br>En son amour, chrétien réjouis-toi,<br>Réjouis-toi toujours !<br><br>Espère, adore et crois ! Jésus seul est le maître;<br>Au loin déjà je vois sa gloire enfin paraître.<br>En son amour, chrétien réjouis-toi,<br>Réjouis-toi toujours !<br><br>Christ règne dans le ciel au sein de la lumière,<br>Et son règne éternel bientôt viendra sur terre.<br>En son amour, chrétien réjouis-toi,<br>Réjouis-toi toujours !'),
(408, NULL, 'Jamais dieu ne délaisse', '50a', 'AF 398', 'Jamais Dieu ne délaisse<br>Qui se confie en lui.<br>Si le monde m’oppresse,<br>Jésus est mon appui.<br>Ce Dieu bon et fidèle<br>Garde en sa paix les siens<br>Pour la vie éternelle,<br>Et les comble de biens.<br>Si ta rigueur extrême<br>Nos péchés veut compter,<br>Ô Majesté suprême,<br>Qui pourra subsister ?<br>Mais ta juste colère<br>Fait place à ta bonté<br>Afin qu’on te révère Avec humilité.<br><br>Je veux, sachant qu’il m’aime<br>Me remettre à ses soins.<br>Beaucoup mieux que moi-même,<br>Il connaît mes besoins.<br>Ce Dieu plein de tendresse<br>Confondrait-il ma foi ?<br>Non, plus le mal me presse,<br>Plus il est près de moi.<br>Seigneur par l’efficace<br>Du sang versé pour moi,<br>Accorde-moi la grâce<br>De vivre par la foi.<br>C’est la vie éternelle<br>Que j’attends ici-bas,<br>Jusqu’au jour qui m’appelle<br>À être auprès de toi.'),
(409, NULL, 'Le Fils de dieu', '51a', 'LL', 'Le Fils de Dieu est venu sur la terre,<br>Le Fils de Dieu qui nous a tant aimé<br>Est-il est venu soulager la misère,<br>Sauver de la mort, l’enfer, le péché.<br>Ô Jésus je te dois la vie,<br>Tout ce que j’ai me vient de ta bonté !<br>Le salut où tu nous convies,<br>Quel don précieux ! Quel merveilleux bienfait !<br><br>Le Fils de Dieu satisfait la justice ;<br>Il est l’agneau chargé de nos péché.<br>Vit-on jamais un plus grand sacrifice ?<br>Le Dieu de gloire meurt pour nous sauver !<br>Ô Jésus, Sauveur, que je t’aime,<br>Toi dont l’amour a jailli le premier !<br>Pour nous tu t’es donné toi-même,<br>Et avec Dieu nous as réconciliés !<br><br>Le Fils de Dieu reviendra sur la terre<br>Et toute langue enfin confessera<br>Qu’il est le Christ, à la gloire du Père.<br>Toute sa gloire enfin resplendira !<br>Ô Jésus, je te dois la vie,<br>Tout ce que j’ai me vient de ton amour !<br>Mais ne tarde plus, je te prie,<br>J’attends le jour heureux de ton retour.<br>Mais ne tarde plus, je te prie,<br>J’attends le jour heureux de ton retour.'),
(410, NULL, 'Oui, ton amour', '52a', 'AF 57', 'Oui, ton amour est un amour sublime !<br>Il est plus haut que la plus haute cime<br>Et que l’azur insondable des cieux.<br>Comment pourrais-je, ô Dieu, vers cet abîme,<br>Lever les yeux ?<br>Pourtant, Seigneur, si je ne puis comprendre,<br>Ah, que, du moins, ma voix se fasse entendre,<br>Et que mon cœur, par la grâce inspiré,<br>Chante aujourd’hui l’amour puissant et tendre<br>Qui m’a sauvé.<br><br>Oui, c’est pour moi, je le crois et j’adore.<br>Ô mon Sauveur, qu’il retentisse encore,<br>Ce beau message, celui de ton amour !<br>Redis-le moi jusqu’à la douce aurore<br>De ton retour !<br>Et quand luira cette aurore bénie,<br>Quand à Jésus, pour toujours réunie,<br>Mon âme, enfin, mon âme le verra,<br>D’elle vers lui ma louange infinie S’élèvera.'),
(411, NULL, 'Poussez vers dieu', '53a', 'Ps 66', 'Poussez vers Dieu, habitants de la terre,<br>Des cris de joie et d’allégresse !<br>Chantez la gloire de son nom glorieux,<br>Célébrez-le, adorez-le par vos louanges !<br><br>Ô, notre Dieu, grandes sont tes œuvres,<br>Tes ennemis sont confondus.<br>Toute la terre devant toi se prosterne<br>(Toute la terre se prosterne)<br>Et psalmodie en ton honneur, en ton nom !<br><br>Allons vers lui, allons dans sa maison<br>Avec nos chants et nos offrandes.<br>Accomplissons nos voeux envers le Roi,<br>Ouvrons nos lèvres et consacrons nos coeurs à lui.'),
(412, NULL, 'Que le seigneur de gloire', '54a', '', 'Que le Seigneur de gloire, et que le Dieu de paix,<br>Le Dieu qui a ressuscité Jésus,<br>Jésus, notre divin berger,<br>Opère en tout votre être ce qui le glorifie<br>Par Jésus-Christ à qui soit la gloire<br>Aux siècles des siècles, Amen !<br><br>Que le Père et le Fils vous bénissent<br>Et vous donnent la paix divine.<br>À Dieu seul rendons la gloire<br>Et la puissance éternelles !<br><br>Que, de Jésus, la grâce, que, de Jésus, la paix,<br>Que, de notre Seigneur, la charité,<br>Vous soient pleinement accordées.<br>Et que tous ceux qui l’aiment d’une éternelle flamme<br>Soient vivifiés et lui rendent gloire<br>Aux siècles des siècles, Amen !'),
(413, NULL, 'Oh ! prends mon âme', '55a', 'AC 602', 'Oh ! Prends mon âme,<br>Prends-la, Seigneur !<br>Et que ta flamme<br>Brûle en mon cœur !<br>Que tout mon être<br>Vive pour toi.<br>Sois seul mon maître,<br>Ô, divin roi !<br><br>Source de vie<br>De paix, d’amour,<br>Vers toi, je crie,<br>La nuit, le jour !<br>Calme ma crainte,<br>Sois mon soutien.<br>Entends ma plainte,<br>Toi, mon seul bien.<br><br>Du mal perfide<br>Oh ! garde-moi !<br>Viens, sois mon guide,<br>Chef de ma foi.<br>Quand la nuit voile<br>Tout à mes yeux,<br>Sois mon étoile,<br>Brille des cieux !<br><br>Voici l’aurore<br>D’un jour nouveau,<br>Le ciel se dore La nuit,<br>De feux plus beaux.<br>Jésus s’apprête,<br>Pourquoi gémir ?<br>Levons nos têtes,<br>Il va venir.'),
(414, NULL, 'Tiens dans ta main', '56a', 'AF 206', 'Tiens dans ta main, ta main puissante et forte<br>Ton faible enfant qui seul ne peut marcher.<br>Vers toi, Seigneur, tout mon désir se porte,<br>Sur mon chemin garde-moi de broncher.<br>Tiens dans ta main, ma main parfois tremblante<br>Quand vient l''épreuve et ses jours de douleur.<br>Donne à ma foi parfois si chancelante<br>Un nouveau zèle rempli de douceur.<br><br>Tiens dans ta main tous les jours de ma vie;<br>Qu''ils soient des jours de peine ou de bonheur.<br>Que de t''aimer soit toute mon envie<br>Car je suis à toi, Jésus, mon Sauveur !<br><br>Tiens dans ta main mon coeur plein de faiblesse,<br>Sans ton secours je ne puis faire un pas.<br>Écoute-moi, regarde ma tristesse,<br>Viens, secours-moi, ne m''abandonne pas !<br>Tiens dans ta main mon âme toute entière,<br>Révèle-moi, Jésus, tout ton amour;<br>Sois mon rocher, mon ancre, ma lumière,<br>Mon roi, mon maître, Jésus, chaque jour.'),
(415, NULL, 'Je rassemblerai tous mes enfants', '57a', 'Jr 32', 'Je rassemblerai tous Mes enfants<br>Des pays où je les ai bannis.<br>Je les ramènerai dans ce lieu promis<br>Et je les y ferai habiter.<br>Ils seront mon peuple et je serai leur Dieu,<br>Je leur donnerai un même coeur,<br>Une seule et même voix,<br>Afin qu''ils me craignent toujours.<br><br>Je ferai une alliance éternelle,<br>Je prendrai plaisir à les bénir.<br>J’agirai dans leur coeur en vue du bonheur.<br>Ils ne s’éloigneront plus de moi.<br>Je leur donnerai la crainte de mon nom<br>Et me réjouirai à leur sujet;<br>Oui, je leur ferai du bien<br>Afin qu''ils me servent toujours.'),
(416, NULL, 'Je ne sais pas le jour', '58a', 'AF 424', 'Je ne sais pas le jour<br>Où je verrai mon roi,<br>Mais je sais qu''il me veut<br>Dans sa sainte demeure.<br>Les ombres alors fuiront<br>La lumière à cette heure.<br>Ce sera la gloire pour moi.<br>Oui, dans la gloire je serai,<br>Avec lui je régnerai.<br>L’ombre fuira la lumière à cette heure.<br>Oui, dans la gloire je serai,<br>Avec lui je régnerai.<br>L’ombre fuira la lumière à cette heure.<br><br>Je ne sais quels seront<br>Les chants des bienheureux,<br>Les accords, les accents,<br>Des hymnes angéliques,<br>Mais je sais que, joignant<br>Ma voix à leurs cantiques,<br>Bientôt j''adorerai comme eux.<br>Oui, avec eux j''adorerai,<br>Le Seigneur je servirai.<br>Ma voix chantera sans cesse leurs cantiques.<br>Oui, avec eux j''adorerai,<br>Le Seigneur je servirai.<br>Ma voix chantera sans cesse leurs cantiques.<br><br>Je ne sais que sera<br>Le palais éternel<br>Ou la cité céleste<br>Belle et glorieuse,<br>Mais là est l''espérance<br>Vivante et bienheureuse<br>Où s''ouvrira pour moi le ciel.<br>Là s''ouvrira pour moi le ciel<br>À cette heure solennelle.<br>Elle est là mon espérance bienheureuse.<br>Là s''ouvrira pour moi le ciel<br>À cette heure solennelle.<br>Elle est là mon espérance bienheureuse.'),
(417, NULL, 'Si ta parole', '59a', 'AF 72;183', 'Si ta parole, si ta voix, divin maître,<br>Ne disaient à ma foi ton oeuvre parfaite,<br>Où trouverais-je au sein de ma misère<br>Force et lumière ?<br><br>Si tu n''étais descendu sur la terre<br>Expier mon péché au mont du calvaire,<br>Comment pourrais-je entrer dans ta présence<br>Plein de confiance ?<br><br>Pour t''adorer, ô mon Dieu, toi qui m''aimes<br>En Jésus-Christ,<br>Je veux entrer dans ta sainteté même<br>Dès aujourd''hui.<br>Et, revêtu du manteau du salut,<br>J''ai l''assurance d''être secouru.<br><br>Si tu n''étais remonté vers le Père,<br>Triomphant de la mort et de l''adversaire,<br>Où trouverais-je au sein de la souffrance<br>Mon espérance ?<br><br>Pour avancer au milieu des souffrances<br>Et des combats,<br>Je veux louer de Jésus la puissance<br>À chaque pas.<br>Car habitant au sein de ma faiblesse,<br>Elle engloutit la mort et la détresse.'),
(418, NULL, 'Demeure en paix', '60a', 'ATLG 333', 'Demeure en paix, dit Dieu au coeur en peine;<br>Jésus est là comme un tendre berger.<br>Il est puissant, sa grâce est souveraine;<br>De tes fardeaux il veut te décharger.<br>C''est vers le ciel que son amour te mène;<br>Il est fidèle et ne saurait changer.<br><br>Demeure en paix, car malgré ta misère<br>Jésus est là comme hier et demain.<br>Ne doute pas de l''amour du calvaire<br>Qui dans la nuit éclaire ton chemin.<br>Tu comprendras bientôt tous les mystères<br>Quand cesseront ton deuil et ton chagrin.<br><br>Demeure en paix au milieu de tes larmes.<br>Ne sais-tu pas que Jésus est vivant ?<br>Il chassera tes craintes et tes alarmes<br>Car à sa voix obéit l''ouragan.<br>Même la mort est soumise à ses armes;<br>En ton Sauveur vit éternellement.'),
(419, NULL, 'Non, rien en ma personne', '61a', 'CV 231', 'Non, rien en ma personne<br>N''est digne d''être aimé.<br>Ce que Jésus me donne<br>Peut seul être estimé.<br>Si le monde présente<br>À mes yeux, à mon coeur,<br>Sa gloire séduisante,<br>Je regarde au Sauveur.<br><br>Jésus est ma justice,<br>Ma gloire, mon appui.<br>Il m''aime, il m''est propice<br>Et je peux tout par lui.<br>Il m''aime, il m''est propice<br>Et je peux tout par lui.<br><br>Nul ne peut à mon âme<br>Disputer son bonheur.<br>De l''enfer, de sa flamme<br>Je ne sens nulle peur.<br>Et quand un ange même<br>Viendrait pour me tenter,<br>Des bras du Dieu qui m''aime<br>Il ne pourrait m''ôter.<br><br>Si parfois sur ma route<br>Descend l''obscurité,<br>Si l''épreuve ou le doute<br>Me voilent sa bonté,<br>Jésus est la lumière<br>Qui dissipe mes peurs,<br>L''étoile matinière<br>Qui se lève en mon coeur.<br>'),
(420, NULL, 'Sur la terre', '333', 'Hb 11', 'Sur la terre où nous sommes voyageurs,<br>Où nous sommes étrangers,<br>Nous cherchons une patrie meilleure:<br>La céleste cité.<br><br><span style=''padding-left:15px''></span>Non, Dieu n’a pas honte d’être notre Dieu,<br><span style=''padding-left:15px''></span>(Non, Dieu, non Dieu n’a pas honte d’être notre Dieu,)<br><span style=''padding-left:15px''></span>Non, Dieu n’a pas honte d’être notre Dieu,<br><span style=''padding-left:15px''></span>(Gloire à lui, Il est notre Dieu !)<br><span style=''padding-left:15px''></span>Car depuis l’éternité il a préparé<br><span style=''padding-left:15px''></span>(Dès l’éternité il a préparé)<br><span style=''padding-left:15px''></span>Pour nous dans les lieux célestes une cité.<br><span style=''padding-left:15px''></span>(Une céleste cité.)<br><br>Cette ville aux solides fondements,<br>Dieu en est le constructeur;<br>Que nos cœurs attendent patiemment<br>Le retour du Seigneur.'),
(421, NULL, 'Élève ta voix et crie', '334', 'Es 40', 'Élève ta voix et crie,<br>Parle au coeur de mon peuple,<br>Élève ta voix et crie<br>Que Dieu est Tout-Puissant.<br>Il est là, il est vainqueur,<br>Il vient, il est le Seigneur.<br>Ne doute pas, le Seigneur est là,<br>Et jamais il ne t''abandonne.<br><br>Ne crains pas de proclamer<br>Cette bonne nouvelle<br>Toi qui portes dans ton coeur<br>La parole de Dieu.<br>Avec force il vient bientôt<br>Rassembler tout son troupeau.<br>Ne doute pas, le Seigneur est là,<br>Et jamais il ne t''abandonne.<br><br>Proclame que le Seigneur<br>Est le berger suprême<br>Qui rassemble de son bras<br>Tous ses agneaux perdus.<br>Il les porte sur son sein<br>Jusqu''au bout du droit chemin.<br>Ne doute pas, le Seigneur est là,<br>Et jamais il ne t''abandonne.'),
(422, NULL, 'Que tout fidèle', '335', 'Ps 32;1 Jn 5;Ep 6', 'Que tout fidèle adresse à Dieu sa prière<br>Et les grandes eaux ne le submergeront pas.<br>Dieu sait délivrer tous ceux qui le prient<br>De toute épreuve au moment qu’il a choisi.<br><br><span style=''padding-left:15px''></span>Priez sans cesse, approchez-vous du Père<br><span style=''padding-left:15px''></span>En suppliant, en remerciant<br><span style=''padding-left:15px''></span>Par le Saint-Esprit de Dieu.<br><span style=''padding-left:15px''></span>Priez sans cesse, que chacun intercède<br><span style=''padding-left:15px''></span>Pour tous les frères<br><span style=''padding-left:15px''></span>Par l’Esprit de notre Père.<br><br>Que tout fidèle intercédant pour son frère<br>Espère en son Dieu car il fait sa volonté.<br>Si nous le prions selon Son désir,<br>Nous savons qu’il nous a déjà exaucés.'),
(423, NULL, 'Vous qui êtes parmi ceux', '336', 'Lc 22', 'Vous qui êtes parmi ceux qui ont persévéré<br>Avec moi dans mon chemin au sein de mes épreuves,<br>Voici que je dispose du royaume en votre faveur. (bis)<br><br>Votre ennemi sans relâche vient vous assaillir.<br>Comme le froment votre âme est passée au crible<br>Mais sans cesse je prie que votre foi jamais ne défaille. (bis)<br><br>Je suis toujours avec vous jusqu''à la fin du monde.<br>Conservez votre courage, j''ai vaincu le monde.<br>À ma table dans mon royaume je vous inviterai. (bis)'),
(424, NULL, 'Toi tu dis', '337', 'Ap 3', 'Toi tu dis : Je me suis enrichi.<br>Toi tu dis : Je n''ai besoin de rien.<br>Mais tu ne vois pas<br>Que tu es misérable,<br>Aveugle et nu.<br><br><span style=''padding-left:15px''></span>C’est pourquoi je te conseille<br><span style=''padding-left:15px''></span>D’acheter de moi,<br><span style=''padding-left:15px''></span>Oui, de l’or éprouvé par le feu,<br><span style=''padding-left:15px''></span>Un collyre pour oindre tes yeux<br><span style=''padding-left:15px''></span>Et un vêtement blanc,<br><span style=''padding-left:15px''></span>Pour que tu sois riche et que tu voies,<br><span style=''padding-left:15px''></span>Pour que tu sois vêtu.<br><br>Moi je suis Jésus-Christ, ton Seigneur.<br>Je reprends et châtie ceux que j''aime.<br>Repens-toi, mon fils,<br>Reviens et sois fidèle,<br>Rempli de zèle.'),
(425, NULL, 'Nous te contemplons', '338', 'Hb 2', 'Nous te contemplons, Jésus le Seigneur,<br>Nous te contemplons couronné d’honneur,<br>Toi qui as souffert pour nous, pécheurs,<br>Toi notre sacrificateur.<br>Tu es remonté auprès de ton Père<br>Et tu t’es assis à sa droite.<br>Tu règnes avec lui<br>Et tu intercèdes pour les tiens.<br>Nous te contemplons<br>Et nous t’adorons, Seigneur.'),
(426, NULL, 'Nous espérons en toi', '339', 'Cl 1', 'Nous espérons en toi,<br>Toi, l’image du vrai Dieu<br>En qui tout fut créé,<br>Et qui réconcilias<br>Ce qui est dans les cieux,<br>Ce qui est sur la terre,<br>Avec notre Dieu, le Père.<br><br>Nous confessons ton nom,<br>Toi en qui Dieu a placé<br>Toute vraie plénitude.<br>Jésus, nous te louons<br>Car tu es le premier,<br>Tu soutiens toute chose.<br>Tous tes rachetés t’adorent.'),
(427, NULL, 'Écoute-moi', '340', 'Ps 50', 'Écoute-moi, ainsi je parlerai,<br>Prête l’oreille et je t’avertirai.<br>Je suis ton Dieu, ton sauveur et ton Père,<br>Ma main a fondé les cieux et la terre.<br><br><span style=''padding-left:15px''></span>Offre des actions de grâce à ton Dieu,<br><span style=''padding-left:15px''></span>Envers le très-haut, accomplis tes voeux,<br><span style=''padding-left:15px''></span>Et dans la détresse invoque-moi,<br><span style=''padding-left:15px''></span>Car je te délivrerai et tu me glorifieras.<br><br>Je ne recevrai pas en sacrifice<br>Tes oeuvres bonnes, fruits de ta justice.<br>M’offrirais-tu tes richesses et tes biens ?<br>Tout ce qui est sous les cieux m’appartient.'),
(428, NULL, 'Les tourments et le chagrin', '341', 'Ps 16;116', 'Les tourments et le chagrin me tenaient,<br>La mort et l''angoisse m''environnaient.<br>Je me disais : Je suis bien malheureux.<br>Mais dans ta compassion tu m''as fait grâce, ô Dieu !<br>Éternel tu as entendu ma voix<br>Et tu as tendu ton oreille vers moi.<br>Tu m''as sauvé de toutes mes détresses<br>Et tu as changé mon deuil en allégresse.<br><br><span style=''padding-left:15px''></span>Comment rendrai-je à l’Éternel<br><span style=''padding-left:15px''></span>Ses nombreux bienfaits envers moi ?<br><span style=''padding-left:15px''></span>J''élèverai la coupe des délivrances.<br><span style=''padding-left:15px''></span>J''invoquerai son nom.<br><br>Celui qui a cultivé dans les pleurs<br>Moissonnera dans la joie, le bonheur.<br>Celui qui s''en est allé en pleurant<br>Récoltera les gerbes en un chant triomphant.<br>Ô Dieu, j''offrirai ma reconnaissance<br>Dans ta maison où règne ta puissance.<br>J''accomplirai mes voeux dans l''assemblée<br>Et je te louerai car tu m''as délivré.'),
(429, NULL, 'Oui, voici je conduirai mon serviteur', '342', 'Es 42;Ps 91', 'Oui, voici je conduirai mon serviteur,<br>Je le mènerai sur des sentiers qu''il ne connaît pas.<br>Oui, voici je bénirai mon serviteur.<br>J''aplanirai ses voies tortueuses,<br>Devant lui je changerai<br>Les ténèbres en lumière.<br><br><span style=''padding-left:15px''></span>Puisqu''il m''aime, je le délivrerai,<br><span style=''padding-left:15px''></span>Je le protègerai puisqu''il connaît mon nom.<br><span style=''padding-left:15px''></span>S''il m''invoque, moi, je lui répondrai.<br><span style=''padding-left:15px''></span>Je le couvrirai de gloire,<br><span style=''padding-left:15px''></span>Je multiplierai ses jours<br><span style=''padding-left:15px''></span>Et je lui ferai connaître mon salut.<br><br>Oui, c''est moi qui ai choisi mon serviteur.<br>Je le soutiendrai et je mettrai mon Esprit sur lui.<br>Car je suis l''Éternel, son Dieu, son sauveur.<br>Parce qu''il a du prix à mes yeux,<br>Je le prendrai par la main<br>Et je serai son guide.'),
(430, NULL, 'N''ayez aucune crainte', '343', '1 Pi 3;Es 8', 'N''ayez aucune crainte, ne soyez pas troublés<br>Devant les épreuves et les dangers<br>Mais que dans votre coeur et dans votre pensée,<br>Jésus-Christ soit toujours sanctifié.<br><br>Que la crainte de Dieu règne dans votre coeur;<br>Il sera pour vous un sanctuaire.<br>Que Dieu soit honoré, toujours seul redouté,<br>Et le nom de Jésus sanctifié.'),
(431, NULL, 'Aujourd''hui, si tu entends', '344', '', 'Aujourd''hui si tu entends sa voix<br>Ne ferme pas ton coeur au Seigneur,<br>Et s''il frappe à ta porte, ouvre-lui sans tarder,<br>Reçois Jésus afin d''être sauvé.<br><br>Aujourd''hui si Dieu par sa parole<br>T''appelle à entrer dans son repos,<br>N''endurcis pas ton coeur, obéis sans tarder,<br>Viens à Jésus, tu seras déchargé.<br><br>Aujourd''hui c''est le jour du salut<br>Où tu entends la voix de Jésus.<br>Ne le méprise pas, non ne t''en moque pas,<br>Et le salut à tes yeux brillera.'),
(432, NULL, 'Béni soit le nom de Dieu', '345', 'Dn 2', 'Béni soit le nom de Dieu<br>D''éternité en éternité<br>Car c''est à lui qu''appartiennent<br>Toute puissance et toute sagesse.<br>C''est lui qui change les temps<br>Et les circonstances,<br>Qui renverse et établit<br>Les rois de la terre.<br>Aux sages il accorde la sagesse,<br>La science et l''intelligence,<br>Il révèle ce qui est caché,<br>Il sait ce qui est dans les ténèbres.<br>La lumière demeure avec lui,<br>Elle demeure avec lui.<br>Éternel, Dieu, je te loue,<br>Je te célèbre et te glorifie.<br>Tu as bien voulu donner<br>Sagesse et force à ton serviteur.<br>Ce qu''il t''avait demandé<br>Tu l''as fait connaître,<br>Et tu lui as révélé<br>Tes desseins cachés.<br>Ô Dieu, soit loué !'),
(433, NULL, 'Louez le Seigneur', '346', 'Ps 149', 'Louez le Seigneur, l''Éternel<br>Dans l''assemblée de ses fidèles !<br>Que tout son peuple dans la joie<br>Chante un cantique au roi des rois !<br>Oui, faites entendre tous vos chants<br>Et retentir vos instruments !<br>Par lui les humbles sont vêtus<br>De la parure du salut.<br>Que les louanges à notre Dieu<br>À jamais s''entendent en tout lieu !'),
(434, NULL, 'Je chanterai des cantiques', '347', 'Ps 108', '<span style=''padding-left:15px''></span>Je chanterai des cantiques au Seigneur<br><span style=''padding-left:15px''></span>Et je jouerai de mes instruments.<br><span style=''padding-left:15px''></span>Mon coeur est ferme, je veux louer Dieu sans cesse,<br><span style=''padding-left:15px''></span>C''est là ma gloire !<br><br>Dieu a dit : Je triompherai<br>Oui c''est lui qui me donne la victoire.<br>Avec lui nous déploierons nos forces,<br>C''est lui qui détruira notre ennemi.'),
(435, NULL, 'Je suis ton Dieu', '348', 'Es 48', 'Je suis ton Dieu et je t''ai appelé<br>D''une contrée, d''un pays éloignés;<br>Oui, je savais que tu me serais infidèle<br>Et que ton coeur depuis toujours était rebelle.<br>Dans mon amour je t''ai mis à l''épreuve<br>Pour que tu sois purifié.<br><br><span style=''padding-left:15px''></span>Approche-toi de moi, écoute-moi<br><span style=''padding-left:15px''></span>Car sur ton coeur est gravé mon saint nom.<br><span style=''padding-left:15px''></span>Approche-toi de moi, obéis-moi;<br><span style=''padding-left:15px''></span>Je rassasierai à jamais ton âme.<br><br>Je te conduis sur ta voie, ton chemin<br>Et je t''instruis pour ta joie, pour ton bien.<br>Si tu écoutes et reçois mes commandements,<br>Si tu les gardes et les pratiques en me servant,<br>Oui, ton bonheur coulera comme un fleuve,<br>Et ton salut comme la mer.'),
(436, NULL, 'Que mon âme', '349', 'Ps 145;146', '<span style=''padding-left:15px''></span>Que mon âme, jamais ne se lasse<br><span style=''padding-left:15px''></span>De bénir le Seigneur et sa grâce.<br><span style=''padding-left:15px''></span>Aussi longtemps que je vivrai,<br><span style=''padding-left:15px''></span>Éternel, oui, je te bénirai. (fin : Bis)<br><br>Dieu est bon pour ses enfants;<br>Il connaît tous leurs tourments.<br>Il soutient ceux qui tombent<br>Et relève les humiliés.<br><br>Dieu entend tous ceux qui crient;<br>Il est près de ceux qui prient.<br>Il voit ceux qui le craignent,<br>Il exauce leurs désirs.'),
(437, NULL, 'Quand en moi', '350', 'Ps 94;Rm 15', 'Quand en moi s''agitent mes pensées,<br>Toi, Seigneur, tu consoles mon âme.<br>Quand je dis : Il ne voit pas ma cause,<br>Dieu me parle et je reprends courage.<br><br><span style=''padding-left:15px''></span>Ta parole est ma consolation, Seigneur.<br><span style=''padding-left:15px''></span>Elle remplit mon âme de ta joie.<br><span style=''padding-left:15px''></span>Par sa persévérance et sa consolation,<br><span style=''padding-left:15px''></span>J''ai dans le coeur l''éternelle espérance.<br><span style=''padding-left:15px''></span>Et quand viendra la fin de cet étroit chemin,<br><span style=''padding-left:15px''></span>J''hériterai du grand salut.<br><br>Quand le monde répand sa tristesse,<br>C''est la mort qui menace mon âme.<br>Mais les jugements de ta parole<br>Me reprennent et me donnent ton zèle.'),
(438, NULL, 'Nous savons que notre maison', '351', '2 Co 5;2 Th 1;Ap 22', 'Nous savons que notre maison sur la terre<br>N’est que passagère et sera détruite,<br>Mais nous avons une maison dans les cieux<br>Que le Seigneur a faite pour nous.<br><br><span style=''padding-left:15px''></span>Heureux ceux qui lavent leur manteau<br><span style=''padding-left:15px''></span>(Heureux ceux qui peuvent entrer)<br><span style=''padding-left:15px''></span>Pour entrer dans la cité,<br><span style=''padding-left:15px''></span>(Dans la cité,)<br><span style=''padding-left:15px''></span>Et manger de l’arbre de la vie<br><span style=''padding-left:15px''></span>(Pour avoir la vie)<br><span style=''padding-left:15px''></span>Dans la maison du Seigneur.<br><span style=''padding-left:15px''></span>(Du Seigneur : 1ere fois) <br><br>Jamais la chaleur, la soif et la disette,<br>Dans ce lieu paisible ne pourront nous nuire.<br>Car notre Dieu veut nous donner du repos<br>Quand Jésus apparaîtra du ciel.'),
(439, NULL, 'Ce n''est pas par la force', '352', 'Za;Ti;1 Tm;2 Tm', '<br><span style=''padding-left:15px''></span>Ce n’est pas par la force<br><span style=''padding-left:15px''></span>Ni par la puissance,<br><span style=''padding-left:15px''></span>Mais par mon Esprit<br><span style=''padding-left:15px''></span>Que s’accomplira cette oeuvre.<br><br><span style=''padding-left:15px''></span>Coda:<br><span style=''padding-left:15px''></span>Que s’accomplra cette oeuvre.<br><br>Toi, petit troupeau,<br>Garde fidèlement<br>Le trésor que je t’ai donné<br>Et accomplis mon oeuvre.<br><br>Ne sois pas honteux,<br>Préserve ta conscience.<br>Lutte en mettant ton espérance<br>Dans le Dieu des fidèles.<br><br>En gardant la foi,<br>Combat le bon combat.<br>Proclame toujours ma parole;<br>Elle seule sauvera.'),
(440, NULL, 'Bien-aimés, comme des enfants', '353', '1 Pi 1', '<span style=''padding-left:15px''></span>Bien-aimés, comme des enfants nouveau-nés,<br><span style=''padding-left:15px''></span>Toujours il vous faut désirer<br><span style=''padding-left:15px''></span>Le lait divin de la parole de vérité.<br><span style=''padding-left:15px''></span>Bien-aimés, si vous avez vraiment goûté<br><span style=''padding-left:15px''></span>De notre Seigneur la bonté,<br><span style=''padding-left:15px''></span>Alors il vous faut désirer<br><span style=''padding-left:15px''></span>La parole, bien-aimés !<br><br>L''homme vivra non pas de pain seulement<br>Mais de ce qui sortira de la bouche du Dieu vivant.<br>Ô, Jésus-Christ, tu es la parole de vie,<br>Le pain céleste qui fait vivre ceux<br>Qui, de toi, se nourrissent.'),
(441, NULL, 'Ô Dieu de toute grâce', '354', '1 Pi 5', 'Ô Dieu de toute grâce,<br>Tu nous as appelés<br>À ta gloire éternelle<br>Par Jésus, le sauveur.<br>Après un peu de temps<br>Passé dans la souffrance,<br>Tu nous affermiras,<br>Ta main nous formera.<br><br>La foi est épurée<br>Au travers du creuset.<br>Mais au jour du Seigneur,<br>Elle nous vaudra l''honneur<br>D''avoir aimé Jésus<br>Sans l''avoir jamais vu;<br>Nous aurons dans la joie<br>Le salut de la foi.'),
(442, NULL, 'Nous avons connu', '355', '1 Jn 3', 'Nous avons connu l’amour<br>Car Jésus s’est donné pour nous.<br>Nous avons connu l’amour;<br>Donnons-nous aussi,<br>Donnons pour les frères notre vie.<br><br>Non, je ne veux pas rejeter<br>La grâce de Dieu,<br>Car seule elle remplit ma vie<br>Du fruit de la justice.<br><br>Non, rien n’est saint ni bon en moi,<br>Mais il m’a choisi.<br>Dieu veut que je porte les fruits<br>D’amour et de justice.'),
(443, NULL, 'Jésus dit à son Père', '356', 'Mt 11:1 Co 1', 'Jésus dit à son Père:<br>oh ! mon Père, je te loue,<br>Car les choses célestes<br>Sont cachées aux sages<br>De ce monde.<br><br><span style=''padding-left:15px''></span>Mais tu as révélé aux enfants<br><span style=''padding-left:15px''></span>Tes merveilles cachées.<br><span style=''padding-left:15px''></span>Le royaume de Dieu est donné<br><span style=''padding-left:15px''></span>À tous ceux qui leur ressemblent.<br><span style=''padding-left:15px''></span>Tu les as révélées aux enfants,<br><span style=''padding-left:15px''></span>À chacun des petits<br><span style=''padding-left:15px''></span>Qui confessent la foi en ton nom.<br><span style=''padding-left:15px''></span>Je te loue, ô Père, qu’il en soit ainsi.<br><br>Tu détruis la sagesse,<br>Celle des intelligents.<br>Où se trouve le sage ?<br>Ô Dieu, tu le frappes<br>De folie.'),
(444, NULL, 'Ô vous qui avez faim', '357', 'Mt 5', 'Ô vous qui avez faim<br>Et qui avez soif<br>D''une vraie justice,<br>Vous qui êtes blessés<br>Et qui n''attendez plus de guérison,<br>Il est une promesse<br>Donnée par Jésus<br>Dans son évangile:<br>Oui, vous serez rassasiés.<br><br>Ô vous qui soupirez<br>Après une paix<br>Qui soit éternelle,<br>Oh venez à Jésus<br>Qui la donne à celui qui croit en lui.<br>Si vous la procurez<br>À votre prochain,<br>Dieu fait la promesse<br>De vous appeler ses fils.'),
(445, NULL, 'Quand nous étions étrangers', '358', 'Rm 6;2 Co 5', 'Quand nous étions étrangers<br>À la vie éternelle, la vie divine,<br>Nous n''étions que des pécheurs<br>Esclaves du péché et de nous-mêmes.<br>Mais maintenant, étant affranchis du péché,<br>Nous marchons dans la sainteté.<br><br><span style=''padding-left:15px''></span>Si quelqu''un est en Jésus<br><span style=''padding-left:15px''></span>Voici tout est nouveau,<br><span style=''padding-left:15px''></span>Il est une nouvelle créature.<br><span style=''padding-left:15px''></span>Si quelqu''un est en Jésus<br><span style=''padding-left:15px''></span>Voici tout est nouveau,<br><span style=''padding-left:15px''></span>Les choses anciennes sont passées,<br><span style=''padding-left:15px''></span>Vraiment passées.<br><br>Christ étant ressuscité<br>Et vivant aujourd''hui<br>Pour Dieu son Père,<br>Nous devons nous regarder<br>Comme vivants en lui<br>Pour notre Père.<br>Livrons nos membres<br>Comme instruments de justice<br>À Dieu qui nous a délivrés.'),
(446, NULL, 'Plus que les gardes', '359', 'Ps 130', 'Plus que les gardes s''attendent au matin,<br>J''espère en l''Éternel, mon Dieu.<br>Sur sa parole je compte ardemment<br>Et à lin je m''attends.<br><br>Peuple de Dieu fidèle,<br>Peuple du Seigneur,<br>Attends-toi à l''Éternel, toujours.<br>Car en Jésus se trouvent en abondance<br>La pleine délivrance et le pardon.'),
(447, NULL, 'Prends courage !', '360', '', 'Prends courage le Seigneur t''appelle,<br>N''aie donc pas de crainte et fortifie-toi,<br>Jésus s''adresse à toi, il te dit "Lève-toi !"<br>Sa parole est toute-puissante,<br>Elle redonne vie à tous ceux qui croient.<br><br><span style=''padding-left:15px''></span>Dites à ceux qui ont le coeur troublé:<br><span style=''padding-left:15px''></span>Voici Jésus, il vient pour vous sauver.<br><span style=''padding-left:15px''></span>Oui, reprenez courage et levez-vous !<br><br>Prends courage, Jésus te pardonne.<br>Il a le pouvoir d''ôter les péchés<br>Car il s''adresse à toi, il te dit : "Crois en moi !"<br>Sa parole est pleine de grâce,<br>Elle retentit en tous ceux qui croient.<br>'),
(448, NULL, 'Oui je regarde', '361', 'Ph 3', 'Oui, je regarde toute chose<br>Comme une perte et non un gain.<br>Ces vaines gloires je dépose<br>Car je connais un plus grand bien.<br>Quel est ce bien, ce bien suprême<br>Pour qui je veux vivre ici-bas ?<br>C’est le Seigneur Jésus qui m’aime,<br>Qui a donné sa vie pour moi.<br><br><span style=''padding-left:15px''></span>Comment répondre à cette grâce<br><span style=''padding-left:15px''></span>Qui m’a sauvé, qui m’a saisi ?<br><span style=''padding-left:15px''></span>Je vais, je cours, rien ne me lasse<br><span style=''padding-left:15px''></span>Car le Seigneur est mon appui.<br><span style=''padding-left:15px''></span>Je vais, je cours, laissant le reste<br><span style=''padding-left:15px''></span>Pour remporter enfin le prix,<br><span style=''padding-left:15px''></span>Prix de l’appel, l’appel céleste<br><span style=''padding-left:15px''></span>De notre Dieu en Jésus-Christ.<br><br>Caché en lui, c’est sa justice<br>Que Dieu me donne par la foi.<br>Je suis lavé de tous mes vices<br>Et de mes fautes d’autrefois.<br>Ô je voudrais mieux le connaître,<br>Mon coeur répond à son appel,<br>Afin de suivre aussi mon maître<br>Quand s’ouvrira enfin le ciel.'),
(449, NULL, 'Notre cité n''est pas sur terre', '362', 'Ph 3;Ep 1;2 Pi 3', 'Notre cité n’est pas sur terre<br>Mais dans les cieux près du Seigneur.<br>Nous attendons du sanctuaire<br>Jésus notre Sauveur.<br>Quand il viendra dans sa victoire,<br>Par son pouvoir il changera<br>Nos corps de chair en corps de gloire,<br>L’humiliation en chant de joie.<br><br>Que l’Eternel de gloire sans cesse<br>Ouvre les yeux de notre coeur,<br>Et qu’il accorde la sagesse<br>À tous ses serviteurs,<br>Oui, pour connaître l’espérance<br>Qui à l’appel est attachée,<br>Pour que nous voyions la puissance<br>De Jésus-Christ ressuscité.<br><br>Nous attendons parmi les frères<br>L’avènement du jour de Dieu.<br>Lorsque disparaîtra la terre<br>S’enflammeront les cieux.<br>Là s’accomplira la promesse,<br>Le nouveau ciel apparaîtra.<br>Nous verrons la cité céleste<br>Où la justice habitera, habitera.');
INSERT INTO `hymn` (`id`, `musical_part_id`, `title`, `num`, `ref`, `lyrics`) VALUES
(450, NULL, 'Éternel, qui pourrait subsister', '363', 'Ps 130', 'Éternel, qui pourrait subsister devant toi<br>Si tu gardais le souvenir des fautes ?<br>Éternel, qui pourrait se tenir devant toi<br>Si tu te souvenais de nos péchés ?<br> <br><span style=''padding-left:15px''></span>Espère en l’Éternel, ô mon âme,<br><span style=''padding-left:15px''></span>C’est en lui que se trouve le pardon.<br><span style=''padding-left:15px''></span>Oui, je m’attends à lui,<br><span style=''padding-left:15px''></span>J’espère en sa parole,<br><span style=''padding-left:15px''></span>C’est de lui que viendra ma délivrance.<br><br>Qui est semblable à toi, ô Dieu de toute grâce,<br>Et qui prends ton plaisir à pardonner ?<br>Car au fond de la mer tu jetteras nos fautes,<br>Oui, tu témoigneras de ta bonté.'),
(451, NULL, 'Je suis l''Éternel ton Dieu', '364', 'Ex 15;Ml 4', 'Je suis l’Éternel ton Dieu qui te guérit.<br>Je suis l’Éternel ton Dieu qui te guérit.<br>Sois bien attentif à la voix de ton Dieu<br>Et suis le chemin qui est droit à ses yeux.<br>Je suis l’Éternel ton Dieu qui te guérit.<br><br>Pour les hommes qui me craignent,<br>Le soleil de la justice<br>Paraîtra, et sous mes ailes<br>La guérison sera propice.<br><br>Je te garderai, je veux te secourir,<br>Pour te protéger, t’aimer et te guérir.<br>Mais sois attentif à mes commandements,<br>Apprends à aimer toute ma volonté.<br>Je suis l’Éternel ton Dieu qui te guérit.'),
(452, NULL, 'Vers Jésus mon Sauveur', '365', '', 'Vers Jésus mon Sauveur, je lève les yeux<br>Pour contempler celui qui, du haut des cieux,<br>Intercède pour moi auprès de son père<br>Afin de détacher mes yeux de la terre.<br><br><span style=''padding-left:15px''></span>Auprès de toi j''ai une place qui m’attend,<br><span style=''padding-left:15px''></span>Auprès de toi, le Père dont je suis l’enfant<br><span style=''padding-left:15px''></span>Me recevra car tu as tout payé pour moi.<br><span style=''padding-left:15px''></span>Jésus je t’aime, tu es le roi des rois.<br><br>L’horizon s’assombrit, tu es toujours là,<br>Détachant mes regards des choses d’en bas<br>En les tournant vers toi qui combats pour moi<br>Afin de me garder toujours dans la foi.'),
(453, NULL, 'Maintenant Seigneur', '366', 'Lc 2', 'Maintenant Seigneur tu laisses ton serviteur<br>S’en aller en paix selon ta parole,<br>Car mes yeux ont vu, oui, mes yeux ont vu ton salut<br>(Car mes yeux ont vu ton salut)<br>Que tu as préparé pour être <br>Lumière pour éclairer les nations,<br>(Oh ! Lumière pour éclairer les nations)<br>Gloire de ton peuple.<br>Lumière pour éclairer les nations,<br>(Oh ! Lumière pour éclairer les nations)<br>Gloire de ton peuple.<br>Maintenant Seigneur tu laisses ton serviteur<br>(Maintenant je vais en paix)<br>S’en aller en paix car mes yeux ont vu<br>(Oui, en paix car mes yeux ont vu)<br>Ton salut.'),
(454, NULL, 'Les apôtres', '367', '', 'Les apôtres, inspirés par l’Esprit,<br>Ont transmis une foi pure.<br>Garde-nous, ô Seigneur Jésus-Christ,<br>D’en oublier la source pure.<br><br><span style=''padding-left:15px''></span>La parole, Seigneur, celle que tu as donnée,<br><span style=''padding-left:15px''></span>Est pour nous le trésor, la perle inestimable.<br><span style=''padding-left:15px''></span>Jésus notre Sauveur est le verbe révélé<br><span style=''padding-left:15px''></span>Pour qui nous renonçons<br><span style=''padding-left:15px''></span>À tous nos biens périssables.<br><br>Dans la joie mais aussi dans les pleurs<br>Nous proclamons ta parole<br>Dans la foi, la soumission du cœur,<br>Nous détournant de nos idoles.<br><br>À ta gloire, ô Père, tu créas<br>L’univers et notre terre.<br>Un jour vient où tout disparaîtra,<br>Mais ta parole est éternelle.'),
(455, NULL, 'Je te louerais au jour de ma détresse', '368', 'Ps 143', 'Je te louerai au jour de ma détresse,<br>Je te louerai pour ta fidélité.<br>Ô Dieu, écoute ma prière,<br>Seigneur, j’espère en ta bonté.<br>Tu es mon Dieu, mon unique espérance,<br>Fais-moi entendre ta voix dès le matin.<br>Maintiens mon coeur en ta présence,<br>Mon Dieu, aide-moi, prends ma main !<br><br><span style=''padding-left:15px''></span>Seigneur, j’élève à toi mon âme,<br><span style=''padding-left:15px''></span>Délivre-moi de l’ennemi<br><span style=''padding-left:15px''></span>(1) Qui rôde et qui poursuit mon âme<br><span style=''padding-left:15px''></span>Cherchant à dévorer ma vie.<br><span style=''padding-left:15px''></span>(2) C’est toi l’asile de mon âme,<br><span style=''padding-left:15px''></span>Oui, c’est toi qui me rends la vie. <br><br>Auprès de toi je cherche mon asile.<br>Enseigne-moi à faire ta volonté.<br>Toujours que l’Esprit me conduise,<br>Ô Dieu, sur ton chemin tracé.<br>Quand dans la nuit, mon âme se consume,<br>Et quand mon cœur abattu est troublé,<br>De toi je médite les œuvres,<br>Mon âme espère en ta bonté.'),
(456, NULL, 'Encore un peu', '369', 'Hb 10', '<span style=''padding-left:15px''></span>Encore un peu, un peu de temps,<br><span style=''padding-left:15px''></span>Et le Seigneur qui doit venir viendra.<br><span style=''padding-left:15px''></span>Encore un peu, un peu de temps,<br><span style=''padding-left:15px''></span>Et le Seigneur qui doit venir viendra.<br><br>N’oublie jamais tous ces témoins<br>Qui ont persévéré jusqu’à la fin.<br>Heureux celui qui aura cru,<br>(Oh ! Heureux l’homme)<br>Qui aura cru sans avoir vu.<br><br>Le jour approche, la nuit s’en va,<br>L’étoile du matin est déjà là.<br>Lève les yeux, relève la tête,<br>(Oh ! Lève-toi)<br>Voici qu’arrive enfin le maître.<br><br>Garde la foi, veille toujours,<br>Nourris ton cœur, ta foi de son amour.<br>Garde la foi et prie sans cesse,<br>(Oh ! Prie sans cesse)<br>Nourris ton cœur de ses promesses.');
